INSERT INTO [t8376] VALUES (1434,'ثم دخلت سنة أربع وثمانين ومائتين في المحرم منا دخل رأس رافع بن هرثمة إلى بغداد فأمر الخليفة بنصبه في الجانب الشرقي إلى الظهر، ثم بالجانب الغربي إلى الليل (١) .','4100','1341','0');
