INSERT INTO [t8376] VALUES (1436,'ثم دخلت سنة خمس وثمانين ومائتين فيها وثب صالح بن مدرك الطائي على الحجاج بالاجفر فأخذ أموالهم ونساءهم، يقال: إنه أخذ منهم ما قيمته ألف (١) ألف دينار.','4103','1341','0');
