INSERT INTO [t8376] VALUES (1458,'ثم دخلت سنة خمس وتسعين ومائتين فيها كانت المفاداة بين المسلمين والروم، وكان من جملة من استنقذ من أيدي الروم من نساء ورجال نحوا من ثلاثة آلاف نسمة، وفي المنتصف من صفر منها كانت وفاة إسماعيل بن أحمد الساماني أمير خراسان وما وراء النهر، وقد كان عاقلا عادلا حسن السيرة في رعيته حليما كريما.','4130','1341','0');
INSERT INTO [t8376] VALUES (1459,'وفاة الخليفة المكتفي بالله أبو محمد بن المعتضد','4130','1458','0');
