INSERT INTO [t8376] VALUES (1475,'وفيها توفي','4145','1474','0');
INSERT INTO [t8376] VALUES (1476,'ثم دخلت سنة ثلثمائة من الهجرة فيها كثر ماء دجلة وتراكمت الامطار ببغداد، وتناثرت نجوم كثيرة في ليلة الاربعاء لسبع بقين من جمادى الآخرة.','4147','1341','0');
