INSERT INTO [t8376] VALUES (1484,'وفيها توفي','4159','1483','0');
INSERT INTO [t8376] VALUES (1485,'وفيها توفي','4160','1483','0');
INSERT INTO [t8376] VALUES (1486,'ثم دخلت سنة سبع وثلاثمائة في صفر منها وقع حريك بالكرخ في الباقلانتين، هلك فيه خلق كثير من الناس.','4162','1341','0');
INSERT INTO [t8376] VALUES (1487,'وفيها توفي','4162','1486','0');
INSERT INTO [t8376] VALUES (1488,'ثم دخلت سنة ثمان وثلاثمائة فيها غلت الاسعار في هذه السنة ببغداد فاضطربت العامة وقصدوا دار حامد بن العباس الذي ضمن براثى من الخليفة فغلت الاسعار بسبب ذلك، وعدوا في ذلك اليوم - وكان يوم الجمعة - على الخطيب، فمنعوه الخطبة وكسروا المنابر وقتلوا الشرطة وحرقوا جسورا كثيرة، فأمر الخليفة بقتال العامة ثم نقض الضمان الذي كان حامد بن العباس ضمنه فانحطت الاسعار، وبيع الكر بناقص خسمة دنانير، فطابت أنفس الناس بذلك وسكنوا (٤) .','4163','1341','0');
