INSERT INTO [t8376] VALUES (1498,'أبو بشر الدولابي','4178','1494','0');
INSERT INTO [t8376] VALUES (1499,'أبو جعفر بن جرير الطبري','4178','1494','0');
INSERT INTO [t8376] VALUES (1500,'ثم دخلت سنة إحدى عشرة وثلثمائة فيها دخل أبو طاهر سليمان بن أبي سعيد الجنابي أمير القرامطة في ألف وسبعمائة فارس إلى البصرة ليلا، نصب السلالم الشعر في سورها فدخلها قهرا وفتحوا أبوابها وقتلوا من لقوه من أهلها، وهرب أكثر الناس فألقوا أنفسهم في الماء فغرق كثير منهم، ومكث بها سبعة عشر يوما يقتل ويأسر من نسائها وذراريها، ويأخذ ما يختار من أموالها.','4181','1341','0');
INSERT INTO [t8376] VALUES (1501,'وفيها توفي','4181','1500','0');
INSERT INTO [t8376] VALUES (1502,'الخلال أحمد بن محمد بن هاون أبو بكر الخلال','4181','1500','0');
INSERT INTO [t8376] VALUES (1503,'وفيها توفي عمر بن محمد بجير البجيري','4182','1500','0');
INSERT INTO [t8376] VALUES (1504,'ثم دخلت سنة ثنتي عشرة وثلثمائة في المحرم منها اعترض القرمطي أبو طاهر الحسين بن أبي سعيد الجنابي لعنه الله، ولعن أباه.','4183','1341','0');
INSERT INTO [t8376] VALUES (1505,'وفيها توفي','4183','1504','0');
INSERT INTO [t8376] VALUES (1506,'وفيها توفي','4184','1504','0');
INSERT INTO [t8376] VALUES (1507,'ثم دخلت سنة ثلاث عشرة وثلاثمائة قال ابن الجوزي: في ليلة بقيت من المحرم انقض كوكب من ناحية الجنوب إلى الشمال قبل مغيب الشمس، فأضاءت الدنيا منه وسمع له صوت كصوت الرعد الشديد.','4186','1341','0');
INSERT INTO [t8376] VALUES (1508,'ثم دخلت سنة أربع عشرة وثلاثمائة فيها كتب ملك الروم، وهو الدمستق لعنه الله، إلى أهل السواحل أن يحملوا إليه الخراج، فأبوا عليه فركب إليهم في جنوده في أول هذه السنة، فعاث في الارض فسادا، ودخل ملطية فقتل من أهلها خلقا وأسر وأقام بها ستة عشر يوما، وجاء أهلها إلى بغداد يستنجدون الخليفة عليه.','4187','1341','0');
INSERT INTO [t8376] VALUES (1509,'وفيها توفي','4187','1508','0');
INSERT INTO [t8376] VALUES (1510,'ثم دخلت سنة خمس عشرة وثلاثمائة في صفر منها كان قدوم علي بن عيسى الوزير من دمشق، وقد تلقاه الناس إلى أثناء الطريق، فمنهم من لقيه إلى الانبار، ومنهم دون ذلك.','4188','1341','0');
INSERT INTO [t8376] VALUES (1511,'وفيها توفي','4188','1510','0');
INSERT INTO [t8376] VALUES (1512,'وفيها توفي','4190','1510','0');
INSERT INTO [t8376] VALUES (1513,'وفيها توفي','4191','1510','0');
INSERT INTO [t8376] VALUES (1514,'ثم دخلت سنة ست عشرة وثلاثمائة فيها عاث أبو طاهر سليمان بن أبي سعيد الجنابي القرمطي في الارض فسادا، حاصر الرحبة فدخلها قهرا وقتل من أهلها خلقا، وطلب منه أهل قرقيسيا الامان فأمنهم، وبعث سراياه إلى ما حولها من الاعراب فقتل منهم خلقا، حتى صار الناس إذا سمعوا بذكره يهربون من سماع اسمه، وقدر على الاعراب إمارة يحملونها إلى هجر في كل سنة، عن كل رأس ديناران (١) .','4192','1341','0');
INSERT INTO [t8376] VALUES (1515,'ثم دخلت سنة سبع عشرة وثلاثمائة فيها كان خلع المقتدر وتولية القاهر محمد بن المعتضد بالله: في المحرم منها اشتدت الوحشة بين مؤنس الخادم والمقتدر بالله، وتفاقم الحال وآل إلى أن اجتمعوا على خلع المقتدر وتولية القاهر محمد بن المعتضد، فبايعوه بالخلافة وسلموا عليه بها، ولقبوه القاهر بالله.','4193','1341','0');
INSERT INTO [t8376] VALUES (1516,'وفيها توفي','4193','1515','0');
INSERT INTO [t8376] VALUES (1517,'ذكر أخذ القرامطة الحجر الاسود إلى بلادهم','4195','1515','0');
INSERT INTO [t8376] VALUES (1518,'وفيها توفي','4197','1515','0');
INSERT INTO [t8376] VALUES (1519,'ثم دخلت سنة ثمان عشرة وثلثمائة فيها عزل الخليفة المقتدر وزيره أبا علي بن مقلة، وكانت مدة وزارته سنتين وأربعة أشهر وثلاثة أيام، واستوزر مكانه سليمان بن الحسن بن مخلد، وجعل علي بن عيسى ناظرا معه.','4199','1341','0');
INSERT INTO [t8376] VALUES (1520,'وفيها توفي','4200','1519','0');
INSERT INTO [t8376] VALUES (1521,'ثم دخلت سنة تسع عشرة وثلاثمائة في المحرم منها دخل الحجيج بغداد، وقد خرج مؤنس الخادم إلى الحج فيها في جيش كثيف،','4201','1341','0');
INSERT INTO [t8376] VALUES (1522,'ثم دخلت سنة عشرين وثلاثمائة من الهجرة فيها كان مقتل المقتدر بالله الخليفة، وكان سبب ذلك أن مؤنسا الخادم خرج من بغداد في المحرم منها مغاضبا الخليفة في ممالكيه وحشمه، متوجها نحو الموصل، ورد من أثناء الطريق مولاه يسرى (١) إلى المقتدر ليستعلم له أمره، وبعث معه رسالة يخاطب بها أمير المؤمنين ويعاتبه في أشياء.','4203','1341','0');
INSERT INTO [t8376] VALUES (1523,'وفيها توفي','4203','1522','0');
INSERT INTO [t8376] VALUES (1524,'ترجمة المقتدر بالله','4205','1522','0');
INSERT INTO [t8376] VALUES (1525,'خلافة القاهر لما قتل المقتدر بالله','4206','1522','0');
INSERT INTO [t8376] VALUES (1526,'وفيها توفي','4207','1522','0');
INSERT INTO [t8376] VALUES (1527,'ثم دخلت سنة إحدى وعشرين وثلثمائة في صفر منها أحضر القاهر رجلا كان يقطع الطريق فضرب بين يديه ألف سوط، ثم ضربت عنقه وقطع أيدي أصحابه وأرجلهم.','4208','1341','0');
INSERT INTO [t8376] VALUES (1528,'وفيها توفي','4211','1527','0');
INSERT INTO [t8376] VALUES (1529,'ثم دخلت سنة ثنتين وعشرين وثلثمائة فيها قصد ملك الروم (٢) ملطية في خمسين ألفا فحاصرهم ثم أعطاهم الامان حتى تمكن منهم، فقتل منهم خلقا كثيرا وأسر ما لا يحصون كثرة، فإنا لله وإنا إليه راجعون.','4213','1341','0');
INSERT INTO [t8376] VALUES (1530,'ذكر خلع القاهر وسمل عينيه وعذابه','4214','1529','0');
