INSERT INTO [t8376] VALUES (1535,'ثم دخلت سنة ثلاث وعشرين وثلثمائة فيها أحضر ابن شنبوذ (٣) المقري فأنكر عليه جماعة من الفقهاء والقراء حروفا انفرد بها فاعترف','4218','1341','0');
