INSERT INTO [t8376] VALUES (1655,'ثم دخلت سنة سبعين وثلثمائة فيها ورد الصاحب بن عباد من جهة مؤيد الدولة إلى أخيه عضد الدولة فتلقاه عضد الدولة إلى ظاهر البلد وأكرمه وأمر الاعيان باحترامه، وخلع عليه وزاده في إقطاعه، ورد معه هدايا كثيرة.','4350','1341','0');
INSERT INTO [t8376] VALUES (1656,'وممن توفي فيها','4350','1655','0');
INSERT INTO [t8376] VALUES (1657,'ثم دخلت سنة إحدى وسبعين وثلثمائة في ربيع الاول منها وقع حريق عظيم بالكرخ، وفيها سرق شئ نفيس لعضد الدولة فتعجب الناس من جرأة من سرقه مع شدة هيبة عضد الدولة، ثم مع هذا اجتهدوا كل الاجتهاد فلم يعرفوا من أخذ.','4351','1341','0');
INSERT INTO [t8376] VALUES (1658,'وممن توفي فيها','4351','1657','0');
INSERT INTO [t8376] VALUES (1659,'ثم دخلت سنة ثنتين وسبعين وثلثمائة قال ابن الجوزي: في المحرم منها جرى الماء الذي ساقه عضد الدولة إلى داره وبستانه.','4353','1341','0');
INSERT INTO [t8376] VALUES (1660,'وفيها توفي','4353','1659','0');
INSERT INTO [t8376] VALUES (1661,'ثم دخلت سنة ثلاث وسبعين وثلثمائة فيها غلت الاسعار ببغداد حتى بلغ الكر من الطعام إلى أربع آلاف وثمانمائة، ومات كثير من الناس جوعا، وجافت الطرقات من الموتى من الجوع، ثم تساهل الحال في ذي الحجة منها، وجاء الخبر بموت مؤيد الدولة بن ركن الدولة، وأن أبا القاسم بن عباد الوزير بعث إلى أخيه فخر الدولة','4356','1341','0');
INSERT INTO [t8376] VALUES (1662,'وممن توفي فيها','4356','1661','0');
INSERT INTO [t8376] VALUES (1663,'ثم دخلت سنة أربع وسبعين ثلثمائة فيها جرى الصلح بين صمصامة وبين عمه فخر الدولة، فأرسل الخليفة لفخر الدولة خلعا','4357','1341','0');
INSERT INTO [t8376] VALUES (1664,'فيها كانت وفاة','4357','1663','0');
INSERT INTO [t8376] VALUES (1665,'ثم دخلت سنة خمس وسبعين وثلثمائة فيها خلع الخليفة على صمصامة الدولة وسوره وطوقه وأركب على فرس بسرج ذهب، وبين يديه جنيب مثله، وفيها ورد الخبر بأن اثنين من سادة القرامطة وهما إسحاق وجعفر، دخلا الكوفة في حفل عظيم فانزعجت النفوس بسبب ذلك، وذلك لصرامتهما وشجاعتهما، ولان عضد الدولة مع شجاعته كان يصانعهما، وأقطعهما أراضي من أراضي واسط، وكذلك عز الدولة من قبله أيضا.','4358','1341','0');
INSERT INTO [t8376] VALUES (1666,'وفيها توفي','4358','1665','0');
INSERT INTO [t8376] VALUES (1667,'وفيها توفي','4359','1665','0');
INSERT INTO [t8376] VALUES (1668,'ثم دخلت سنة ست وسبعين وثلثمائة قال ابن الجوزي: في محرمها كثرت الحيات في بغداد فهلك بسبب ذلك خلق كثير.','4360','1341','0');
INSERT INTO [t8376] VALUES (1669,'ثم دخلت سنة سبع وسبعين وثلثمائة','4361','1341','0');
INSERT INTO [t8376] VALUES (1670,'وفيها توفي','4361','1669','0');
INSERT INTO [t8376] VALUES (1671,'وممن توفي فيها','4361','1669','0');
INSERT INTO [t8376] VALUES (1672,'ثم دخلت سنة ثمان وسبعين وثلثمائة','4362','1341','0');
INSERT INTO [t8376] VALUES (1673,'وفيها توفي','4362','1672','0');
INSERT INTO [t8376] VALUES (1674,'ثم دخلت سنة تسع وسبعين وثلثمائة','4363','1341','0');
INSERT INTO [t8376] VALUES (1675,'فيها كانت وفاة شرف الدولة بن عضد الدولة بن بويه الديلمي','4363','1674','0');
INSERT INTO [t8376] VALUES (1676,'وفيها توفي','4364','1674','0');
INSERT INTO [t8376] VALUES (1677,'ثم دخلت سنة ثمانين وثلثمائة من الهجرة فيها قلد الشريف أبو أحمد الحسن بن موسى الموسوي نقابة الاشراف الطالبيين والنظر في المظالم وإمرة الحاج، وكتب عهده بذلك واستخلف ولداه المرتضى أبو القاسم والرضي أبو الحسين على النقابة وخلع عليهما.','4365','1341','0');
INSERT INTO [t8376] VALUES (1678,'ثم دخلت سنة إحدى وثمانين وثلثمائة فيها كان القبض على الخليفة الطائع لله وخلافة القادر بالله أبي العباس أحمد بن الامير إسحاق بن المقتدر بالله، وكان ذلك في يوم السبت التاسع عشر من شعبان منها، وذلك أنه جلس الخليفة على عادته في الرواق وقعد الملك بهاء الدولة على السرير، ثم أرسل من اجتذب الخليفة','4365','1341','0');
INSERT INTO [t8376] VALUES (1679,'وفيها توفي','4365','1678','0');
INSERT INTO [t8376] VALUES (1680,'وممن توفي فيها','4367','1678','0');
INSERT INTO [t8376] VALUES (1681,'ثم دخلت سنة ثنتين وثمانين وثلثمائة','4368','1341','0');
INSERT INTO [t8376] VALUES (1682,'ثم دخلت سنة ثلاث وثمانين وثلثمائة فيها أمر القادر بالله بعمارة مسجد الحربية وكسوته، وأن يجري مجرى الجوامع في الخطب وغيرها وذلك بعد أن استفتى العلماء في جواز ذلك.','4369','1341','0');
INSERT INTO [t8376] VALUES (1683,'وممن توفي فيها','4369','1682','0');
INSERT INTO [t8376] VALUES (1684,'ثم دخلت سنة أربع وثمانين وثلثمائة فيها عظم الخطب بأمر العيارين، عاثوا ببغداد فسادا وأخذوا الاموال والعملات الثقال ليلا ونهارا، وحرقوا مواضع كثيرة، وأخذوا من الاسواق الجبايات، وتطلبهم الشرط فلم يفد ذلك شيئا ولا فكروا في الدولة، بل استمروا على ما هم عليه من أخذ الاموال، وقتل الرجال، وإرعاب النساء والاطفال، في سائر المحال.','4370','1341','0');
INSERT INTO [t8376] VALUES (1685,'وفيها توفي','4370','1684','0');
INSERT INTO [t8376] VALUES (1686,'وفيها توفي','4371','1684','0');
INSERT INTO [t8376] VALUES (1687,'ثم دخلت سنة خمس وثمانين وثلثمائة فيها استوزر ابن ركن الدولة بن بويه أبا العباس أحمد بن إبراهيم الضبي، الملقب بالكافي، وذلك بعد وفاة الصاحب إسماعيل بن عباد، وكان من مشاهير الوزراء.','4372','1341','0');
INSERT INTO [t8376] VALUES (1688,'وممن توفي فيها','4372','1687','0');
INSERT INTO [t8376] VALUES (1689,'ثم دخلت سنة ست وثمانين وثلثمائة في محرمها كشف أهل البصرة عن قبر عتيق فإذا هم بميت طري عليه ثيابه وسيفه، فطنوه الزبير بن العوام، فأخرجوه وكفنوه ودفنوه واتخذوا عند قبره مسجدا، ووقف عليه أوقاف كثيرة، وجعل عنده خدام وقوام وفرش وتنوير.','4378','1341','0');
INSERT INTO [t8376] VALUES (1690,'وفيها توفي','4378','1689','0');
INSERT INTO [t8376] VALUES (1691,'وفيها توفي','4379','1689','0');
INSERT INTO [t8376] VALUES (1692,'ثم دخلت سنة سبع وثمانين وثلثمائة فيها توفي فخر الدولة أبو الحسن علي بن ركن الدولة بن بويه، وأقيم ولده رستم في الملك مكانه، وكان عمره أربع سنين، وقام خواص أبيه بتدبير الملك في الرعايا.','4380','1341','0');
INSERT INTO [t8376] VALUES (1693,'وممن توفي فيها','4380','1692','0');
INSERT INTO [t8376] VALUES (1694,'ثم دخلت سنة ثمان وثمانين وثلثمائة قال ابن الجوزي: في ذي الحجة منها سقط في بغداد برد عظيم، بحيث جمد الماء في الحمامات، وبول الدواب في الطرقات.','4384','1341','0');
INSERT INTO [t8376] VALUES (1695,'وممن توفي فيها','4384','1694','0');
INSERT INTO [t8376] VALUES (1696,'ثم دخلت سنة تسع وثمانين وثلثمائة فيها قصد محمود بن سبكتكين بلاد خراسان فاستلب ملكها من أيدي السامانية، وواقعهم مرات متعددة في هذه السنة وما قبلها، حتى أزال اسمهم ورسمهم عن البلاد بالكلية، وانقرضت دولتهم بالكلية، ثم صمد لقتال ملك الترك بما وراء النهر، وذلك بعد موت الخاقان الكبير الذي يقال له فائق، وجرت له معهم حروب وخطوب.','4386','1341','0');
INSERT INTO [t8376] VALUES (1697,'وممن توفي فيها','4386','1696','0');
INSERT INTO [t8376] VALUES (1698,'ثم دخلت سنة تسعين وثلثمائة من الهجرة النبوية فيها ظهر بأرض سجستان معدن من ذهب كانوا يحفرون فيه مثل الآبار، ويخرجون منه ذهبا أحمر.','4387','1341','0');
INSERT INTO [t8376] VALUES (1699,'وممن توفي فيها','4387','1698','0');
INSERT INTO [t8376] VALUES (1700,'ثم دخلت سنة إحدى وتسعين وثلثمائة','4390','1341','0');
INSERT INTO [t8376] VALUES (1701,'وفيها توفي','4390','1700','0');
INSERT INTO [t8376] VALUES (1702,'ثم دخلت سنة ثنتين وتسعين وثلثمائة في محرمها غزا يمين الدولة محمود بن سبكتكين بلاد اله','4392','1341','0');
INSERT INTO [t8376] VALUES (1703,'وممن توفي فيها','4392','1702','0');
INSERT INTO [t8376] VALUES (1704,'ثم دخلت سنة ثلاث وتسعين وثلثمائة وفيها كانت وفاة الطائع لله على ما سنذكره وفيها منع عميد الجيوش الشيعة من النحو على الحسين في يوم عاشوراء، ومنع جهلة السنة بباب البصرة وباب الشعير من النوح على مصعب بن الزبير بعد ذلك بثمانية أيام، فامتنع الفريقان ولله الحمد والمنة.','4394','1341','0');
INSERT INTO [t8376] VALUES (1705,'وممن توفي فيها','4394','1704','0');
INSERT INTO [t8376] VALUES (1706,'فيها كانت وفاة','4394','1704','0');
INSERT INTO [t8376] VALUES (1707,'ثم دخلت سنة أربع وتسعين وثلثمائة وفيها ولى بهاء الدولة الشريف أبا أحمد الحسين بن أحمد بن موسى الموسوي، قضاء القضاة والحج والمظالم، ونقابة الطالبيين، ولقب بالطاهر الاوحد، ذوي المناقب، وكان التقليد له بسيراج، فلما وصل الكتاب إلى بغداد لم يأذن له الخليفة القادر في قضاء القضاة، فتوقف حاله بسبب ذلك.','4395','1341','0');
INSERT INTO [t8376] VALUES (1708,'ثم دخلت سنة خمس وتسعين وثلثمائة فيها عاد مهذب الدولة إلى البطيحة ولم يمانعه ابن واصل، وقرر عليه في كل سنة لبهاء الدولة خمسين ألف دينار.','4397','1341','0');
INSERT INTO [t8376] VALUES (1709,'وممن توفي فيها','4397','1708','0');
INSERT INTO [t8376] VALUES (1710,'ثم دخلت سنة ست وتسعين وثلثمائة قال ابن الجوزي: في ليلة الجمعة مستهل شعبان طلع نجم يشبه الزهرة في كبره وكثرة ضوئه عن يسار القبلة يتموج، وله شعاع على الارض كشعاع القمر.','4398','1341','0');
INSERT INTO [t8376] VALUES (1711,'وممن توفي فيها','4398','1710','0');
INSERT INTO [t8376] VALUES (1712,'ثم دخلت سنة سبع وتسعين وثلثمائة فيها كان خروج أبي ركوة على الحاكم العبيدي صاحب مصر.','4400','1341','0');
INSERT INTO [t8376] VALUES (1713,'وفيها قتل أبو العباس بن واصل','4400','1712','0');
INSERT INTO [t8376] VALUES (1714,'ثم دخلت سنة ثمان وتسعين وثلثمائة فيها غزا يمين الدولة محمود بن سبكتكين بلاد الهند، ففتح حصونا كثيرة، وأخذ أموالا جزيلة وجواهر نفيسة، وكان في جملة ما وجد بيت طوله ثلاثون ذراعا وعرضه خمسة عشر ذراعا مملوءا فضة، ولما رجع إلى غزنة بسط هذه الاموال كلها في صحن داره وأذن لرسل الملك فدخلوا عليه فرأوا ما بهرهم وهالهم، وفي يوم الاربعاء الحادي عشر من ربيع الآخر وقع ببغداد ثلج عظيم، بحيث بقي على وجه الارض ذراعا ونصفا (١) ، ومكث أسبوعا لم يذب (٢) وبلغ سقوطه إلى تكريت والكوفة وعبادان','4401','1341','0');
INSERT INTO [t8376] VALUES (1715,'وفيها توفي','4401','1714','0');
INSERT INTO [t8376] VALUES (1716,'وممن توفي فيها','4403','1714','0');
INSERT INTO [t8376] VALUES (1717,'ثم دخلت سنة تسع وتسعين وثلثمائة','4404','1341','0');
INSERT INTO [t8376] VALUES (1718,'فيها قتل علي بن ثمال نائب الرحبة','4404','1717','0');
INSERT INTO [t8376] VALUES (1719,'وممن توفي فيها','4405','1717','0');
INSERT INTO [t8376] VALUES (1720,'ثم دخلت سنة أربعمائة من الهجرة في ربيع الآخر نقصت دجلة نقصا كثيرا، حتى ظهرت جزائر لم تغرق، وامتنع سير السفن في أعاليها من أذنة (٢) والراشدية، فأمر بكرى تلك الاماكن، وفيها كمل السور على مشهد أمير المؤمنين علي عليه السلام الذي بناه أبو إسحاق الارجاني، وذلك أن أبا محمد بن سهلان مرض فنذر إن عوفي ليبنينه فعوفي،.','4406','1341','0');
INSERT INTO [t8376] VALUES (1721,'وممن توفي فيها','4406','1720','0');
INSERT INTO [t8376] VALUES (1722,'ثم دخلت سنة إحدى وأربعمائة في يوم الجمعة الرابع من المحرم منها خطب بالموصل للحاكم العبيدي عن أمر صاحبها قرواش بن مقلد أبي منيع، وذلك لقهره رعيته، وقد سرد ابن الجوزي صفة الخطبة بحروفها.','4408','1341','0');
INSERT INTO [t8376] VALUES (1723,'وممن توفي فيها','4408','1722','0');
INSERT INTO [t8376] VALUES (1724,'وفيها توفي','4412','1722','0');
INSERT INTO [t8376] VALUES (1725,'ثم دخلت سنة ثلاث وأربعمائة في سادس عشر محرمها قلد الشريف الرضي أبو الحسن الموسوي نقابة الطالبيين في سائر الممالك وقرئ تقليده في دار الوزير فخر الملك، بمحضر الاعيان، وخلع عليه السواد، وهو أول طالبي خلع عليه السواد.','4413','1341','0');
INSERT INTO [t8376] VALUES (1726,'وفيها توفي','4414','1725','0');
INSERT INTO [t8376] VALUES (1727,'وفيها توفي','4418','1725','0');
INSERT INTO [t8376] VALUES (1728,'وممن توفي فيها','4419','1725','0');
INSERT INTO [t8376] VALUES (1729,'ج ١٢','4423','0','0');
INSERT INTO [t8376] VALUES (1730,'ثم دخلت سنة ست وأربعمائة','4425','1729','0');
INSERT INTO [t8376] VALUES (1731,'وفيها توفي','4425','1729','0');
INSERT INTO [t8376] VALUES (1732,'ثم دخلت سنة سبع وأربعمائة في ربيع الاول منها، احترق مشهد الحسين بن علي [بكربلاء] وأروقته، وكان سبب ذلك أن القومة أشعلوا شمعتين كبيرتين فمالتا في الليل على التازير، ونفذت النار منه إلى غيره حتى كان ما كان.','4428','1729','0');
INSERT INTO [t8376] VALUES (1733,'ثم دخلت سنة ثمان وأربعمائة فيها وقعت فتنة عظيمة بين أهل السنة والروافض ببغداد، قتل فيها خلق كثير من الفريقين.','4429','1729','0');
INSERT INTO [t8376] VALUES (1734,'وفيها توفي','4429','1733','0');
INSERT INTO [t8376] VALUES (1735,'وفيها توفي','4430','1733','0');
INSERT INTO [t8376] VALUES (1736,'وفيها توفي من الاعيان','4431','1733','0');
INSERT INTO [t8376] VALUES (1737,'ثم دخلت سنة عشر وأربعمائة فيها ورد كتاب يمين الدولة محمود بن سبكتكين، يذكر فيه ما افتتحه من بلاد الهند في السنة الخالية، وفيه أنه دخل مدينة فيها ألف قصر مشيد، وألف بيت للاصنام.','4432','1729','0');
INSERT INTO [t8376] VALUES (1738,'ثم دخلت سنة إحدى عشرة وأربعمائة فيها عدم الحاكم بمصر، وذلك أنه لما كان ليلة الثلاثاء لليلتين بقيتا من شوال فقد الحاكم بن المعز الفاطمي صاحب مصر، فاستبشر المؤمنون والمسلمون بذلك، وذلك لانه كان جبارا عنيدا، وشيطانا مريدا.','4433','1729','0');
INSERT INTO [t8376] VALUES (1739,'وممن توفي فيها','4433','1738','0');
INSERT INTO [t8376] VALUES (1740,'ثم دخلت سنة اثنتي عشرة وأربعمائة فيها تولى القاضي أبو جعفر أحمد بن محمد السمناني لحسبة والمواريث ببغداد، وخلع عليه','4435','1729','0');
INSERT INTO [t8376] VALUES (1741,'وممن توفي فيها','4436','1740','0');
INSERT INTO [t8376] VALUES (1742,'ثم دخلت سنة ثلاث عشرة وأربعمائة فيها جرت كائنة غريبة عظيمة، ومصيبة عامة،','4439','1729','0');
INSERT INTO [t8376] VALUES (1743,'وفيها توفي','4439','1742','0');
INSERT INTO [t8376] VALUES (1744,'وفيها توفي','4440','1742','0');
INSERT INTO [t8376] VALUES (1745,'وفيها توفي','4442','1742','0');
INSERT INTO [t8376] VALUES (1746,'ثم دخلت سنة خمس عشرة وأربعمائة فيها ألزم الوزير جماعة الاتراك والمولدين والشريف المرتضى ونظام الحضرة أبا الحسن الزينبي وقاضي القضاة أبا الحسن بن أبي الشوارب، والشهود، بالحضور لتجديد البيعة لشرف الدولة، فلما','4443','1729','0');
INSERT INTO [t8376] VALUES (1747,'وممن توفي فيها','4444','1746','0');
INSERT INTO [t8376] VALUES (1748,'ثم دخلت سنة ست عشرة وأربعمائة فيها قوي أمر العيارين ببغداد ونهبوا الدور جهرة، وستهانوا بأمر السلطان،','4445','1729','0');
INSERT INTO [t8376] VALUES (1749,'وممن توفي فيها','4446','1748','0');
INSERT INTO [t8376] VALUES (1750,'وفيها توفي','4448','1748','0');
INSERT INTO [t8376] VALUES (1751,'ثم دخلت سنة ثمان عشرة وأربعمائة في ربيع الاول منها وقع برد أهلك شيئا كثيرا من الزروع والثمار، وقتل خلقا كثيرا من','4449','1729','0');
INSERT INTO [t8376] VALUES (1752,'وفيها توفي','4451','1751','0');
INSERT INTO [t8376] VALUES (1753,'ثم دخلت سنة تسع عشرة وأربعمائة فيها وقع بين الجيش وبين جلال الدولة ونهبوا دار وزيره، وجرت له أمور طويلة، آل الحال فيها إلى اتفاقهم على إخراجه من البلد، فهئ له برذون رث، فخرج وفي يده طبر نهارا، فجعلوا لا','4453','1729','0');
INSERT INTO [t8376] VALUES (1754,'وممن توفي فيها','4453','1753','0');
INSERT INTO [t8376] VALUES (1755,'وممن توفي فيها','4456','1753','0');
INSERT INTO [t8376] VALUES (1756,'ثم دخلت سنة إحدى وعشرين وأربعمائة فيها توفي الملك الكبير المجاهد المغازي، فاتح بلاد الهند محمود بن سبكتكين رحمه الله','4457','1729','0');
INSERT INTO [t8376] VALUES (1757,'ذكر من توفي فيها','4458','1756','0');
INSERT INTO [t8376] VALUES (1758,'ثم دخلت سنة اثنتين وعشرين وأربعمائة','4461','1729','0');
INSERT INTO [t8376] VALUES (1759,'فيها كانت وفاة القادر بالله الخليفة','4461','1758','0');
INSERT INTO [t8376] VALUES (1760,'خلافة القائم بالله أبي جعفر عبد الله بن القادر بالله،','4461','1758','0');
INSERT INTO [t8376] VALUES (1761,'فيها كانت وفاة','4461','1758','0');
INSERT INTO [t8376] VALUES (1762,'وفيها توفي','4462','1758','0');
INSERT INTO [t8376] VALUES (1763,'ثم دخلت سنة ثلاث وعشرين وأربعمائة','4463','1729','0');
INSERT INTO [t8376] VALUES (1764,'وفيها توفي','4465','1763','0');
INSERT INTO [t8376] VALUES (1765,'ثم دخلت سنة خمس وعشرين وأربعمائة فيها غزا السلطان مسعود بن محمود بلاد الهند، وفتح حصونا كثيرة، وكان من جملتها أنه حاصر قلعة حصينة (٢) فخرجت من السور عجوز كبيرة ساحرة، فأخذت مكنسة فبلتها ورشتها من ناحية جيش المسلمين، فمرض السلطان تلك الليلة مرضا شديدا، فارتحل عن تلك القلعة، فلما استقل ذاهبا عنها عوفي عافية كاملة، فرجع إلى غزنة سالما.','4466','1729','0');
INSERT INTO [t8376] VALUES (1766,'وممن توفي فيها','4466','1765','0');
INSERT INTO [t8376] VALUES (1767,'ثم دخلت سنة أربع وعشرين وأربعمائة فيها تفاقم الحال بأمر العيارين، وتزايد أمرهم، وأخذوا العملات الكثيرة، وقوي أمر مقدمهم البرجم','4466','1729','0');
INSERT INTO [t8376] VALUES (1768,'وفيها توفي','4467','1767','0');
INSERT INTO [t8376] VALUES (1769,'ثم دخلت سنة ست وعشرين وأربعمائة في محرمها كثر تردد الاعراب في قطع الطرقات إلى حواشي بغداد وما حولها، بحيث كانوا يسلبون النساء ما عليهن، ومن أسروه أخذوا ما معه وطالبوه بفداء نفسه، واستفحل أمر العيارين وكثرت شرورهم، وفي مستهل صفر زادت دجلة بحيث ارتفع الماء على الضياع ذراعين، وسقط من البصرة في مدة ثلاثة نحو من ألفي دار.','4469','1729','0');
INSERT INTO [t8376] VALUES (1770,'وممن توفي فيها','4469','1769','0');
INSERT INTO [t8376] VALUES (1771,'ثم دخلت سنة سبع وعشرين وأربعمائة في المحرم منها تكاملت قنطرة عيسى التي كانت سقطت، وكان الذي ولي مشارفة الانفاق عليها الشيخ أبو الحسين القدوري الحنفي، وفي المحرم وما بعده تفاقم أمر العيارين، وكبسوا الدور وتزايد شرهم جدا.','4471','1729','0');
INSERT INTO [t8376] VALUES (1772,'وفيها توفي','4471','1771','0');
INSERT INTO [t8376] VALUES (1773,'ثم دخلت سنة ثمان وعشرين وأربعمائة فيها خلع الخليفة على أبي تمام محمد بن محمد بن علي الزينبي، وقلده ما كان إلى أبيه من نقابة العباسيين والصلاة.','4472','1729','0');
INSERT INTO [t8376] VALUES (1774,'وممن توفي فيها','4472','1773','0');
INSERT INTO [t8376] VALUES (1775,'وممن توفي فيها','4473','1773','0');
INSERT INTO [t8376] VALUES (1776,'ثم دخلت سنة تسع وعشرين وأربعمائة','4476','1729','0');
INSERT INTO [t8376] VALUES (1777,'وممن توفي فيها','4477','1776','0');
INSERT INTO [t8376] VALUES (1778,'ثم دخلت سنة ثلاثين وأربعمائة فيها التقى الملك مسعود بن محمود، والملك طغرلبك السلجوقي، ومعه أخوه داود، في شعبان، فهزمهما مسعود، وقتل من أصحابهما خلقا كثيرا.','4478','1729','0');
INSERT INTO [t8376] VALUES (1779,'وممن توفي فيها','4478','1778','0');
INSERT INTO [t8376] VALUES (1780,'ثم دخلت سنة إحدى وثلاثين وأربعمائة','4481','1729','0');
INSERT INTO [t8376] VALUES (1781,'وممن توفي فيها','4481','1780','0');
INSERT INTO [t8376] VALUES (1782,'ثم دخلت سنة اثنتين وثلاثين وأربعمائة فيها عظم شأن السلجوقية، وارتفع شأن ملكهم طغرلبك، وأخيه داود','4482','1729','0');
INSERT INTO [t8376] VALUES (1783,'وممن توفي فيها','4483','1782','0');
INSERT INTO [t8376] VALUES (1784,'ثم دخلت سنة ثلاث وثلاثين وأربعمائة فيها ملك طغرلبك جرجان وطبرستان، ثم عاد إلى نيسابور مؤيدا منصورا.','4484','1729','0');
INSERT INTO [t8376] VALUES (1785,'وممن توفي فيها','4484','1784','0');
INSERT INTO [t8376] VALUES (1786,'ثم دخلت سنة أربع وثلاثين وأربعمائة فيها أمر الملك جلال الدولة أبا طاهر بجباية أموال الجوالي، ومنع أصحاب الخليفة من قبضها فانزعج لذلك الخليفة القائم بالله، وعزم على الخروج من بغداد.','4485','1729','0');
INSERT INTO [t8376] VALUES (1787,'ثم دخلت سنة خمس وثلاثين وأربعمائة','4486','1729','0');
INSERT INTO [t8376] VALUES (1788,'وفيها توفي','4486','1787','0');
INSERT INTO [t8376] VALUES (1789,'وممن توفي فيها','4486','1787','0');
INSERT INTO [t8376] VALUES (1790,'وفيها توفي','4487','1787','0');
INSERT INTO [t8376] VALUES (1791,'ثم دخلت سنة ست وثلاثين وأربعمائة فيها دخل الملك أبو كاليجار بغداد وأمر بضرب الطبل في أوقات الصلوات الخم','4487','1729','0');
INSERT INTO [t8376] VALUES (1792,'وممن توفي فيها','4488','1791','0');
INSERT INTO [t8376] VALUES (1793,'ثم دخلت سنة سبع وثلاثين وأربعمائة فيها بعث السلطان طغرلبك السلجوقي أخاه إبراهيم إلى بلاد الجبل فملكها،','4490','1729','0');
INSERT INTO [t8376] VALUES (1794,'وممن توفي فيها','4490','1793','0');
INSERT INTO [t8376] VALUES (1795,'ثم دخلت سنة ثمان وثلاثين وأربعمائة استهلت هذه السنة والموتان كثير في الدواب جدا، حتى جافت بغداد قال ابن الجوزي: وربما أحضر بعض الناس الاطباء لاجل دوابهم فيسقونها ماء الشعير ويطببونها، وفيها حاصر السلطان بن طغرلبك أصبهان فصالحه أهلها على مال يحملونه إليه، وأن يخطب له بها، فأجابوه إلى ذلك.','4491','1729','0');
INSERT INTO [t8376] VALUES (1796,'وممن توفي فيها','4491','1795','0');
INSERT INTO [t8376] VALUES (1797,'ثم دخلت سنة تسع وثلاثين وأربعمائة','4492','1729','0');
INSERT INTO [t8376] VALUES (1798,'وممن توفي فيها','4492','1797','0');
INSERT INTO [t8376] VALUES (1799,'وممن توفي فيها','4495','1797','0');
INSERT INTO [t8376] VALUES (1800,'ثم دخلت سنة إحدى وأربعين وأربعمائة في عاشر المحرم تقدم إلى أهل الكرخ أن لا يعملوا بدع النوح، فجرى بينهم وبين أهل باب البصرة ما يزيد على الحد، من الجراح والقتل','4496','1729','0');
INSERT INTO [t8376] VALUES (1801,'وفيها توفي','4497','1800','0');
INSERT INTO [t8376] VALUES (1802,'ثم دخلت سنة اثنتين وأربعين وأربعمائة فيها فتح السلطان طغرلبك أصبهان (١) بعد حصار سنة، فنقل إليها حواصله من الري وجعلها دار إقامته، وخرب قطعة من سورها، وقال: إنما يحتاج إلى السور من تضعف قوته، وإنما حصنني عساكري وسيفي، وقد كان فيها أبو منصور قرامز بن علاء الدولة أبي جعفر بن كالويه (٢) ، فأخرجه منها وأقطعه بعض بلادها.','4499','1729','0');
INSERT INTO [t8376] VALUES (1803,'وممن توفي فيها','4499','1802','0');
INSERT INTO [t8376] VALUES (1804,'ثم دخلت سنة ثلاث وأربعين وأربعمائة في صفر منها وقع الحرب بين الروافض والسنة، فقتل من الفريقين خلق كثير، وذلك أن الروافض نصبوا أبراجا وكتبوا عليها بالذهب: محمد وعلي خير البشر، فمن رضي فقد شكر، ومن أبى فقد كفر.','4501','1729','0');
INSERT INTO [t8376] VALUES (1805,'وممن توفي فيها','4501','1804','0');
INSERT INTO [t8376] VALUES (1806,'ثم دخلت سنة أربع وأربعين وأربعمائة فيها كتبت تذكرة الخلفاء المصريين وأنهم أدعيا كذبة لا نسب لهم صحيحة إلى رسول الله صلى الله عليه وسلم، نسخا كثيرة، وكتب فيها الفقهاء والقضاة والاشراف.','4502','1729','0');
INSERT INTO [t8376] VALUES (1807,'وفيها توفي','4502','1806','0');
INSERT INTO [t8376] VALUES (1808,'ثم دخلت سنة خمس وأربعين وأربعمائة.','4503','1729','0');
INSERT INTO [t8376] VALUES (1809,'وفيها توفي','4503','1808','0');
INSERT INTO [t8376] VALUES (1810,'ثم دخلت سنة ست وأربعين وأربعمائة فيها غزا السلطان طغرلبك بلاد الروم بعد أخذه بلاد أذربيجان، فغنم من بلاد الروم وسبى وعمل أشياء حسنة، ثم عاد سالما فأقام بأذربيجان سنة.','4504','1729','0');
INSERT INTO [t8376] VALUES (1811,'ثم دخلت سنة سبع وأربعين وأربعمائة فيها ملك طغرلبك بغداد، وهو أول ملوك السلجوقية، ملكها وبلاد العراق.','4505','1729','0');
INSERT INTO [t8376] VALUES (1812,'وممن توفي فيها','4505','1811','0');
INSERT INTO [t8376] VALUES (1813,'وممن توفي فيها','4506','1811','0');
INSERT INTO [t8376] VALUES (1814,'ثم دخلت سنة ثمان وأربعين وأربعمائة في يوم الخميس لثمان بقين من المحرم عقد الخليفة على خديجة بنت أخي السلطان طغرلبك على صداق مائة ألف دينار، وحضر هذا العقد عميد الملك الكندري، وزير طغرلبك، وبقية العلويين وقاضي القضاة الدامغاني والماوردي، ورئيس الرؤساء ابن المسلمة.','4507','1729','0');
INSERT INTO [t8376] VALUES (1815,'وفيها توفي','4509','1814','0');
INSERT INTO [t8376] VALUES (1816,'ثم دخلت سنة تسع وأربعين وأربعمائة فيها كان الغلاء والفناء مستمرين ببغداد وغيرها من البلاد','4511','1729','0');
INSERT INTO [t8376] VALUES (1817,'وممن توفي فيها','4512','1816','0');
INSERT INTO [t8376] VALUES (1818,'ثم دخلت سنة خمسين وأربعمائة فيها كنت فتنة الخبيث البساسيري، وهو أرسلان التركي','4517','1729','0');
INSERT INTO [t8376] VALUES (1819,'وفيها توفي','4520','1818','0');
INSERT INTO [t8376] VALUES (1820,'ثم دخلت سنة إحدى وخمسين وأربعمائة استهلت هذه السنة وبغداد في حكم البساسيري، يخطب فيها لصاحب مصر الفاطمي، والخليفة العباسي بحديثة عانة، ثم لما كان يوم الاثنين ثاني عشر صفر أحضر القضاة أبا عبد الله الدامغاني وجماعة من الوجوه والاعيان والاشراف، وأخذ عليهم البيعة لصاحب مصر المستنصر الفاطمي، ثم دخل دار الخلافة وهؤلاء المذكورون معه وأمر بنقض تاج دار الخلافة، فنقض بعض الشراريف، ثم قيل له إن القبح في هذا أكثر من المصلحة.','4522','1729','0');
INSERT INTO [t8376] VALUES (1821,'فصل','4522','1820','0');
INSERT INTO [t8376] VALUES (1822,'مقتل البساسيري على يدي السلطان طغرلبك','4524','1820','0');
INSERT INTO [t8376] VALUES (1823,'ترجمة أرسلان أبو الحارس البساسيري التركي','4525','1820','0');
INSERT INTO [t8376] VALUES (1824,'ثم دخلت سنة اثنتين وخمسين وأربعمائة في يوم الخميس السابع عشر من صفر، دخل السلطان بغداد مرجعه من واسط، بعد قتل البساسيري، وفي يوم الحادي والعشرين جلس الخليفة في داره وأحضر الملك طغرلبك، ومد سماطا عظيما فأكل الامراء منه والعامة، ثم في يوم الخميس ثاني ربيع الاول عمل السلطان سماطا للناس، وفي يوم الثلاثاء تاسع جمادى الآخرة قدم الامير عدة الدين أبو القاسم عبد الله بن ذخيرة الدين بن أمير المؤمنين القائم بأمر الله.','4527','1729','0');
INSERT INTO [t8376] VALUES (1825,'وممن توفي فيها','4527','1824','0');
INSERT INTO [t8376] VALUES (1826,'وممن توفي فيها','4529','1824','0');
INSERT INTO [t8376] VALUES (1827,'ثم دخلت سنة أربع وخمسين وأربعمائة','4530','1729','0');
INSERT INTO [t8376] VALUES (1828,'وممن توفي فيها','4530','1827','0');
INSERT INTO [t8376] VALUES (1829,'ثم دخلت سنة خمس وخمسين وأربعمائة فيها دخل السلطان طغرلبك بغداد، وعزم الخليفة على تلقيه، ثم ترك ذلك وأرسل وزيره أبا نصر عوضا عنه، وكان من الجيش أذية كثيرة للناس في الطريق، وتعرضوا للحريم حتى هجموا على النساء في الحمامات، فخلصهن منهم العامة بعد جهد.','4531','1729','0');
INSERT INTO [t8376] VALUES (1830,'وممن توفي فيها','4532','1829','0');
INSERT INTO [t8376] VALUES (1831,'ثم دخلت سنة ست وخمسين وأربعمائة فيها قبض السلطان ألب أرسلان على وزير عمه عميد الملك الكندري، وسجنه ببيته ثم أرسل إليه من قتله، واعتمد في الوزارة على نظام الملك، وكان وزير صدق، يكرم العلماء والفقراء، ولما عصى الملك شهاب الدولة قتلمش (٢) ، وخرج عن الطاعة، وأراد أخذ ألب أرسلان، خاف منه ألب أرسلان فقال له الوزير: أيها الملك لا تخف، فإني قد استدمت لك جندا ما بارزوا عسكرا إلا كسروه، كائنا من كان.','4533','1729','0');
INSERT INTO [t8376] VALUES (1832,'وممن توفي فيها','4534','1831','0');
INSERT INTO [t8376] VALUES (1833,'ثم دخلت سنة سبع وخمسين واربعمائة فيها سار جماعة من العراق إلى الحج بخفارة، فلم يمكنهم المسير فعدلوا إلى الكوفة ورجعوا.','4535','1729','0');
INSERT INTO [t8376] VALUES (1834,'ثم دخلت سنة ثمان وخمسين وأربعمائة في يوم عاشوراء أغلق أهل الكرخ دكاكينهم وأحضروا نساء ينحن على الحسين، كما جرت به عادتهم السالفة في بدعتهم المتقدمة المخالفة، فحين وقع ذلك أنكرته العامة، وطلب الخليفة أبا','4536','1729','0');
INSERT INTO [t8376] VALUES (1835,'وفيها توفي','4537','1834','0');
INSERT INTO [t8376] VALUES (1836,'ثم دخلت سنة تسع وخمسين وأربعمائة فيها بنى أبو سعيد (٢) المستوفي الملقب بشرف الملك، مشهد الامام أبي حنيفة ببغداد، وعقد عليه قبة، وعمل بإزائه مدرسة، فدخل أبو جعفر بن البياضي زائرا لابي حنيفة فأنشد: ألم تر أن العلم كان مضيعا (٣) * فجمعه هذا المغيب في اللحد كذلك كانت هذه الارض ميتة * فأنشرها جود العميد أبي السعد وفيها هبت ريح حارة فمات بسببها خلق كثير، وورد أن ببغداد تلف شجر كثير من الليمون والاترج.','4539','1729','0');
INSERT INTO [t8376] VALUES (1837,'وممن توفي فيها','4540','1836','0');
INSERT INTO [t8376] VALUES (1838,'ثم دخلت سنة ستين وأربعمائة','4540','1729','0');
INSERT INTO [t8376] VALUES (1839,'كانت زلزلة بأرض فلسطين','4540','1838','0');
INSERT INTO [t8376] VALUES (1840,'وفيها توفي','4541','1838','0');
INSERT INTO [t8376] VALUES (1841,'ثم دخلت سنة إحدى وستين وأربعمائة في ليلة النصف من شعبان منها كان حريق جامع دمشق،','4541','1729','0');
INSERT INTO [t8376] VALUES (1842,'وممن توفي فيها','4542','1841','0');
INSERT INTO [t8376] VALUES (1843,'ثم دخلت سنة اثنتين وستين وأربعمائة قال ابن الجوزي: فمن الحوادث فيها أنه كان على ثلاث ساعات في يوم الثلاثاء الحادي عشر من جمادى الاولى، وهو ثامن عشرين أذار، كانت زلزلة عظيمة بالرملة وأعمالها، فذهب أكثرها وانهدم سورها، وعم ذلك بيت المقدس ونابلس، وانخسفت إيليا، وجفل البحر حتى انكشفت أرضه، ومشى ناس فيه ثم عاد وتغير، وانهدم إحدى زوايا جامع مصر، وتبعت هذه الزلزلة في ساعتها زلزلتان أخريان.','4543','1729','0');
INSERT INTO [t8376] VALUES (1844,'وفيها توفي','4544','1843','0');
INSERT INTO [t8376] VALUES (1845,'وفيها توفي','4546','1843','0');
INSERT INTO [t8376] VALUES (1846,'ثم دخلت سنة أربع وستين وأربعمائة فيها قام الشيخ أبو إسحاق الشيرازي مع الحنابلة في الانكار على المفسدين، والذين يبيعون الخمور، وفي إبطال المواجرات وهن البغايا، وكتبوا إلى السلطان في ذلك فجاءت كتبه في الانكار.','4550','1729','0');
INSERT INTO [t8376] VALUES (1847,'وممن توفي فيها','4550','1846','0');
INSERT INTO [t8376] VALUES (1848,'ثم دخلت سنة خمس وستين وأربعمائة','4551','1729','0');
INSERT INTO [t8376] VALUES (1849,'وفاة السلطان ألب أرسلان','4551','1848','0');
INSERT INTO [t8376] VALUES (1850,'وفيها توفي','4552','1848','0');
INSERT INTO [t8376] VALUES (1851,'ثم دخلت سنة ست وستين واربعمائة في صفر منها جلس الخليفة جلوسا عاما وعلى رأسه حفيده الامير عدة الدين، أبو القاسم عبد الله بن المهتدي بالله، وعمره يومئذ ثماني عشرة سنة، وهو في غاية الحسن، وحضر الامراء والكبراء فعقد الخليفة بيده لواء السلطان ملكشاه، كثر الزحام يومها، وهنأ الناس بعضهم بعضا بالسلامة.','4555','1729','0');
INSERT INTO [t8376] VALUES (1852,'وممن توفي فيها','4555','1851','0');
INSERT INTO [t8376] VALUES (1853,'ثم دخلت سنة سبع وستين وأربعمائة في صفر منها مرض الخليفة القائم بأمر الله مرضا شديدا انتفخ منه حلقه، وامتنع من الفصد، فلم يزل الوزير فخر الدولة عليه حتى افتصد وانصلح الحال، وكان الناس قد انزعجوا ففرحوا بعافيته وجاء في هذا الشهر سيل عظيم قاسى الناس منه شدة عظيمة، ولم تكن أكثر أبنية بغداد تكاملت من الغرق الاول، فخرج الناس إلى الصحراء فجلسوا على رؤوس التلول تحت المطر، ووقع وباء عظيم','4556','1729','0');
INSERT INTO [t8376] VALUES (1854,'خلافة المقتدي بأمر الله','4557','1853','0');
INSERT INTO [t8376] VALUES (1855,'وممن توفي فيها','4558','1853','0');
INSERT INTO [t8376] VALUES (1856,'وممن توفي فيها','4560','1853','0');
INSERT INTO [t8376] VALUES (1857,'ثم دخلت سنة تسع وستين وأربعمائة فيها كان ابتداء عمارة قلعة دمشق','4562','1729','0');
INSERT INTO [t8376] VALUES (1858,'وممن توفي فيها','4563','1857','0');
INSERT INTO [t8376] VALUES (1859,'ثم دخلت سنة سبعين وأربعمائة قال ابن الجوزي: في ربيع الاول منها وقعت صاعقة بمحلة النوبة من الجانب الغربي، على نخلتين في مسجد فأحرقت أعاليهما، وصعد الناس فأطفأوا النار، ونزلوا بالسعف وهو يشتعل نارا.','4565','1729','0');
INSERT INTO [t8376] VALUES (1860,'وممن توفي فيها','4566','1859','0');
INSERT INTO [t8376] VALUES (1861,'ثم دخلت سنة إحدى وسبعين وأربعمائة فيها ملك السلطان الملك المظفر تاج الملوك تتش بن ألب أرسلان السلجوقي دمشق وقتل ملكها','4568','1729','0');
INSERT INTO [t8376] VALUES (1862,'وممن توفي فيها','4568','1861','0');
INSERT INTO [t8376] VALUES (1863,'ثم دخلت سنة ثنتين وسبعين وأربعمائة فيها ملك محمود (١) بن مسعود بن محمود بن سبكتكين صاحب غزنة قلاعا كثيرة حصينة من بلاد الهند، ثم عاد إلى بلاده سالما غانما.','4569','1729','0');
INSERT INTO [t8376] VALUES (1864,'وممن توفي فيها','4569','1863','0');
INSERT INTO [t8376] VALUES (1865,'ثم دخلت سنة ثلاث وسبعين وأربعمائة فيها استولى تكش أخو السلطان ملك شاه على بعض بلاد خراسان.','4570','1729','0');
INSERT INTO [t8376] VALUES (1866,'وممن توفي فيها','4570','1865','0');
INSERT INTO [t8376] VALUES (1867,'ثم دخلت سنة أربع وسبعين وأربعمائة فيها ولي أبو كامل منصور بن نور الدولة دبيس ما كان يليه أبوه من الاعمال، وخلع عليه السلطان والخليفة، وفيها ملك شرف الدولة مسلم بن قريش حران، وصالح صاحب الرهاء.','4571','1729','0');
INSERT INTO [t8376] VALUES (1868,'وفيها توفي','4572','1867','0');
INSERT INTO [t8376] VALUES (1869,'ثم دخلت سنة خمس وسبعين وأربعمائة فيها قدم مؤيد الملك فنزل في مدرسة أبيه، وضربت الطبول على بابه في أوقات الصلوات الثلاث.','4573','1729','0');
INSERT INTO [t8376] VALUES (1870,'وتوفي فيها من الاعيان ... عبد الوهاب بن محمد ابن إسحاق بن محمد بن يحيى بن منده، أبو عمر الحافظ من بيت الحديث، رحل إلى الآفاق وسمع الكثير، وتوفي بأصبهان.','4573','1869','0');
INSERT INTO [t8376] VALUES (1871,'وممن توفي فيها','4575','1869','0');
INSERT INTO [t8376] VALUES (1872,'ثم دخلت سنة سبع وسبعين وأربعمائة فيها كانت الحرب بين فخر الدولة بن جهير وزير الخليفة وبين ابن مروان صاحب ديار بكر، فاستولى ابن جهير على ملك العرب وسبى حريمهم وأخذ البلاد ومعه سيف الدولة صدقة بن منصور بن دبيس بن علي بن مزيد الاسدي، فافتدى خلقا من العرب فشكره الناس على ذلك، وامتدحه الشعراء.','4576','1729','0');
INSERT INTO [t8376] VALUES (1873,'وممن توفي فيها','4577','1872','0');
INSERT INTO [t8376] VALUES (1874,'ثم دخلت سنة ثمان وسبعين وأربعمائة في المحرم منها زلزلت أرجان فهلك خلق كثير من الروم ومواشيهم.','4578','1729','0');
INSERT INTO [t8376] VALUES (1875,'وممن توفي فيها','4578','1874','0');
INSERT INTO [t8376] VALUES (1876,'وفيها توفي','4583','1874','0');
INSERT INTO [t8376] VALUES (1877,'وممن توفي فيها','4583','1874','0');
INSERT INTO [t8376] VALUES (1878,'ثم دخلت سنة ثمانين وأربعمائة في المحرم منها نقل جهاز ابنة السلطان ملكشاه إلى دار الخلافة على مائة وثلاثين جملا مجللة بالديباج الرومي','4584','1729','0');
INSERT INTO [t8376] VALUES (1879,'وممن توفي فيها','4585','1878','0');
INSERT INTO [t8376] VALUES (1880,'ثم دخلت سنة إحدى وثمانين وأربعمائة فيها كانت فتن عظيمة بين الروافض والسنة ببغداد، وجرت خطوب كثيرة.','4587','1729','0');
INSERT INTO [t8376] VALUES (1881,'وممن توفي فيها','4587','1880','0');
INSERT INTO [t8376] VALUES (1882,'ثم دخلت سنة ثنتين وثمانين وأربعمائة في المحرم درس أبو بكر الشاشي في المدرسة التاجية بباب إبرز، التي أنشأها الصاحب تاج الدين أبو الغنائم على الشافعية، وفيها كانت فتن عظيمة بين الروافض والسنة، ورفعوا المصاحف،','4588','1729','0');
INSERT INTO [t8376] VALUES (1883,'وممن توفي فيها','4588','1882','0');
INSERT INTO [t8376] VALUES (1884,'ثم دخلت سنة ثلاث وثمانين وأربعمائة في المحرم منها ورد إلى الفقيه أبي عبد الله الطبري منشور نظام الملك بتدريس النظامية،','4590','1729','0');
INSERT INTO [t8376] VALUES (1885,'وممن توفي فيها','4590','1884','0');
INSERT INTO [t8376] VALUES (1886,'ثم دخلت سنة أربع وثمانين وأربعمائة في المحرم منها كتب المنجم الذي أحرق البصرة إلى أهل واسط يدعوهم إلى طاعته،','4590','1729','0');
INSERT INTO [t8376] VALUES (1887,'وممن توفي فيها','4591','1886','0');
INSERT INTO [t8376] VALUES (1888,'ثم دخلت سنة خمس وثمانين وأربعمائة فيها أمر السلطان ملكشاه ببناء سور سوق المدينة المعروفة بطغرلبك، إلى جانب دار الملك،','4592','1729','0');
