INSERT INTO [t8376] VALUES (1897,'وممن توفي فيها','4603','1892','0');
INSERT INTO [t8376] VALUES (1898,'ثم دخلت سنة ثمان وثمانين وأربعمائة فيها قدم يوسف بن أبق التركماني من جهة تتش صاحب دمشق إلى بغداد لاجل إقامة الدعوة له ببغداد','4604','1729','0');
