INSERT INTO [t8376] VALUES (1901,'ثم دخلت سنة تسعين وأربعمائة من الهجرة فيها كان ابتداء ملك الخوارزمية،','4611','1729','0');
INSERT INTO [t8376] VALUES (1902,'وممن توفي فيها','4612','1901','0');
INSERT INTO [t8376] VALUES (1903,'ثم دخلت سنة إحدى وتسعين وأربعمائة في جمادى الاولى منها ملك الافرنج مدينة إنطاكية بعد حصار شديد، بمواطأة بعض','4612','1729','0');
INSERT INTO [t8376] VALUES (1904,'ثم دخلت سنة ثنتين وتسعين وأربعمائة وفيها أخذت الفرنج بيت المقدس.','4613','1729','0');
