INSERT INTO [t8376] VALUES (1908,'وممن توفي فيها','4617','1907','0');
INSERT INTO [t8376] VALUES (1909,'ثم دخلت سنة أربع وتسعين وأربعمائة فيها عظم الخطب بأصبهان ونواحيها بالباطنية فقتل السلطان منهم خلقا كثيرا','4618','1729','0');
INSERT INTO [t8376] VALUES (1910,'وممن توفي فيها','4619','1909','0');
INSERT INTO [t8376] VALUES (1911,'ثم دخلت سنة خمس وتسعين وأربعمائة في ثالث المحرم منها قبض على أبي الحسن علي بن محمد المعروف بالكيا الهراسي، وعزل عن تدريس النظامية، وذلك أنه رماه بعضهم عند السلطان بأنه باطني، فشهد له جماعة من العلماء - منهم ابن عقيل - ببراءته من ذلك، وجاءت الرسالة من دار الخلافة يوم الثلاثاء بخلاصه.','4621','1729','0');
INSERT INTO [t8376] VALUES (1912,'وممن توفي فيها','4622','1911','0');
INSERT INTO [t8376] VALUES (1913,'وفيها قتل أبو المظفر الخجندي','4622','1911','0');
INSERT INTO [t8376] VALUES (1914,'ثم دخلت سنة سبع وتسعين وأربعمائة فيها قصد الفرنج لعنهم الله الشام فقاتلهم المسلمون فقتلوا من الفرنج اثني عشر ألفا، ورد الله الذين كفروا بغيظهم لم ينالوا خيرا، وقد أسر في هذه الوقعة بردويل صاحب الرها.','4623','1729','0');
INSERT INTO [t8376] VALUES (1915,'وفيها توفي','4623','1914','0');
INSERT INTO [t8376] VALUES (1916,'وممن توفي فيها','4623','1914','0');
INSERT INTO [t8376] VALUES (1917,'ثم دخلت سنة ثمان وتسعين وأربعمائة فيها توفي السلطان بركيارق وعهد إلى ولده الصغير ملكشاه، وعمره أربع سنين وشهورا (١) ، وخطب له ببغداد، ونثر عند ذكره الدنانير والدراهم، وجعل أتابكه الامير إياز ولقب جلال الدولة، ثم جاء السلطان محمد إلى بغداد فخرج إليه أهل الدولة ليتلقوه وصالحوه، وكان الذي أخذ البيعة','4624','1729','0');
INSERT INTO [t8376] VALUES (1918,'وممن توفي فيها','4624','1917','0');
INSERT INTO [t8376] VALUES (1919,'وممن توفي فيها','4626','1917','0');
