INSERT INTO [t8376] VALUES (1921,'وفيها توفي','4628','1920','0');
INSERT INTO [t8376] VALUES (1922,'ثم دخلت سنة إحدى وخمسمائة من الهجرة','4630','1729','0');
INSERT INTO [t8376] VALUES (1923,'ثم دخلت سنة ثنتين وخمسمائة في يوم الجمعة الثاني والعشرين من شعبان تزوج الخليفة المستظهر بالخاتون بنت ملكشاه أخت السلطان محمد، على صداق مائة ألف دينار، ونثر الذهب، وكتب العقد بأصبهان.','4632','1729','0');
INSERT INTO [t8376] VALUES (1924,'وممن توفي فيها','4632','1923','0');
INSERT INTO [t8376] VALUES (1925,'ثم دخلت سنة ثلاث وخمسمائة فيها أخذت الفرنج مدينة طرابلس وقتلوا من فيها من الرجال، وسبوا الحريم والاطفال ; وغنموا الامتعة والاموال، ثم أخذوا مدينة جبلة (١) بعدها بعشر ليال، فلا حول ولا قوة إلا بالله الكبير المتعال.','4633','1729','0');
INSERT INTO [t8376] VALUES (1926,'وممن توفي فيها','4633','1925','0');
INSERT INTO [t8376] VALUES (1927,'ثم دخلت سنة أربع وخمسمائة في أولها تجهز جماعة من البغاددة من الفقهاء وغيرهم، ومنهم ابن الذاغوني، للخروج إلى الشام لاجل الجهاد، وقتال الفرنج، وذلك حين بلغهم أنهم فتحوا مدائن عديدة، من ذلك مدينة صيدا في ربيع الاول (١) ، وكذا غيرها من المدائن، ثم رجع كثير منهم حين بلغهم كثرة الفرنج.','4634','1729','0');
INSERT INTO [t8376] VALUES (1928,'وممن توفي فيها','4634','1927','0');
INSERT INTO [t8376] VALUES (1929,'ثم دخلت سنة خمس وخمسمائة.','4635','1729','0');
INSERT INTO [t8376] VALUES (1930,'وممن توفي فيها','4636','1929','0');
INSERT INTO [t8376] VALUES (1931,'ثم دخلت سنة ست وخمسمائة في جمادى الآخرة منها جلس ابن الطبري مدرسا بالنظامية وعزل عنها الشاشي.','4637','1729','0');
INSERT INTO [t8376] VALUES (1932,'وممن توفي فيها','4637','1931','0');
INSERT INTO [t8376] VALUES (1933,'ثم دخلت سنة سبع وخمسمائة فيها كانت وقعة عظيمة بين المسلمين والفرنج في أرض طبرية','4639','1729','0');
INSERT INTO [t8376] VALUES (1934,'وممن توفي فيها','4639','1933','0');
INSERT INTO [t8376] VALUES (1935,'ثم دخلت سنة ثمان وخمسمائة فيها وقع حريق عظيم ببغداد.','4641','1729','0');
INSERT INTO [t8376] VALUES (1936,'ثم دخلت سنة تسع وخمسمائة فيها جهز السلطان غياث الدين محمد بن ملكشاه صاحب العراق جيشا كثيفا مع الامير برشق (٣) بن إيلغازي صاحب ماردين إلى صاحب دمشق طغتكين، وإلى آقسنقر البرشقي ليقاتلهما، لاجل عصيانهما عليه، وقطع خطبته، وإذا فرغ منهما عمد لقتال الفرنج.','4642','1729','0');
INSERT INTO [t8376] VALUES (1937,'وفيها قتل صاحب تاج الدولة ألب أرسلان بن رضوان بن تتش','4642','1936','0');
