INSERT INTO [t8376] VALUES (1939,'وفيها توفي','4643','1938','0');
INSERT INTO [t8376] VALUES (1940,'ثم دخلت سنة إحدى عشرة وخمسمائة في رابع (٥) صفر منها انكسف القمر كسوفا كليا، وفي تلك الليلة هجم الفرنج على ربض حماه فقتلوا خلقا كثيرا، ورجعوا إلى بلادهم.','4645','1729','0');
