INSERT INTO [t8376] VALUES (1950,'وممن توفي فيها','4654','1949','0');
INSERT INTO [t8376] VALUES (1951,'ثم دخلت سنة خمس عشرة وخمسمائة فيها أقطع السلطان محمود الامير إيلغازي مدينة ميافارقين، فبقيت في يد أولاده إلى أن أخذها صلاح الدين يوسف بن أيوب، في سنة ثمانين وخمسمائة.','4655','1729','0');
