INSERT INTO [t8376] VALUES (1955,'ثم دخلت سنة سبع عشرة وخمسمائة في يوم عاشوراء منها عاد الخليفة من الحلة إلى بغداد مؤيدا منصورا من قتال دبيس.','4660','1729','0');
INSERT INTO [t8376] VALUES (1956,'وممن توفي فيها','4661','1955','0');
INSERT INTO [t8376] VALUES (1957,'ثم دخلت سنة ثمان عشر وخمسمائة فيها ظهرت الباطنية بآمد فقاتلهم أهلها فقتلوا منهم سبعمائة.','4662','1729','0');
