INSERT INTO [t8378] VALUES (1,'مقدمة','2','0','0');
INSERT INTO [t8378] VALUES (2,'قال الفقير إلى رحمة ربه ومغفرته محمد بن أبى بكر بن عبد القادر الرازي','2','1','0');
INSERT INTO [t8378] VALUES (3,'سورة فاتحة الكتاب','3','0','0');
INSERT INTO [t8378] VALUES (4,'فإن قيل: الرحمن أبلغ في الوصف بالرحمة من الرحيم بالنقل عن الزجاج وغيره، فكيف قدمه وعادة العرب في صفات المدح الترقى من الأدنى إلى الأعلى؟','3','3','0');
INSERT INTO [t8378] VALUES (5,'فإن قيل: ما فائدة دخول لا في قوله: (ولا الضالين) . وقوله: (غير المغضوب عليهم) . والضالين كاف في المقصود؟','4','3','0');
INSERT INTO [t8378] VALUES (6,'سورة البقرة','5','0','0');
INSERT INTO [t8378] VALUES (7,'فإن قيل: كيف قال: (لا ريب فيه) على سبيل الاستغراق وكم ضال قد ارتاب فيه، ويؤيد ذلك قوله تعالى: (وإن كنتم في ريب مما نزلنا على عبدنا) ؟','5','6','0');
INSERT INTO [t8378] VALUES (8,'فإن قيل: كيف حصر الفساد في المنافقين بقوله: (ألا إنهم هم المفسدون) ومعلوم أن غيرهم مفسد؟','6','6','0');
INSERT INTO [t8378] VALUES (9,'فإن قيل: كيف عرف النار ونكرها في سورة التحريم؟','7','6','0');
INSERT INTO [t8378] VALUES (10,'فإن قيل: كيف قال: (فبدل الذين ظلموا قولا غير الذي قيل لهم) . وهم إنما بدلوا القول الذي قيل لهم، لأنهم قيل لهم قولوا حطة فقالوا حنطة؟','8','6','0');
INSERT INTO [t8378] VALUES (11,'فإن قيل: كيف قال: (ويقتلون النبيين بغير الحق) . وقتل النبيين لا يكون إلا بغير الحق؟','9','6','0');
INSERT INTO [t8378] VALUES (12,'فإن قيل: ما الفائدة في قوله: (فويل للذين يكتبون الكتاب بأيديهم) والكتابة لا تكون إلا باليد؟','10','6','0');
INSERT INTO [t8378] VALUES (13,'فإن قيل. قوله تعالى: (وما أنزل على الملكين) . يدل على أن الله تعالى أنزل علم السحر على الملكين فلم يكن حراما؟','11','6','0');
INSERT INTO [t8378] VALUES (14,'فإن قيل: كيف قال: (ولو أنهم آمنوا واتقوا لمثوبة من عند الله خير لو كانوا يعلمون) .','12','6','0');
INSERT INTO [t8378] VALUES (15,'فإن قيل: قوله تعالى: (فإن آمنوا بمثل ما آمنتم به فقد اهتدوا) . إن أريد به الله تعالى فلا مثل له وإن أريد به دين الإسلام فلا مثل له أيضا، لأن دين الحق واحد؟','13','6','0');
INSERT INTO [t8378] VALUES (16,'فإن قيل: كيف قال: (وما جعلنا القبلة التي كنت عليها إلا لنعلم من يتبع الرسول ممن ينقلب على عقبيه) .','14','6','0');
INSERT INTO [t8378] VALUES (17,'فإن قيل: كيف يكون للظالمين من اليهود أو غيرهم حجة على المؤمنين حتي قال: (للئلا يكون للناس عليكم حجة إلا الذين ظلموا منهم) ؟','15','6','0');
INSERT INTO [t8378] VALUES (18,'فإن قيل: كيف قال: (والناس أجمعين) . وأهل دينه لا يلعنونه إذا مات على دينهم؟','16','6','0');
INSERT INTO [t8378] VALUES (19,'فإن قيل: كيف وجه صحة التشبيه في قوله تعالى: (ومثل الذين كفروا كمثل الذي ينعق) . وظاهره تشبيه الكفار بالراعى؟','17','6','0');
INSERT INTO [t8378] VALUES (20,'فإن قيل: كيف قال: (الوصية للوالدين والأقربين) . عطف الأقربين على الوالدين، وهما أقرب الأقربين والعطف يقتضى المغايرة؟','18','6','0');
INSERT INTO [t8378] VALUES (21,'فإن قيل: كيف قال: (كتب عليكم الصيام كما كتب على الذين من قبلكم) . وصوم هذه الأمة ليس كصوم أمة موسى وعيسى عليهما الصلاة والسلام؟','19','6','0');
INSERT INTO [t8378] VALUES (22,'فإن قيل: قوله تعالى: (فإني قريب أجيب دعوة الداع إذا دعان) . يدل على أنه يجيب دعاء الداعين، ونحن نرى كثيرا من الداعين لا يستجاب لهم؟','20','6','0');
INSERT INTO [t8378] VALUES (23,'فإن قيل: ما فائدة تكرار الأمر بالذكر في قوله تعالى: (فإذا أفضتم من عرفات فاذكروا الله عند المشعر الحرام واذكروه كما هداكم) ؟','21','6','0');
INSERT INTO [t8378] VALUES (24,'فإن قيل: كيف قال: (فمن تعجل في يومين فلا إثم عليه ومن تأخر فلا إثم عليه) . ومعلوم أن المتعجل التارك بعض الرمى إذا لم يكن عليه إثم لا يكون على المتأخر الآتى بالرمى كاملا.','22','6','0');
INSERT INTO [t8378] VALUES (25,'فإن قيل: كيف قال: (وإلى الله ترجع الأمور) . وهو يدل على أنها كانت إلى غيره كقولهم رجع إلى فلان عبده ومنصبه؟','23','6','0');
INSERT INTO [t8378] VALUES (26,'فإن قيل: كيف طابق الجوب السؤال في قوله: (يسألونك ماذا ينفقون قل ما أنفقتم من خير فللوالدين والأقربين) . فإنهم سألوا عن بيان ما ينفقون، وأجيبوا ببيان المصرف؟','24','6','0');
INSERT INTO [t8378] VALUES (27,'فإن قيل: كيف قال: (وبعولتهن أحق بردهن) . ولا حق للنساء في الرجعة، وأفعل تقتضى الاشتراك؟','25','6','0');
INSERT INTO [t8378] VALUES (28,'فإن قيل: كيف قال في الماء (ومن لم يطعمه) ولم يقل ومن لم يشربه والماء مشروب لا مأكول؟','26','6','0');
INSERT INTO [t8378] VALUES (29,'فإن قيل: كيف قال: (من قبل أن يأتي يوم لا بيع فيه ولا خلة ولا شفاعة) . وفى يوم القيامة شفاعة للأنبياء وغيرهم بدليل قوله تعالى: (من ذا الذي يشفع عنده إلا بإذنه) . وقوله: (ولا يشفعون إلا','27','6','0');
INSERT INTO [t8378] VALUES (30,'فإن قيل: كيف قال: (الله ولي الذين آمنوا يخرجهم) . بلفظ المضارع، ولم يقل أخرجهم بلفظ الماضي، والإخراج قد وجد لأن الإيمان قد وجد؟','28','6','0');
INSERT INTO [t8378] VALUES (31,'فإن قيل: كيف انتقل إبراهيم عليه الصلاة والسلام إلى حجة أخرى وعدل عن نصرة الأولى، مع أنها لم تنقطع بما عارضه به نمرود من قتل أحد المحبوسين وإطلاق الآخر، فإن إبراهيم عليه الصلاة والسلام ما أراد هذا الاحياء والإماتة؟','29','6','0');
INSERT INTO [t8378] VALUES (32,'فإن قيل: كيف قال عزير منكرا مستبعدا: (أنى يحيي هذه الله بعد موتها) . وهو نبي والنبى لا يخفى عليه قدرة الله تعالى على إحياء قرية خربة وإعادة أهلها إليها؟','30','6','0');
INSERT INTO [t8378] VALUES (33,'فإن قيل: كيف مدح المنافقين بترك المن، ونهى عن المن أيضا، مع أنه وصف نفسه بالمنان؟','31','6','0');
INSERT INTO [t8378] VALUES (34,'فإن قيل: قوله تعالى: (لا يسألون الناس إلحافا) . يدل على أنهم كانوا يألون برفق فكيف قال: (يحسبهم الجاهل أغنياء من التعفف) ؟','32','6','0');
INSERT INTO [t8378] VALUES (35,'فإن قيل: كيف قال: (إنما البيع مثل الربا) . والكلام في الربا، ومقصودهم تشبيهه بالبيع فقياسه إنما الربا مثل البيع؟','33','6','0');
INSERT INTO [t8378] VALUES (36,'فإن قيل: انتظار المعسر فرض بالنص، والتصدق عليه تطوع، فكيف قال: (وأن تصدقوا خير لكم) ؟','34','6','0');
INSERT INTO [t8378] VALUES (37,'فإن قيل: ما فائدة ذكر القلب في قوله: (فإنه آثم قلبه) . مع أن الجملة هى بالأثم لا القلب وحده؟','35','6','0');
INSERT INTO [t8378] VALUES (38,'فإن قيل: أي شرف للرسول عليه الصلاة والسلام في مدحه بالإيمان، مع أنه في مرتبة الرسالة، ودرجتها وهى أعلى من درجة الإيمان فما فائدة قوله: (آمن الرسول) ؟','36','6','0');
INSERT INTO [t8378] VALUES (39,'فإن قيل: بين لا يضاف إلا إلى اثنتين فصاعدا فكيف قال: (لا نفرق بين أحد من رسله) ؟','37','6','0');
INSERT INTO [t8378] VALUES (40,'سورة آل عمران','39','0','0');
INSERT INTO [t8378] VALUES (41,'فإن قيل: كيف قال تعالى: (نزل عليك الكتاب بالحق) . ثم قال تعالى: (وأنزل التوراة والإنجيل) ؟','39','40','0');
INSERT INTO [t8378] VALUES (42,'فإن قيل: كيف قال: (منه آيات محكمات) . ومن للتبعيض. وقال في موضع آخر: (كتاب أحكمت آياته) وهذا يقتضى كون جميع آياته محكمة؟','40','40','0');
INSERT INTO [t8378] VALUES (43,'فإن قيل: ما فائدة تكرار قوله: (لا إله إلا هو) في قوله: (شهد الله أنه لا إله إلا هو والملائكة وأولو العلم قائما بالقسط لا إله إلا هو) ؟','42','40','0');
INSERT INTO [t8378] VALUES (44,'فإن قيل: كيف قال: (بيدك الخير) . خص الخير بالذكر وبيده تعالى الخير والشر والنفع والضر؟','43','40','0');
INSERT INTO [t8378] VALUES (45,'فإن قيل: ما فائدة قوله (وليس الذكر كالأنثى) . وهو معلوم من غير ذكر؟','44','40','0');
INSERT INTO [t8378] VALUES (46,'فإن قيل: المستعمل في مثله إدخال حرف النفى على القاصر، وحرف التشبيه على الكامل كقولهم: ليس الفضة كالذهب، وليس العبد كالحر، فوزانه ليست الأنثى كالذكر؟','45','40','0');
INSERT INTO [t8378] VALUES (47,'فإن قيل: ما فائدة تخصيص يحيى عليه الصلاة والسلام وقوله: (أن الله يبشرك بيحيى مصدقا بكلمة من الله) . وكل واحد من المؤمنين مصدق بجميع كلمات الله تعالى؟','46','40','0');
INSERT INTO [t8378] VALUES (48,'فإن قيل: ما فائدة تكرار ذكر الاصطفاء في قوله تعالى: (إن الله اصطفاك وطهرك واصطفاك) ؟','47','40','0');
INSERT INTO [t8378] VALUES (49,'فإن قيل: أي معجزة لعيسى عليه الصلاة والسلام في تكليم الناس كهلا وأى خصوصية له في هذا حتي قال: (ويكلم الناس في المهد وكهلا) ؟','48','40','0');
INSERT INTO [t8378] VALUES (50,'فإن قيل: كيف قال: (إن مثل عيسى عند الله كمثل آدم) . وآدم (خلق من التراب، وعيس من الهواء) ، وآدم خلق من غير أب وأم، وعيسى خلق من أم؟','49','40','0');
INSERT INTO [t8378] VALUES (51,'فإن قيل: كيف قال: (إن الذين كفروا بعد إيمانهم ثم ازدادوا كفرا لن تقبل توبتهم) . ومعلوم أن المرتد كيفما ازداد كفرا فإنه مقبول التوبة؟','50','40','0');
INSERT INTO [t8378] VALUES (52,'فإن قيل: كيف قال: (ولو آمن أهل الكتاب لكان خيرا لهم) . ولا يصح أن يقال هذا خير من ذلك إلا إذ كان في كل واحد منهما خير؟','51','40','0');
INSERT INTO [t8378] VALUES (53,'فإن قيل: كيف قال: (إن تمسسكم حسنة تسؤهم وإن تصبكم سيئة يفرحوا) . فوصف الحسنة بالمس والسيئة بالاصابة؟','52','40','0');
INSERT INTO [t8378] VALUES (54,'فإن قيل: كيف قال: (والذين إذا فعلوا فاحشة أو ظلموا أنفسهم) . عطفه عليهم بكلمة أو فعل الفاحشة داخل في ظلم النفس وهو (من) أبلغ أنواع ظلم النفس؟','53','40','0');
INSERT INTO [t8378] VALUES (55,'فإن قيل: كيف قال: (ومن يغلل يأت بما غل يوم القيامة) . وقال في موضع آخر: (ولقد جئتمونا فرادى كما خلقناكم أول مرة) ؟','54','40','0');
INSERT INTO [t8378] VALUES (56,'فإن قيل: كيف جعل لكلا الفريقين درجات، وأحد الفريقين لهم دركات لا درجات؟','55','40','0');
INSERT INTO [t8378] VALUES (57,'فإن قيل: كيف قال: (وأن الله ليس بظلام للعبيد) . وظلام صيغة مبالغة من الظلم، ولا يلزم منه نفى الظالم، وعلى العكس يلزم فهلا قال: ليس بظالم ليكون أبلغ في نفى الظلم عن ذاته المقدسة؟','56','40','0');
INSERT INTO [t8378] VALUES (58,'فإن قيل: ما فائدة قوله تعالى: (ولا تكتمونه) في قوله: (وإذ أخذ الله ميثاق الذين أوتوا الكتاب لتبيننه للناس ولا تكتمونه) . والأول مغن عن الثانى؟','57','40','0');
INSERT INTO [t8378] VALUES (59,'فإن قيل: كيف قال: (ربنا إنك من تدخل النار فقد أخزيته) وقال في موضع آخر: (يوم لا يخزي الله النبي والذين آمنوا معه) . ويلزم من هذا أن لا يدخل المؤمنون النار كما قالت المعتزلة والخوارج؟','58','40','0');
INSERT INTO [t8378] VALUES (60,'فإن قيل: ما فائدة قولهم: (وتوفنا مع الأبرار) . (مع أنهم لا ينفعهم توفيهم مع الأبرار) . بل النافع لهم كونهم مع الأبرار سواء توفاهم معهم أو قبلهم أو بعدهم؟','59','40','0');
INSERT INTO [t8378] VALUES (61,'فإن قيل: كيف نهى عن التقلب وهو ليس مما ينهى؟','60','40','0');
INSERT INTO [t8378] VALUES (62,'فإن قيل: كيف قال: (أولئك لهم أجرهم عند ربهم إن الله سريع الحساب) . وقوله لهم " أجرهم عند ربهم " موضع البشارة بالثواب وسرعة الحساب، إنما تذكر في موضع التهديد والعقاب؟','61','40','0');
INSERT INTO [t8378] VALUES (63,'سورة النساء','62','0','0');
INSERT INTO [t8378] VALUES (64,'فإن قيل: في قوله تعالى: (وخلق منها زوجها) . إذ كانت حواء مخلوقة من آدم، ونحن مخلوقون منه أيضا، تكون حواء إلى آدم نسبة الولد لأنها متفرعة منه، فتكون أختا لنا لا أما؟','62','63','0');
INSERT INTO [t8378] VALUES (65,'فإن قيل: كيف قال: (ولأبويه لكل واحد منهما السدس مما ترك إن كان له ولد) . مع أنه لو كان الولد بنتا فللأب الثلث؟','63','63','0');
INSERT INTO [t8378] VALUES (66,'فإن قيل: كيف قال: (إنما التوبة على الله) . ولم يقل إنما التوبة على العبد، مع أن التوبة واجبة على العبد؟','64','63','0');
INSERT INTO [t8378] VALUES (67,'فإن قيل: كيف قال: (أتأخذونه بهتانا) وأخذ مهر المرأة ظلم وليس ببهتان، لأن البهتان الكذب؟','65','63','0');
INSERT INTO [t8378] VALUES (68,'فإن قيل: كيف قال: (إنه كان فاحشة) . بلفظ الماضي مع أن نكاح منكوحة الأب فاحشة في الحال وفى المستقبل إلى يوم القيامة؟','66','63','0');
INSERT INTO [t8378] VALUES (69,'فإن قيل: كيف قال في نكاح الإماء: (فانكحوهن بإذن أهلهن وآتوهن أجورهن) . والمهر ملك المولى، وإنما يجب تسليمه إلى المولى لا إلى الأمة؟','67','63','0');
INSERT INTO [t8378] VALUES (70,'فإن قيل: كيف قال: (يريد الله ليبين لكم) والإرادة إنما تقرن بأن، يقال: أريد أن تفعل وقال الله تعالى: (يريد الله أن يخفف عنكم) ؟','68','63','0');
INSERT INTO [t8378] VALUES (71,'فإن قيل: كيف قال: (وكان أمر الله مفعولا) . والمفعول مخلوق وأمر الله تعالى وقوله غير مخلوق؟','70','63','0');
INSERT INTO [t8378] VALUES (72,'فإن قيل هذه الآية (تدل) على أن غير الشرك من الذنوب لا يقطع بانتفاء مغفرته، بل يرجى مغفرته','71','63','0');
INSERT INTO [t8378] VALUES (73,'فإن قيل: كيف قال: (ألم تر إلى الذين يزكون أنفسهم بل الله يزكي من يشاء) . ذمهم على ذلك وقال أيضا: (فلا تزكوا أنفسكم هو أعلم بمن اتقى) . وقد زكى النبى عليه الصلاة والسلام نفسه فقال: "والله','72','63','0');
INSERT INTO [t8378] VALUES (74,'فإن قيل: كيف قال: (كلما نضجت جلودهم بدلناهم جلودا غيرها ليذوقوا العذاب) . أخبر أنه يعذب جلودا لم تعص مكان الجلود العاصية وتعذيب البريء ظلم؟','73','63','0');
INSERT INTO [t8378] VALUES (75,'فإن قيل: كيف قال: (ومن يطع الله والرسول فأولئك مع الذين أنعم الله عليهم من النبيين والصديقين والشهداء والصالحين) .','74','63','0');
INSERT INTO [t8378] VALUES (76,'فإن قيل: كيف قال: (إن كيد الشيطان كان ضعيفا) . وقال فى حق النساء: (إن كيدكن عظيم) . ومعلوم أن كيد الشيطان أعظم من كيد النسوان؟','75','63','0');
INSERT INTO [t8378] VALUES (77,'فإن قيل: كيف يقال إن الشر والمعصية بإرادة الله تعالى والله تعالى يقول: (وما أصابك من سيئة فمن نفسك) ؟','76','63','0');
INSERT INTO [t8378] VALUES (78,'فإن قيل: كيف قال: (ولولا فضل الله عليكم ورحمته لاتبعتم الشيطان إلا قليلا) . استثني القليل على تقدير انتفاء الفضل والرحمة، مع أنه لولا فضله بالهداية والعصمة ورحمته لاتبع الكل الشيطان من غير استثناء؟','78','63','0');
INSERT INTO [t8378] VALUES (79,'فإن قيل: هذه الآية تقتضى وجود فضله ورحمته المانع من إتباع أكثر الناس الشيطان، مع أن الواقع خلافه، فإن أكثر الناس كفره، ويؤيده قوله عليه الصلاة والسلام " الإسلام في الكفر كالشعرة البيضاء فى الثور الأسود"؟','79','63','0');
INSERT INTO [t8378] VALUES (80,'فإن قيل: قوله تعالى: (كل ما ردوا إلى الفتنة أركسوا فيها) . وأركسه أي رده فيصير معناه كلما ردوا إلى الفتنة ردوا فيها، وهو تكرار؟','80','63','0');
INSERT INTO [t8378] VALUES (81,'فإن قيل: كيف يقال: إن أهل الكبائر من المؤمنين لا يخلدون فى النار والله تعالى يقول: (ومن يقتل مؤمنا متعمدا فجزاؤه جهنم خالدا فيها وغضب الله عليه ولعنه وأعد له عذابا عظيما) ؟','81','63','0');
INSERT INTO [t8378] VALUES (82,'فإن قيل: كيف صح قولهم: (كنا مستضعفين في الأرض) جوابا لقول الملآئكة: "فيم كنتم " والجواب المطابق أن يقولوا كنا في كذا أو لم نكن في شيء؟','82','63','0');
INSERT INTO [t8378] VALUES (83,'فإن قيل: كيف شرط في إباحة القصر للمسافر خوف العدو بقوله: (وإذا ضربتم في الأرض ... الآية) والقصر جائز مع أمن المسافر؟','83','63','0');
INSERT INTO [t8378] VALUES (84,'فإن قيل: كيف قال: (وترجون من الله ما لا يرجون) والكافرون أيضا يرجون الثواب في محاربة المؤمنين، لأنهم يعتقدون أن دينهم حق، وأنهم ينصرون دين الله ويذبون عنه ويقاتلون أعداءه، كما يعتقدون المؤمنون فالرجاء مشترك؟','84','63','0');
INSERT INTO [t8378] VALUES (85,'فإن قيل: ما فائدة قوله: (أو يظلم نفسه) بعد قوله: (ومن يعمل سوءا) وظلم النفس من عمل السوء، فهلا اقتصر علي الأول لأن الثانى داخل فيه؟','85','63','0');
INSERT INTO [t8378] VALUES (86,'فإن قيل: كيف قال: (إلا من أمر) ثم قال: (ومن يفعل ذلك) ؟','86','63','0');
INSERT INTO [t8378] VALUES (87,'فإن قيل: كيف قال: (إن يدعون من دونه إلا إناثا) .','87','63','0');
INSERT INTO [t8378] VALUES (88,'فإن قيل: كيف خص المؤمنين الصالحين بأنهم لا يظلمون بقوله: (ومن يعمل من الصالحات ... الآية) مع أن غيرهم لا يظلم أيضا؟','88','63','0');
INSERT INTO [t8378] VALUES (89,'فإن قيل: قوله تعالى: (الذين يتربصون بكم فإن كان لكم فتح من الله قالوا ألم نكن معكم) وإن كان للكافرين لم سمى ظفر المسلمين فتحا وظفر الكافرين نصيبا؟','89','63','0');
INSERT INTO [t8378] VALUES (90,'فإن قيل: الجهر بالسوء غير محبوب لله تعالى أصلا، بل المحبوب عنده العفو والصفح والتجاوز. فكيف قال: (لا يحب الله الجهر بالسوء من القول إلا من ظلم) . أى إلا جهر من ظلم؟','90','63','0');
INSERT INTO [t8378] VALUES (91,'فإن قيل: اليهود كانوا كافرين بعيسى يسمونه الساحر ابن الساحرة والفاعل ابن الفاعلة، فكيف أقروا أنه رسول الله بقولهم: (وقولهم إنا قتلنا المسيح عيسى ابن مريم رسول الله) ؟','91','63','0');
INSERT INTO [t8378] VALUES (92,'فإن قيل: كيف يكون للناس على الله حجة قبل الرسل، وهم محجوجون بما نصبه لهم من الأدلة العقلية الموصلة إلى معرفته حتى قال: (لئلا يكون للناس على الله حجة بعد الرسل) ؟','92','63','0');
INSERT INTO [t8378] VALUES (93,'فإن قيل: كلام الله تعالى صفة قديمة (قائمة) بذاته وعيسى عليه الصلاة والسلام مخلوق حادث، فكيف صح إطلاق الكلمة عليه في قوله تعالى: (رسول الله وكلمته) ؟','93','63','0');
INSERT INTO [t8378] VALUES (94,'سورة المائدة','94','0','0');
INSERT INTO [t8378] VALUES (95,'فإن قيل: كيف وجه الارتباط والمناسبة بين قوله تعالى: (يا أيها الذين آمنوا أوفوا بالعقود) وقوله: (أحلت لكم بهيمة الأنعام) ؟','94','94','0');
INSERT INTO [t8378] VALUES (96,'فإن قيل: ما فائدة قوله تعالى: (مكلبين) بعد قوله تعالى: (وما علمتم من الجوارح) والمكلب هو المعلم من كلاب الصيد؟','95','94','0');
INSERT INTO [t8378] VALUES (97,'فإن قيل: المؤمن به هو الله تعالى لقوله: (قولوا آمنا بالله) فالمكفور به يكون هو الله أيضا، ويؤيده قوله تعالى: (كيف تكفرون بالله) وإذا ثبت هذا فكيف قال: (ومن يكفر بالإيمان) مع أنه لا يصح أن يقال: آمن بالإيمان فكذل','96','94','0');
INSERT INTO [t8378] VALUES (98,'فإن قيل: كيف قال في آخر قوله تعالى: (ولقد أخذ الله ميثاق بني إسرائيل ... الآية) . (فمن كفر بعد ذلك منكم فقد ضل سواء السبيل) (مع أن الذين كفروا قبل ذلك أيضا فقد ضلوا سواء السبيل) ؟','97','94','0');
INSERT INTO [t8378] VALUES (99,'فإن قيل: كيف قال: (قد جاءكم من الله نور وكتاب مبين (١٥) يهدي به الله من اتبع رضوانه) مع أن العبد ما لم يهده الله أولا لا يتبع رضوانه فيلزم الدور؟','98','94','0');
INSERT INTO [t8378] VALUES (100,'فإن قيل: كيف يصح الاحتجاج عليهم بقوله تعالى: (قل فلم يعذبكم بذنوبكم) مع أنهم ينكرون تعذيبهم بذنوبهم، ويدعون أن ما يذنبون بالنهار يغفر بالليل، وما يذنبون بالليل يغفر بالنهار؟','99','94','0');
INSERT INTO [t8378] VALUES (101,'فإن قيل: كيف قال: (يا قوم اذكروا نعمة الله عليكم إذ جعل فيكم أنبياء وجعلكم ملوكا) ولم يكن قوم موسي عليه الصلاة والسلام ملوكا؟','100','94','0');
INSERT INTO [t8378] VALUES (102,'فإن قيل: كيف التوفيق بين قوله تعالى: (ادخلوا الأرض المقدسة التي كتب الله لكم) وبين قوله: (قال فإنها محرمة عليهم) ؟','101','94','0');
INSERT INTO [t8378] VALUES (103,'فإن قيل: كيف قال: (إذ قربا قربانا) ولم يقل قربانين، والذى قرباه كان قربانين، لأن كل واحد منهما قرب قربانا؟','102','94','0');
INSERT INTO [t8378] VALUES (104,'فإن قيل: كيف قال هابيل لقابيل: (إني أريد أن تبوء بإثمي وإثمك) أى تتصرف بهما، مع أن إرادة السوء والوقوع في المعصية للأجنبي حرام فكيفه للأخ؟','103','94','0');
INSERT INTO [t8378] VALUES (105,'فإن قيل: كيف يكون قتل الواحد كقتل الكل، واحياء الواحد كإحياء الكل، والدليل يأباه من وجهين:','104','94','0');
INSERT INTO [t8378] VALUES (106,'فإن قيل: كيف وجه قوله تعالى: (إنما جزاء الذين يحاربون الله ورسوله ... الآية) وحقيقة المحاربة بين العبد والرب ممتنعة؟','105','94','0');
INSERT INTO [t8378] VALUES (107,'فإن قيل: ما فائدة قوله تعالى: (فإن جاءوك فاحكم بينهم أو أعرض عنهم) وحال النبي عليه الصلاة والسلام مع أهل الكتاب إذا تحاكموا إليه لايخلوا من هذين القسمين، لأنه إما أن يحكم بينهم أو يعرض عنهم؟','106','94','0');
INSERT INTO [t8378] VALUES (108,'فإن قيل: كيف قال: (فإن تولوا فاعلم أنما يريد الله أن يصيبهم ببعض ذنوبهم) مع أن الكفار معاقبون بكل ذنوبهم؟','107','94','0');
INSERT INTO [t8378] VALUES (109,'فإن قيل: كيف قال: (أذلة على المؤمنين) ولم يقل أذلة للمؤمنين، وإنما يقال ذل له، لا ذل عليه؟','108','94','0');
INSERT INTO [t8378] VALUES (110,'فإن قيل: المثوبة مختصة بالاحسان فكيف قال: (قل هل أنبئكم بشر من ذلك مثوبة عند الله ... الآية) ؟','109','94','0');
INSERT INTO [t8378] VALUES (111,'فإن قيل: ما فائدة قوله: (يا أيها الرسول بلغ ما أنزل إليك من ربك وإن لم تفعل فما بلغت رسالته) .','110','94','0');
INSERT INTO [t8378] VALUES (112,'فإن قيل: كيف ضمن الله تعالى لرسوله العصمة بقوله: (والله يعصمك من الناس) ثم إنه شج وجهه يوم أحد وكسرت رباعيته؟','111','94','0');
INSERT INTO [t8378] VALUES (113,'فإن قيل: ما فائدة قوله: (وضلوا عن سواء السبيل) بعد قوله: (قد ضلوا من قبل) ؟','112','94','0');
INSERT INTO [t8378] VALUES (114,'فإن قيل: مع هذا الإضمار كيف قال: من عمل الشيطان) وتعاطى الخمر والقمار ونحوهما من عمل الإنسان حقيقة؟','113','94','0');
INSERT INTO [t8378] VALUES (115,'فإن قيل: كيف يحسن أن يفعل الله تعالى فعلا يتوسل به إلى تحصيل علم حتى قال: (يا أيها الذين آمنوا ليبلونكم الله بشيء من الصيد تناله أيديكم ورماحكم ليعلم الله من يخافه بالغيب) ؟','114','94','0');
INSERT INTO [t8378] VALUES (116,'فإن قيل: كيف قال: (هديا بالغ الكعبة) مع أن الشرط بلوغه إلى الحرم لا غير؟','115','94','0');
INSERT INTO [t8378] VALUES (117,'فإن قيل: قول تعالى: (يا أيها الذين آمنوا عليكم أنفسكم لا يضركم من ضل) . يدل على عدم وجوب الأمر بالمعروف والنهى عن المنكر وهما واجبان؟','116','94','0');
INSERT INTO [t8378] VALUES (118,'فإن قيل: أي معجزة لعيسى عليه الصلاة والسلام في تكليم الناس كهلا حتى قال: (تكلم الناس في المهد وكهلا) ؟','117','94','0');
INSERT INTO [t8378] VALUES (119,'فإن قيل: كيف قال عيسى عليه الصلاة والسلام: (ما قلت لهم إلا ما أمرتني به ... الآية) مع أنه قال لهم كثيرا من الكلام المباح غير الأمر بالتوحيد؟','118','94','0');
INSERT INTO [t8378] VALUES (120,'فإن قيل: كيف قال: (هذا يوم ينفع الصادقين صدقهم) يعنى يوم القيامة، والصدق نافع في الدنيا والآخرة، ولفظ الآية في قوة الحصر؟','119','94','0');
INSERT INTO [t8378] VALUES (121,'فإن قيل: في السموات والأرض العقلاء وغيرهم، فهلا غلب العقلاء فقال الله تعالى: (له ملك السماوات والأرض) ؟','120','94','0');
INSERT INTO [t8378] VALUES (122,'سوره الأنعام','121','0','0');
INSERT INTO [t8378] VALUES (123,'فإن قيل: ما فائدة قوله تعالى: (وجهركم) بعد قوله: (يعلم سركم) ومعلوم أن من يعلم السر يعلم الجهر بالطريق الأولى؟','121','122','0');
INSERT INTO [t8378] VALUES (124,'فإن قيل: قوله تعالى: (قل أي شيء أكبر شهادة) يقتضى أن يسمى الله تعالى شيئا ولو صح ذلك لصح نداؤه به كالحى والقيوم ونحوها؟','122','122','0');
INSERT INTO [t8378] VALUES (125,'فإن قيل: في قوله تعالى: (ثم لم تكن فتنتهم إلا أن قالوا والله ربنا ما كنا مشركين)','123','122','0');
INSERT INTO [t8378] VALUES (126,'فإن قيل: كيف قال لمحمد عليه الصلاه والسلام: (فلا تكونن من الجاهلين) فخاطبه بأفحش الخطابين وقال لنوح عليه الصلاة والسلام: (إني أعظك أن تكون من الجاهلين)','124','122','0');
INSERT INTO [t8378] VALUES (127,'فإن قيل: ما فائدة قوله تعالى: (وما من دابة في الأرض) والدابة لا تكون إلا في الأرض','125','122','0');
INSERT INTO [t8378] VALUES (128,'فإن قيل: قوله تعالى: (قل أرأيتكم إن أتاكم عذاب الله أو أتتكم الساعة) إلى أن قال: (فيكشف ما تدعون إليه) ومن جملة ما ذكر الدعاء فيه عذاب الساعة، وهو لا يكشف عن المشركين؟','126','122','0');
INSERT INTO [t8378] VALUES (129,'فإن قيل: كيف قال تعالى: (ويعلم ما جرحتم بالنهار) اى ماكسبتم وهو يعلم ما جرحوا ليلا ونهارا؟','127','122','0');
INSERT INTO [t8378] VALUES (130,'فإن قيل: كيف خص كون قوله الحق، وله الملك يوم القيامة فقال: (قوله الحق وله الملك يوم ينفخ في الصور) مع أن قوله الحق في كل وقت، وله الملك في كل زمان؟','128','122','0');
INSERT INTO [t8378] VALUES (131,'فإن قيل: كيف أفرد قوله تعالى: (أو قال أوحي إلي) بالذكر بعد قوله: (ومن أظلم ممن افترى على الله كذبا) وذلك أيضا افتراء؟','129','122','0');
INSERT INTO [t8378] VALUES (132,'فإن قيل: في قوله تعالى: (لا تدركه الأبصار وهو يدرك الأبصار) كيف خص بإدراكه لها ولم يقل وهو يدرك كل شيء، مع أنه أبلغ في التمدح؟','130','122','0');
INSERT INTO [t8378] VALUES (133,'فإن قيل: في قوله تعالى: (فكلوا مما ذكر اسم الله عليه) كيف علق الكون من المؤمنين بأكل الذبيحة المسمى عليها، والكون من المؤمنين حاصل، وإن لم تؤكل الذبيحة أصلا؟','131','122','0');
INSERT INTO [t8378] VALUES (134,'فإن قيل: كيف كرر ذكر شهادتهم على أنفسهم في قوله تعالى: (يا معشر الجن والإنس إن استطعتم ... الآية) والمعنى فيهما واحد؟','132','122','0');
INSERT INTO [t8378] VALUES (135,'فإن قيل: ما فائدة قوله تعالى: (سفها بغير علم) والسفه ليكون إلا عن جهل؟','133','122','0');
INSERT INTO [t8378] VALUES (136,'فإن قيل: كيف قال تعالى: (فإن كذبوك فقل ربكم ذو رحمة واسعة) والموضوع موضوع العقوبة، فكان يحسن أن يقال فيه ذو عقوبة شديدة أو عظيمة ونحو ذلك؟','134','122','0');
INSERT INTO [t8378] VALUES (137,'فإن قيل: كيف خص مال اليتيم بالنهى عن قربانه بغير الأحسن، ومال البالغ كذلك أيضا؟','135','122','0');
INSERT INTO [t8378] VALUES (138,'سورة الأعراف','137','0','0');
INSERT INTO [t8378] VALUES (139,'فإن قيل: كيف قال تعالى: (أهلكناها فجاءها بأسنا) والاهلاك إنما هو بعد مجيء البأس وهو العذاب؟','137','138','0');
INSERT INTO [t8378] VALUES (140,'فإن قيل: كيف قال تعالى لأبليس: (فاهبط منها فما يكون لك أن تتكبر فيها) أى في السماء، وليس له ولا لغيره أن يتكبر فى الأرض أيضا؟','138','138','0');
INSERT INTO [t8378] VALUES (141,'فإن قيل: أي آية لله في اللباس والكسوة حتى قال في آية اللباس والكسوة: (ذلك من آيات الله) ؟','139','138','0');
INSERT INTO [t8378] VALUES (142,'فإن قيل: كيف قال: (كما بدأكم تعودون) وهو بدأنا أولا نطفة ثم علقة ثم مضغة ثم عظاما ثم لحما كما ذكر، ونحن لا نعود عند الموت ولا عند البعث بعد الموت على ذلك الترتيب؟','140','138','0');
INSERT INTO [t8378] VALUES (143,'فإن قيل: لم قال نوح عليه السلام: (ليس بي ضلالة) بالتاء ولم يقل ليس بى ضلال،','141','138','0');
INSERT INTO [t8378] VALUES (144,'فإن قيل: كيف قال صالح لقومه بعدما أخذتهم الرجفة وماتوا: (يا قوم لقد أبلغتكم رسالة ربي ونصحت لكم ولكن لا تحبون الناصحين)','142','138','0');
INSERT INTO [t8378] VALUES (145,'فإن قيل: لم قال شعيب عليه الصلاة والسلام لقومه: (ولا تفسدوا في الأرض بعد إصلاحها) وهم ما زالوا كافرين مفدسين لا مصلحين؟','143','138','0');
INSERT INTO [t8378] VALUES (146,'فإن قيل: كيف قال تعالى هنا: (قال الملأ من قوم فرعون إن هذا لساحر عليم) وقال في سورة الشعراء: (قال للملإ حوله إن هذا لساحر عليم) فنسب هذا القول إلى فرعون؟','144','138','0');
INSERT INTO [t8378] VALUES (147,'فإن قيل: كيف قال الله تعالى هنا حكاية عن السحرة الذين آمنوا وعن فرعون: (قالوا آمنا برب العالمين) إلى قول تعالى: (وتوفنا مسلمين) ثم حكى عنهم هذا المعنى في سورة طه وسورة الشعراء بزيادة ونقصان في الألفاظ المنسوبة إليهم','145','138','0');
INSERT INTO [t8378] VALUES (148,'فإن قيل: كيف الجمع بين قوله تعالى: (ودمرنا ما كان يصنع فرعون وقومه وما كانوا يعرشون) أى أهلكنا وقوله تعالى: (فأخرجناهم من جنات وعيون (٥٧) وكنوز ومقام كريم (٥٨) كذلك وأورثناها','146','138','0');
INSERT INTO [t8378] VALUES (149,'فإن قيل: قوله تعالى: (وواعدنا موسى ثلاثين ليلة وأتممناها بعشر) المواعدة كانت أمره بالصوم في هذا العدد، فكيف ذكر الليالى مع أنها ليست محلا للصوم بل يقع في القلب أن ذكر الأيام أولى لأنها محل الصوم الذي وقعت به المواعدة؟','147','138','0');
INSERT INTO [t8378] VALUES (150,'فإن قيل: لم قال موسى عليه الصلاة والسلام: (وأنا أول المؤمنين) وكان قبله كثيرا من المؤمنين، وهم الأنبياء ومن آمن بهم؟','148','138','0');
INSERT INTO [t8378] VALUES (151,'فإن قيل: كيف عبر عن الندم بالسقوط في اليد في: قوله تعالى: (ولما سقط في أيديهم) وأى مناسبة بينهما؟','149','138','0');
INSERT INTO [t8378] VALUES (152,'فإن قيل: كيف قال تعالى: (أخذ الألواح وفي نسختها) ولم يقل وفيها، وإنما يقال نسختها لشىء كتب مرة ثم نقل، فأما أول مكتوب لا يسمى نسخة، والألواح لم تنقل من مكتوب آخر؟','150','138','0');
INSERT INTO [t8378] VALUES (153,'فإن قيل: الحليم من صفات الله تعالى فكيف قال: (إن ربك سريع العقاب) وسرعة العقاب تنافى صفة الحلم، لأن الحليم هو الذى لا يعجل بالعقوبة على العصاة؟','151','138','0');
INSERT INTO [t8378] VALUES (154,'فإن قيل: كيف قال: (إن أنا إلا نذير وبشير لقوم يؤمنون) وهو عليه الصلاة والسلام كان نذيرا وبشيرا للناس كافة، كما قال تعالى: (وما أرسلناك إلا كافة للناس بشيرا ونذيرا) ؟','152','138','0');
INSERT INTO [t8378] VALUES (155,'سورة الأنفال','154','0','0');
INSERT INTO [t8378] VALUES (156,'فإن قيل: قوله تعالى: (إنما المؤمنون الذين إذا ذكر الله وجلت قلوبهم) إلى آخر الآيتين يدل على أن من لم يتصف بجميع تلك الصفات لا يكون مؤمنا، لأن كلمة إنما للحصر؟','154','155','0');
INSERT INTO [t8378] VALUES (157,'فإن قيل: قوله تعالى: (كما أخرجك ربك من بيتك بالحق) تشبيه فأين المشبه والمشبه به؟','155','155','0');
INSERT INTO [t8378] VALUES (158,'فإن قيل: كيف قال تعالى: (يا أيها الذين آمنوا أطيعوا الله ورسوله ولا تولوا عنه) ثنى في الأمر ثم أفرد في النهي؟','156','155','0');
INSERT INTO [t8378] VALUES (159,'فإن قيل: ما معني قوله تعالى: (ولو علم الله فيهم خيرا لأسمعهم ... الآية) قلنا: معناه ولو علم الله فيهم تصديقا وإيمانا في المستقبل لأنطق لهم','157','155','0');
INSERT INTO [t8378] VALUES (160,'فإن قيل: ما فائدة ذكر السماء في قوله: (فأمطر علينا حجارة من السماء) والمطر إنما يكون من السماء؟','158','155','0');
INSERT INTO [t8378] VALUES (161,'فإن قيل: كيف قال تعالى: (وما كان صلاتهم عند البيت إلا مكاء وتصدية) والمكاء الصفير والتصدية التصفيق وهما ليسا بصلاة؟','159','155','0');
INSERT INTO [t8378] VALUES (162,'فإن قيل: كيف قال تعالى: (قل للذين كفروا إن ينتهوا يغفر لهم ما قد سلف وإن يعودوا) وهم لم ينتهوا عن الكفر، فكيف قال (وإن يعودو) والعود إلى الشيء إنما يكون بعد تركه والإقلاع عنه؟','160','155','0');
INSERT INTO [t8378] VALUES (163,'فإن قيل: قوله تعالى: (ولا تنازعوا فتفشلوا وتذهب ريحكم) يدل على حرمة المنازعة والجدل أيضا لأنه منازعة فكيف تجوز المناظرة وهى منازعة وجدل؟','161','155','0');
INSERT INTO [t8378] VALUES (164,'فإن قيل: أي مناسبة بين الشرط والجزاء في قوله تعالى: (ومن يتوكل على الله فإن الله عزيز حكيم) ؟','162','155','0');
INSERT INTO [t8378] VALUES (165,'فإن قيل: قولو تعالى: (ذلك بأن الله لم يك مغيرا نعمة أنعمها على قوم حتى يغيروا ما بأنفسهم) وذلك إشارة إلى هلاك كفار مكة وآل فرعون، ولم تكن لهم حال مرضية غيروها؟','163','155','0');
INSERT INTO [t8378] VALUES (166,'فإن قيل: كيف قال تعالى: (والله يريد الآخرة) مع أنه أراد الدنيا أيضا، لأنه لولا إرادته إياها لما وجدت، فما فائدة هذا التخصيص؟','164','155','0');
INSERT INTO [t8378] VALUES (167,'سورة التوبة','165','0','0');
INSERT INTO [t8378] VALUES (168,'فإن قيل: لأى سبب تركت كتابة البسملة في أول هذه السورة بخلاف سائر السور؟','165','167','0');
INSERT INTO [t8378] VALUES (169,'فإن قيل: ما فائدة قوله تعالى: (ذلك قولهم بأفواههم) وقول كل أحد إنما يكون بفمه؟','166','167','0');
INSERT INTO [t8378] VALUES (170,'فإن قيل: كيف قال تعالى: (ولا ينفقونها في سبيل الله) والمذكور الذهب والفضة فأعاد الضمير على أحدهما؟','167','167','0');
INSERT INTO [t8378] VALUES (171,'فإن قيل: ما فائدة قوله تعالى: (إن عدة الشهور عند الله اثنا عشر شهرا) وهى عند الناس كذلك أيضا في كل ملة سواء كانت الشهور قمرية أو شمسية؟','168','167','0');
INSERT INTO [t8378] VALUES (172,'فإن قيل: كيف قال تعالى: (فلا تظلموا فيهن أنفسكم) خص الأربعة الحرم بذلك، وظلم النفس منهى عنه في كل زمان؟','169','167','0');
INSERT INTO [t8378] VALUES (173,'فإن قيل: كيف قال تعالى: (فلا تظلموا فيهن أنفسكم) والإنسان لا يظلم نفسه بل يظلم غيره؟','170','167','0');
INSERT INTO [t8378] VALUES (174,'فإن قيل: قوله تعالى: (لا يستأذنك الذين يؤمنون بالله واليوم الآخر) إن كان نهيا فأين الجزم؟','171','167','0');
INSERT INTO [t8378] VALUES (175,'فإن قيل: كيف قال تعالى: (وقيل اقعدوا مع القاعدين) أخبر أنهم أمروا بالقعود، وذمهم على القعود والتخلف عن الخروج للجهاد والاستئذان في القعود؟','172','167','0');
INSERT INTO [t8378] VALUES (176,'فإن قيل: لما عدل في آية الصدقات عن اللام إلى (فى) في المصارف الأربعة الأخيرة؟','173','167','0');
INSERT INTO [t8378] VALUES (177,'فإن قيل: لم عدى فعل الإيمان إلى الله تعالى بالباء، وإلى المؤمنين باللام في قوله تعالى: (يؤمن بالله ويؤمن للمؤمنين) ؟','174','167','0');
INSERT INTO [t8378] VALUES (178,'فإن قيل: كيف قال الله تعالى: (يحذر المنافقون أن تنزل عليهم سورة) وسور القرآن إنما تنزل على النبى عليه الصلاة والسلام لا على المنافقين؟','175','167','0');
INSERT INTO [t8378] VALUES (179,'فإن قيل: كيف قال تعالى: (تنبئهم بما في قلوبهم) وانبائهم بما في قلوبهم تحصيل الحاصل، لأنهم عالمون به فما فائدته؟','176','167','0');
INSERT INTO [t8378] VALUES (180,'فإن قيل: أي فائدة في قوله تعالى: (فاستمتعوا بخلاقهم) مع أن قوله تعالى: (فاستمتعتم بخلاقكم كما استمتع الذين من قبلكم بخلاقهم) بوضع الظاهر موضع المضمر مغن عنه، كما قال تعالى: (وخضتم كالذي','177','167','0');
INSERT INTO [t8378] VALUES (181,'فإن قيل: قوله تعالى: (أولئك حبطت أعمالهم في الدنيا والآخرة) حبوط العمل إن كان عبارة عن بطلان ثوابه، فذلك إنما يكون في الآخرة، وإن كان عبارة عن بطلان منفعته فاعمال المنافقين في الدنيا ليست باطلة المنفعة، لأنهم ينتفعون بها','178','167','0');
INSERT INTO [t8378] VALUES (182,'فإن قيل قوله تعالى: (وما لهم في الأرض من ولي ولا نصير) لم خص الأرض بالنفى، مع أن المنافقين ليس لهم ولى ولا نصير من عذاب الله تعالى في الأرض ولا في السماء في الدنيا ولا في الآخرة؟','179','167','0');
INSERT INTO [t8378] VALUES (183,'فإن قيل: لو كان المراد ما ذكرتم لما خفى ذلك على النبى عليه الصلاة والسلام وهو أفصح العرب، وأعلمهم بأساليب الكلام وتمثيلاته، حتى قال لما أنزلت هذه الآية: إن الله تعالى قد رخص لي، فسأزيد على السبعين، وفى رواية أخرى: فاستغفر لهم أكثر من السبعين ل','180','167','0');
INSERT INTO [t8378] VALUES (184,'فإن قيل: كيف قال تعالى: (ما على المحسنين من سبيل والله غفور رحيم) والمغفرة والرحمة إنما تكون للمسيئين لا للمحسنين؟','181','167','0');
INSERT INTO [t8378] VALUES (185,'فإن قيل: إن كان الله تعالى قد وصف العرب بالجهل في القرآن بقوله تعالى: (وأجدر ألا يعلموا حدود ما أنزل الله على رسوله) فكيف يصر الاحتجاج بألفاظهم وأشعارهم على كتاب الله تعالى وسنة رسوله عليه الصلاة والسلام؟','182','167','0');
INSERT INTO [t8378] VALUES (186,'فإن قيل: كيف قال تعالى: (والناهون عن المنكر) بالواو وما قبلها من الصفات بغير واو؟','183','167','0');
INSERT INTO [t8378] VALUES (187,'فإن قيل: قوله تعالى: (فأما الذين آمنوا فزادتهم إيمانا) يدل على أن الإيمان يقبل بالزيادة؟','184','167','0');
INSERT INTO [t8378] VALUES (188,'سورة يونس عليه السلام','185','0','0');
INSERT INTO [t8378] VALUES (189,'فإن قيل: كيف قال تعالى: (وآخر دعواهم أن الحمد لله رب العالمين) مع أن أقوال أهل الجنة وأحوالهم لا آخر لها، لأن الجنة دار الخلود؟','185','188','0');
INSERT INTO [t8378] VALUES (190,'فإن قيل: كيف شبه تعالى الحياة الدنيا بماء السماء، دون ماء الأرض فقال: (إنما مثل الحياة الدنيا كماء أنزلناه من السماء) ؟','186','188','0');
INSERT INTO [t8378] VALUES (191,'فإن قيل: قوله تعالى: (قل من يرزقكم من السماء والأرض ... الآية) يدل على أنهم معترفون بأن الله تعالى هو الخالق والرازق والمدبر لجميع المخلوقات، فكيف يعترفون بذلك كله ثم يعبدون الأصنام؟','187','188','0');
INSERT INTO [t8378] VALUES (192,'فإن قيل: كيف قال تعالى: (قل هل من شركائكم من يبدأ الخلق ثم يعيده قل الله يبدأ الخلق ثم يعيده) وهم غير معترفين بوجود الإعادة اصلا لا من الله ولا من غيره؟','188','188','0');
INSERT INTO [t8378] VALUES (193,'فإن قيل: كيف قال تعالى: (قل بفضل الله وبرحمته فبذلك فليفرحوا) ولم يقل فبذينك والمشار إليه اثنان الفضل والرحمة؟','189','188','0');
INSERT INTO [t8378] VALUES (194,'فإن قيل: كيف قال تعالى: وما تكون في شأن وما تتلو منه من قرآن) فأفرد ثم قال: (ولا تعملون من عمل) فجمع والخطاب للنبى عليه الصلاة والسلام؟','190','188','0');
INSERT INTO [t8378] VALUES (195,'فإن قيل: كيف قال تعالى هنا: (العزة لله جميعا) وقال في موضع آخر: (ولله العزة ولرسوله وللمؤمنين) ؟','191','188','0');
INSERT INTO [t8378] VALUES (196,'فإن قيل: كيف نوع الخطاب في قوله تعالى: (وأوحينا إلى موسى وأخيه أن تبوآ لقومكما بمصر بيوتا واجعلوا بيوتكم قبلة وأقيموا الصلاة وبشر المؤمنين) فثنى أولا ثم جمع ثم أفرد؟','192','188','0');
INSERT INTO [t8378] VALUES (197,'فإن قيل: كيف قال تعالى: (قال قد أجيبت دعوتكما) أضافها إليهما، والدعوة إنما صدرت من موسى عليه الصلاة والسلام قال الله تعالى: (وقال موسى ربنا إنك آتيت فرعون وملأه ... الآية) ؟','193','188','0');
INSERT INTO [t8378] VALUES (198,'فإن قيل: قوله تعالى: (مما أنزلنا إليك) يدل على أن الخطاب للنبى عليه الصلاة والسلام لا لغيره؟','194','188','0');
INSERT INTO [t8378] VALUES (199,'فإن قيل: قوله تعالى: (ولو شاء ربك لآمن من في الأرض كلهم جميعا) ما فائدة قوله: "جميعا" بعد قوله: "كلهم " وهو يفيد الشمول والإحاطة؟','195','188','0');
INSERT INTO [t8378] VALUES (200,'سورة هود عليه السلام','197','0','0');
INSERT INTO [t8378] VALUES (201,'فإن قيل: كيف قال تعالى: (وأن استغفروا ربكم ثم توبوا إليه) مع أن التوبة مقدمة على الاستغفار؟','197','200','0');
INSERT INTO [t8378] VALUES (202,'فإن قيل: كيف خص تعالى الدابة بذكر ضمان الرزق، والطير كذلك رزقه على الله تعالى، وهو غير الدابة بدليل قوله تعالى: (وما من دابة في الأرض ولا طائر يطير بجناحيه) ؟','198','200','0');
INSERT INTO [t8378] VALUES (203,'فإن قيل: كيف قال تعالى: (وضائق به صدرك) ولم يقل: وضيق؟','199','200','0');
INSERT INTO [t8378] VALUES (204,'فإن قيل: قوله تعالى: (وحبط ما صنعوا فيها) يدل على بطلان أعمالهم فما فائدة قوله بعده: (وباطل ما كانوا يعملون) ؟','200','200','0');
INSERT INTO [t8378] VALUES (205,'فإن قيل: قوله تعالى: (لاعاصم اليوم من أمر الله) لا يناسبه المستثنى في إلظاهر، وهو قوله: (إلا من رحم) لأن المرحوم معصوم فظاهره يقتضى لا معصوم إلا من رحم، أي لا مععوم من الغرق بالطوفان إلا من رحمه الله بالانجاء في السفينة؟','201','200','0');
INSERT INTO [t8378] VALUES (206,'فإن قيل: كيف صح أمر السماء والأرض بقوله تعالى: (وقيل يا أرض ابلعي ماءك ويا سماء أقلعي) وهما لا يعقلان، والأمر والنهى إنما يكون لمن يعقل، ويفهم الخطاب؟','202','200','0');
INSERT INTO [t8378] VALUES (207,'فإن قيل: هو كان رسولا ولم يظهر معجزة، ولهذا قال له قومه: (يا هود ما جئتنا ببينة) فبأى شيء لزمتهم رسالته؟','203','200','0');
INSERT INTO [t8378] VALUES (208,'فإن قيل: قوله تعالى: (فإن تولوا فقد أبلغتكم) جعل التولى شرطا والإبلاغ جزاء، والإبلاغ كان سابقا على التولى؟','204','200','0');
INSERT INTO [t8378] VALUES (209,'فإن قيل: "بعدا " معناه عند العرب الدعاء بالهلاك، كذا نقله الزمخشري فما معني الدعاء عليهم بالهلاك بعد هلاكهم؟','205','200','0');
INSERT INTO [t8378] VALUES (210,'فإن قيل: كيف قال: (بقيت الله خير لكم إن كنتم مؤمنين) شرط الإيمان فيه كون البقية خيرا لهم (وهى خير لهم) مطلقا لأن المراد ببقية الله لهم ما يبقى لهم من الحلال بعد إيفاء الكيل والوزن، وذلك خير لهم وإن كانوا كفار لأنهم يسلمون','206','200','0');
INSERT INTO [t8378] VALUES (211,'فإن قيل: قولهم: (ولولا رهطك لرجمناك وما أنت علينا بعزيز) كلام واقع فيه وفى رهطه، وأنهم الأعزة عليهم دونه، فكيف صح قوله: (أرهطي أعز عليكم من الله) ؟','207','200','0');
INSERT INTO [t8378] VALUES (212,'فإن قيل: كيف قال تعالى: (إذا أخذ القرى وهي ظالمة) والقرى لا تكون ظالمة، لأن الظلم من صفات من يعقل أو من صفات الحيوان دون الجماد؟','208','200','0');
INSERT INTO [t8378] VALUES (213,'فإن قيل: كيف قال تعالى: (فمنهم شقي وسعيد) وكلمة (من) للتبعيض، ومعلوم أن الناس كلهم إما شقى أو سعيد، فما معنى التبعيض هنا؟','209','200','0');
INSERT INTO [t8378] VALUES (214,'فإن قيل: كيف قال تعالى: (ما دامت السماوات والأرض) وأراد به بيان دوام الخلود، لأن أهل الجنة وأهل النار مخلدون فيهما خلودا لا نهاية له. والسموات والأرض دوامهما منقطع، لأنهما يوم القيامة تنهدمان','210','200','0');
INSERT INTO [t8378] VALUES (215,'فإن قيل: إذا كان المراد بهذا التأقيت دوام الخلود دواما لا آخر له، فكيف يصح الاستثناء في قوله تعالى: (إلا ما شاء ربك) ؟','211','200','0');
INSERT INTO [t8378] VALUES (216,'فإن قيل: قوله تعالى: (ولذلك خلقهم) إشارة إلى ماذا؟','213','200','0');
INSERT INTO [t8378] VALUES (217,'فإن قيل: كيف الجمع بين قوله تعالى: (وكلا نقص عليك من أنباء الرسل) وقوله تعالى: (ورسلا قد قصصناهم عليك من قبل ورسلا لم نقصصهم عليك) ؟','215','200','0');
INSERT INTO [t8378] VALUES (218,'فإن قيل: ما فائدة تخصيص هذه السورة بقوله تعالى: (وجاءك في هذه الحق) مع أن الحق جاءه في كل سور القرآن؟','216','200','0');
INSERT INTO [t8378] VALUES (219,'سورة يوسف عليه السلام','218','0','0');
INSERT INTO [t8378] VALUES (220,'فإن قيل: كيف قال: (إني رأيت أحد عشر كوكبا والشمس والقمر) ولم يقل ثلاثة عشر كوكبا وهو أوجز وأحصر، والذى رآه كان أحد عشر كوكبا غير الشمس والقمر؟','218','219','0');
INSERT INTO [t8378] VALUES (221,'فإن قيل: كيف قالوا: (يرتع ويلعب) وكانوا عاقلين بالغين وأنبياء أيضا في قول البعض وكيف رضى يعقوب عليه الصلاة والسلام بذلك؟','219','219','0');
INSERT INTO [t8378] VALUES (222,'فإن قيل: كيف قال تعالى: (وأوحينا إليه) وهو يومئذ لم يكن بالغا، والوحى إنما يكون بعد الأربعين؟','220','219','0');
INSERT INTO [t8378] VALUES (223,'فإن قيل: كيف قال تعالى: (وشهد شاهد من أهلها) ولم يكن قوله شهادة؟','221','219','0');
INSERT INTO [t8378] VALUES (224,'فإن قيل: كيف قال تعالى: (وقالت اخرج عليهن) وإنما يقال خرجت إلى السوق وطرقت عليه الباب فخرج إلى؟','222','219','0');
INSERT INTO [t8378] VALUES (225,'فإن قيل: كيف قال تعالى: (أمر ألا تعبدوا إلا إياه) فسر الأمر بالنهى أو بما جزاؤه بالنهى وهما ضدان؟','223','219','0');
INSERT INTO [t8378] VALUES (226,'فإن قيل: الأنبياء عليهم الصلاة والسلام أعظم الناس زهدا في الدنيا ورغبة في الآخرة، فكيف قال يوسف عليه الصلاة والسلام: (اجعلني على خزائن الأرض) طلب أن يكون معتمدا على الخزائن ومتوليا لها، وهو من أكبر مناصب الدنيا؟','224','219','0');
INSERT INTO [t8378] VALUES (227,'فإن قيل: كيف تأسف يعقوب عليه الصلاة والسلام على يوسف دون أخيه بقوله: (يا أسفى على يوسف) والرزء الأحداث أشد على النفس وأعظم أثرا؟','225','219','0');
INSERT INTO [t8378] VALUES (228,'فإن قيل: كيف ذكر يوسف عليه الصلاة والسلام نعمة الله تعالى عليه في إخراجه من السجن فقال: (وقد أحسن بي إذ أخرجني من السجن) ولم يذكر نعمته عليه فى','226','219','0');
INSERT INTO [t8378] VALUES (229,'فإن قيل: كيف قال يوسف عليه الصلاة والسلام: (توفني مسلما) وهو يعلم أن كل نبى لا يموت إلا مسلما؟','227','219','0');
INSERT INTO [t8378] VALUES (230,'فإن قيل: هذه التلبية توحيد كلها ولا شريك فيها، لأن معنى قولهم: إلا شريكا هو لك، إلا شريكا هو مملوك لك، موصوفا بأنك تملكه وتملك ما ملك؟','228','219','0');
INSERT INTO [t8378] VALUES (231,'فإن قيل: على الوجه الأول أنه ليس بصحيح، لأنا لو جعلنا اللام حقيقه في المعنى العام وهو الاختصاص يلزم منه الكفر حيث وجد نفى الشريك، من غير استثناء، لأنه يلزم منه نفى ملكه تعالى شريك زيد وعمرو ونحوهما، وهو كفر، واللازم منتف لأنه إيمان محض بلا خلاف','229','219','0');
INSERT INTO [t8378] VALUES (232,'سورةالرعد','230','0','0');
INSERT INTO [t8378] VALUES (233,'فإن قيل: كيف قال تعالى: (ومن هو مستخف بالليل وسارب بالنهار) ولم يقل ومن هو سارب بالنهار، ليتناول معنى الاستواء المتخفى والسارب، والا فقد تناول واحدا هو مستخف وسارب أي ظاهر، وليتناسب لفظ الجملة الأولى والثانية، فإنه قال','230','232','0');
INSERT INTO [t8378] VALUES (234,'فإن قيل: كيف المطابقة بين قوله تعالى: (من هو قائم على كل نفس بما كسبت) وقوله تعالى: (وجعلوا لله شركاء) ؟','231','232','0');
INSERT INTO [t8378] VALUES (235,'فإن قيل: كيف قال تعالى: (وقد مكر الذين من قبلهم) أثبت لهم مكرا ثم نفاه بقوله تعالى: (فلله المكر جميعا) ؟','232','232','0');
INSERT INTO [t8378] VALUES (236,'سورة إبراهيم عليه السلام','233','0','0');
INSERT INTO [t8378] VALUES (237,'فإن قيل: قوله تعالى: (وما أرسلنا من رسول إلا بلسان قومه ليبين لهم) هذا في حق غير النبى عليه الصلاة والسلام من','233','236','0');
INSERT INTO [t8378] VALUES (238,'فإن قيل: كيف قال تعالى في سورة البقرة: (يذبحون) وفى سورة الأعراف (يقتلون) بغير واو فيهما، وقال هنا: (ويذبحون) بالواو والقصة واحدة؟','234','236','0');
INSERT INTO [t8378] VALUES (239,'فإن قيل: كيف كرر تعالى الأمر بالتوكل وكيف قال أولا: (وعلى الله فليتوكل المؤمنون) وقال ثانيا: (وعلى الله فليتوكل المتوكلون) ؟','235','236','0');
INSERT INTO [t8378] VALUES (240,'فإن قيل: كيف طابق الجواب السؤال في قوله تعالى: (وبرزوا لله جميعا فقال الضعفاء للذين استكبروا إنا كنا لكم تبعا فهل أنتم مغنون عنا من عذاب الله من شيء قالوا لو هدانا الله ل','236','236','0');
INSERT INTO [t8378] VALUES (241,'فإن قيل: كيف اتصل وارتبط قولهم: (سواء علينا أجزعنا أم صبرنا) بما قبله؟','237','236','0');
INSERT INTO [t8378] VALUES (242,'فإن قيل: كيف قال تعالى: (ويضل الله الظالمين) وقد رأينا كثيرا من الظالمين هداهم الله بالإسلام وبالتوبة، وصاروا من الأتقياء؟','238','236','0');
INSERT INTO [t8378] VALUES (243,'فإن قيل: كيف طابق الأمر بإقامة الصلاة وإنفاق المال وصف اليوم بأنه لا بيع فيه ولا خلال؟','239','236','0');
INSERT INTO [t8378] VALUES (244,'فإن قيل: كيف قال تعالى: (وسخر لكم الشمس والقمر دائبين وسخر لكم الليل والنهار) والمسخر للإنسان هو الذي يكون في طاعته يصرفه كيف يشاء في أمره ونهيه كالدابة والعبد والفلك، كما قال الله تعالى: (وتقولوا سبح','240','236','0');
INSERT INTO [t8378] VALUES (245,'فإن قيل: كيف قال تعالى: (وآتاكم من كل ما سألتموه) والله تعالى لم يعطنا كل ما سألناه ولا بعضا من كل فرد مما سألناه؟','241','236','0');
INSERT INTO [t8378] VALUES (246,'فإن قيل: كيف قال تعالى: (وإن تعدوا نعمة الله لا تحصوها) والإحصاء والعدد بمعنى واحد كذا نقله الجوهرى، فيكون المعنى وأن تعدوا نعمة الله لا تعدوها، وأنه متناقض كقولك: وأن تر زيدا لا تبصره، وإذ الرؤية والأبصار واحد؟','242','236','0');
INSERT INTO [t8378] VALUES (247,'فإن قيل: كيف قال: (رب إنهن أضللن كثيرا من الناس) جعل الأصنام مضلة، والمضل ضال، وقال في موضع آخر: (ويعبدون من دون الله ما لا يضرهم ولا ينفعهم) ونظائره كثيرة فكيف التوفيق بينهما؟','243','236','0');
INSERT INTO [t8378] VALUES (248,'فإن قيل قوله: (الحمد لله الذي وهب لي على الكبر إسماعيل وإسحاق) شكر على نعمة الولد فكيف يناسب قوله بعده: (إن ربي لسميع الدعاء) ؟','244','236','0');
INSERT INTO [t8378] VALUES (249,'فإن قيل: الله تعالى منزه ومتعال عن السهو والغفلة، والنبى عليه الصلاة والسلام أعلم الناس بصفات جلاله وكماله، فكيف يحسبه النبى عليه الصلاة والسلام غافلا حتى نهاه عن ذلك بقوله تعالى: (ولا تحسبن الله غافلا عما يعمل الظالمون)','245','236','0');
INSERT INTO [t8378] VALUES (250,'سورة الحجر','247','0','0');
INSERT INTO [t8378] VALUES (251,'فإن قيل: كيف قالوا: (وقالوا يا أيها الذي نزل عليه الذكر إنك لمجنون) اعترفوا بنبوته لأن الذكر هو القرآن المنزل عليه، ثم وصفوه بالجنون؟','247','250','0');
INSERT INTO [t8378] VALUES (252,'فإن قيل: قوله تعالى: (فسجد الملائكة كلهم أجمعون) دل على الشمول والإحاطة وأفاد التوكيد فما فائدة قوله تعالى: "أجمعون "؟','248','250','0');
INSERT INTO [t8378] VALUES (253,'فإن قيل: كيف قالت الملائكة: (قدرنا إنها لمن الغابرين) أي قضينا، والقضاء لله تعالى لا لهم؟','249','250','0');
INSERT INTO [t8378] VALUES (254,'فإن قيل: كيف قال تعالى هنا: (فوربك لنسألنهم أجمعين (٩٢) عما كانوا يعملون) وقال في سورة الرحمن: (فيومئذ لا يسأل عن ذنبه إنس ولا جان) ؟','250','250','0');
INSERT INTO [t8378] VALUES (255,'سورة النحل','251','0','0');
INSERT INTO [t8378] VALUES (256,'فإن قيل: لم قدمت الاراحة وهى مدخرة في الواقع على الروح وهو مقدم في الواقع في قوله تعالى: (حين تريحون وحين تسرحون) ؟','251','255','0');
INSERT INTO [t8378] VALUES (257,'فإن قيل: إنما ثبت ذلك بالقياس على الأنعام، فإنه منصوص عليه فيها بقوله تعالى: (والأنعام خلقها لكم فيها دفء ومنافع) والمراد به كل منفعة معهودة منها عرفا لا كل منفعة، فثبت مثل ذلك في الخيل والبغال والحمير؟','252','255','0');
INSERT INTO [t8378] VALUES (258,'فإن قيل: قوله تعالى: (أفمن يخلق كمن لا يخلق) المراد بمن لا يخلق الأصنام بدليل قوله تعالى بعده: (والذين يدعون من دون الله لا يخلقون شيئا وهم يخلقون) فكيف جيء بمن المختصة بأولى العلم والعقل؟','253','255','0');
INSERT INTO [t8378] VALUES (259,'فإن قيل: هذا إلزام للذين عبدوا الأصنام، وسموها آلهة تشبيها بالله فقد جعلوا غير الخالق مثل الخالق، فظاهر الإلزام يقتضى أن يقال لهم: إفمن لا يخلق كمن يخلق؟','254','255','0');
INSERT INTO [t8378] VALUES (260,'فإن قيل: قوله تعالى: (وإذا قيل لهم ماذا أنزل ربكم قالوا أساطير الأولين) كيف يعترفون بأنه من عند الله تعالى بالسؤال المعاد في ضمن الجواب، ثم يقولون: هو أساطير الأولين؟','255','255','0');
INSERT INTO [t8378] VALUES (261,'فإن قيل: قوله تعالى: (إنما قولنا لشيء ... الآية) يدل على أن المعدوم شيء، ويدل على أن خطاب المعدوم جائز، والأول منتف عند أكثر العلماء، والثانى منتف بالإجماع؟','256','255','0');
INSERT INTO [t8378] VALUES (262,'فإن قيل: قوله تعالى: (ولو يؤاخذ الله الناس بظلمهم ما ترك عليها من دابة) يقتضى أنه لو أخذ الظالمين بظلمهم لأهلك غير الظالمين من الناس، ولأهلك جميع الدواب غير الناس، ومؤاخذة البريء بسبب ظلم الظالم لا يحسن بالحكيم؟','257','255','0');
INSERT INTO [t8378] VALUES (263,'فإن قيل: لا نسلم أن غير الإنسان من لحيوان مخلوق لمصالح الإنسان، وسنده أنه كان مخلوقا قبل خلق الإنسان بالنقل عن','258','255','0');
INSERT INTO [t8378] VALUES (264,'فإن قيل: كيف قال تعالى: (من الجبال بيوتا ومن الشجر) ولم يقل في الجبال وفى الشجر والاستعمال إنما هو بفى يقال اتخذ فلان بيتا في الجبل أو في الصحراء ونحو ذلك؟','259','255','0');
INSERT INTO [t8378] VALUES (265,'فإن قيل: لما أفرد في قوله تعالى: "ما لا يملك " ثم جمع في قوله: " ولا يستطيعون "؟','260','255','0');
INSERT INTO [t8378] VALUES (266,'فإن قيل: ما فائدة قوله تعالى: (مملوكا) بعد قوله تعالى: (عبدا) وما فائدة قوله: " لا يقدر على شيء) بعد قوله: (مملوكا) ؟','261','255','0');
INSERT INTO [t8378] VALUES (267,'فإن قيل: (أو) في الخبر للشك، والشك على الله تعالى محال، فما معنى قوله تعالى: (إلا كلمح البصر أو هو أقرب) ؟','262','255','0');
INSERT INTO [t8378] VALUES (268,'فإن قيل: كيف قال تعالى: (يعرفون نعمت الله ثم ينكرونها وأكثرهم الكافرون) مع أن كلهم كافرون؟','263','255','0');
INSERT INTO [t8378] VALUES (269,'فإن قيل: لم قالت الأصنام للمشركين: (إنكم لكاذبون) وكانوا صادقين فيما قالوه؟','264','255','0');
INSERT INTO [t8378] VALUES (270,'فإن قيل: كثير من أحكام الشريعة لم تعلم من القرآن نصا ولا استنباطا، كعدد ركعات الصلوات ومقادير ديات الأعضاء، ومدة السفر والمسح، والحيض، ومقدار حد الشرب، ونصاب الزكاة، والسرقة، وما أشبه ذلك مما يطول ذكره؟','265','255','0');
INSERT INTO [t8378] VALUES (271,'فإن قيل: (من) تتناول الذكر والأنثى لغة، ويؤيده قوله تعالى: (من جاء بالحسنة ... الآية)','266','255','0');
INSERT INTO [t8378] VALUES (272,'فإن قيل: كيف قال تعالى: (وإن الله لا يهدي القوم الكافرين) وكثير من الصحابة وغيرهم كانوا كافرين فهداهم الله تعالى إلى الإيمان؟','267','255','0');
INSERT INTO [t8378] VALUES (273,'فإن قيل: كيف قال تعالى: (فأذاقها الله لباس الجوع) ولم يقل فكساها الله لباس الجوع، والإذاقة لا تناسب اللباس وإنما تناسبه الكسوة؟','268','255','0');
INSERT INTO [t8378] VALUES (274,'سورة الإسراء','270','0','0');
INSERT INTO [t8378] VALUES (275,'فإن قيل: كيف قال تعالى: (بعبده ليلا) ولم يقل بنبيه أو برسوله أو بحبيبه أو بصفيه ونحو ذلك مع أن المقصود من ذلك الإسراء تعظيمه وتبجيله؟','270','274','0');
INSERT INTO [t8378] VALUES (276,'فإن قيل: كيف قال تعالى: (باركنا حوله) ولم يقل باركنا عليه أو باركنا فيه، مع أن البركة في المسجد تكون أكثر من خارج المسجد وحوله خصوصا المسجد الأقصى؟','271','274','0');
INSERT INTO [t8378] VALUES (277,'فإن قيل: (وإن أسأتم فلها) ولم يقل فعليها كما قال تعالى: (من عمل صالحا فلنفسه ومن أساء فعليها) ؟','272','274','0');
INSERT INTO [t8378] VALUES (278,'فإن قيل: كيف قال تعالى (وجعلنا آية النهار مبصرة) (والإبصار) من صفات ما له حياة، والمراد بآية النهار إما الشمس أو النهار نفسه وكلاهما غير مبصرة؟','273','274','0');
INSERT INTO [t8378] VALUES (279,'فإن قيل: ما الفائدة في ذكر عدد السنين في قوله تعالى: (ولتعلموا عدد السنين والحساب) مع أنه لو اقتصر على قوله: "لتعلموا الحساب " دخل فيه عدد السنين إذ هو من جملة الحساب؟','274','274','0');
INSERT INTO [t8378] VALUES (280,'فإن قيل: قوله تعالى: (ولا تزر وازرة وزر أخرى) يرد ما جاء في الأخبار أن في يوم القيامة يؤخذ من حسنات المغتاب والمديون ويزاد في حسنات رب الدين، والشخص الذي أغتيب','275','274','0');
INSERT INTO [t8378] VALUES (281,'فإن قيل: على هذا حقيقة أمرهم بالفسق أن يقول لهم أفسقوا وهذا لا يكون من الله تعالى، فلا يقدر الفسق محذوفا ولا مأمورا به؟','276','274','0');
INSERT INTO [t8378] VALUES (282,'فإن قيل: على الوجه الأول لو كان المضمر المحذوف الأمر بالطاعة لما كان مخصوصا بالمترفين، لأن أمر الله تعالى بالطاعة عام للمترفين وغيرهم؟','277','274','0');
INSERT INTO [t8378] VALUES (283,'فإن قيل: قوله تعالى: (من كان يريد العاجلة ... الآية) (يدل) على أن من لم يزهد في الدنيا ولم يتركها كان من أهل النار والأمر بخلافه؟','278','274','0');
INSERT INTO [t8378] VALUES (284,'فإن قيل: كيف قال تعالى: (ولا تقربوا الزنا) ولم يقل ولا تزنوا؟','279','274','0');
INSERT INTO [t8378] VALUES (285,'فإن قيل: كيف قال تعالى: (تسبح له السماوات السبع والأرض ومن فيهن) وقوله: "من فيهن " يتناول الآدميين كلهم','280','274','0');
INSERT INTO [t8378] VALUES (286,'فإن قيل: لو كان المراد هو التسبيح بلسان الحال لما قال: (ولكن لا تفقهون تسبيحهم) لأن التسبيح بلسان الحال مفهوم لنا أي مفهوم ومعلوم؟','281','274','0');
INSERT INTO [t8378] VALUES (287,'فإن قيل: كيف أجمل ذكر الأنبياء كلهم بقوله تعالى: (ولقد فضلنا بعض النبيين على بعض) ثم خص داود بالذكر، فقال: (وآتينا داوود زبورا) ؟','282','274','0');
INSERT INTO [t8378] VALUES (288,'فإن قيل: لم نكر الزبور هنا وعرفه في قوله تعالى: (ولقد كتبنا في الزبور من بعد الذكر) ؟','283','274','0');
INSERT INTO [t8378] VALUES (289,'فإن قيل: قوله تعالى: (وما منعنا أن نرسل بالآيات إلا أن كذب بها الأولون ... الآية) فيها أسئلة أولها','284','274','0');
INSERT INTO [t8378] VALUES (290,'فإن قيل: كيف قال: (والشجرة الملعونة في القرآن) وليس في القرآن لعن شجرة ما؟','287','274','0');
INSERT INTO [t8378] VALUES (291,'فإن قيل: كيف خص أصحاب اليمين بقراءة كتابهم بقوله تعالى: (فمن أوتي كتابه بيمينه فأولئك يقرءون كتابهم) ولم خصهم بنفى الظلم عنهم بقوله تعالى: (ولا يظلمون فتيلا) مع أن أصحاب الشمال يقرءون كتابهم ولا يظلمون أيضا','288','274','0');
INSERT INTO [t8378] VALUES (292,'فإن قيل: كيف قال موسى عليه الصلاة والسلام لفرعون: (قال لقد علمت ما أنزل هؤلاء) يعنى الآيات (إلا رب السماوات والأرض بصائر) يعنى بينات وحججا واضحات، وفرعون لم يعلم ذلك لأنه لو علم ذلك لم يقل لموسى: (إني لأ','289','274','0');
INSERT INTO [t8378] VALUES (293,'فإن قيل: كيف قال موسى عليه الصلاة والسلام: (وإني لأظنك يا فرعون مثبورا) وموسى كان عالما بذلك لا شك عنده فيه؟','290','274','0');
INSERT INTO [t8378] VALUES (294,'سورة الكهف','292','0','0');
INSERT INTO [t8378] VALUES (295,'فإن قيل: قوله تعالى: (قيما) بمعنى مستقيما وقوله: (ولم يجعل له عوجا) مغن عن قوله: "قيما " لأنه متى انتفى العوج ثبتت الاستقامة، لأن العوج في المعانى كالعوج في الأعيان','292','294','0');
INSERT INTO [t8378] VALUES (296,'فإن قيل: كيف قال تعالى: (ثم بعثناهم لنعلم أي الحزبين أحصى لما لبثوا أمدا) وهو عالم بذلك في الأزل؟','293','294','0');
INSERT INTO [t8378] VALUES (297,'فإن قيل: كيف قال تعالى: (لا مبدل لكلماته) وقال في موضع آخر: (وإذا بدلنا آية مكان آية) ويلزم من تبديل الآية بالآية تبديل الكلمات فكيف الجمع بينهما؟','295','294','0');
INSERT INTO [t8378] VALUES (298,'فإن قيل: كيف أفرد تعالى الجنة بعد التثنية فقال: (ودخل جنته) ؟','296','294','0');
INSERT INTO [t8378] VALUES (299,'فإن قيل: ما فائدة قوله: "أنا" في قوله: (إن ترن أنا أقل) ؟','297','294','0');
INSERT INTO [t8378] VALUES (300,'فإن قيل: كيف قال: (هنالك الولاية لله الحق) يعنى في يوم القيامة أو في مقام الآخرة، والولاية بكسر الواو السلطان والملك، وبفتح الواو التولى والنصرة، وكل ذلك لله تعالى في الدنيا والآخرة، يعز من يشاء ويذل من يشاء، وينصر من يشاء ويخ','298','294','0');
INSERT INTO [t8378] VALUES (301,'فإن قيل: كيف قال تعالى: (وحشرناهم) بلفظ الماضي وما قبله مضارعان، وهما قوله تعالى: (ويوم نسير الجبال وترى الأرض بارزة) أي لا شيء عليها يسترها كما كان في الدنيا؟','299','294','0');
INSERT INTO [t8378] VALUES (302,'فإن قيل: قوله تعالى: (إلا إبليس كان من الجن) يدل على أنه من الجن، وقوله تعالى في موضع آخر: (وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس) يدل على أنه من الملآئكة، فكيف الجمع بينهما؟','300','294','0');
INSERT INTO [t8378] VALUES (303,'فإن قيل: كيف قال تعالى: (أفتتخذونه وذريته أولياء من دوني) والأولياء: الأصدقاء والأحباب وهم ضد الأعداء، ويؤيده','301','294','0');
INSERT INTO [t8378] VALUES (304,'فإن قيل: كيف قال تعالى هنا: (ويوم يقول نادوا شركائي الذين زعمتم فدعوهم فلم يستجيبوا لهم) أي لم تجب الأصنام المشركين، فنفى عن الأصنام النطق، وقال تعالى في سورة النحل: (وإذا رأى الذين أشركوا شر','302','294','0');
INSERT INTO [t8378] VALUES (305,'فإن قيل: كيف قال تعالى: (نسيا حوتهما) والناسى إنما كان يوشع وحده بدليل قوله لموسى عليه الصلاة والسلام معتذرا: (فإني نسيت الحوت) أي قصة الحوت وخبره: (وما أنسانيه إلا الشيطان أن أذكره) ؟','303','294','0');
INSERT INTO [t8378] VALUES (306,'فإن قيل: هذا التفسير يدل على أن النسيان من يوشع أو منهما كان بعد حياة الحوت وذهابه في البحر، وظاهر الآية يدل على أن النسيان كان سابقا على ذهابه في البحر متصلا ببلوغ مجمع البحرين لقوله تعالى: (فلما بلغا مجمع بينهما نسيا حوتهم','304','294','0');
INSERT INTO [t8378] VALUES (307,'فإن قيل: كيف قال تعالى: (حتى إذا ركبا في السفينة خرقها) بغير فاء، و (حتى إذا لقيا غلاما فقتله) بالفاء؟','305','294','0');
INSERT INTO [t8378] VALUES (308,'فإن قيل: ما فائدة إعادة ذكر الأهل في قوله تعالى: (استطعما أهلها) وهلا قال: "استطعماهم " لأنه قد سبق ذكر الأهل مرة؟','306','294','0');
INSERT INTO [t8378] VALUES (309,'فإن قيل: لأى سبب لم يفارقه الخضر عليه الصلاة والسلام عند الاعتراض الأول والثانى، وفارقه عند الثالث؟','307','294','0');
INSERT INTO [t8378] VALUES (310,'فإن قيل: ذو القرنين كان نبيا أو تقيا حكيما على اختلاف القولين، فكيف خفى عليه هذا حتى وقع في ظن المستحيل الذي لا يقبله العقل؟','308','294','0');
INSERT INTO [t8378] VALUES (311,'فإن قيل: قوله تعالى: (يا ذا القرنين إما أن تعذب وإما أن تتخذ فيهم حسنا) يدل على أنه كان نبيا لأن الله تعالى خاطبه؟','309','294','0');
INSERT INTO [t8378] VALUES (312,'سورة مريم عليها السلام','311','0','0');
INSERT INTO [t8378] VALUES (313,'فإن قيل: النداء الصوت والصياح يقال ناداه نداء أي صاح به، فكيف وصفه تعالى بكونه خفيا؟','311','312','0');
INSERT INTO [t8378] VALUES (314,'فإن قيل: كيف طلب العلامة على وجود الولد بعد ما بشره الله تعالى به، أكان عنده شك بعد بشارة الله تعالى في وجوده حتى طلب العلامة؟','312','312','0');
INSERT INTO [t8378] VALUES (315,'فإن قيل: اتفق العلماء على أن الوحى لم ينزل على امرأة، ولم يرسل جبريل عليه الصلاة والسلام برسالة إلى امرأة قط، ولهذا قالوا في قوله تعالى: (وأوحينا إلى أم موسى أن أرضعيه)','313','312','0');
INSERT INTO [t8378] VALUES (316,'فإن قيل: ما وجه قراءة الجمهور: (لأهب لك) والواهب للولد هو الله تعالى لا جبريل عليه الصلاة والسلام؟','314','312','0');
INSERT INTO [t8378] VALUES (317,'فإن قيل: ما كان حزن مريم وقولها: (يا ليتني مت قبل هذا وكنت نسيا منسيا) لفقد الطعام والشراب حتى تسلى بالسرى والرطب، بل كان لخوف أن يتهمها أهلها بفعل الفاحشة؟','315','312','0');
INSERT INTO [t8378] VALUES (318,'فإن قيل: كيف قال تعالى: (نكلم من كان في المهد صبيا) وكل واحد كان في المهد صبيا؟','316','312','0');
INSERT INTO [t8378] VALUES (319,'فإن قيل: كيف جاء السلام في قصة يحيى عليه السلام منكرا، وفى قصة عيسى عليه الصلاة والسلام معرفا؟','317','312','0');
INSERT INTO [t8378] VALUES (320,'فإن قيل: الاستغفار للكافرين لا يجوز، فكيف وعد إبواهيم عليه الصلاة والسلام أباه بالاستغفار له بقرله: (سأستغفر لك ربي) ؟','318','312','0');
INSERT INTO [t8378] VALUES (321,'فإن قيل: كيف قال تعالى: (ووهبنا له من رحمتنا أخاه هارون نبيا) وهارون كان أكبر من موسى عليهما الصلاة والسلام فما معنى هبته له؟','319','312','0');
INSERT INTO [t8378] VALUES (322,'فإن قيل: كيف قال تعالى: (إنه كان وعده مأتيا) ولم يقل آتيا كما قال تعالى: (إن ما توعدون لآت) ؟','320','312','0');
INSERT INTO [t8378] VALUES (323,'فإن قيل: ما معنى انفطار السموات وانشقاق الأرض وخرور الجبال من دعوتهم الولد لله تعالى، ومن أين تؤثر هذه الكلمة في الجمادات؟','321','312','0');
INSERT INTO [t8378] VALUES (324,'فإن قيل: كيف قال الله تعالى: (لقد أحصاهم وعدهم عدا) والإحصاء العد على ما نقله الجوهرى، أو الحصر على ما نقله بعض أئمة التفسير، كماسبق ذكره في سورة إبراهيم عليه الصلاة والسلام في قوله تعالى: (وإن تعدوا نعمت الله لا','322','312','0');
INSERT INTO [t8378] VALUES (325,'سورة طه عليه الصلاة والسلام','324','0','0');
INSERT INTO [t8378] VALUES (326,'فإن قيل: كيف حكى الله تعالى قول موسى عليه الصلاة والسلام لأهله عند رؤية النار في هذه السورة، وفى سورة النمل، وفى سورة القصص بعبارات مختلفة، وهذه القضية لم تقع إلا مرة واحدة، فكيف اختلفت (عبارات) موسى عليه الصلاة والسلام فيها؟','324','325','0');
INSERT INTO [t8378] VALUES (327,'فإن قيل: كيف زاد موسى عليه الصلاة والسلام على حرف: الجواب وليس ذلك من شيمة البلغاء خصوما في مخاطبة الملوك؟','325','325','0');
INSERT INTO [t8378] VALUES (328,'فإن قيل: كيف نقل أنها كانت تضيء له بالليل، وتدفع نه الهوام، وتثمر له إذا اشتهى الثمار فغرسها في الأرض من ساعتها، ويركزها فينبع الماء من مركزها، فإذا رفعها نضب، وكان يستقى بها فتطول بطول البئر وتقصر بقصرها، فهلا عدد هذه المنافع؟','326','325','0');
INSERT INTO [t8378] VALUES (329,'فإن قيل: ما فائدة قوله تعالى: (إذ أوحينا إلى أمك ما يوحى) وهذا لا بيان فيه؟','327','325','0');
INSERT INTO [t8378] VALUES (330,'فإن قيل: الخوف والخشية واحد في اللغة، فكيف قال تعالى: (لا تخاف دركا ولا تخشى) ؟','328','325','0');
INSERT INTO [t8378] VALUES (331,'فإن قيل: قوله تعالى: (وما أعجلك عن قومك يا موسى)','329','325','0');
INSERT INTO [t8378] VALUES (332,'فإن قيل: أليس أن أئمة اللغة قالوا العوج بالكسر في المعانى، وبالفتح في الأعيان، ولهذا قال ثعلب: تقول في الأمر والدين عوج، وفى العصا ونحوها عوج والجبال والأرض عين فكيف صح فيهما المكسور في قوله تعالى: (لا ترى فيها عوجا ولا أمتا) ؟','330','325','0');
INSERT INTO [t8378] VALUES (333,'فإن قيل: كيف قال تعالى: (فلا يخرجنكما من الجنة فتشقى) (ولم يقل يقل فتشقيا) والخطاب لآدم وحواء عليهما السلام؟','331','325','0');
INSERT INTO [t8378] VALUES (334,'فإن قيل: هل يجوز أن يقال كان آدم محاميا غاصيا أخذا من قوله تعالى: (وعصى آدم ربه فغوى) ؟','332','325','0');
INSERT INTO [t8378] VALUES (335,'فإن قيل: أي كلمة هى الكلمة التى سبقت من الله تعالى فكانت مانعة من تعذيب هذه الأمة في الدنيا عذاب الاستئصال حتى قال تعالى: (ولولا كلمة سبقت من ربك لكان لزاما) ؟','333','325','0');
INSERT INTO [t8378] VALUES (336,'فإن قيل: أصحاب الصراط السوى والمهتدون واحد، فما فائدة التكرار في قوله تعالى: (فستعلمون من أصحاب الصراط السوي ومن اهتدى) ؟','334','325','0');
INSERT INTO [t8378] VALUES (337,'سورة الأنبياء عليهم السلام','335','0','0');
INSERT INTO [t8378] VALUES (338,'فإن قيل: كيف قال تعالى: (اقترب للناس حسابهم) وصفه بالقرب، وقد مضى من وقت هذا الإخبار أكثر من ستمائة عام ولم يوجد يوم الحساب بعد؟','335','337','0');
INSERT INTO [t8378] VALUES (339,'فإن قيل: النجوى المسارة فما معنى قوله تعالى: (وأسروا النجوى) ؟','336','337','0');
INSERT INTO [t8378] VALUES (340,'فإن قيل: كيف قال تعالى: (ولا يستحسرون) والاستحسار مبالغة في الحسور وهو الاعياء. فكان الأبلغ في وصفهم أن ينفى عنهم أدنى الحسور أو مطلقه لا أقصاه؟','337','337','0');
INSERT INTO [t8378] VALUES (341,'فإن قيل: كيف قال تعالى: (وجعلنا من الماء كل شيء حي)','338','337','0');
INSERT INTO [t8378] VALUES (342,'فإن قيل: كيف قال تعال: (ولا يسمع الصم الدعاء إذا ما ينذرون) مع أن الصم لا يسمعون الدعاء إذا ما ينشرون أيضا؟','339','337','0');
INSERT INTO [t8378] VALUES (343,'فإن قيل: كيف صح مخاطبة النار بقوله تعالى: (نار كوني بردا وسلاما على إبراهيم) ولخطاب إنما يكون مع من يعقل؟','340','337','0');
INSERT INTO [t8378] VALUES (344,'فإن قيل: كيف قال تعالى هنا: (والتي أحصنت فرجها فنفخنا فيها من روحنا) وقال في سورة التحريم','341','337','0');
INSERT INTO [t8378] VALUES (345,'فإن قيل: كيف قال تعالى هنا: (إن الذين سبقت لهم منا الحسنى أولئك عنها مبعدون) وقال تعالى في موضع آخر: (وإن منكم إلا واردها) وواردوها يكون قريبا منها لا بعيدا؟','342','337','0');
INSERT INTO [t8378] VALUES (346,'فإن قيل: كيف قال تعالى هنا: (وإن أدري أقريب أم بعيد ما توعدون) مع اخباره تعالى إياهم بقرب الساعة بقوله تعالى: (أتى أمر الله) وقوله تعالى: (اقتربت الساعة) ونحوهما؟','343','337','0');
INSERT INTO [t8378] VALUES (347,'سورة الحج','345','0','0');
INSERT INTO [t8378] VALUES (348,'فإن قيل: قوله تعالى: (إن زلزلة الساعة شيء عظيم) يدل على أن المعدوم شيء؟','345','347','0');
INSERT INTO [t8378] VALUES (349,'فإن قيل: قوله تعالى: (أقرب من نفعه) يدل على أن في عبادة الصنم نفعا وإن كان فيها ضرر؟','346','347','0');
INSERT INTO [t8378] VALUES (350,'فإن قيل: كيف قال تعالى: (أذن للذين يقاتلون) مع أنهم ما كانوا يقاتلون قبل نزول هذه الآية؟','347','347','0');
INSERT INTO [t8378] VALUES (351,'فإن قيل: كيف قال تعالى: (وكذب موسى) ولم يقل وقوم موسى كما قال تعالى فيما قبله؟','348','347','0');
INSERT INTO [t8378] VALUES (352,'فإن قيل: ما الفرق بين الرسول والنبى مع أن كليهما مرسل بدليل قوله تعالى: (وما أرسلنا من قبلك من رسول ولا نبي) ؟','349','347','0');
INSERT INTO [t8378] VALUES (353,'فإن قيل: كيف قال تعالى: (وما جعل عليكم في الدين من حرج) مع أن قطع اليد التى تساوى خمسة آلاف درهم بسبب سرقة عشرة دراهم حرج في الدين، وكذا رجم المحصن','350','347','0');
INSERT INTO [t8378] VALUES (354,'فإن قيل: كيف قال تعالى: (ملة أبيكم إبراهيم) وإبراهيم عليه الصلاة والسلام لم يكن أبا للأمه كلها؟','351','347','0');
INSERT INTO [t8378] VALUES (355,'سورة المؤمنين','352','0','0');
INSERT INTO [t8378] VALUES (356,'فإن قيل: كيف قال الله تعالى: (والذين هم لفروجهم حافظون (٥) إلا على أزواجهم) وحفظ الفرج إنما يعدي بعن لا بعلى، يقال فلان يحفظ فرجه عن الحرام، ولا يقال على الحرام؟','352','355','0');
INSERT INTO [t8378] VALUES (357,'فإن قيل: قوله تعالى: (أم يقولون به جنة) خبر عن كفار مكه فكيف قال تعالى: (بل جاءهم بالحق) أي بالتوحيد أو بالقرآن، ولم يقل وكلهم كانوا للتوحيد كارهين بدليل قولهم (به جنة) ؟','353','355','0');
INSERT INTO [t8378] VALUES (358,'فإن قيل كيف قال تعالى: (فلا أنساب بينهم يومئذ ولا يتساءلون) وقال تعالى في موضع آخر: (وأقبل بعضهم على بعض يتساءلون) ؟','354','355','0');
INSERT INTO [t8378] VALUES (359,'سورة النور','355','0','0');
INSERT INTO [t8378] VALUES (360,'فإن قيل: كيف قدمت المرأه في آية حد الزنا، وقدم الرجل في آية حد السرقة؟','355','359','0');
INSERT INTO [t8378] VALUES (361,'فإن قيل: كيف قال تعالى: (ولا تكرهوا فتياتكم على البغاء إن أردن تحصنا)','356','359','0');
INSERT INTO [t8378] VALUES (362,'فإن قيل: كيف مثل الله تعالى نوره اي معرفته وهداه في قلب المؤمن بنور المصباح في قوله تعالى: (مثل نوره كمشكاة فيها مصباح) ولم يمثله بنور الشمس، مع أن نورها أتم وأكمل؟','357','359','0');
INSERT INTO [t8378] VALUES (363,'فإن قيل: هب أنه تعالى لم يمثله بنور الشمس لما ذكرتم فكيف لم يمثله بنور الشمع، مع أنه أتم وأكمل وأشرق من نور المصباح؟','358','359','0');
INSERT INTO [t8378] VALUES (364,'فإن قيل: كيف قال تعالى: (والله خلق كل دابة من ماء) وبعض الدواب ليس مخلوقا من الماء كآدم عليه الصلاة والسلام وناقة صالح وغيرهم؟','359','359','0');
INSERT INTO [t8378] VALUES (365,'فإن قيل إذا كان الجواب هذا فما فائدة تخصيص الدابة بالذكر او تخصيص الشيء الحي؟','360','359','0');
INSERT INTO [t8378] VALUES (366,'فإن قيل: كيف أباح الله للقواعد من النساء وهن العجائز التجرد من الثياب بحضرة الرجال بقوله تعالى: (والقواعد من النساء. . . الآية) ؟','361','359','0');
INSERT INTO [t8378] VALUES (367,'فإن قيل معنى السلام هو السلامه فاذا قال الرجل لغيره السلام عليك، كان معناه سلمت منى وأمنت، فما معنى قوله تعالى: (فإذا دخلتم بيوتا فسلموا على أنفسكم) ؟','362','359','0');
INSERT INTO [t8378] VALUES (368,'سورة الفرقان','363','0','0');
INSERT INTO [t8378] VALUES (369,'فإن قيل: الخلق هو تقدير ومنه قوله تعالى: (وإذ تخلق من الطين) أي تقدر فما معنى قوله تعالى: (وخلق كل شيء فقدره تقديرا) فكأنه قال تعالى: وقدر كل شى فقدره تقديرا؟','363','368','0');
INSERT INTO [t8378] VALUES (370,'فإن قيل: كيف شبههم سبحانه وتعالى بالأنعام في الضلال بقوله تعالى: (إن هم إلا كالأنعام) مع أن الأنعام تعرف الله سبحانه وتعالى وتسبحهه بدليل قوله تعالى: (وإن من شيء إلا يسبح بحمده ولكن لا تفقهون تسبيحه','364','368','0');
INSERT INTO [t8378] VALUES (371,'فإن قيل: قوله تعالى: (وأنزلنا من السماء ماء طهورا (٤٨) لنحيي به بلدة ميتا) كيف ذكر الصفه والموصوف مؤنث ولا يؤنثها كما أنثها في قوله: (وآية لهم الأرض الميتة) ؟','365','368','0');
INSERT INTO [t8378] VALUES (372,'فإن قيل: كيف خص تعالى الأنعام بذكر السقى دون غيرها من الحيوان الصامت؟','366','368','0');
INSERT INTO [t8378] VALUES (373,'فإن قيل: كيف قال تعالى هنا: (قل ما أسألكم عليه من أجر) أأجرا لأن من لتأكيد النفى وعمومه وقال تعالى في آية أخرى: (قل لا أسألكم عليه أجرا إلا المودة في القربى) فأثبت سؤال الأجر عليه؟','367','368','0');
INSERT INTO [t8378] VALUES (374,'سورة الشعراء','369','0','0');
INSERT INTO [t8378] VALUES (375,'فإن قيل: كيف قال تعالى: (فظلت أعناقهم لها خاضعين) والأعناق لا تعقل؟','369','374','0');
INSERT INTO [t8378] VALUES (376,'فإن قيل: كيف قال موسى عليه الصلاة والسلام معتذرا عن قتل القبطي والنبي لا يكون ضالا؟','370','374','0');
INSERT INTO [t8378] VALUES (377,'فإن قيل: كيف قال موسي عليه الصلاه والسلام: (رب السماوات والأرض وما بينهما إن كنتم موقنين) علق كونه تعالى رب السموات والأرض وما بينهما بشرط كون فرعون وقومه موقنين وهذا الشرط منتف والربوبيه ثابته فكيف صح التعليق؟','371','374','0');
INSERT INTO [t8378] VALUES (378,'فإن قيل: كيف قال أولا: (إن كنتم موقنين) وقال آخرا: (إن كنتم تعقلون) ؟','372','374','0');
INSERT INTO [t8378] VALUES (379,'فإن قيل: كيف كرر الله تعالى ذكر قصة موسي عليه الصلاة والسلام أكثر من قصص غيره من الأنبياء عليهم الصلاة والسلام؟','373','374','0');
INSERT INTO [t8378] VALUES (380,'فإن قيل: هذا الجواب يبطل بقوله: (والذي يميتني) بقول الخضر عليه الصلاه والسلام: (فأردنا أن يبدلهما) ؟','374','374','0');
INSERT INTO [t8378] VALUES (381,'فإن قيل: كيف قال تعالى: (وأزلفت الجنة للمتقين) أي قربت والجنه لا تنقل من مكانها ولا تحول؟','375','374','0');
INSERT INTO [t8378] VALUES (382,'فإن قيل: قوله تعالى: (فعقروها فأصبحوا نادمين) كيف أخذهم العذاب بعد ما ندموا على جنايتهم وقد قال صلي الله عليه وسلم: الندم توبة؟','376','374','0');
INSERT INTO [t8378] VALUES (383,'فإن قيل: ما الفرق بين حذف الواو في قصة صالح عليه الصلاة والسلام وإثباتها في قصة شعيب عليه الصلاة والسلام في قولهم: (ما أنت إلا بشر مثلنا) و (وما أنت إلا بشر مثلنا) ؟','377','374','0');
INSERT INTO [t8378] VALUES (384,'سورة النمل','378','0','0');
INSERT INTO [t8378] VALUES (385,'فإن قيل: العطف يقتضى المغايرة فكيف عطف الكتاب المبين على القرآن والمراد به القرآن؟','378','384','0');
INSERT INTO [t8378] VALUES (386,'فإن قيل: كيف قال تعالى هنا: (سآتيكم) وقال قى سورة طه: (لعلى آتيكم) وأحدهما قطع، والآخر ترج، والقصة واحدة؟','379','384','0');
INSERT INTO [t8378] VALUES (387,'فإن قيل: ما وجه صحة الاستثناء في قوله تعالى: (إني لا يخاف لدي المرسلون إلا من ظلم....... الآية) ؟','380','384','0');
INSERT INTO [t8378] VALUES (388,'فإن قيل: كيف حل له تعذيب الهدهد حتى قال: (لأعذبنه عذابا شديدا) ؟','381','384','0');
INSERT INTO [t8378] VALUES (389,'فإن قيل: قوله تعالى: (فألقه إليهم ثم تول عنهم فانظر ماذا يرجعون) إذا تولى عنهم فكيف يعلم جوابهم؟','382','384','0');
INSERT INTO [t8378] VALUES (390,'فإن قيل: كيف قالت: (وأسلمت مع سليمان لله رب العالمين) وهى إنما أسلمت بعده، على يده لا معه، لأنه كان مسلما قبلها؟','383','384','0');
INSERT INTO [t8378] VALUES (391,'فإن قيل: كيف قال تعالى: (قل لا يعلم من في السماوات والأرض الغيب إلا الله) ونحن نعلم الجنة والنار وأحوال القيامة وكلها غيب؟','384','384','0');
INSERT INTO [t8378] VALUES (392,'فإن قيل: قضاء الله تعالى وحكمه واحد فما معنى قوله تعالى: (إن ربك يقضي بينهم بحكمه) وهو بمنزلة قوله تعالى: (إن ربك يقضي بينهم)','385','384','0');
INSERT INTO [t8378] VALUES (393,'فإن قيل: كيف قال تعالى: (ألم يروا أنا جعلنا الليل ليسكنوا فيه والنهار مبصرا) ولم يراع المقابلة بقوله تعالى: (والنهار مبصرا) أي ليبصروا فيه؟','386','384','0');
INSERT INTO [t8378] VALUES (394,'سورة القصص','388','0','0');
INSERT INTO [t8378] VALUES (395,'فإن قيل: ما فائدة وحى الله تعالى إلى أم موسى عليه السلام بإرضاعه وهى ترضعه طبعا سواء أمرت بذلك أم لا؟','388','394','0');
INSERT INTO [t8378] VALUES (396,'فإن قيل: موسى عليه السلام ما سقى لابنتى شعيب طلبا للأجر، فكيف أجاب دعوتها لما قالت له: (إن أبي يدعوك ليجزيك أجر ما سقيت لنا) ؟','389','394','0');
INSERT INTO [t8378] VALUES (397,'فإن قيل: ما معنى قول تعالى: (واضمم إليك جناحك من الرهب) ؟','390','394','0');
INSERT INTO [t8378] VALUES (398,'فإن قيل: قوله تعالى: (وما كنت بجانب الغربي إذ قضينا إلى موسى الأمر) أي أحكمنا إليه الوحى مغنى عن قوله تعالى: (وما كنت من الشاهدين) أي من الحاضرين عند ذلك؟','391','394','0');
INSERT INTO [t8378] VALUES (399,'فإن قيل: كيف قال تعالى في آخر آية الليل: (بضياء أفلا تسمعون) وقال في آخر آية النهار: (بليل تسكنون فيه أفلا تبصرون) ؟','392','394','0');
INSERT INTO [t8378] VALUES (400,'سورة العنكبوت','393','0','0');
INSERT INTO [t8378] VALUES (401,'فإن قيل: كيف قال تعالى: (وما هم بحاملين من خطاياهم من شيء) ثم قال: (وليحملن أثقالهم وأثقالا مع أثقالهم) ؟','393','400','0');
INSERT INTO [t8378] VALUES (402,'فإن قيل: كيف نكر الرزق ثم عرفه في قوله تعالى: (إن الذين تعبدون من دون الله لا يملكون لكم رزقا فابتغوا عند الله الرزق) ؟','394','400','0');
INSERT INTO [t8378] VALUES (403,'فإن قيل: كيف قالوا: (إنا مهلكو أهل هذه القرية) يعنون مدينة قوم لوط عليه السلام، ولم يقولوا تلك القرية مع أن مدينة قوم لوط كانت بعيدة عن موضع إبراهيم عليه السلام، غائبة عند وقت هذا الخطاب؟','395','400','0');
INSERT INTO [t8378] VALUES (404,'فإن قيل: كيف قال تعالى: (أوهن البيوت لبيت العنكبوت لو كانوا يعلمون) وكل أحد يعلم أن أضعف البيوت تتخذها الهوام بيت العنكبوت؟','396','400','0');
INSERT INTO [t8378] VALUES (405,'فإن قيل: كيف لم يؤكد سبحانه في التلاوة ولم يقل: وما كنت تتلوا من قبله من كتاب بلسانك؟','397','400','0');
INSERT INTO [t8378] VALUES (406,'سورة الروم','399','0','0');
INSERT INTO [t8378] VALUES (407,'فإن قيل: كيف ذكر الضمير في قوله تعالى: (وهو أهون عليه) والمراد به الإعادة لسبق قوله تعالى: (وهو الذي يبدأ الخلق ثم يعيده) ؟','399','406','0');
INSERT INTO [t8378] VALUES (408,'فإن قيل: كيف معنى قوله تعالى: (وما آتيتم من ربا ... الآية) على اختلاف القراءتين بالمد والقصر؟','401','406','0');
INSERT INTO [t8378] VALUES (409,'فإن قيل: ما فائدة قوله تعالى: (من قبله) بعد قوله تعالى: (من قبل أن ينزل عليهم) ؟','402','406','0');
INSERT INTO [t8378] VALUES (410,'فإن قيل: كيف قال تعالى هنا: (ولا هم يستعتبون) وقال تعالى في موضع آخر: (وإن يستعتبوا فما هم من المعتبين) فجعلهم مرة طالبين للإعتاب، ومرة مطلوبا منهم الإعتاب؟','403','406','0');
INSERT INTO [t8378] VALUES (411,'سورة لقمان عليه السلام','404','0','0');
INSERT INTO [t8378] VALUES (412,'فإن قيل: كيف يحل سماع الغناء بعد قوله تعالى: (ومن الناس من يشتري لهو الحديث. ... الآية) .','404','411','0');
INSERT INTO [t8378] VALUES (413,'فإن قيل: كيف وقع قوله تعالى: (ووصينا الإنسان بوالديه ... الآيتان) في أثناء وصية لقمان لابنه، وما الجامع بينهما؟','405','411','0');
INSERT INTO [t8378] VALUES (414,'فإن قيل: كيف قال تعالى: (إن أنكر الأصوات لصوت الحمير) فجمع الأصوات وأفرد صوت الحمير؟','406','411','0');
INSERT INTO [t8378] VALUES (415,'فإن قيل: في قوله تعالى: (إن الله عنده علم الساعة.... الآية) كيف أضاف العلم إلى نفسه في الأمور الثلاثة من الخمسة المغيبات، ونفى العلم عن العباد في الأمرين الآخرين، مع أن الأمور الخمسة سواء في اختصاص الله تعالى بعلمها وانتفاء علم','407','411','0');
INSERT INTO [t8378] VALUES (416,'سورة السجدة','408','0','0');
INSERT INTO [t8378] VALUES (417,'فإن قيل: كيف قال تعالى هنا: (يدبر الأمر من السماء إلى الأرض ثم يعرج إليه في يوم كان مقداره ألف سنة مما تعدون) وقال تعالى في سورة المعارج: (تعرج الملائكة والروح إليه في يو','408','416','0');
INSERT INTO [t8378] VALUES (418,'فإن قيل: كيف قال تعالى هنا: (من سلالة من ماء مهين) وقال تعالى في موضع آخر: (من سلالة من طين) ؟','409','416','0');
INSERT INTO [t8378] VALUES (419,'فإن قيل: كيف قال تعالى هنا: (قل يتوفاكم ملك الموت) وقال تعالى في موضع آخر: (توفته رسلنا) وقال تعالى في موضع آخر: (الله يتوفى الأنفس حين موتها) ؟','410','416','0');
INSERT INTO [t8378] VALUES (420,'فإن قيل: قوله تعالى: (أفمن كان مؤمنا كمن كان فاسقا لا يستوون) يدل على أن الفاسق لا يكون مؤمنا؟','411','416','0');
INSERT INTO [t8378] VALUES (421,'فإن قيل: على قول من فسر الفتح فتح مكة أو بفتح يوم بدر كيف وجه الجواب، وقد نفع بعض الكفار، إيمانهم في ذينك اليومين وهم الطلقاء الذين آمنوا؟','412','416','0');
INSERT INTO [t8378] VALUES (422,'سورة الأحزاب','413','0','0');
INSERT INTO [t8378] VALUES (423,'فإن قيل: كيف قال تعالي: (يا أيها النبي) ولم يقل يا محمد كما قال تعالى: يا موسى ويا عيسى ويا داود ونحوه؟','413','422','0');
INSERT INTO [t8378] VALUES (424,'فإن قيل: ما فائدة ذكر الجوف في قوله تعالى: (ما جعل الله لرجل من قلبين في جوفه) ؟','414','422','0');
INSERT INTO [t8378] VALUES (425,'فإن قيل: كيف قال تعالى: (وأزواجه أمهاتهم) جعل أزواج النبى عليه السلام بمنزلة أمهات المؤمنين حكما، وما جعل النبى عليه السلام بمنزلة أبيهم حكما، كما قال تعالى: (ما كان محمد أبا أحد من رجالكم) ؟','415','422','0');
INSERT INTO [t8378] VALUES (426,'فإن قيل: كيف قدم عليه نوح عليهما السلام في نظير هذه الآية وهى قول تعالى: (شرع لكم من الدين ما وصى به نوحا والذي أوحينا إليك) ؟','416','422','0');
INSERT INTO [t8378] VALUES (427,'فإن قيل: كيف قال تعالى في وصف حال المؤمنين التى أمتن عليهم فيها: (وبلغت القلوب الحناجر) ولو بلغت القلوب الحناجر لماتوا، ولم يبق للإمتنان وجه؟','417','422','0');
INSERT INTO [t8378] VALUES (428,'فإن قيل: كيف أظهر تعالى الأسمين مع تقدم ذكرهما في قول تعالى: (ولما رأى المؤمنون الأحزاب قالوا هذا ما وعدنا الله ورسوله وصدق الله ورسوله) ؟','418','422','0');
INSERT INTO [t8378] VALUES (429,'فإن قيل: كيف قال تعالى: (يا نساء النبي لستن كأحد من النساء) ولم يقل كواحدة من النساء؟','419','422','0');
INSERT INTO [t8378] VALUES (430,'فإن قيل: كيف قال تعالى: (ما كان محمد أبا أحد من رجالكم) مع أنه كان أبا للطاهر والطيب والقاسم وإبراهيم؟','420','422','0');
INSERT INTO [t8378] VALUES (431,'فإن قيل: كيف شبه الله تعالى النبى صلى الله عليه وصلم بالسراج دون الشمس والشمس أتم وأكمل؟','421','422','0');
INSERT INTO [t8378] VALUES (432,'فإن قيل: كيف خص تعالى المؤمنات بعدم وجوب العدة في الطلاق قبل المسيس في قوله تعالى: (يا أيها الذين آمنوا إذا نكحتم المؤمنات ثم طلقتموهن ... الآية) مع أن حكم الكتابية كذلك أيضا؟','422','422','0');
INSERT INTO [t8378] VALUES (433,'فإن قيل: كيف ذكر سبحانه الأقارب في قوله تعالى: (لا جناح عليهن في آبائهن.... الآية) ولم يذكر العم والخال وحكمهما حكم من ذكر في رفع الجناح؟','423','422','0');
INSERT INTO [t8378] VALUES (434,'سورة سبأ','425','0','0');
INSERT INTO [t8378] VALUES (435,'فإن قيل: كيف قال تعالى: (أفلم يروا إلى ما بين أيديهم وما خلفهم من السماء والأرض) ولم يقل إلى ما فوقهم وما تحتهم من السماء والأرض؟','425','434','0');
INSERT INTO [t8378] VALUES (436,'فإن قيل: كيف قال تعالى: (قل ادعوا الذين زعمتم من دون الله) أي الذين زعمتموهم آلهة من دون الله مع أن المشركين ما زعموا غير الله إلها دون الله، بل مع الله على وجه الشركة؟','426','434','0');
INSERT INTO [t8378] VALUES (437,'سورة فاطر','427','0','0');
INSERT INTO [t8378] VALUES (438,'فإن قيل: في قوله تعالى: (والله الذي أرسل الرياح فتثير سحابا فسقناه إلى بلد ميت فأحيينا به الأرض بعد موتها) كيف جاء "فتثير" مضارعا دون ما قبله وما بعده؟','427','437','0');
INSERT INTO [t8378] VALUES (439,'فإن قيل: ما فائدة قوله تعالى: (ربنا أخرجنا نعمل صالحا غير الذي كنا نعمل) مع أنه يوهم أنهم يعملون صالحا غير الذي عملوه، وهم ما عملوا صالحا، بل سيئا؟','428','437','0');
INSERT INTO [t8378] VALUES (440,'سورة يس عليه السلام','429','0','0');
INSERT INTO [t8378] VALUES (441,'فإن قيل: كيف قال تعالى أولا: (إنا إليكم مرسلون) وقال الله تعالى ثانيا: (إنا إليكم لمرسلون) ؟','429','440','0');
INSERT INTO [t8378] VALUES (442,'فإن قيل: كيف قال تعالى: (وآية لهم) أي لأهل مكة، (أنا حملنا ذريتهم) أي ذرية أهل مكة أو ذرية قوم نوح عليه الصلاة والسلام (في الفلك المشحون) والذرية اسم للأولاد والمحمول في سفينة نوح عليه السلام آباء أهل مكة لا أولادهم','430','440','0');
INSERT INTO [t8378] VALUES (443,'فإن قيل: قولهم: (من بعثنا من مرقدنا) سؤال عن البعث فكيف طابقه ما بعده جوابا؟','431','440','0');
INSERT INTO [t8378] VALUES (444,'فإن قيل: كيف قال تعالى: (وما علمناه الشعر) مع أنه عليه الصلاة والسلام قد روى عنه ما هو شعر وهو قوله عليه الصلاة والسلام: أنا النبى لا كذب. . . أنا ابن عبد المطلب','432','440','0');
INSERT INTO [t8378] VALUES (445,'فإن قيل: كيف قال تعالى: (مما عملت أيدينا أنعاما) والله تعالى منزه عن الجارحة؟','433','440','0');
INSERT INTO [t8378] VALUES (446,'سورة الصافات','434','0','0');
INSERT INTO [t8378] VALUES (447,'فإن قيل: كيف جمع تعالى المشارق هنا، وثناهما في سورة الرحمن، وكيف اقتصر هنا على ذكر المشارق، وذكر ثم المغربين أيضا، وذكر المغارب مع المشارق مجموعين','434','446','0');
INSERT INTO [t8378] VALUES (448,'فإن قيل: لأى فائدة ذكر الله تعالى تزيين السماء الدنيا، وكان رؤيته بين الساء الدنيا ظإهرا لا يحتاج إلى ذكره بقوله: (إنا زينا السماء الدنيا) فينبغى أن يذكر لنفسه سماء غير الدنيا؟','435','446','0');
INSERT INTO [t8378] VALUES (449,'فإن قيل: كيف مدح سبحانه نوح عليه الصلاة والسلام بقوله: (إنه من عبادنا المؤمنين) مع أن مرتبة الرسل فوق مرتبة المؤمنين؟','436','446','0');
INSERT INTO [t8378] VALUES (450,'فإن قيل: كيف استجاز إبراهيم عليه السلام أن يقول: (إنى سقيم) ولم يكن سقيما؟','437','446','0');
INSERT INTO [t8378] VALUES (451,'فإن قيل: قوله تعالى: (فراغ عليهم ضربا باليمين (٩٣) فأقبلوا إليه يزفون) أي يسرعون، يدل على أنهم عرفوا أنه هو الكاسر لها، وقوله تعالى في سورة الأنبياء: (قالوا من فعل هذا بآلهتنا إنه لمن الظالم','438','446','0');
INSERT INTO [t8378] VALUES (452,'فإن قيل: ما معنى قوله عليه السلام: (سيهدين) وهو كان مهتديا؟','439','446','0');
INSERT INTO [t8378] VALUES (453,'فإن قيل: كيف قيل له: (قد صدقت الرؤيا) وإنما يكون مصدقا لها لو وجد منه الذبح ولم يوجد؟','440','446','0');
INSERT INTO [t8378] VALUES (454,'فإن قيل: كيف قال تعالى: (وإن لوطا لمن المرسلين (١٣٣) إذ نجيناه وأهله أجمعين) وهو كان من المرسلين قبل زمان التنجية؟','441','446','0');
INSERT INTO [t8378] VALUES (455,'فإن قيل: ما فائدة تكرار الأمر بالتولية والإبصار في قوله تعالى: (فتول عنهم حتى حين) ، (وأبصرهم) الآيات؟','442','446','0');
INSERT INTO [t8378] VALUES (456,'سورة ص','443','0','0');
INSERT INTO [t8378] VALUES (457,'فإن قيل: أين جواب القسم في قوله تعالى: (ص والقرآن ذي الذكر) ؟','443','456','0');
INSERT INTO [t8378] VALUES (458,'فإن قيل: كيف قل الملكان لما دخلا علي داود عليه السلام: (خصمان بغى بعضنا على بعض) ، والملآئكة لا يوجد منهم البغى والظلم، وكيف قال: (إن هذا أخي له تسع وتسعون نعجة) إلى آخره، ولم يكن كما قال؟','444','456','0');
INSERT INTO [t8378] VALUES (459,'فإن قيل: ما معنى تكرار الحب في قوله عليه السلام: (أحببت حب الخير) وما معنى تعديته بعن وظاهره، أحببت حبا مثل حب الخير، كما تقول أحببت حب زيد، أي أحببت حبا مثل حب زيد؟','445','456','0');
INSERT INTO [t8378] VALUES (460,'فإن قيل: كيف قال تعالى في وصف أيوب عليه السلام: (إنا وجدناه صابرا) مع أن الصبر هو ترك الشكوى من ألم البلوى على ما قيل، وهو قد شكا؟','446','456','0');
INSERT INTO [t8378] VALUES (461,'فإن قيل: قوله تعالى: (وإن عليك لعنتي إلى يوم الدين) يدل على أن غاية لعنة الله تعالى لإبليس هى يوم القيامة ثم تنقطع؟','447','456','0');
INSERT INTO [t8378] VALUES (462,'سورة الزمر','448','0','0');
INSERT INTO [t8378] VALUES (463,'فإن قيل: كيف قال تعالى: (إن الله لا يهدي من هو كاذب كفار) وكم من كاذب كفار قد هداه الله تعالى فأسلم وصدق؟','448','462','0');
INSERT INTO [t8378] VALUES (464,'فإن قيل: كيف قال تعالى: (وأنزل لكم من الأنعام ثمانية أزواج) مع أن الأنعام مخلوقة في الأرض، لا منزلة من السماء؟','449','462','0');
INSERT INTO [t8378] VALUES (465,'فإن قيل: كيف قال تعالى في وصف الذي جاء بالصدق وصدق به: (ليكفر الله عنهم أسوأ الذي عملوا ويجزيهم أجرهم بأحسن الذي كانوا يعملون) مع أنه سبحانه وتعالى يكفر عنهم سيئ أعمالهم، ويجزيهم بحسنها أيضا؟','450','462','0');
INSERT INTO [t8378] VALUES (466,'فإن قيل: كيف قال تعالى: (واتبعوا أحسن ما أنزل إليكم من ربكم) والقرآ كله حسن؟','451','462','0');
INSERT INTO [t8378] VALUES (467,'فإن قيل: كيف عبر سبحانه عن الذهاب بأهل الجنة والنار بلفظ السوق وفيه نوع إهانة؟','452','462','0');
INSERT INTO [t8378] VALUES (468,'سورة غافر','453','0','0');
INSERT INTO [t8378] VALUES (469,'فإن قيل: كيف قال تعالى: {ما يجادل في آيات الله إلا الذين كفروا} مع أن الذين آمنوا أيضا يجادلون فيها , هل هي منسوخة أم محكمة؟ وهل فيها مجاز أم كلها حقيقية؟ وهل هي مخلوقة أم قديمة؟ وغير ذلك؟','453','468','0');
INSERT INTO [t8378] VALUES (470,'فإن قيل: قوله تعالى {لا يخفى على الله منهم شيء} بيان وتقرير لبروزهم في قوله تعالى {يوم هم بارزون} والله تعالى لا يخفى عليه شيء برزوا أو لم يبرزو؟','454','468','0');
INSERT INTO [t8378] VALUES (471,'فإن قيل: التولى والإدبار واحد فما فائدة قوله تعالى: (يوم تولون مدبرين) ؟','456','468','0');
INSERT INTO [t8378] VALUES (472,'فإن قيل: مثل السيئة سيئة فما معنى قوله تعالى: (ومن جاء بالسيئة فلا يجزى إلا مثلها) ؟','457','468','0');
INSERT INTO [t8378] VALUES (473,'فإن قيل: كيف قال تعالى: (وعلى الفلك تحملون) ولم يقل وفى الفلك كما قال تعالى: (قلنا احمل فيها من كل زوجين اثنين) ؟','458','468','0');
INSERT INTO [t8378] VALUES (474,'سورة فصلت','459','0','0');
INSERT INTO [t8378] VALUES (475,'فإن قيل: ما فائدة زيادة " من " في قوله تعالى: (ومن بيننا وبينك حجاب) مع أن المعنى حاصل بقوله تعالى: وبيننا وبينك حجاب؟','459','474','0');
INSERT INTO [t8378] VALUES (476,'فإن قيل: كيف قال تعالى في وصف أهل النار: (فإن يصبروا فالنار مثوى لهم) مع أنهم إن لم يصبروا على عذاب النار وجزعوا فالنار مثوى لهم أيضا؟','460','474','0');
INSERT INTO [t8378] VALUES (477,'فإن قيل: ما فائدة قوله تعالى: (ولا للقمر) بعد قوله تعالى: (لا تسجدوا للشمس) وهو مستفاد من الأول بالطريق الأولى؟','461','474','0');
INSERT INTO [t8378] VALUES (478,'سورة الشورى','462','0','0');
INSERT INTO [t8378] VALUES (479,'فإن قيل: كيف قال تعالي: (كذلك يوحي إليك وإلى الذين من قبلك) بلفظ المضارع، والوحى إلي من قبله ماضى؟','462','478','0');
INSERT INTO [t8378] VALUES (480,'فإن قيل: ما معنى قوله تعالى: (إلا المودة في القربى) ولم لم يقل سبحانه إلا مودة القربى، أي القرابة، أو إلا المودة للقربى؟','463','478','0');
INSERT INTO [t8378] VALUES (481,'فإن قيل: كيف يقال إن الله تعالى كلم محمدا عليه السلام ليلة المعراج مواجهة بغير حجاب ولا واسطة، وقد حصل الله تعالى تكليمه للبشر في طريق الوحى وهو الإلهام، كما كلم أم موسى عليه السلام','464','478','0');
INSERT INTO [t8378] VALUES (482,'فإن قيل: في قوله تعالى: (ما كنت تدري ما الكتاب ولا الإيمان) كيف كان لا يعلم الإيمان قبل أن يوحى إليه، والإيمان هو التصديق بوجود الصانع وتوحيده، والأنبياء عليهم السلام كلهم كانوا مؤمنين بالله تعالى قبل أن يوحى إليهم بأدلة عقولهم','465','478','0');
INSERT INTO [t8378] VALUES (483,'سورة الزخرف','466','0','0');
INSERT INTO [t8378] VALUES (484,'فإن قيل: كيف قال الله تعالى: (إنا جعلناه قرآنا عربيا) ولم يقل قلناه أو أنزلناه، والقرآن ليس بمجعول لأن الجعل هو الخلق، ومنه قوله تعالى: (وجعل الظلمات والنور) وقوله تعالى: (فجعل منه الزوجين الذكر وا','466','483','0');
INSERT INTO [t8378] VALUES (485,'فإن قيل: كيف قال عيسى عليه السلام لأمته: (ولأبين لكم بعض الذي تختلفون فيه) والنبى المبعوث إلى أمة يبين لهم كل ما يختلفون فيه؟','467','483','0');
INSERT INTO [t8378] VALUES (486,'فإن قيل: كيف وصف سبحانه أهل النار فيها بكونهم مبلسين، والمبلس هو الآيس من الرحمة والفرج، ثم قال تعالى: (ونادوا يا مالك ليقض علينا ربك) فطلبوا الفرج بالموت؟','468','483','0');
INSERT INTO [t8378] VALUES (487,'سورة الدخان','470','0','0');
INSERT INTO [t8378] VALUES (488,'فإن قيل: كيف وعد الله تعالى أهل الجنة لبس الاستبرق وهو، غليظ الديباج مع أن لبس الغليظ من الديباج عند السعداء من أهل الدنيا عيب ونقص؟','471','487','0');
INSERT INTO [t8378] VALUES (489,'سورة الجاثية','472','0','0');
INSERT INTO [t8378] VALUES (490,'فإن قيل: كيف طابق الجواب السؤال في قوله تعالى: (وإذا تتلى عليهم آياتنا بينات ما كان حجتهم إلا أن قالوا ائتوا بآبائنا إن كنتم صادقين (٢٥) قل الله يحييكم ثم يميتكم ثم يجمعكم','472','489','0');
INSERT INTO [t8378] VALUES (491,'سورة الأحقاف','473','0','0');
INSERT INTO [t8378] VALUES (492,'فإن قيل: كيف قال تعالى: (أولئك الذين نتقبل عنهم أحسن ما عملوا) مع أن حسن ما عملوا يتقبل عنهم أيضا؟','473','491','0');
INSERT INTO [t8378] VALUES (493,'سورة محمد صلى الله عليه وسلم','475','0','0');
INSERT INTO [t8378] VALUES (494,'فإن قيل: كيف قال الله تعالى: (كذلك يضرب الله للناس أمثالهم) ولم يسبق ضرب مثل؟','475','493','0');
INSERT INTO [t8378] VALUES (495,'سورة الفتح','477','0','0');
INSERT INTO [t8378] VALUES (496,'فإن قيل: كيف جعل سبحانه فتح مكة علة للمغفرة فقال تعالى: (إنا فتحنا لك فتحا مبينا (١) ليغفر لك الله ... الآية) ؟','477','495','0');
INSERT INTO [t8378] VALUES (497,'فإن قيل: ما معنى قوله: (ويهديك صراطا مستقيما) وهو مهدى إلى الصراط المستقيم، ومهدى به أمته أيضا؟','478','495','0');
INSERT INTO [t8378] VALUES (498,'فإن قيل: ما فائدة قوله تعالى: (وأهلها) بعد قوله (وكانوا أحق بها) ؟','479','495','0');
INSERT INTO [t8378] VALUES (499,'فإن قيل: قوله تعالى: (ليغيظ بهم الكفار) تعليل لماذا؟','480','495','0');
INSERT INTO [t8378] VALUES (500,'سورة الحجرات','481','0','0');
INSERT INTO [t8378] VALUES (501,'فإن قيل: كيف قال تعالى: (يا أيها الذين آمنوا لا تقدموا بين يدي الله ورسوله) والمراد به نهيهم أن يتقدموا على رسول الله صلى الله عليه وسلم بقول أو فعل، لا أن يقدموا غيرهم؟','481','500','0');
INSERT INTO [t8378] VALUES (502,'فإن قيل: كيف قال تعالى: (أن تحبط أعمالكم) أي مخافة أن تحبط أعمالكم مع أن الأعمال إنما تحبط بالكفر لا بغيره من المعاصى','482','500','0');
INSERT INTO [t8378] VALUES (503,'فإن قيل: كيف يقال إن الإيمان والإسلام بمعنى واحد، والله سبحانه وتعالى يقول: (قل لم تؤمنوا ولكن قولوا أسلمنا) ؟','483','500','0');
INSERT INTO [t8378] VALUES (504,'سورة ق','484','0','0');
INSERT INTO [t8378] VALUES (505,'فإن قيل: جواب القسم في قوله تعالى: (ق والقرآن المجيد) ؟','484','504','0');
INSERT INTO [t8378] VALUES (506,'فإن قيل: كيف قال تعالى: (ألقيا) والخطاب لواحد وهو مالك خازن النار؟','485','504','0');
INSERT INTO [t8378] VALUES (507,'فإن قيل: كيف قال تعالى: (غير بعيد) ولم يقل غير بعيدة وهو وصف للجنة؟','486','504','0');
INSERT INTO [t8378] VALUES (508,'فإن قيل: كيف قان تعالى: (إن في ذلك لذكرى لمن كان له قلب) وكل إنسان له قلب بل كل حيوان؟','487','504','0');
INSERT INTO [t8378] VALUES (509,'سورة الذريات','488','0','0');
INSERT INTO [t8378] VALUES (510,'فإن قيل: كيف قال تعالى: (إنما توعدون لصادق) والصادق وصف الواعد لا وصف الوعد؟','488','509','0');
INSERT INTO [t8378] VALUES (511,'فإن قيل: كيف قال الله تعالى: (ومن كل شيء خلقنا زوجين) أي صنفين، مع أن العرش والكرسى والقلم واللوح لم يخلق منها إلا واحد؟','489','509','0');
INSERT INTO [t8378] VALUES (512,'فإن قيل: كيف قال تعالى: (وما خلقت الجن والإنس إلا ليعبدون) وإذا قلنا، خلقهم للعبادة كان مريدا لها منهم فكيف أرادها منهم ولم توجد منهم؟','490','509','0');
INSERT INTO [t8378] VALUES (513,'فإن قيل: ما فائدة قوله تعالى: (وما أريد أن يطعمون) بعد قوله: (ما أريد منهم من رزق) ؟','491','509','0');
INSERT INTO [t8378] VALUES (514,'سورة الطور','492','0','0');
INSERT INTO [t8378] VALUES (515,'فإن قيل: كيف قال تعالى: (وزوجناهم بحور عين) مع أن الحور ألعين في الجنة مملوكات ملك يمين لا ملك نكاح؟','492','514','0');
INSERT INTO [t8378] VALUES (516,'فإن قيل: ما معنى الجمع في قوله تعالى: (فإنك بأعيننا) ؟','493','514','0');
INSERT INTO [t8378] VALUES (517,'سورة النجم','494','0','0');
INSERT INTO [t8378] VALUES (518,'فإن قيل: الضلال والغواية واحدة، فما فائدة قوله تعالى: (ما ضل صاحبكم وما غوى) ؟','494','517','0');
INSERT INTO [t8378] VALUES (519,'فإن قيل: كيف قال الله تعالى: (الثالثة الأخرى) فوصف الثالثة بالأخرى والعرب إنما تصف بالأخرى الثانية لا الثالثة، فظاهر اللفظ يقتضى أن يكون قد سبق ثالثة أولى، ثم لحقتها الثالثة الأخرى فتكون ثالثتان؟','495','517','0');
INSERT INTO [t8378] VALUES (520,'فإن قيل: كيف قال تعالى بعد تعديد النقم: (فبأي آلاء ربك تتمارى) والآلاء النعم؟','496','517','0');
INSERT INTO [t8378] VALUES (521,'سورة القمر','497','0','0');
INSERT INTO [t8378] VALUES (522,'فإن قيل: ما فائدة إعادة التكذيب في قوله تعالى: (كذبت قبلهم قوم نوح فكذبوا عبدنا) وهلا قال تعالى كذبت قبلهم قوم نوح عبدنا؟','497','521','0');
INSERT INTO [t8378] VALUES (523,'فإن قيل: كيف قال اللة تعالى: (أعجاز نخل منقعر) أي منقلع، ولم يقل منقعرة؟','498','521','0');
INSERT INTO [t8378] VALUES (524,'سورة الرحمن عز وجل','499','0','0');
INSERT INTO [t8378] VALUES (525,'فإن قيل: أي مناسبة بين رفع السماء ووضع الميزان حتى قرن بينهما؟','499','524','0');
INSERT INTO [t8378] VALUES (526,'فإن قيل: كيف قال تعالى: (رب المشرقين ورب المغربين) فكرر ذكر الرب ولم يكرره في سورة المعارج بل أفرده فقال تعالى: (فلا أقسم برب المشارق والمغارب) وكذا في سورة المزمل: (رب المشرق والمغرب) ، (ل','500','524','0');
INSERT INTO [t8378] VALUES (527,'فإن قيل: كيف قال تعالى: (سنفرغ لكم أيه الثقلان) والله تعالى لا يشغله شيء؟','501','524','0');
INSERT INTO [t8378] VALUES (528,'فإن قيل: كيف قال الله تعالى: (لم يطمثهن إنس قبلهم ولا جان) أي لم يفتضهن، ونساء الدنيا لا يفتضهن الجان أيضا، فما فائدة تخصيص الحور بذلك؟','502','524','0');
INSERT INTO [t8378] VALUES (529,'سورة الواقعة','503','0','0');
INSERT INTO [t8378] VALUES (530,'فإن قيل: ما فائدة التكرار في قوله تعالى: (والسابقون السابقون) ؟','503','529','0');
INSERT INTO [t8378] VALUES (531,'فإن قيل: كيف قال تعالى: (نحن خلقناكم فلولا تصدقون) أي فهلا تصدقون، مع أنهم مصدقون أنه خلقهم بدليل قوله تعالى: (ولئن سألتهم من خلقهم ليقولن الله) ؟','504','529','0');
INSERT INTO [t8378] VALUES (532,'فإن قيل: التسبيح التنزيه عن السوء، فما معنى باسم في قوله تعالى: (فسبح باسم ربك العظيم) وهلا قال تعالى فسبح ربك العظيم؟','505','529','0');
INSERT INTO [t8378] VALUES (533,'فإن قيل: فإذا لم تفارقه فكيف سماه تعالى منزلا وتنزيلا، وقال سبحانه: (نزل به الروح الأمين) ونظائره كثيرة، وإذا فارقه وباينه يكون مخلوقا، لأن كل مباين له فهو غيره، وكل ما هو غيره فهو مخلوق؟','506','529','0');
INSERT INTO [t8378] VALUES (534,'سورة الحديد','507','0','0');
INSERT INTO [t8378] VALUES (535,'فإن قيل: كيف قال تعالى: (وما لكم لا تؤمنون بالله) ثم قال سبحانه: (إن كنتم مؤمنين) ؟','507','534','0');
INSERT INTO [t8378] VALUES (536,'فإن قيل: كيف وصف سبحانه هؤلاء المذكورين بكونهم شهداء ومنهم من لم يقتل؟','508','534','0');
INSERT INTO [t8378] VALUES (537,'فإن قيل: كيف قال تعالى هنا: (وجنة عرضها كعرض السماء والأرض) (وقال تعالى في سورة آل عمران: (وجنة عرضها السماوات والأرض) (فكيف) يكون عرضها كعرض السماء الواحدة وكعرض السموات السبع؟','509','534','0');
INSERT INTO [t8378] VALUES (538,'فإن قيل: كيف قال تعالى: (يا أيها الذين آمنوا اتقوا الله وآمنوا برسوله) مع أن المؤمنين مؤمنون برسوله؟','510','534','0');
INSERT INTO [t8378] VALUES (539,'سورة المجادلة','511','0','0');
INSERT INTO [t8378] VALUES (540,'فإن قيل: لأى معنى خص الله تعالى الثلاثة والخمسة بالذكر في النجوى دون غيرها من الأعداد؟','511','539','0');
INSERT INTO [t8378] VALUES (541,'سورة الحشر','512','0','0');
INSERT INTO [t8378] VALUES (542,'فإن قيل: كيف قال تعالى: (والذين تبوءوا الدار والإيمان من قبلهم) والإيمان ليس مكانا يتبوأ لأن معنى التبوء اتخاذ المكان منزلا؟','512','541','0');
INSERT INTO [t8378] VALUES (543,'فإن قيل: كيف يستقيم التفضيل وهم ما كانوا يرهبون الله، لأنهم لو رهبوه لتركوا النفاق والكفر؟','513','541','0');
INSERT INTO [t8378] VALUES (544,'فإن قيل: كيف قال تعالى: (لغد) وأراد به يوم القيامة، والغد عبارة عن يوم بينه وبيننا ليلة واحدة؟','514','541','0');
INSERT INTO [t8378] VALUES (545,'فإن قيل: ما معنى قوله تعالى: (لو أنزلنا هذا القرآن على جبل ... الآية) ؟','515','541','0');
INSERT INTO [t8378] VALUES (546,'سورة الممتحنة','516','0','0');
INSERT INTO [t8378] VALUES (547,'فإن قيل: من ماذا استثنى قوله تعالى: (إلا قول إبراهيم لأبيه) ؟','516','546','0');
INSERT INTO [t8378] VALUES (548,'سورة الصف','518','0','0');
INSERT INTO [t8378] VALUES (549,'فإن قيل: ما فائدة " قد " في قوله تعالى: (وقد تعلمون أني رسول الله إليكم) ؟','518','548','0');
INSERT INTO [t8378] VALUES (550,'فإن قيل: ما وجه صحة التشبيه وظاهره تشبيه كونهم أنصار الله بقول عيسى عليه السلام: (من أنصاري إلى الله) ؟','519','548','0');
INSERT INTO [t8378] VALUES (551,'سورة الجمعة','520','0','0');
INSERT INTO [t8378] VALUES (552,'فإن قيل: كيف قال تعالى: (فاسعوا إلى ذكر الله) والسعى العدو، والعدو إلى صلاة الجمعة وإلى كل صلاة مكروه؟','520','551','0');
INSERT INTO [t8378] VALUES (553,'سورة المنافقون','521','0','0');
INSERT INTO [t8378] VALUES (554,'فإن قيل: ما فائدة قوله تعالى: (والله يعلم إنك لرسوله) ؟','521','553','0');
INSERT INTO [t8378] VALUES (555,'سورة التغابن','523','0','0');
INSERT INTO [t8378] VALUES (556,'فإن قيل: كيف قال تعالى: (فمنكم كافر ومنكم مؤمن) قدم الكافر في الذكر؟','523','555','0');
INSERT INTO [t8378] VALUES (557,'سورة الطلاق','525','0','0');
INSERT INTO [t8378] VALUES (558,'فإن قيل: كيف قال تعالى: (يا أيها النبي إذا طلقتم النساء) أفرد الخطاب أولا ثم جمعه ثانيا؟','525','557','0');
INSERT INTO [t8378] VALUES (559,'فإن قيل: كيف قال تعالى: (ومن يتوكل على الله فهو حسبه) أى من يثق به فيما نابه كفاه الله شر ما أهمه، وقد رأينا كثيرا من الناس يتوكل على الله في بعض أموره وحوائجه ولا يكفيه الله همه؟','526','557','0');
INSERT INTO [t8378] VALUES (560,'فإن قيل: إذا كانت المطلقة طلاقا بائنا تجب لها النفقة عند بعض العلماء، فما فائدة قوله تعالى: (وإن كن أولات حمل فأنفقوا عليهن حتى يضعن حملهن) عند ذلك القائل؟','527','557','0');
INSERT INTO [t8378] VALUES (561,'سورة التحريم','529','0','0');
INSERT INTO [t8378] VALUES (562,'فإن قيل: قرله تعالى: (وصالح المؤمنين) إن كان المراد به الفرد فأى فرد هو، وأيضا فإنه لا يناسب مقابلة الملآئكة الذين هم جمع، وإن كان المراد به الجمع فهلا كان مكتوبا في المصحف بالواو؟','529','561','0');
INSERT INTO [t8378] VALUES (563,'فإن قيل: قوله تعالى بعد ذلك تعظيم للملآئكة ومظاهرتهم، وقد تقدمت نصرة الله تعالى وجبريل وصالح المؤمنين، ونصرة الله سبحانه أعظم؟','530','561','0');
INSERT INTO [t8378] VALUES (564,'فإن قيل: ما فائدة قوله تعالى: (ويفعلون ما يؤمرون) بعد قوله سبحانه: (لا يعصون الله ما أمرهم) ؟','531','561','0');
INSERT INTO [t8378] VALUES (565,'فإن قيل: وكيف قال تعالى: (وكانت من القانتين) ولم يقل سبحانه من القانتات؟','532','561','0');
INSERT INTO [t8378] VALUES (566,'سورة الملك','533','0','0');
INSERT INTO [t8378] VALUES (567,'فإن قيل: ما فائدة تقديم الموت على الحياة في قوله تعالى: (الذي خلق الموت والحياة) ؟','533','566','0');
INSERT INTO [t8378] VALUES (568,'سورة القلم','535','0','0');
INSERT INTO [t8378] VALUES (569,'فإن قيل: كيف قال تعالى: (ولا يستثنون) أي ولا يقولون إن شاء الله فسمى ألشرط استثناء؟','535','568','0');
INSERT INTO [t8378] VALUES (570,'فإن قيل: كيف قال تعالى: (وقد كانوا يدعون إلى السجود) وهم إنما كانوا يدعون إلى الصلاة، فإن المراد بالآية دعاؤهم إلى الجماعات بأذان المؤذن حين يقول حى على الصلاة؟','536','568','0');
INSERT INTO [t8378] VALUES (571,'سورة الحاقة','537','0','0');
INSERT INTO [t8378] VALUES (572,'فإن قيل: كيف قال تعالى: (بريح صرصر) ولم يقل صرصرة، كما قال تعالى: (عاتية) وهو صفة لمؤنث، لأنها الشديدة الصوت أو الشديدة البرد؟','537','571','0');
INSERT INTO [t8378] VALUES (573,'فإن قيل: كيف قال تعالى: (إني ظننت أني ملاق حسابيه) ؟','538','571','0');
INSERT INTO [t8378] VALUES (574,'فإن قيل: كيف قال تعالى: (إنه لقول رسول كريم) يعنى أن القرآن قول جبريل عليه السلام مع أنه قول الله تعالى لا قول جبريل؟','539','571','0');
INSERT INTO [t8378] VALUES (575,'سورة المعارج','540','0','0');
INSERT INTO [t8378] VALUES (576,'فإن قيل: كيف قال تعالى: (إن الإنسان خلق هلوعا) ويفسره ما بعده والإنسان في حال خلقه ما كان موصوفا بهذه الصفات؟','540','575','0');
INSERT INTO [t8378] VALUES (577,'سورة نوح عليه السلام','541','0','0');
INSERT INTO [t8378] VALUES (578,'فإن قيل: كيف قال تعالى: (ويؤخركم إلى أجل مسمى) فإن كان المراد به تأخيرهم عن الأجل المقدر لهم في الأزل فهو محال لقوله تعالى: (ولن يؤخر الله نفسا إذا جاء أجلها) وقوله تعالى: (إن أجل الله إذا جاء ل','541','577','0');
INSERT INTO [t8378] VALUES (579,'فإن قيل: كيف دعا نوح عليه السلام على قومه بقوله: (ولا تزد الظالمين إلا ضلالا) مع أنه أرسل ليهديهم ويرشدهم؟','542','577','0');
INSERT INTO [t8378] VALUES (580,'سورة الجن','543','0','0');
INSERT INTO [t8378] VALUES (581,'فإن قيل: كيف قال تعالى: (وأنه لما قام عبد الله) ولم يقل سبحانه رسول الله أو نبى الله، والمراد به النبى عليه الصلاة والسلام؟','543','580','0');
INSERT INTO [t8378] VALUES (582,'سورة الزمل','544','0','0');
INSERT INTO [t8378] VALUES (583,'فإن قيل: كيف قال تعالى: (السماء منفطر به) (ولم يقل سبحانه منفطرة به) والسماء مؤنثة؟','544','582','0');
INSERT INTO [t8378] VALUES (584,'سورة المدثر','545','0','0');
INSERT INTO [t8378] VALUES (585,'فإن قيل: ما فائدة قوله تعالى: (غير يسير) بعد قوله قوله سبحانه: (فذلك يومئذ يوم عسير) ؟','545','584','0');
INSERT INTO [t8378] VALUES (586,'فإن قيل: كيف طابق قوله تعالى: (ما سلككم في سقر) وهو سؤال للمجرمين قوله تعالى: (يتساءلون (٤٠) عن المجرمين) وهو سؤال عنهم، وإنما المطابق الظاهر يسألون المجرمين ما سلككم في سقر أو يتساءلون عن المجرمين ما سلكهم في سقر: أي يسأ','546','584','0');
INSERT INTO [t8378] VALUES (587,'سورة القيامة','548','0','0');
INSERT INTO [t8378] VALUES (588,'فإن قيل: ما معنى قوله تعالى: (فإذا قرأناه فاتبع قرآنه) والقارئ له على النبى صلى الله عليه وسلم إنما هو جبرايل عليه السلام؟','548','587','0');
INSERT INTO [t8378] VALUES (589,'فإن قيل: النطفة المنى، فما فائدة قوله تعالى: (ألم يك نطفة من مني يمنى) ؟','549','587','0');
INSERT INTO [t8378] VALUES (590,'سورة الإنسان','550','0','0');
INSERT INTO [t8378] VALUES (591,'فإن قيل: كيف قال الله تعالى: (من نطفة أمشاج) فوصف المفرد وهى النطفة بالجمع (وهو) الأمشاج لأنه جمع مشج والأمشاج الأخلاط، والمراد أنه مخلوق من نطفة مختلطة من ماء الرجل والمرأة؟','550','590','0');
INSERT INTO [t8378] VALUES (592,'فإن قيل: ما معنى قوله تعالى: (كانت قواريرا) ؟','551','590','0');
INSERT INTO [t8378] VALUES (593,'فإن قيل: أي شرف لتلك الدار يسقى الله تعالى عباده الشراب الطهور فيها مع أنه تعالى في الدنيا سقاهم ذلك بدليل قوله تعالى: (وأسقيناكم ماء فراتا) وقوله تعالى: (فأنزلنا من السماء ماء فأسقيناكموه) ؟','552','590','0');
INSERT INTO [t8378] VALUES (594,'فإن قيل: ما معنى النهى عن طاعة أحدهما، وهلا نهى عن طاعتهما؟','553','590','0');
INSERT INTO [t8378] VALUES (595,'سورة المرسلات','554','0','0');
INSERT INTO [t8378] VALUES (596,'فإن قيل: قوله تعالى: (هذا يوم لا ينطقون) ينفى وجود الاعتذار منهم لأن الاعتذار إنما يكون بالنطق، فما فائدة نفى الاعتذار بعد نفى النطق؟','554','595','0');
INSERT INTO [t8378] VALUES (597,'سورة النبأ','555','0','0');
INSERT INTO [t8378] VALUES (598,'فإن قيل: كيف اتصل وارتبط قوله تعالى: (ألم نجعل الأرض مهادا) بما قبله؟','555','597','0');
INSERT INTO [t8378] VALUES (599,'سورة النازعات','557','0','0');
INSERT INTO [t8378] VALUES (600,'فإن قيل: كيف قال الله تعالى: (والنازعات) . (والناشطات) بلفظ التأنيث، وكذا ما بعده، والكل أوصاف للملآئكة، والملآئكة ليسوا إناثا؟','557','599','0');
INSERT INTO [t8378] VALUES (601,'سورة عبس','559','0','0');
INSERT INTO [t8378] VALUES (602,'فإن قيل: كيف قال الله تعالى: (كلا إنها تذكرة) ثم قال سبحانه وتعالى: (فمن شاء ذكره) ولم يقل ذكرها؟','559','601','0');
INSERT INTO [t8378] VALUES (603,'سورة التكوير','561','0','0');
INSERT INTO [t8378] VALUES (604,'فإن قيل: كيف قال الله تعالى: (وإذا الموءودة سئلت (٨) بأي ذنب قتلت) والسؤال إنما يحسن للقاتل لا للمقتول؟','561','603','0');
INSERT INTO [t8378] VALUES (605,'سورة الانفطار','563','0','0');
INSERT INTO [t8378] VALUES (606,'فإن قيل: لأى فائدة ذكر صفة الكرم دون سائر صفاته في قوله تعالى: (ما غرك بربك الكريم) ؟','563','605','0');
INSERT INTO [t8378] VALUES (607,'سورة المطففين','564','0','0');
INSERT INTO [t8378] VALUES (608,'فإن قيل: هلا قال الله تعالى إذا اكتالوا أو اتزنوا على الناس يستوفون كما قال سبحانه في مقابلة: (وإذا كالوهم أو وزنوهم يخسرون) ؟','564','607','0');
INSERT INTO [t8378] VALUES (609,'سورة الانشقاق','565','0','0');
INSERT INTO [t8378] VALUES (610,'فإن قيل: أين جواب "إذا " في قوله تعالى: (إذا السماء انشقت) ؟','565','609','0');
INSERT INTO [t8378] VALUES (611,'سورة البروج','566','0','0');
INSERT INTO [t8378] VALUES (612,'فإن قيل: أين جواب القسم؟','566','611','0');
INSERT INTO [t8378] VALUES (613,'سورة الطارق','567','0','0');
INSERT INTO [t8378] VALUES (614,'فإن قيل: أين جواب القسم؟','567','613','0');
INSERT INTO [t8378] VALUES (615,'سورة الأعلى','568','0','0');
INSERT INTO [t8378] VALUES (616,'فإن قيل: كيف قال الله تعالى: (فذكر إن نفعت الذكرى) مع أنه كان صلى الله عليه وسلم مأمورا بالذكرى نفعت أو لم تنفع؟','568','615','0');
INSERT INTO [t8378] VALUES (617,'سورة الغاشية','569','0','0');
INSERT INTO [t8378] VALUES (618,'فإن قيل: كيف قال الله تعالى: (وجوه يومئذ خاشعة (٢) عاملة ناصبة (٣) تصلى نارا حامية) مع أن جميع أبدانهم أيضا تصلى النار؟','569','617','0');
INSERT INTO [t8378] VALUES (619,'سورة الفجر','571','0','0');
INSERT INTO [t8378] VALUES (620,'فإن قيل: كيف نكر الليالى العشر دون سائر ما أقسم به، وهلا عرفها بلام العهد وهى ليالى معلومة معهودة فإنها ليالى عشر ذى الحجة فى فى قول الجمهور؟','571','619','0');
INSERT INTO [t8378] VALUES (621,'فإن قيل: كيف قال الله تعالى في الجملة الأولى: (فأكرمه) ولم يقل في الجملة الثانية فأهانه؟','572','619','0');
INSERT INTO [t8378] VALUES (622,'سورة البلد','573','0','0');
INSERT INTO [t8378] VALUES (623,'فإن قيل: كيف قال تعالى: (ووالد وما ولد) ولم يقل سبحانه وتعالى ومن ولد؟','573','622','0');
INSERT INTO [t8378] VALUES (624,'سورة الشمس','574','0','0');
INSERT INTO [t8378] VALUES (625,'فإن قيل: كيف نكر الله تعالى النفس دون سائر ما أقسم به؟','574','624','0');
INSERT INTO [t8378] VALUES (626,'سورة الليل','575','0','0');
INSERT INTO [t8378] VALUES (627,'فإن قيل: كيف قال الله تعالى: (لا يصلاها إلا الأشقى) مع أن الشقى أيضا يصلاها: أي يقاسى حرها وعذابها؟','575','626','0');
INSERT INTO [t8378] VALUES (628,'سورة الضحى','577','0','0');
INSERT INTO [t8378] VALUES (629,'فإن قيل: كيف وصف صلى الله عليه وسلم بالضلال والنبى عليه الصلاة والسلام معاذ الله أن يكون ضالا: أي كافرا لا قبل النبوة ولا بعدها، والضال أكثر ما ورد في القرآن بمعنى الكافر؟','577','628','0');
INSERT INTO [t8378] VALUES (630,'سورة الشرح','579','0','0');
INSERT INTO [t8378] VALUES (631,'فإن قيل: أي فائدة في زيادة ذكر لك وعنك والكلام تام بدونهما؟','579','630','0');
INSERT INTO [t8378] VALUES (632,'فإن قيل: وإذا أثبت في قراءته غير مكرر، فكيف قال: والذى نفسى بيده لو كان العسر في جحر لطلبه اليسر حتى يدخل عليه، إنه لن يغلب عسر يسرين؟','580','630','0');
INSERT INTO [t8378] VALUES (633,'سورة التين','581','0','0');
INSERT INTO [t8378] VALUES (634,'فإن قيل: كيف وجه صحة الاستثناء في قوله تعالى: (إلا الذين آمنوا وعملوا الصالحات فلهم أجر غير ممنون) ؟','581','633','0');
INSERT INTO [t8378] VALUES (635,'سورة العلق','582','0','0');
INSERT INTO [t8378] VALUES (636,'فإن قيل: أين مفعول خلق الأول؟','582','635','0');
INSERT INTO [t8378] VALUES (637,'سورة القدر','583','0','0');
INSERT INTO [t8378] VALUES (638,'فإن قيل: ما معنى قوله تعالى: (من كل أمر) وتنزلهم من الأمر لا معنى له؟','583','637','0');
INSERT INTO [t8378] VALUES (639,'سورة البينة','584','0','0');
INSERT INTO [t8378] VALUES (640,'فإن قيل: المراد بالرسول هنا محمد صلى الله عليه وسلم بلا خلاف، فكيف قال تعالى: (يتلو صحفا) وظاهره يدل على قراءة المكتوب من الكتاب وهو منتف في حقه صلى الله عليه وسلم لأنه كان أميا؟','584','639','0');
INSERT INTO [t8378] VALUES (641,'فإن قيل: ما الفرق بين الصحف والكتب حتى قال تعالى: (صحفا مطهرة (٢) فيها كتب) ؟','584','639','0');
INSERT INTO [t8378] VALUES (642,'سورة الزلزلة','586','0','0');
INSERT INTO [t8378] VALUES (643,'فإن قيل: ما معنى إضافة الزلزال الذي هو المصدر إلى الأرض، وهلا قال زلازلا كما قال تعالى: (كلا إذا دكت الأرض دكا دكا) وما أشبهه؟','586','642','0');
INSERT INTO [t8378] VALUES (644,'سورة العاديات','587','0','0');
INSERT INTO [t8378] VALUES (645,'فإن قيل: كيف قال الله تعالى: (إن ربهم بهم يومئذ لخبير) مع أنه تعالى أخبر بهم في كل في زمان، فما وجه تخصيص ذلك اليوم؟','587','644','0');
INSERT INTO [t8378] VALUES (646,'سورة القارعة','588','0','0');
INSERT INTO [t8378] VALUES (647,'فإن قيل: كيف قال الله تعالى: (وأما من خفت موازينه) أي رجحت سيئاته على حسناته: (فأمه هاوية) أي فمسكنه النار. وأكثر المؤمنين سيئاتهم راحجة على حسناتهم؟','588','646','0');
INSERT INTO [t8378] VALUES (648,'سورة التكاثر','589','0','0');
INSERT INTO [t8378] VALUES (649,'فإن قيل: أين جواب (لو تعلمون) ؟','589','648','0');
INSERT INTO [t8378] VALUES (650,'سورة العصر','590','0','0');
INSERT INTO [t8378] VALUES (651,'فإن قيل: الاستثناء الذي في السورة لا يدل على أن المؤمنين الموصوفين في ربح مع أن الاستثناء إنما سيق لمدحهم بمضادة حالهم لحال من لم يتناوله الاستثناء؟','590','650','0');
INSERT INTO [t8378] VALUES (652,'سورة الهمزة','591','0','0');
INSERT INTO [t8378] VALUES (653,'فإن قيل: ما الفرق بين الهمزة واللمزة؟','591','652','0');
INSERT INTO [t8378] VALUES (654,'سورة الفيل','592','0','0');
INSERT INTO [t8378] VALUES (655,'فإن قيل: ما معنى الأبابيل، وهل هو واحد أو جمع؟','592','654','0');
INSERT INTO [t8378] VALUES (656,'سورة قريش','593','0','0');
INSERT INTO [t8378] VALUES (657,'فإن قيل: بأى شيء تتعلق اللام في قوله تعالى: (لإيلاف قريش) ؟','593','656','0');
INSERT INTO [t8378] VALUES (658,'سورة الماعون','595','0','0');
INSERT INTO [t8378] VALUES (659,'فإن قيل: كيف توعد الله الساهى عن الصلاة، والحديث ينفى مؤاخذته وهو قوله صلى الله عليه وسلم: "رفع عن أمتى الخطأ والنسيان "؟','595','658','0');
INSERT INTO [t8378] VALUES (660,'سورة الكوثر','596','0','0');
INSERT INTO [t8378] VALUES (661,'فإن قيل: ما الكوثر؟','596','660','0');
INSERT INTO [t8378] VALUES (662,'سورة الكافرون','597','0','0');
INSERT INTO [t8378] VALUES (663,'فإن قيل: كيف قال الله تعالى: (ولا أنتم عابدون ما أعبد) ولم يقل "من " مع أنه القياس؟','597','662','0');
INSERT INTO [t8378] VALUES (664,'فإن قيل: هلا قال تعالى: "ولا أنتم عابدون ما عبدت "، بلفظ الماضي، كما قال: (ولا أنا عابد ما عبدتم) ؟','598','662','0');
INSERT INTO [t8378] VALUES (665,'سورة النصر','599','0','0');
INSERT INTO [t8378] VALUES (666,'فإن قيل: أي مناسبة بين الأمر بالاستغفار وبين ما قبله، فإن مجيء الفتح والنصر والظفر يناسب الشكر والحمد لا الاستغفار والتوبة؟','599','665','0');
INSERT INTO [t8378] VALUES (667,'سورة المسد','600','0','0');
INSERT INTO [t8378] VALUES (668,'فإن قيل: كيف ذكره الله تعالى بكنيته دون اسمه، مع أن ذلك إكرام واحترام؟','600','667','0');
INSERT INTO [t8378] VALUES (669,'سورة الإخلاص','601','0','0');
INSERT INTO [t8378] VALUES (670,'فإن قيل: فالمشهور في كلام العرب أن الأحد يتسعمل بعد النفى، والواحد يتسعمل بعد الإثبات، يقال: في الدار واحد، وما في الدار أحد، وجاءنى واحد وما جاءنى أحد، ومنه قوله تعالى: (وإلهكم إله واحد) وقوله تعالى: (الواحد القهار) . (و','601','669','0');
INSERT INTO [t8378] VALUES (671,'سورة الفلق','602','0','0');
INSERT INTO [t8378] VALUES (672,'فإن قيل: قوله تعالى: (من شر ما خلق) يتناول كل ما بعده، فما الفائدة في الإعادة؟','602','671','0');
INSERT INTO [t8378] VALUES (673,'فإن قيل: كيف عرف سبحانه النفاثات ونكر ما قبلها وما بعدها؟','602','671','0');
INSERT INTO [t8378] VALUES (674,'سورة الناس','603','0','0');
INSERT INTO [t8378] VALUES (675,'فإن قيل: كيف خص الناس بالذكر في قوله تعالى: (قل أعوذ برب الناس) وهو رب كل شيء؟','603','674','0');
INSERT INTO [t8378] VALUES (676,'فإن قيل: هل قوله تعالى: (من الجنة والناس) بيان للذى يوسوس على أن الشيطان الموسوس ضربان جنى وإنسى كما قال تعالى: (شياطين الإنس والجن) أو بيان للناس الذي أضيفت الوسوسة إلى صدورهم، والناس المذكورة آخرا بمعنى الإنس؟','603','674','0');
