INSERT INTO [t8630] VALUES (1,'فإن الإجماع قاعدة من قواعد الملة الحنيفية، يرجع إليه، ويفزع نحوه، ويكفر من خالفه إذا قامت عليه الحجة بأنه إجماع.','2','0','0');
INSERT INTO [t8630] VALUES (2,'وقوم قالوا: الإجماع هو إجماع الصحابة رضي الله عنهم فقط','4','0','0');
INSERT INTO [t8630] VALUES (3,'إنما نعني بقولنا العلماء من حفظ عنه الفتيا','5','0','0');
INSERT INTO [t8630] VALUES (4,'وأجمعوا أنه لا يجوز التوضؤ بشيء من المائعات وغيرها، حاشا الماء والنبيذ','5','0','0');
INSERT INTO [t8630] VALUES (5,'وأما الماء الجاري فاتفقوا على جواز استعماله ما لم تظهر فيه نجاسة','5','0','0');
INSERT INTO [t8630] VALUES (6,'واتفقوا على أن غسل الذراعين إلى منتهى المرفقين فرض في الوضوء','5','0','0');
INSERT INTO [t8630] VALUES (7,'واتفقوا على أن الاستنجاء بالحجارة وبكل طاهر - ما لم يكن طعاما أو رجيعا أو نجسا أو جلدا أو عظما أو فحما أو حممة - جائز','5','0','0');
INSERT INTO [t8630] VALUES (8,'واتفقوا أن كل إناء - ما لم يكن فضة ولا ذهبا ولا صفرا ولا رصاصا ولا نحاسا ولا مغصوبا ولا إناء كتابي ولا جلد ميتة ولا جلد ما لا يؤكل لحمه وإن ذكي - فإن الوضوء منه والأكل والشرب جائز كل ذلك','6','0','0');
INSERT INTO [t8630] VALUES (9,'وأجمعوا أن الحائض - وإن رأت الطهر - ما لم تغسل فرجها أو تتوضأ فوطؤها حرام','6','0','0');
INSERT INTO [t8630] VALUES (10,'واتفقوا أن الصلاة لا تسقط، ولا يحل تأخيرها عمدا عن وقتها، عن العاقل البالغ بعذر أصلا، وأنها تؤدى على قدر طاقة المرء من جلوس واضطجاع بإيماء، وكيف أمكنه','6','0','0');
INSERT INTO [t8630] VALUES (11,'واتفقوا أن المرأة لا تؤم الرجال وهم يعلمون أنها امرأة، فإن فعلوا فصلاتهم فاسدة بالإجماع','7','0','0');
INSERT INTO [t8630] VALUES (12,'وروي عن أشهب أن من ائتم بامرأة وهو لا يدري حتى خرج الوقت ثم علم فصلاته تامة، وكذا من ائتم بكافر وهو لا يعلم أنه كافر','7','0','0');
INSERT INTO [t8630] VALUES (13,'واتفقوا على أن وضع الرأس في الأرض والرجلين في السجود فرض','7','0','0');
INSERT INTO [t8630] VALUES (14,'واتفقوا على أن الفكرة في أمور الدنيا لا تفسد الصلاة','7','0','0');
INSERT INTO [t8630] VALUES (15,'واتفقوا على جواز الصلاة في كل مكان ما لم يكن جوف الكعبة، أو الحجر، أو ظهر الكعبة، أو معاطن الإبل، أو مكانا فيه نجاسة، أو حماما، أو مقبرة، أو إلى قبر، أو عليه، أو مكانا مغصوبا يقدر على مفارقته، أو مكانا يستهزأ فيه بالإسلام، أو مسجد الضرار، أو بلاد ثمو','7','0','0');
INSERT INTO [t8630] VALUES (16,'واتفقوا على أن صلاة العيدين، وكسوف الشمس، وقيام ليالي رمضان، ليست فرضا، وكذلك التهجد على غير النبي صلى الله عليه وسلم','8','0','0');
INSERT INTO [t8630] VALUES (17,'واتفقوا أن كل صلاة - ما عدا الصلوات الخمس، وعدا الجنائز، والوتر، وما نذره المرء - ليست فرضا','8','0','0');
INSERT INTO [t8630] VALUES (18,'واتفقوا أن من أسقط الجلسة الوسطى من صلاة الظهر والعصر والمغرب والعتمة ساهيا أن عليه سجدتي السهو','8','0','0');
INSERT INTO [t8630] VALUES (19,'واتفقوا أن في مائتي درهم خمسة دراهم، ما لم يكن حلي امرأة، أو حلية سيف، أو منطقة، أو مصحفا، أو خاتما','8','0','0');
INSERT INTO [t8630] VALUES (20,'واتفقوا على أن وقت الوقوف ليس قبل الظهر في التاسع من ذي الحجة','8','0','0');
INSERT INTO [t8630] VALUES (21,'واتفقوا أنه من فعل - من كل ما ذكرنا أنه يجتنبه في إحرامه - شيئا عامدا أو ناسيا أنه لا يبطل حجه ولا إحرامه. واتفقوا أن من جادل في الحج فإن حجه لا يبطل ولا إحرامه. واختلفوا فيمن قتل صيدا متعمدا، فقال مجاهد: بطل حجه وعليه الهدي','9','0','0');
INSERT INTO [t8630] VALUES (22,'قال: ومن عجائب الدنيا أن الآية وردت كما تلونا، فأبطلوا الحج بالرفث، ولم يبطلوه بالفسوق. وقال: كل من تعمد معصية - أي معصية كانت - وهو ذاكر لحجه منذ يحرم إلى أن يتم طوافه بالبيت للإفاضة ورمي جمرة العقبة فقد بطل حجه. قال: وأعجب شيء دعواهم ا','9','0','0');
INSERT INTO [t8630] VALUES (23,'واتفقوا أن كل صدقة واجبة في الحج أو إطعام، أنه إن أداه بمكة أجزأه، واختلفوا فيمن أدى ذلك في غير مكة - حاشا جزاء الصيد - فإنهم اتفقوا أنه لا يجزئ إلا بمكة','9','0','0');
INSERT INTO [t8630] VALUES (24,'واتفقوا أن من يوم النحر - وهو العاشر من ذي الحجة - إلى انسلاخ ذي الحجة وقتا لطواف الإفاضة، ولما بقي من سنن الحج','10','0','0');
INSERT INTO [t8630] VALUES (25,'واتفقوا على أن إيجاب الهدي فرض على المحصر','10','0','0');
INSERT INTO [t8630] VALUES (26,'واتفقوا على أن من حلف لخصمه دون أن يحلفه حاكم أو من حكماه على أنفسهما أنه لا يبرأ بتلك اليمين من الطلب','10','0','0');
INSERT INTO [t8630] VALUES (27,'وأجمعوا على أن كل من لزمه حق في ماله أو ذمته لأحد فرض عليه أداء الحق إلى من هو له عليه - إذا أمكنه ذلك وبقي له بعد ذلك ما يعيش به أياما هو ومن تلزمه نفقته','10','0','0');
INSERT INTO [t8630] VALUES (28,'وأجمعوا أن المملوكة لا يجبر سيدها على إنكاحها، ولا على أن يطأها - وإن طلبت هي ذلك - ولا على بيعها من أجل منعه لها الوطء والإنكاح','11','0','0');
INSERT INTO [t8630] VALUES (29,'واتفقوا أن التعريض للمرأة وهي في العدة حلال إذا كانت العدة في غير رجعية أو كانت من وفاة','11','0','0');
INSERT INTO [t8630] VALUES (30,'واتفقوا أن الطلاق إلى أجل أو بصفة واقع إن وافق وقت طلاق، ثم اختلفوا في وقت وقوعه: فمن قائل الآن، ومن قائل هو إلى أجله','11','0','0');
INSERT INTO [t8630] VALUES (31,'واتفقوا أن عدة المسلمة الحرة المطلقة - التي ليست حاملا، ولا مستريبة، وهي لم تحض أو لا تحيض إلا أن البلوغ متوهم منها - ثلاثة أشهر متصلة','13','0','0');
INSERT INTO [t8630] VALUES (32,'واتفقوا أن استقراض ما عدا الحيوان جائز. واختلفوا في جواز استقراض الرقيق والجواري والحيوان','13','0','0');
INSERT INTO [t8630] VALUES (33,'واتفقوا أن الوصية بالمعاصي لا تجوز، وأن الوصية بالبر وبما ليس ببر ولا معصية ولا تضييعا للمال جائزة','13','0','0');
INSERT INTO [t8630] VALUES (34,'واتفقوا على أنه إن أعطى - يعني: من يقبل منه الجزية عن نفسه وحدها - أربعة مثاقيل ذهب في كل عام، على أن يلتزموا ما ذكره من شروط الذمة، فقد حرم دم من وفى بذلك وماله وأهله وظلمه','13','0','0');
INSERT INTO [t8630] VALUES (35,'واتفقوا أنه لا ينفل من ساق مغنما أكثر من ربعه في الدخول، ولا أكثر من ثلثه في الخروج','14','0','0');
INSERT INTO [t8630] VALUES (36,'واتفقوا أن الحر البالغ العاقل الذي ليس بسكران إذا أمن أهل الكتاب الحربيين على أداء الجزية على الشروط التي قدمنا، أو على الجلاء، أو أمن سائر الكفار على الجلاء بأنفسهم وعيالهم وذراريهم وترك بلادهم واللحاق بأرض حرب أخرى لا بأرض ذمة ولا ب','14','0','0');
INSERT INTO [t8630] VALUES (37,'واتفقوا أن أولاد أهل الجزية ومن تناسل منهم فإن الحكم الذي عقده أجدادهم وإن بعدوا جار على هؤلاء لا يحتاج إلى تجديده مع من حدث منهم','14','0','0');
INSERT INTO [t8630] VALUES (38,'واتفقوا أنه لا يجوز أن يكون على المسلمين في','14','0','0');
INSERT INTO [t8630] VALUES (39,'وقت واحد في جميع الدنيا إمامان، لا متفقان ولا مفترقان، ولا في مكانين ولا في مكان واحد','15','0','0');
INSERT INTO [t8630] VALUES (40,'واتفقوا أنه إذا كان الإمام من ولد علي، وكان عدلا، ولم تتقدم بيعته بيعة أخرى لإنسان حي، وقام عليه من هو دونه، أن قتال الآخر واجب','15','0','0');
INSERT INTO [t8630] VALUES (41,'وإنما أدخلنا هذا الاتفاق على جوازه لخلاف الزيدية: هل تجوز إمامة غير علوي أم لا؟ وإن كنا مخطئين لهم في ذلك، ومعتقدين صحة بطلان هذا القول، وأن الإمامة لا تتعدى فهر بن مالك، وأنها جائزة في جميع أفخاذهم، ولكن لم يكن بد في صفة الاجماع الجاري عند ا','15','0','0');
INSERT INTO [t8630] VALUES (42,'واتفقوا أن من خالف الإجماع المتيقن بعد علمه بأنه إجماع فإنه كافر','16','0','0');
INSERT INTO [t8630] VALUES (43,'واتفقوا أن السمن إذا وقع فيه فأر أو فأرة، فمات أو ماتت وهو مائع أنه لا يؤكل','16','0','0');
INSERT INTO [t8630] VALUES (44,'واتفقوا أن من نذر معصية فإنه لا يجوز له الوفاء بها. واختلفوا أيلزمه لذلك كفارة أم لا؟ واختلفوا في النذر المطلق الذي ليس معلقا بصفة، وفي النذر الخارج مخرج اليمين، أيلزم أم لا؟ وفيه كفارة أم لا؟ واتفقوا أن من نذر ما لا طاعة فيه ولا معصية','18','0','0');
INSERT INTO [t8630] VALUES (45,'واتفقوا أن ازالة المرء عن نفسه ظلما بأن يظلم من لم يظلمه قاصدا إلى ذلك لا يحل، وذلك مثل أن يحل عدو المسلمين بساحة قوم، فيقول أعطوني مال فلان، أو أعطوني فلانا، وهو لا حق له عنده بحكم دين الإسلام، أو قال أعطوني امرأة أو أمة فلان، أو افعلوا كذا','18','0','0');
INSERT INTO [t8630] VALUES (46,'اتفقوا أن الله وحده لا شريك له، خالق كل شيء غيره،','19','0','0');
