INSERT INTO [t9065] VALUES (8,'قوله تعالى (إن الذين كفروا سواء عليهم أأنذرتهم أم لم تنذرهم لا يؤمنون (٦)','15','7','0');
INSERT INTO [t9065] VALUES (9,'وقوله: (ومن الناس من يقول آمنا بالله وباليوم الآخر وما هم بمؤمنين (٨)','16','7','0');
