INSERT INTO [t9065] VALUES (11,'وقوله: (يضل به كثيرا ويهدي به كثيرا وما يضل به إلا الفاسقين (٢٦)','27','7','0');
INSERT INTO [t9065] VALUES (12,'وقوله: (ولا تقربا هذه الشجرة)','32','7','0');
INSERT INTO [t9065] VALUES (13,'وقوله: (فمن تبع هداي)','35','7','0');
INSERT INTO [t9065] VALUES (14,'وقوله: (وإذ قلتم يا موسى لن نؤمن لك حتى نرى الله جهرة فأخذتكم الصاعقة وأنتم تنظرون (٥٥) ثم بعثناكم من بعد موتكم)','38','7','0');
INSERT INTO [t9065] VALUES (15,'قوله: (إنها بقرة لا ذلول)','42','7','0');
INSERT INTO [t9065] VALUES (16,'قوله: (فقلنا اضربوه ببعضها)','42','7','0');
INSERT INTO [t9065] VALUES (17,'قوله: (وقد كان فريق منهم يسمعون كلام الله ثم يحرفونه من بعد ما عقلوه)','43','7','0');
INSERT INTO [t9065] VALUES (18,'قوله: (ومنهم أميون لا يعلمون الكتاب إلا أماني وإن هم إلا يظنون (٧٨)','44','7','0');
INSERT INTO [t9065] VALUES (19,'قوله: (فويل للذين يكتبون الكتاب بأيديهم ثم يقولون هذا من عند الله ليشتروا به ثمنا قليلا فويل لهم مما كتبت أيديهم وويل لهم مما يكسبون (٧٩)) .','45','7','0');
INSERT INTO [t9065] VALUES (20,'وقوله: (وقالوا لن تمسنا النار إلا أياما معدودة قل أتخذتم عند الله عهدا فلن يخلف الله عهده أم تقولون على الله ما لا تعلمون (٨٠))','47','7','0');
INSERT INTO [t9065] VALUES (21,'قوله: (بلى من كسب سيئة وأحاطت به خطيئته فأولئك أصحاب النار هم فيها خالدون (٨١)','48','7','0');
INSERT INTO [t9065] VALUES (22,'قوله: (أفتطمعون أن يؤمنوا لكم وقد كان فريق منهم يسمعون كلام الله)','49','7','0');
INSERT INTO [t9065] VALUES (23,'قوله: (وإذ أخذنا ميثاقكم لا تسفكون دماءكم ولا تخرجون أنفسكم من دياركم) .','53','7','0');
INSERT INTO [t9065] VALUES (24,'قوله: (وإذا قيل لهم آمنوا بما أنزل الله قالوا نؤمن بما أنزل علينا ويكفرون بما وراءه وهو الحق مصدقا لما معهم)','54','7','0');
INSERT INTO [t9065] VALUES (25,'قوله: (فيتعلمون منهما ما يفرقون به بين المرء وزوجه وما هم بضارين به من أحد إلا بإذن الله)','55','7','0');
INSERT INTO [t9065] VALUES (26,'وقوله: (ود كثير من أهل الكتاب لو يردونكم من بعد إيمانكم كفارا حسدا من عند أنفسهم من بعد ما تبين لهم الحق) .','56','7','0');
INSERT INTO [t9065] VALUES (27,'قوله: (أم كنتم شهداء إذ حضر يعقوب الموت إذ قال لبنيه ما تعبدون من بعدي قالوا نعبد إلهك وإله آبائك إبراهيم وإسماعيل وإسحاق إلها واحدا ونحن له مسلمون (١٣٣) .','67','7','0');
INSERT INTO [t9065] VALUES (28,'قوله: (وكذلك جعلناكم أمة وسطا لتكونوا شهداء على الناس ويكون الرسول عليكم شهيدا)','70','7','0');
INSERT INTO [t9065] VALUES (29,'وقوله: (إن الذين يكتمون ما أنزلنا من البينات والهدى من بعد ما بيناه للناس في الكتاب أولئك يلعنهم الله ويلعنهم اللاعنون (١٥٩) إلا الذين تابوا وأصلحوا وبينو','78','7','0');
INSERT INTO [t9065] VALUES (30,'قوله: (ليس البر أن تولوا وجوهكم قبل المشرق والمغرب) إلى قوله: (أولئك الذين صدقوا وأولئك هم المتقون (١٧٧)','79','7','0');
INSERT INTO [t9065] VALUES (31,'قوله: (كتب عليكم إذا حضر أحدكم الموت إن ترك خيرا الوصية للوالدين والأقربين)','80','7','0');
INSERT INTO [t9065] VALUES (32,'قوله: (وقاتلوا في سبيل الله الذين يقاتلونكم)','82','7','0');
INSERT INTO [t9065] VALUES (33,'قوله: (هل ينظرون إلا أن يأتيهم الله في ظلل من الغمام والملائكة)','84','7','0');
INSERT INTO [t9065] VALUES (34,'قوله: (كان الناس أمة واحدة فبعث الله النبيين مبشرين ومنذرين) إلى قوله (فهدى الله الذين آمنوا لما اختلفوا فيه من الحق بإذنه والله يهدي من يشاء إلى صراط مستقيم','85','7','0');
INSERT INTO [t9065] VALUES (35,'قوله: (نساؤكم حرث لكم فأتوا حرثكم أنى شئتم)','89','7','0');
INSERT INTO [t9065] VALUES (36,'وقوله: (والذين يتوفون منكم ويذرون أزواجا يتربصن بأنفسهن أربعة أشهر وعشرا)','93','7','0');
INSERT INTO [t9065] VALUES (37,'قوله: (ولا تعزموا عقدة النكاح حتى يبلغ الكتاب أجله)','93','7','0');
INSERT INTO [t9065] VALUES (38,'قوله: (وأن تعفوا أقرب للتقوى)','96','7','0');
INSERT INTO [t9065] VALUES (39,'قوله: (تلك الرسل فضلنا بعضهم على بعض منهم من كلم الله)','99','7','0');
INSERT INTO [t9065] VALUES (40,'قوله: (ولو شاء الله ما اقتتل الذين من بعدهم من بعد ما جاءتهم البينات ولكن اختلفوا فمنهم من آمن ومنهم من كفر ولو شاء الله ما اقتتلوا ولكن الله يفعل ما يري','99','7','0');
INSERT INTO [t9065] VALUES (41,'قوله: (من ذا الذي يشفع عنده إلا بإذنه) .','100','7','0');
INSERT INTO [t9065] VALUES (42,'قوله: (يا أيها الذين آمنوا أنفقوا من طيبات ما كسبتم ومما أخرجنا لكم من الأرض)','111','7','0');
INSERT INTO [t9065] VALUES (43,'سورة آل عمران','122','0','0');
INSERT INTO [t9065] VALUES (44,'وقوله: (قل إن كنتم تحبون الله فاتبعوني يحببكم الله ويغفر لكم ذنوبكم والله غفور رحيم (٣١))','125','43','0');
INSERT INTO [t9065] VALUES (45,'قوله: (إن الله اصطفى آدم ونوحا وآل إبراهيم وآل عمران على العالمين (٣٣))','126','43','0');
INSERT INTO [t9065] VALUES (46,'قوله: (وما كنت لديهم إذ يلقون أقلامهم أيهم يكفل مريم وما كنت لديهم إذ يختصمون (٤٤)) .','133','43','0');
INSERT INTO [t9065] VALUES (47,'وقوله: (إذ قال الله يا عيسى إني متوفيك ورافعك إلي)','137','43','0');
INSERT INTO [t9065] VALUES (48,'وقوله: (ومطهرك من الذين كفروا)','138','43','0');
INSERT INTO [t9065] VALUES (49,'وقوله: (وجاعل الذين اتبعوك فوق الذين كفروا إلى يوم القيامة)','138','43','0');
INSERT INTO [t9065] VALUES (50,'قوله: (وجاعل الذين اتبعوك)','139','43','0');
INSERT INTO [t9065] VALUES (51,'قوله: (إن مثل عيسى عند الله كمثل آدم خلقه من تراب ثم قال له كن فيكون (٥٩) الحق من ربك فلا تكن من الممترين (٦٠))','141','43','0');
INSERT INTO [t9065] VALUES (52,'قوله: (يا أيها الذين آمنوا لا تكونوا كالذين كفروا وقالوا لإخوانهم إذا ضربوا في الأرض أو كانوا غزى لو كانوا عندنا ما ماتوا وما قتلوا ليجعل الله ذلك حسرة في قلوبهم)','155','43','0');
INSERT INTO [t9065] VALUES (53,'قوله: (أفمن اتبع رضوان الله كمن باء بسخط من الله)','157','43','0');
INSERT INTO [t9065] VALUES (54,'وقوله: (الذين قالوا لإخوانهم وقعدوا لو أطاعونا ما قتلوا قل فادرءوا عن أنفسكم الموت إن كنتم صادقين (١٦٨)','158','43','0');
INSERT INTO [t9065] VALUES (55,'وقوله: (ولا يحزنك الذين يسارعون في الكفر إنهم لن يضروا الله شيئا يريد الله ألا يجعل لهم حظا في الآخرة)','158','43','0');
INSERT INTO [t9065] VALUES (56,'وقوله: (ولا يحسبن الذين كفروا أنما نملي لهم خير لأنفسهم إنما نملي لهم ليزدادوا إثما)','158','43','0');
INSERT INTO [t9065] VALUES (57,'سورة النساء','162','0','0');
INSERT INTO [t9065] VALUES (58,'وقوله: (فإن خفتم ألا تعدلوا فواحدة أو ما ملكت أيمانكم) .','162','57','0');
INSERT INTO [t9065] VALUES (59,'وقوله: (ولا تؤتوا السفهاء أموالكم التي جعل الله لكم قياما وارزقوهم فيها واكسوهم) .','162','57','0');
INSERT INTO [t9065] VALUES (60,'وقوله: (ومن كان غنيا فليستعفف ومن كان فقيرا فليأكل بالمعروف) .','163','57','0');
INSERT INTO [t9065] VALUES (61,'وقوله: (وإذا حضر القسمة أولو القربى واليتامى والمساكين فارزقوهم منه وقولوا لهم قولا معروفا (٨)','166','57','0');
INSERT INTO [t9065] VALUES (62,'قوله: (يوصيكم الله في أولادكم للذكر مثل حظ الأنثيين) .','168','57','0');
INSERT INTO [t9065] VALUES (63,'قوله: (فإن لم يكن له ولد وورثه أبواه فلأمه الثلث)','169','57','0');
INSERT INTO [t9065] VALUES (64,'قوله: (من بعد وصية يوصي بها أو دين)','170','57','0');
INSERT INTO [t9065] VALUES (65,'وقوله: (وأن تجمعوا بين الأختين إلا ما قد سلف) .','172','57','0');
INSERT INTO [t9065] VALUES (66,'وقوله: (والمحصنات من النساء إلا ما ملكت أيمانكم)','174','57','0');
INSERT INTO [t9065] VALUES (67,'قوله: (يريد الله ليبين لكم ويهديكم سنن الذين من قبلكم)','178','57','0');
INSERT INTO [t9065] VALUES (68,'قوله: (وحلائل أبنائكم الذين من أصلابكم)','179','57','0');
INSERT INTO [t9065] VALUES (69,'قوله: (فما لكم في المنافقين فئتين والله أركسهم بما كسبوا أتريدون أن تهدوا من أضل الله ومن يضلل الله فلن تجد له سبيلا (٨٨)','183','57','0');
INSERT INTO [t9065] VALUES (70,'وقوله: (إن خفتم)','185','57','0');
INSERT INTO [t9065] VALUES (71,'وقوله: (وإذا كنت فيهم فأقمت لهم الصلاة فلتقم طائفة منهم معك وليأخذوا أسلحتهم فإذا سجدوا فليكونوا من ورائكم ولتأت طائفة أخرى لم يصلوا فليصلوا معك)','186','57','0');
INSERT INTO [t9065] VALUES (72,'قوله: (إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء) .','194','57','0');
INSERT INTO [t9065] VALUES (73,'قوله: (ومن أصدق من الله قيلا (١٢٢)','197','57','0');
INSERT INTO [t9065] VALUES (74,'قوله: (واتخذ الله إبراهيم خليلا (١٢٥)','197','57','0');
INSERT INTO [t9065] VALUES (75,'وقوله: (ولن تستطيعوا أن تعدلوا بين النساء ولو حرصتم)','199','57','0');
INSERT INTO [t9065] VALUES (76,'قوله: (يا أيها الذين آمنوا آمنوا بالله ورسوله)','201','57','0');
INSERT INTO [t9065] VALUES (77,'وقوله: (إن المنافقين يخادعون الله وهو خادعهم','201','57','0');
INSERT INTO [t9065] VALUES (78,'وقوله: (وكلم الله موسى تكليما (١٦٤))','202','57','0');
INSERT INTO [t9065] VALUES (79,'قوله: (مذبذبين بين ذلك لا إلى هؤلاء ولا إلى هؤلاء ومن يضلل الله فلن تجد له سبيلا (١٤٣)','202','57','0');
INSERT INTO [t9065] VALUES (80,'قوله (بل طبع الله عليها بكفرهم فلا يؤمنون إلا قليلا (١٥٥)','202','57','0');
INSERT INTO [t9065] VALUES (81,'وقوله: (يستفتونك قل الله يفتيكم في الكلالة إن امرؤ هلك ليس له ولد وله أخت فلها نصف ما ترك وهو يرثها إن لم يكن لها ولد)','205','57','0');
INSERT INTO [t9065] VALUES (82,'سورة المائدة','210','0','0');
INSERT INTO [t9065] VALUES (83,'قوله: (فأغرينا بينهم العداوة والبغضاء إلى يوم القيامة)','225','82','0');
INSERT INTO [t9065] VALUES (84,'قوله: (يا أيها الرسول لا يحزنك الذين يسارعون في الكفر من الذين قالوا آمنا بأفواههم ولم تؤمن قلوبهم)','225','82','0');
INSERT INTO [t9065] VALUES (85,'قوله: (يا أيها الرسول لا يحزنك الذين يسارعون في الكفر من الذين قالوا آمنا بأفواههم ولم تؤمن قلوبهم)','225','82','0');
INSERT INTO [t9065] VALUES (86,'وقوله: (ومن يرد الله فتنته فلن تملك له من الله شيئا)','227','82','0');
INSERT INTO [t9065] VALUES (87,'قوله: (ومن لم يحكم بما أنزل الله فأولئك هم الكافرون (٤٤)','228','82','0');
INSERT INTO [t9065] VALUES (88,'قوله: (قل هل أنبئكم بشر من ذلك مثوبة عند الله من لعنه الله وغضب عليه)','237','82','0');
INSERT INTO [t9065] VALUES (89,'قوله: (وألقينا بينهم العداوة والبغضاء إلى يوم القيامة)','240','82','0');
INSERT INTO [t9065] VALUES (90,'قوله: (يا أيها الذين آمنوا لا تقتلوا الصيد وأنتم حرم)','243','82','0');
INSERT INTO [t9065] VALUES (91,'وقوله: (ومن قتله منكم متعمدا)','244','82','0');
INSERT INTO [t9065] VALUES (92,'وقوله: (ومن قتله منكم متعمدا)','245','82','0');
INSERT INTO [t9065] VALUES (93,'وقوله: (ليذوق وبال أمره)','245','82','0');
INSERT INTO [t9065] VALUES (94,'وقوله: (أحل لكم صيد البحر وطعامه)','248','82','0');
INSERT INTO [t9065] VALUES (95,'سورة الأنعام','253','0','0');
INSERT INTO [t9065] VALUES (96,'قوله: (قل إني أمرت أن أكون أول من أسلم)','254','95','0');
INSERT INTO [t9065] VALUES (97,'قوله: (ولا تكونن من المشركين (١٤)','254','95','0');
INSERT INTO [t9065] VALUES (98,'قوله: (وأوحي إلي هذا القرآن لأنذركم به)','255','95','0');
INSERT INTO [t9065] VALUES (99,'قوله: (ومنهم من يستمع إليك وجعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا)','256','95','0');
INSERT INTO [t9065] VALUES (100,'قوله: (وإن كان كبر عليك إعراضهم فإن استطعت أن تبتغي نفقا في الأرض أو سلما في السماء فتأتيهم بآية ولو شاء الله لجمعهم على الهدى فلا تكونن من الجاهلين (٣٥) .','259','95','0');
INSERT INTO [t9065] VALUES (101,'قوله: (وما من دابة في الأرض ولا طائر يطير بجناحيه إلا أمم أمثالكم) .','263','95','0');
INSERT INTO [t9065] VALUES (102,'قوله: (ثم إلى ربهم يحشرون (٣٨)','263','95','0');
INSERT INTO [t9065] VALUES (103,'قوله: (والذين كذبوا بآياتنا صم وبكم في الظلمات من يشإ الله يضلله ومن يشأ يجعله على صراط مستقيم (٣٩)','264','95','0');
INSERT INTO [t9065] VALUES (104,'قوله: (فلولا إذ جاءهم بأسنا تضرعوا ولكن قست قلوبهم وزين لهم الشيطان ما كانوا يعملون (٤٣)','265','95','0');
INSERT INTO [t9065] VALUES (105,'قوله: (ووهبنا له إسحاق ويعقوب كلا هدينا ونوحا هدينا من قبل)','268','95','0');
INSERT INTO [t9065] VALUES (106,'قوله: (ولو أشركوا لحبط عنهم ما كانوا يعملون (٨٨)','268','95','0');
INSERT INTO [t9065] VALUES (107,'قوله: (أولئك الذين هدى الله فبهداهم اقتده)','269','95','0');
INSERT INTO [t9065] VALUES (108,'قوله: (اتبع ما أوحي إليك من ربك لا إله إلا هو وأعرض عن المشركين (١٠٦) ولو شاء الله ما أشركوا)','272','95','0');
INSERT INTO [t9065] VALUES (109,'قوله: (ولا تسبوا الذين يدعون من دون الله فيسبوا الله عدوا بغير علم كذلك زينا لكل أمة عملهم ثم إلى ربهم مرجعهم فينبئهم بما كانوا يعملون (١٠٨)','274','95','0');
INSERT INTO [t9065] VALUES (110,'قوله: (أفغير الله أبتغي حكما)','279','95','0');
INSERT INTO [t9065] VALUES (111,'قوله: (ولا تأكلوا مما لم يذكر اسم الله عليه وإنه لفسق)','286','95','0');
INSERT INTO [t9065] VALUES (112,'قوله: (أومن كان ميتا فأحييناه وجعلنا له نورا يمشي به في الناس كمن مثله في الظلمات ليس بخارج منها)','290','95','0');
INSERT INTO [t9065] VALUES (113,'قوله: (كذلك زين للكافرين ما كانوا يعملون (١٢٢)','291','95','0');
INSERT INTO [t9065] VALUES (114,'وقوله: (وكذلك جعلنا في كل قرية أكابر مجرميها ليمكروا فيها)','292','95','0');
INSERT INTO [t9065] VALUES (115,'وقوله: (قد خسر الذين قتلوا أولادهم سفها بغير علم وحرموا ما رزقهم الله افتراء على الله قد ضلوا وما كانوا مهتدين (١٤٠)) .','294','95','0');
INSERT INTO [t9065] VALUES (116,'قوله: (وجعلوا لله مما ذرأ من الحرث والأنعام نصيبا)','294','95','0');
INSERT INTO [t9065] VALUES (117,'قوله: (وكذلك زين لكثير من المشركين قتل أولادهم شركاؤهم ليردوهم وليلبسوا عليهم دينهم ولو شاء الله ما فعلوه)','294','95','0');
INSERT INTO [t9065] VALUES (118,'بقوله: (لا تأكلوا أموالكم بينكم بالباطل إلا أن تكون تجارة عن تراض منكم) .','298','95','0');
INSERT INTO [t9065] VALUES (119,'قوله: (وهو الذي أنشأ جنات معروشات وغير معروشات والنخل والزرع مختلفا أكله والزيتون والرمان متشابها وغير متشابه كلوا من ثمره إذا أثمر وآتوا حقه يوم حصاد','300','95','0');
INSERT INTO [t9065] VALUES (120,'وقوله: (ومن الأنعام حمولة وفرشا)','302','95','0');
INSERT INTO [t9065] VALUES (121,'قوله: (وفرشا)','303','95','0');
INSERT INTO [t9065] VALUES (122,'وقوله: (وعلى الذين هادوا حرمنا كل ذي ظفر ومن البقر والغنم حرمنا عليهم شحومهما إلا ما حملت ظهورهما أو الحوايا أو ما اختلط بعظم)','313','95','0');
INSERT INTO [t9065] VALUES (123,'قوله: (سيقول الذين أشركوا لو شاء الله ما أشركنا ولا آباؤنا ولا حرمنا من شيء كذلك كذب الذين من قبلهم حتى ذاقوا بأسنا قل هل عندكم من علم فتخرجوه لنا إن تت','313','95','0');
INSERT INTO [t9065] VALUES (124,'قوله: (ثم آتينا موسى الكتاب تماما على الذي أحسن وتفصيلا لكل شيء وهدى ورحمة لعلهم بلقاء ربهم يؤمنون (١٥٤)','316','95','0');
INSERT INTO [t9065] VALUES (125,'قوله: (يوم يأتي بعض آيات ربك لا ينفع نفسا إيمانها لم تكن آمنت من قبل أو كسبت في إيمانها خيرا قل انتظروا إنا منتظرون (١٥٨)','318','95','0');
INSERT INTO [t9065] VALUES (126,'سورة الأعراف','328','0','0');
INSERT INTO [t9065] VALUES (127,'قوله: (اتبعوا ما أنزل إليكم من ربكم ولا تتبعوا من دونه أولياء قليلا ما تذكرون (٣)','328','126','0');
INSERT INTO [t9065] VALUES (128,'وقوله: (إنه يراكم هو وقبيله)','329','126','0');
INSERT INTO [t9065] VALUES (129,'وقوله: (وإذا فعلوا فاحشة قالوا وجدنا عليها آباءنا والله أمرنا بها قل إن الله لا يأمر بالفحشاء)','330','126','0');
INSERT INTO [t9065] VALUES (130,'وقوله: (كما بدأكم تعودون (٢٩) فريقا هدى وفريقا حق عليهم الضلالة)','331','126','0');
INSERT INTO [t9065] VALUES (131,'وقوله: (يا بني آدم خذوا زينتكم عند كل مسجد)','333','126','0');
INSERT INTO [t9065] VALUES (132,'قوله: (حق عليهم الضلالة)','333','126','0');
INSERT INTO [t9065] VALUES (133,'قوله: (قل إنما حرم ربي الفواحش ما ظهر منها وما بطن والإثم)','334','126','0');
INSERT INTO [t9065] VALUES (134,'قوله: (فمن أظلم ممن افترى على الله كذبا أو كذب بآياته أولئك ينالهم نصيبهم من الكتاب)','337','126','0');
INSERT INTO [t9065] VALUES (135,'قوله: (ينالهم نصيبهم من الكتاب حتى إذا جاءتهم رسلنا يتوفونهم)','338','126','0');
INSERT INTO [t9065] VALUES (136,'قوله: (كلما دخلت أمة لعنت أختها)','339','126','0');
INSERT INTO [t9065] VALUES (137,'قوله: (لهم من جهنم مهاد ومن فوقهم غواش)','344','126','0');
INSERT INTO [t9065] VALUES (138,'قوله: (وعلى الأعراف رجال يعرفون كلا بسيماهم)','345','126','0');
INSERT INTO [t9065] VALUES (139,'قوله: (إن ربكم الله الذي خلق السماوات والأرض في ستة أيام ثم استوى على العرش)','346','126','0');
INSERT INTO [t9065] VALUES (140,'وقوله: (ادعوا ربكم تضرعا وخفية)','350','126','0');
INSERT INTO [t9065] VALUES (141,'قوله: (ألا له الخلق والأمر)','350','126','0');
INSERT INTO [t9065] VALUES (142,'قوله: (وهو الذي يرسل الرياح بشرا بين يدي رحمته)','350','126','0');
INSERT INTO [t9065] VALUES (143,'قوله: (حتى إذا أقلت سحابا ثقالا سقناه)','351','126','0');
INSERT INTO [t9065] VALUES (144,'قوله: (كذلك نخرج الموتى لعلكم تذكرون (٥٧)','351','126','0');
INSERT INTO [t9065] VALUES (145,'قوله: (والبلد الطيب يخرج نباته بإذن ربه)','353','126','0');
INSERT INTO [t9065] VALUES (146,'قوله: (ولو أن أهل القرى آمنوا واتقوا لفتحنا عليهم بركات من السماء والأرض ولكن كذبوا فأخذناهم بما كانوا يكسبون (٩٦)','355','126','0');
INSERT INTO [t9065] VALUES (147,'قوله: (أفأمنوا مكر الله فلا يأمن مكر الله إلا القوم الخاسرون (٩٩)','355','126','0');
INSERT INTO [t9065] VALUES (148,'قوله: (أولم يهد للذين يرثون الأرض من بعد أهلها أن لو نشاء أصبناهم بذنوبهم ونطبع على قلوبهم فهم لا يسمعون (١٠٠)','356','126','0');
INSERT INTO [t9065] VALUES (149,'قوله: (ولقد جاءتهم رسلهم بالبينات)','356','126','0');
INSERT INTO [t9065] VALUES (150,'قوله: (ولما جاء موسى لميقاتنا وكلمه ربه)','359','126','0');
INSERT INTO [t9065] VALUES (151,'وقوله: (وكتبنا له في الألواح من كل شيء موعظة وتفصيلا لكل شيء)','363','126','0');
INSERT INTO [t9065] VALUES (152,'وقوله: (وتفصيلا لكل شيء)','364','126','0');
INSERT INTO [t9065] VALUES (153,'قوله: (واتخذ قوم موسى من بعده من حليهم عجلا جسدا له خوار ألم يروا أنه لا يكلمهم ولا يهديهم سبيلا)','364','126','0');
INSERT INTO [t9065] VALUES (154,'قوله: (وألقى الألواح وأخذ برأس أخيه يجره إليه)','365','126','0');
INSERT INTO [t9065] VALUES (155,'قوله: (فلا تشمت بي الأعداء)','366','126','0');
INSERT INTO [t9065] VALUES (156,'قوله: (واختار موسى قومه سبعين رجلا لميقاتنا فلما أخذتهم الرجفة قال رب لو شئت أهلكتهم من قبل وإياي أتهلكنا بما فعل السفهاء منا إن هي إلا فتنتك تضل به','367','126','0');
INSERT INTO [t9065] VALUES (157,'قوله: (قال عذابي أصيب به من أشاء)','369','126','0');
INSERT INTO [t9065] VALUES (158,'قوله: (واتل عليهم نبأ الذي آتيناه آياتنا فانسلخ منها فأتبعه الشيطان فكان من الغاوين (١٧٥) ولو شئنا لرفعناه بها ولكنه أخلد إلى الأرض واتبع هواه)','369','126','0');
INSERT INTO [t9065] VALUES (159,'قوله: (من يهد الله فهو المهتدي ومن يضلل فأولئك هم الخاسرون (١٧٨)','370','126','0');
INSERT INTO [t9065] VALUES (160,'قوله: (ولقد ذرأنا لجهنم كثيرا من الجن والإنس)','371','126','0');
INSERT INTO [t9065] VALUES (161,'قوله: (لهم قلوب لا يفقهون بها ولهم أعين لا يبصرون بها ولهم آذان لا يسمعون بها)','373','126','0');
INSERT INTO [t9065] VALUES (162,'قوله: (والذين كذبوا بآياتنا سنستدرجهم من حيث لا يعلمون (١٨٢)','373','126','0');
INSERT INTO [t9065] VALUES (163,'قوله: (وأملي لهم إن كيدي متين (١٨٣)','373','126','0');
INSERT INTO [t9065] VALUES (164,'قوله: (أولم يتفكروا ما بصاحبهم من جنة إن هو إلا نذير مبين (١٨٤)','374','126','0');
INSERT INTO [t9065] VALUES (165,'قوله: (من يضلل الله فلا هادي له ويذرهم في طغيانهم يعمهون (١٨٦)','375','126','0');
INSERT INTO [t9065] VALUES (166,'قوله: (قل لا أملك لنفسي نفعا ولا ضرا إلا ما شاء الله)','375','126','0');
INSERT INTO [t9065] VALUES (167,'قوله: (ولا يستطيعون لهم نصرا ولا أنفسهم ينصرون (١٩٢)','375','126','0');
INSERT INTO [t9065] VALUES (168,'قوله: (قل ادعوا شركاءكم ثم كيدون فلا تنظرون (١٩٥) إن وليي الله الذي نزل الكتاب وهو يتولى الصالحين (١٩٦)','378','126','0');
INSERT INTO [t9065] VALUES (169,'قوله: (وإما ينزغنك من الشيطان نزغ) .','378','126','0');
INSERT INTO [t9065] VALUES (170,'وقوله: (هو الذي خلقكم من نفس واحدة وجعل منها زوجها ليسكن إليها)','379','126','0');
INSERT INTO [t9065] VALUES (171,'سورة الأنفال','381','0','0');
INSERT INTO [t9065] VALUES (172,'وقوله: (وينزل عليكم من السماء ماء ليطهركم به)','384','171','0');
INSERT INTO [t9065] VALUES (173,'قوله: (يا أيها الذين آمنوا إذا لقيتم الذين كفروا زحفا فلا تولوهم الأدبار (١٥)','384','171','0');
INSERT INTO [t9065] VALUES (174,'قوله: (فقد باء بغضب من الله)','384','171','0');
INSERT INTO [t9065] VALUES (175,'قوله: (ولن تغني عنكم فئتكم شيئا ولو كثرت)','385','171','0');
INSERT INTO [t9065] VALUES (176,'وقوله: (يا أيها الذين آمنوا استجيبوا لله وللرسول إذا دعاكم لما يحييكم واعلموا أن الله يحول بين المرء وقلبه)','387','171','0');
INSERT INTO [t9065] VALUES (177,'قوله: (واتقوا فتنة لا تصيبن الذين ظلموا منكم خاصة)','388','171','0');
INSERT INTO [t9065] VALUES (178,'قوله: (واذكروا إذ أنتم قليل مستضعفون في الأرض تخافون أن يتخطفكم الناس فآواكم وأيدكم بنصره ورزقكم من الطيبات لعلكم تشكرون (٢٦) .','389','171','0');
INSERT INTO [t9065] VALUES (179,'قوله: (يا أيها الذين آمنوا إن تتقوا الله يجعل لكم فرقانا ويكفر عنكم سيئاتكم ويغفر لكم)','390','171','0');
INSERT INTO [t9065] VALUES (180,'وقوله: (يا أيها الذين آمنوا إذا لقيتم فئة فاثبتوا)','391','171','0');
INSERT INTO [t9065] VALUES (181,'قوله: (ولو تواعدتم لاختلفتم في الميعاد ولكن ليقضي الله أمرا كان مفعولا)','391','171','0');
INSERT INTO [t9065] VALUES (182,'وقوله: (ذلك بأن الله لم يك مغيرا نعمة أنعمها على قوم حتى يغيروا ما بأنفسهم)','393','171','0');
INSERT INTO [t9065] VALUES (183,'قوله: (وألف بين قلوبهم لو أنفقت ما في الأرض جميعا ما ألفت بين قلوبهم ولكن الله ألف بينهم)','394','171','0');
INSERT INTO [t9065] VALUES (184,'قوله: (ما كان لنبي أن يكون له أسرى حتى يثخن في الأرض تريدون عرض الدنيا والله يريد الآخرة)','395','171','0');
INSERT INTO [t9065] VALUES (185,'قوله: (لولا كتاب من الله سبق لمسكم فيما أخذتم)','395','171','0');
INSERT INTO [t9065] VALUES (186,'قوله: (وإن استنصروكم في الدين فعليكم النصر)','395','171','0');
INSERT INTO [t9065] VALUES (187,'قوله: (والذين آمنوا وهاجروا وجاهدوا في سبيل الله والذين آووا ونصروا أولئك هم المؤمنون حقا)','396','171','0');
INSERT INTO [t9065] VALUES (188,'سورة التوبة','399','0','0');
INSERT INTO [t9065] VALUES (189,'قوله: (وإن أحد من المشركين استجارك فأجره حتى يسمع كلام الله ثم أبلغه)','399','188','0');
INSERT INTO [t9065] VALUES (190,'وقوله: (فإن تابوا وأقاموا الصلاة وآتوا الزكاة فإخوانكم في الدين)','400','188','0');
INSERT INTO [t9065] VALUES (191,'قوله: (الذين آمنوا وهاجروا وجاهدوا في سبيل الله بأموالهم وأنفسهم أعظم درجة عند الله وأولئك هم الفائزون (٢٠) يبشرهم ربهم برحمة منه ورضوان وجنات لهم فيها ن','419','188','0');
INSERT INTO [t9065] VALUES (192,'قوله: (قاتلوا الذين لا يؤمنون بالله ولا باليوم الآخر ولا يحرمون ما حرم الله ورسوله ولا يدينون دين الحق من الذين أوتوا الكتاب حتى يعطوا الجزية عن يد)','438','188','0');
INSERT INTO [t9065] VALUES (193,'قوله: (هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون (٣٣)','441','188','0');
INSERT INTO [t9065] VALUES (194,'قوله: (إلا تنصروه فقد نصره الله إذ أخرجه الذين كفروا ثاني اثنين إذ هما في الغار إذ يقول لصاحبه لا تحزن إن الله معنا)','454','188','0');
INSERT INTO [t9065] VALUES (195,'قوله: (ولو أرادوا الخروج لأعدوا له عدة ولكن كره الله انبعاثهم فثبطهم)','456','188','0');
INSERT INTO [t9065] VALUES (196,'وقوله: إن تصبك حسنة تسؤهم وإن تصبك مصيبة يقولوا قد أخذنا أمرنا من قبل ويتولوا وهم فرحون (٥٠) قل لن يصيبنا إلا ما كتب الله لنا)','459','188','0');
INSERT INTO [t9065] VALUES (197,'قوله: (قل هل تربصون بنا إلا إحدى الحسنيين)','460','188','0');
INSERT INTO [t9065] VALUES (198,'وقوله: (ونحن نتربص بكم أن يصيبكم الله بعذاب من عنده أو بأيدينا)','461','188','0');
INSERT INTO [t9065] VALUES (199,'قوله: (قل أنفقوا طوعا أو كرها لن يتقبل منكم إنكم كنتم قوما فاسقين (٥٣) وما منعهم أن تقبل منهم نفقاتهم إلا أنهم كفروا بالله وبرسوله ولا يأتون الصلاة إل','461','188','0');
INSERT INTO [t9065] VALUES (200,'قوله: (فلا تعجبك أموالهم ولا أولادهم إنما يريد الله ليعذبهم بها في الحياة الدنيا وتزهق أنفسهم وهم كافرون (٥٥)','461','188','0');
INSERT INTO [t9065] VALUES (201,'قوله: (ليعذبهم بها في الحياة الدنيا)','462','188','0');
INSERT INTO [t9065] VALUES (202,'قوله: (إنما الصدقات للفقراء والمساكين والعاملين عليها والمؤلفة قلوبهم وفي الرقاب والغارمين وفي سبيل الله وابن السبيل) .','463','188','0');
INSERT INTO [t9065] VALUES (203,'وقوله: (لو يجدون ملجأ أو مغارات أو مدخلا لولوا إليه وهم يجمحون (٥٧)','465','188','0');
INSERT INTO [t9065] VALUES (204,'وقوله: (والمؤمنون والمؤمنات بعضهم أولياء بعض يأمرون بالمعروف وينهون عن المنكر ويقيمون الصلاة ويؤتون الزكاة ويطيعون الله ورسوله) .','467','188','0');
INSERT INTO [t9065] VALUES (205,'قوله: (أولئك سيرحمهم الله)','468','188','0');
INSERT INTO [t9065] VALUES (206,'قوله: (يحلفون بالله ما قالوا ولقد قالوا كلمة الكفر وكفروا بعد إسلامهم وهموا بما لم ينالوا وما نقموا إلا أن أغناهم الله ورسوله من فضله فإن يتوبوا يك خيرا لهم','469','188','0');
INSERT INTO [t9065] VALUES (207,'قوله: (الذين يلمزون المطوعين من المؤمنين في الصدقات والذين لا يجدون إلا جهدهم فيسخرون منهم سخر الله منهم)','481','188','0');
INSERT INTO [t9065] VALUES (208,'قوله: (استغفر لهم أو لا تستغفر لهم إن تستغفر لهم سبعين مرة فلن يغفر الله لهم)','481','188','0');
INSERT INTO [t9065] VALUES (209,'قوله: (فرح المخلفون بمقعدهم خلاف رسول الله وكرهوا أن يجاهدوا بأموالهم وأنفسهم في سبيل الله)','483','188','0');
INSERT INTO [t9065] VALUES (210,'وقوله: (ولا تصل على أحد منهم مات أبدا ولا تقم على قبره)','484','188','0');
INSERT INTO [t9065] VALUES (211,'قوله: (وإذا أنزلت سورة أن آمنوا بالله وجاهدوا مع رسوله استأذنك أولو الطول منهم وقالوا ذرنا نكن مع القاعدين (٨٦) رضوا بأن يكونوا مع الخوالف وطبع على قلوبهم فهم','485','188','0');
INSERT INTO [t9065] VALUES (212,'قوله: (وطبع على قلوبهم فهم لا يفقهون (٨٧)','487','188','0');
INSERT INTO [t9065] VALUES (213,'قوله: (لكن الرسول والذين آمنوا معه جاهدوا بأموالهم وأنفسهم وأولئك لهم الخيرات وأولئك هم المفلحون (٨٨)','487','188','0');
INSERT INTO [t9065] VALUES (214,'قوله: (ولا على الذين إذا ما أتوك لتحملهم)','489','188','0');
INSERT INTO [t9065] VALUES (215,'قوله: (يعتذرون إليكم إذا رجعتم إليهم قل لا تعتذروا لن نؤمن لكم قد نبأنا الله من أخباركم)','489','188','0');
INSERT INTO [t9065] VALUES (216,'قوله: (سيحلفون بالله لكم إذا انقلبتم إليهم لتعرضوا عنهم فأعرضوا عنهم إنهم رجس)','490','188','0');
INSERT INTO [t9065] VALUES (217,'قوله: (يحلفون لكم لترضوا عنهم فإن ترضوا عنهم فإن الله لا يرضى عن القوم الفاسقين (٩٦)','490','188','0');
INSERT INTO [t9065] VALUES (218,'قوله: (وصلوات الرسول)','492','188','0');
INSERT INTO [t9065] VALUES (219,'قوله: (والسابقون الأولون من المهاجرين والأنصار والذين اتبعوهم بإحسان رضي الله عنهم ورضوا عنه وأعد لهم جنات تجري تحتها الأنهار خالدين فيها أبدا ذلك الفوز','493','188','0');
INSERT INTO [t9065] VALUES (220,'قوله: (خذ من أموالهم صدقة)','494','188','0');
INSERT INTO [t9065] VALUES (221,'وقوله: (وقل اعملوا فسيرى الله عملكم ورسوله والمؤمنون)','496','188','0');
INSERT INTO [t9065] VALUES (222,'قوله: (لقد تاب الله على النبي والمهاجرين والأنصار) إلى قول: (إن الله هو التواب الرحيم (١١٨)','499','188','0');
INSERT INTO [t9065] VALUES (223,'قوله: (وإذا ما أنزلت سورة فمنهم من يقول أيكم زادته هذه إيمانا فأما الذين آمنوا فزادتهم إيمانا وهم يستبشرون (١٢٤)','502','188','0');
INSERT INTO [t9065] VALUES (224,'قوله: (وأما الذين في قلوبهم مرض فزادتهم رجسا إلى رجسهم وماتوا وهم كافرون (١٢٥)','503','188','0');
INSERT INTO [t9065] VALUES (225,'سورة يونس','504','0','0');
INSERT INTO [t9065] VALUES (226,'وقوله: (هو الذي جعل الشمس ضياء والقمر نورا وقدره منازل لتعلموا عدد السنين والحساب)','504','225','0');
INSERT INTO [t9065] VALUES (227,'وقوله: (وإذا مس الإنسان الضر دعانا لجنبه أو قاعدا أو قائما فلما كشفنا عنه ضره مر كأن لم يدعنا إلى ضر مسه)','504','225','0');
INSERT INTO [t9065] VALUES (228,'وقوله: (كذلك زين للمسرفين ما كانوا يعملون (١٢)','505','225','0');
INSERT INTO [t9065] VALUES (229,'قوله: (وما كان الناس إلا أمة واحدة فاختلفوا ولولا كلمة سبقت من ربك لقضي بينهم فيما فيه يختلفون (١٩)','506','225','0');
INSERT INTO [t9065] VALUES (230,'قوله: (قل الله أسرع مكرا)','506','225','0');
INSERT INTO [t9065] VALUES (231,'قوله: (هو الذي يسيركم في البر والبحر)','506','225','0');
INSERT INTO [t9065] VALUES (232,'قوله: (والله يدعو إلى دار السلام ويهدي من يشاء إلى صراط مستقيم (٢٥)','508','225','0');
INSERT INTO [t9065] VALUES (233,'قوله: (للذين أحسنوا الحسنى وزيادة)','508','225','0');
INSERT INTO [t9065] VALUES (234,'قوله: (كذلك حقت كلمت ربك على الذين فسقوا أنهم لا يؤمنون (٣٣)','508','225','0');
INSERT INTO [t9065] VALUES (235,'وقوله: (وما يتبع أكثرهم إلا ظنا إن الظن لا يغني من الحق شيئا)','509','225','0');
INSERT INTO [t9065] VALUES (236,'قوله: (ويوم يحشرهم كأن لم يلبثوا إلا ساعة من النهار)','510','225','0');
INSERT INTO [t9065] VALUES (237,'قوله: (قل لا أملك لنفسي ضرا ولا نفعا إلا ما شاء الله لكل أمة أجل إذا جاء أجلهم فلا يستأخرون ساعة ولا يستقدمون (٤٩)','510','225','0');
INSERT INTO [t9065] VALUES (238,'قوله: (فما آمن لموسى إلا ذرية من قومه)','512','225','0');
INSERT INTO [t9065] VALUES (239,'قوله: (وقال موسى يا قوم إن كنتم آمنتم بالله فعليه توكلوا إن كنتم مسلمين (٨٤)','513','225','0');
INSERT INTO [t9065] VALUES (240,'قوله: (وأوحينا إلى موسى وأخيه أن تبوآ لقومكما بمصر بيوتا واجعلوا بيوتكم قبلة)','514','225','0');
INSERT INTO [t9065] VALUES (241,'قوله: (ولقد بوأنا بني إسرائيل مبوأ صدق ورزقناهم من الطيبات فما اختلفوا حتى جاءهم العلم) .','515','225','0');
INSERT INTO [t9065] VALUES (242,'قوله: (إن الذين حقت عليهم كلمت ربك لا يؤمنون (٩٦) ولو جاءتهم كل آية حتى يروا العذاب الأليم (٩٧)','516','225','0');
INSERT INTO [t9065] VALUES (243,'قوله: (ولو شاء ربك لآمن من في الأرض كلهم جميعا أفأنت تكره الناس حتى يكونوا مؤمنين (٩٩) وما كان لنفس أن تؤمن إلا بإذن الله ويجعل الرجس على الذين لا يعقلون','517','225','0');
INSERT INTO [t9065] VALUES (244,'قوله: (وأمرت أن أكون من المؤمنين (١٠٤) وأن أقم وجهك للدين حنيفا)','517','225','0');
INSERT INTO [t9065] VALUES (245,'سورة هود','519','0','0');
INSERT INTO [t9065] VALUES (246,'قوله: (وأن استغفروا ربكم ثم توبوا إليه يمتعكم متاعا حسنا إلى أجل مسمى ويؤت كل ذي فضل فضله)','519','245','0');
INSERT INTO [t9065] VALUES (247,'قوله: (أم يقولون افتراه قل فأتوا بعشر سور مثله مفتريات وادعوا من استطعتم من دون الله إن كنتم صادقين (١٣)','519','245','0');
INSERT INTO [t9065] VALUES (248,'(ولا ينفعكم نصحي إن أردت أن أنصح لكم إن كان الله يريد أن يغويكم هو ربكم وإليه ترجعون (٣٤)','520','245','0');
INSERT INTO [t9065] VALUES (249,'قوله: (وأوحي إلى نوح أنه لن يؤمن من قومك إلا من قد آمن)','521','245','0');
INSERT INTO [t9065] VALUES (250,'قوله: (قلنا احمل فيها من كل زوجين اثنين وأهلك إلا من سبق عليه القول)','521','245','0');
INSERT INTO [t9065] VALUES (251,'وقوله: (واستوت على الجودي)','521','245','0');
INSERT INTO [t9065] VALUES (252,'وقوله: (يا قوم لا أسألكم عليه أجرا)','522','245','0');
INSERT INTO [t9065] VALUES (253,'وقوله: (وأقم الصلاة طرفي النهار وزلفا من الليل إن الحسنات يذهبن السيئات)','523','245','0');
INSERT INTO [t9065] VALUES (254,'قوله: (ولا يزالون مختلفين (١١٨) إلا من رحم ربك ولذلك خلقهم وتمت كلمة ربك لأملأن جهنم من الجنة والناس أجمعين (١١٩) .','525','245','0');
INSERT INTO [t9065] VALUES (255,'سورة يوسف','527','0','0');
INSERT INTO [t9065] VALUES (256,'وقوله: (وتكونوا من بعده قوما صالحين (٩)','528','255','0');
INSERT INTO [t9065] VALUES (257,'وقوله: " (وجاءوا أباهم عشاء يبكون (١٦))','529','255','0');
INSERT INTO [t9065] VALUES (258,'قوله: (وراودته التي هو في بيتها عن نفسه وغلقت الأبواب وقالت هيت لك قال معاذ الله إنه ربي أحسن مثواي)','530','255','0');
INSERT INTO [t9065] VALUES (259,'قوله: (وألفيا سيدها لدى الباب)','531','255','0');
INSERT INTO [t9065] VALUES (260,'قوله: (يا صاحبي السجن أما أحدكما فيسقي ربه خمرا وأما الآخر فيصلب فتأكل الطير من رأسه قضي الأمر الذي فيه تستفتيان (٤١)','535','255','0');
INSERT INTO [t9065] VALUES (261,'قوله: (وقال للذي ظن أنه ناج منهما اذكرني عند ربك فأنساه الشيطان ذكر ربه) .','535','255','0');
INSERT INTO [t9065] VALUES (262,'قوله: (قال ما خطبكن إذ راودتن يوسف عن نفسه قلن حاش لله ما علمنا عليه من سوء)','536','255','0');
INSERT INTO [t9065] VALUES (263,'قوله: (وكذلك مكنا ليوسف في الأرض يتبوأ منها حيث يشاء نصيب برحمتنا من نشاء ولا نضيع أجر المحسنين (٥٦) .','536','255','0');
INSERT INTO [t9065] VALUES (264,'قوله: (فلما جهزهم بجهازهم جعل السقاية في رحل أخيه ثم أذن مؤذن أيتها العير إنكم لسارقون (٧٠)','540','255','0');
INSERT INTO [t9065] VALUES (265,'قوله: (قالوا تالله تفتأ تذكر يوسف حتى تكون حرضا أو تكون من الهالكين (٨٥)','542','255','0');
INSERT INTO [t9065] VALUES (266,'قوله: (فلما دخلوا على يوسف آوى إليه أبويه)','543','255','0');
INSERT INTO [t9065] VALUES (267,'قوله: (ورفع أبويه على العرش)','543','255','0');
INSERT INTO [t9065] VALUES (268,'وقوله: (وقال يا أبت هذا تأويل رؤياي من قبل قد جعلها ربي حقا)','544','255','0');
INSERT INTO [t9065] VALUES (269,'سورة الرعد','546','0','0');
INSERT INTO [t9065] VALUES (270,'قوله: (ومن كل الثمرات جعل فيها زوجين اثنين)','546','269','0');
INSERT INTO [t9065] VALUES (271,'قوله تعالى: (وإن تعجب فعجب قولهم)','546','269','0');
INSERT INTO [t9065] VALUES (272,'قوله: (وهم يجادلون في الله وهو شديد المحال (١٣)','547','269','0');
INSERT INTO [t9065] VALUES (273,'قوله: (كذلك يضرب الله الأمثال (١٧) للذين استجابوا لربهم الحسنى والذين لم يستجيبوا له لو أن لهم ما في الأرض جميعا ومثله معه لافتدوا به أولئك لهم سوء الحساب','547','269','0');
INSERT INTO [t9065] VALUES (274,'قوله تعالى: (ويقول الذين كفروا لولا أنزل عليه آية من ربه قل إن الله يضل من يشاء ويهدي إليه من أناب (٢٧) .','548','269','0');
INSERT INTO [t9065] VALUES (275,'قوله: (بل زين للذين كفروا مكرهم وصدوا عن السبيل ومن يضلل الله فما له من هاد (٣٣)','548','269','0');
INSERT INTO [t9065] VALUES (276,'قول: (والله يحكم لا معقب لحكمه)','548','269','0');
INSERT INTO [t9065] VALUES (277,'سورة إبراهيم','549','0','0');
INSERT INTO [t9065] VALUES (278,'قوله: (وما أرسلنا من رسول إلا بلسان قومه ليبين لهم فيضل الله من يشاء ويهدي من يشاء وهو العزيز الحكيم (٤)','549','277','0');
INSERT INTO [t9065] VALUES (279,'لكم من ذنوبكم ويؤخركم إلى أجل مسمى قالوا إن أنتم إلا بشر مثلنا تريدون أن تصدونا عما كان يعبد آباؤنا فأتونا بسلطان مبين (١٠) قالت لهم رسلهم إن نحن إلا ب','550','277','0');
INSERT INTO [t9065] VALUES (280,'وقوله: (يثبت الله الذين آمنوا بالقول الثابت في الحياة الدنيا وفي الآخرة ويضل الله الظالمين ويفعل الله ما يشاء (٢٧)','555','277','0');
INSERT INTO [t9065] VALUES (281,'قوله: (ألم تر كيف ضرب الله مثلا كلمة طيبة كشجرة طيبة) إلى قوله في الكلمة الخبيثة: (ما لها من قرار (٢٦)','555','277','0');
INSERT INTO [t9065] VALUES (282,'قوله: (ألم تر إلى الذين بدلوا نعمت الله كفرا وأحلوا قومهم دار البوار (٢٨) و (ألم تر كيف ضرب الله مثلا كلمة طيبة)','559','277','0');
INSERT INTO [t9065] VALUES (283,'سورة الحجر','567','0','0');
INSERT INTO [t9065] VALUES (284,'قوله: (وما أهلكنا من قرية إلا ولها كتاب معلوم (٤)','568','283','0');
INSERT INTO [t9065] VALUES (285,'سورة النحل','573','0','0');
INSERT INTO [t9065] VALUES (286,'قوله: (خلق الإنسان من نطفة فإذا هو خصيم مبين (٤)','573','285','0');
INSERT INTO [t9065] VALUES (287,'قوله: (والأنعام خلقها لكم فيها دفء)','573','285','0');
INSERT INTO [t9065] VALUES (288,'قوله: (والخيل والبغال والحمير لتركبوها وزينة)','574','285','0');
INSERT INTO [t9065] VALUES (289,'قوله: (هو الذي أنزل من السماء ماء لكم منه شراب ومنه شجر فيه تسيمون (١٠)','574','285','0');
INSERT INTO [t9065] VALUES (290,'وقوله: (وهو الذي سخر البحر لتأكلوا منه لحما طريا وتستخرجوا منه حلية تلبسونها وترى الفلك مواخر فيه ولتبتغوا من فضله ولعلكم تشكرون (١٤)','575','285','0');
INSERT INTO [t9065] VALUES (291,'وقوله: (ليحملوا أوزارهم كاملة يوم القيامة ومن أوزار الذين يضلونهم بغير علم ألا ساء ما يزرون (٢٥)','578','285','0');
INSERT INTO [t9065] VALUES (292,'قوله: (قد مكر الذين من قبلهم فأتى الله بنيانهم من القواعد فخر عليهم السقف من فوقهم)','578','285','0');
INSERT INTO [t9065] VALUES (293,'قوله: (يخافون ربهم من فوقهم ويفعلون ما يؤمرون (٥٠)','593','285','0');
INSERT INTO [t9065] VALUES (294,'قوله: (ويجعلون لله البنات سبحانه ولهم ما يشتهون (٥٧)','598','285','0');
INSERT INTO [t9065] VALUES (295,'قوله: (وإذا بشر أحدهم بالأنثى ظل وجهه مسودا وهو كظيم (٥٨) يتوارى من القوم من سوء ما بشر به)','600','285','0');
INSERT INTO [t9065] VALUES (296,'قوله: (ظل وجهه مسودا وهو كظيم (٥٨)','600','285','0');
INSERT INTO [t9065] VALUES (297,'وقوله: (أيمسكه على هون أم يدسه في التراب)','602','285','0');
INSERT INTO [t9065] VALUES (298,'قوله: (تالله لقد أرسلنا إلى أمم من قبلك)','603','285','0');
INSERT INTO [t9065] VALUES (299,'وقوله: (وإن لكم في الأنعام لعبرة نسقيكم مما في بطونه)','604','285','0');
INSERT INTO [t9065] VALUES (300,'قوله: (من بين فرث ودم لبنا خالصا سائغا للشاربين (٦٦)','605','285','0');
INSERT INTO [t9065] VALUES (301,'قوله: (ومن ثمرات النخيل والأعناب تتخذون منه سكرا ورزقا حسنا)','608','285','0');
INSERT INTO [t9065] VALUES (302,'قوله: (وأوحى ربك إلى النحل أن اتخذي من الجبال بيوتا ومن الشجر ومما يعرشون (٦٨) ثم كلي من كل الثمرات فاسلكي سبل ربك ذللا يخرج من بطونها شراب مختلف ألوانه','612','285','0');
INSERT INTO [t9065] VALUES (303,'قوله: (ولو شاء الله لجعلكم أمة واحدة ولكن يضل من يشاء ويهدي من يشاء ولتسألن عما كنتم تعملون (٩٣)','614','285','0');
INSERT INTO [t9065] VALUES (304,'قوله: (إن إبراهيم كان أمة قانتا لله حنيفا)','629','285','0');
INSERT INTO [t9065] VALUES (305,'قوله: (ادع إلى سبيل ربك بالحكمة والموعظة الحسنة)','630','285','0');
INSERT INTO [t9065] VALUES (306,'وقوله: (واصبر وما صبرك إلا بالله)','631','285','0');
INSERT INTO [t9065] VALUES (307,'سورة بني إسرائيل','632','0','0');
INSERT INTO [t9065] VALUES (308,'قوله: (وقضينا إلى بني إسرائيل في الكتاب لتفسدن في الأرض مرتين)','632','307','0');
INSERT INTO [t9065] VALUES (309,'قوله: (وكل إنسان ألزمناه طائره في عنقه)','633','307','0');
INSERT INTO [t9065] VALUES (310,'قوله: (وقضى ربك ألا تعبدوا إلا إياه)','640','307','0');
INSERT INTO [t9065] VALUES (311,'قوله: (فلا يسرف في القتل)','654','307','0');
INSERT INTO [t9065] VALUES (312,'قوله: (ولا تقربوا مال اليتيم إلا بالتي هي أحسن حتى يبلغ أشده)','661','307','0');
INSERT INTO [t9065] VALUES (313,'قوله: (قل لو كان معه آلهة كما يقولون إذا لابتغوا إلى ذي العرش سبيلا (٤٢) .','688','307','0');
INSERT INTO [t9065] VALUES (314,'وقوله: (تسبح له السماوات السبع والأرض ومن فيهن وإن من شيء إلا يسبح بحمده ولكن لا تفقهون تسبيحهم)','689','307','0');
INSERT INTO [t9065] VALUES (315,'قوله: (وإذا قرأت القرآن جعلنا بينك وبين الذين لا يؤمنون بالآخرة حجابا مستورا (٤٥)','691','307','0');
INSERT INTO [t9065] VALUES (316,'قوله: (وجعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا)','696','307','0');
INSERT INTO [t9065] VALUES (317,'قوله: (نحن أعلم بما يستمعون به إذ يستمعون إليك وإذ هم نجوى) .','697','307','0');
INSERT INTO [t9065] VALUES (318,'قوله: (وإن من قرية إلا نحن مهلكوها قبل يوم القيامة أو معذبوها عذابا شديدا كان ذلك في الكتاب مسطورا (٥٨)','697','307','0');
INSERT INTO [t9065] VALUES (319,'وقوله: (وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس قال أأسجد لمن خلقت طينا (٦١) قال أرأيتك هذا الذي كرمت علي)','699','307','0');
INSERT INTO [t9065] VALUES (320,'قوله: (وإن كادوا ليفتنونك عن الذي أوحينا إليك لتفتري علينا غيره وإذا لاتخذوك خليلا (٧٣) ولولا أن ثبتناك لقد كدت تركن إليهم شيئا قليلا (٧٤)','703','307','0');
INSERT INTO [t9065] VALUES (321,'وقوله: (ومن يهد الله فهو المهتد ومن يضلل فلن تجد لهم أولياء من دونه)','707','307','0');
INSERT INTO [t9065] VALUES (322,'وقوله: (أإذا كنا عظاما ورفاتا أإنا لمبعوثون خلقا جديدا (٩٨) أولم يروا أن الله الذي خلق السماوات والأرض قادر على أن يخلق مثلهم)','707','307','0');
INSERT INTO [t9065] VALUES (323,'سورة الكهف','709','0','0');
INSERT INTO [t9065] VALUES (324,'قوله: (من يهد الله فهو المهتد ومن يضلل فلن تجد له وليا مرشدا (١٧)','709','323','0');
INSERT INTO [t9065] VALUES (325,'وقوله: (ولا تطع من أغفلنا قلبه عن ذكرنا)','728','323','0');
INSERT INTO [t9065] VALUES (326,'وقوله: (ومن أظلم ممن ذكر بآيات ربه فأعرض عنها ونسي ما قدمت يداه إنا جعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا وإن تدعهم إلى الهدى فلن يهتدوا إذ','731','323','0');
INSERT INTO [t9065] VALUES (327,'سورة مريم','747','0','0');
INSERT INTO [t9065] VALUES (328,'قوله: (فهب لي من لدنك وليا (٥)','747','327','0');
INSERT INTO [t9065] VALUES (329,'قوله: (فأوحى إليهم أن سبحوا بكرة وعشيا (١١)','750','327','0');
INSERT INTO [t9065] VALUES (330,'وقوله: (قالوا كيف نكلم من كان في المهد صبيا (٢٩)','763','327','0');
INSERT INTO [t9065] VALUES (331,'وقوله: (فلما اعتزلهم وما يعبدون من دون الله وهبنا له إسحاق ويعقوب وكلا جعلنا نبيا (٤٩) ووهبنا لهم من رحمتنا وجعلنا لهم لسان صدق عليا (٥٠)','766','327','0');
INSERT INTO [t9065] VALUES (332,'قوله: (وقربناه نجيا (٥٢)','777','327','0');
INSERT INTO [t9065] VALUES (333,'وقوله: (لا يسمعون فيها لغوا إلا سلاما)','779','327','0');
INSERT INTO [t9065] VALUES (334,'قوله: (تلك الجنة التي نورث من عبادنا)','780','327','0');
INSERT INTO [t9065] VALUES (335,'وقوله: (رب السماوات والأرض وما بينهما فاعبده واصطبر لعبادته)','781','327','0');
INSERT INTO [t9065] VALUES (336,'وقوله: (ويقول الإنسان أإذا ما مت لسوف أخرج حيا (٦٦)','781','327','0');
INSERT INTO [t9065] VALUES (337,'وقوله: (وإن منكم إلا واردها كان على ربك حتما مقضيا (٧١) ثم ننجي الذين اتقوا ونذر الظالمين فيها جثيا (٧٢)','782','327','0');
INSERT INTO [t9065] VALUES (338,'قوله: (قل من كان في الضلالة فليمدد له الرحمن مدا)','799','327','0');
INSERT INTO [t9065] VALUES (339,'قوله: (ألم تر أنا أرسلنا الشياطين على الكافرين تؤزهم أزا (٨٣)','800','327','0');
INSERT INTO [t9065] VALUES (340,'قوله: (يوم نحشر المتقين إلى الرحمن وفدا (٨٥) ونسوق المجرمين إلى جهنم وردا (٨٦)','801','327','0');
INSERT INTO [t9065] VALUES (341,'قوله: (وقالوا اتخذ الرحمن ولدا (٨٨) لقد جئتم شيئا إدا (٨٩)','802','327','0');
INSERT INTO [t9065] VALUES (342,'قوله: (إن الذين آمنوا وعملوا الصالحات سيجعل لهم الرحمن ودا (٩٦)','804','327','0');
INSERT INTO [t9065] VALUES (343,'سورة طه','805','0','0');
INSERT INTO [t9065] VALUES (344,'قوله: (فلما أتاها نودي يا موسى (١١) إني أنا ربك فاخلع نعليك إنك بالواد المقدس طوى (١٢)','805','343','0');
INSERT INTO [t9065] VALUES (345,'قوله: (قال خذها ولا تخف)','809','343','0');
INSERT INTO [t9065] VALUES (346,'قوله: (لا تخافا إنني معكما أسمع وأرى (٤٦)','811','343','0');
INSERT INTO [t9065] VALUES (347,'قوله: (فإذا حبالهم وعصيهم يخيل إليه من سحرهم أنها تسعى (٦٦)','812','343','0');
INSERT INTO [t9065] VALUES (348,'قوله: (فأوجس في نفسه خيفة)','813','343','0');
INSERT INTO [t9065] VALUES (349,'قوله: (إنه من يأت ربه مجرما فإن له جهنم لا يموت فيها ولا يحيى (٧٤)','814','343','0');
INSERT INTO [t9065] VALUES (350,'قوله: (يوم ينفخ في الصور ونحشر المجرمين يومئذ زرقا (١٠٢) يتخافتون بينهم إن لبثتم إلا عشرا (١٠٣)','816','343','0');
INSERT INTO [t9065] VALUES (351,'وقوله: (يومئذ لا تنفع الشفاعة إلا من أذن له الرحمن ورضي له قولا (١٠٩)','818','343','0');
INSERT INTO [t9065] VALUES (352,'وقوله: (وكذلك نجزي من أسرف ولم يؤمن بآيات ربه)','825','343','0');
INSERT INTO [t9065] VALUES (353,'وقوله: (ولولا كلمة سبقت من ربك لكان لزاما وأجل مسمى (١٢٩)','826','343','0');
INSERT INTO [t9065] VALUES (354,'سورة الأنبياء','828','0','0');
INSERT INTO [t9065] VALUES (355,'وقوله: (وله من في السماوات والأرض ومن عنده لا يستكبرون عن عبادته)','828','354','0');
INSERT INTO [t9065] VALUES (356,'قوله: (ولوطا آتيناه حكما وعلما ونجيناه من القرية التي كانت تعمل الخبائث إنهم كانوا قوم سوء فاسقين (٧٤)','831','354','0');
INSERT INTO [t9065] VALUES (357,'وقوله: (وعلمناه صنعة لبوس لكم لتحصنكم من بأسكم فهل أنتم شاكرون (٨٠)','832','354','0');
INSERT INTO [t9065] VALUES (358,'وقوله: (فنادى في الظلمات أن لا إله إلا أنت سبحانك إني كنت من الظالمين (٨٧)','833','354','0');
INSERT INTO [t9065] VALUES (359,'قوله: (يوم نطوي السماء كطي السجل للكتب)','835','354','0');
INSERT INTO [t9065] VALUES (360,'سورة الحج','836','0','0');
INSERT INTO [t9065] VALUES (361,'قوله: (وترى الناس سكارى وما هم بسكارى ولكن عذاب الله شديد (٢)','836','360','0');
INSERT INTO [t9065] VALUES (362,'وقوله: (ومن الناس من يجادل في الله بغير علم ويتبع كل شيطان مريد (٣) كتب عليه أنه من تولاه فأنه يضله ويهديه إلى عذاب السعير (٤)','837','360','0');
INSERT INTO [t9065] VALUES (363,'وقوله: (ومن يهن الله فما له من مكرم)','837','360','0');
INSERT INTO [t9065] VALUES (364,'وقوله: (ذلك ومن يعظم شعائر الله فإنها من تقوى القلوب (٣٢) لكم فيها منافع إلى أجل مسمى ثم محلها إلى البيت العتيق (٣٣)','838','360','0');
INSERT INTO [t9065] VALUES (365,'وقوله: (فكلوا منها وأطعموا البائس الفقير (٢٨)','839','360','0');
INSERT INTO [t9065] VALUES (366,'وقوله: (ولينصرن الله من ينصره إن الله لقوي عزيز (٤٠) الذين إن مكناهم في الأرض أقاموا الصلاة وآتوا الزكاة وأمروا بالمعروف ونهوا عن المنكر ولله عاقبة الأم','848','360','0');
INSERT INTO [t9065] VALUES (367,'وقوله: (الذين إن مكناهم في الأرض)','849','360','0');
INSERT INTO [t9065] VALUES (368,'وقوله: (وإن يكذبوك فقد كذبت قبلهم قوم نوح وعاد وثمود (٤٢) وقوم إبراهيم وقوم لوط (٤٣) وأصحاب مدين وكذب موسى فأمليت للكافرين ثم أخذتهم فكيف كان نكير (٤٤)','850','360','0');
INSERT INTO [t9065] VALUES (369,'وقوله: (فكأين من قرية أهلكناها وهي ظالمة فهي خاوية) وكذلك ما بعده: (وكأين من قرية أمليت لها وهي ظالمة ثم أخذتها وإلي المصير (٤٨)','852','360','0');
INSERT INTO [t9065] VALUES (370,'وقوله: (ليجعل ما يلقي الشيطان فتنة للذين في قلوبهم مرض والقاسية قلوبهم وإن الظالمين لفي شقاق بعيد (٥٣) وليعلم الذين أوتوا العلم أنه الحق من ربك فيؤمنوا ب','854','360','0');
INSERT INTO [t9065] VALUES (371,'وقوله: والذين هاجروا في سبيل الله ثم قتلوا أو ماتوا ليرزقنهم الله رزقا حسنا وإن الله لهو خير الرازقين (٥٨) ليدخلنهم مدخلا يرضونه وإن الله لعليم حليم (٥٩)','855','360','0');
INSERT INTO [t9065] VALUES (372,'وقوله: (وإذا تتلى عليهم آياتنا بينات تعرف في وجوه الذين كفروا المنكر يكادون يسطون بالذين يتلون عليهم آياتنا)','862','360','0');
INSERT INTO [t9065] VALUES (373,'وقوله: (قل أفأنبئكم بشر من ذلكم النار)','863','360','0');
INSERT INTO [t9065] VALUES (374,'سورة المؤمنون','864','0','0');
INSERT INTO [t9065] VALUES (375,'وقوله: (ولقد خلقنا الإنسان من سلالة من طين (١٢)','865','374','0');
INSERT INTO [t9065] VALUES (376,'وقوله: (ولقد خلقنا فوقكم سبع طرائق وما كنا عن الخلق غافلين (١٧) .','866','374','0');
INSERT INTO [t9065] VALUES (377,'قوله: (ما تسبق من أمة أجلها وما يستأخرون (٤٣)','873','374','0');
INSERT INTO [t9065] VALUES (378,'وقوله: (وآويناهما إلى ربوة ذات قرار ومعين (٥٠)','875','374','0');
INSERT INTO [t9065] VALUES (379,'وقوله: (فذرهم في غمرتهم حتى حين (٥٤) أيحسبون أنما نمدهم به من مال وبنين (٥٥) نسارع لهم في الخيرات بل لا يشعرون (٥٦)','876','374','0');
INSERT INTO [t9065] VALUES (380,'وقوله: (قد كانت آياتي تتلى عليكم فكنتم على أعقابكم تنكصون (٦٦) مستكبرين به سامرا تهجرون (٦٧)','880','374','0');
INSERT INTO [t9065] VALUES (381,'قوله: (أم لم يعرفوا رسولهم فهم له منكرون (٦٩) أم يقولون به جنة)','887','374','0');
INSERT INTO [t9065] VALUES (382,'وقوله: (قال كم لبثتم في الأرض عدد سنين (١١٢) قالوا لبثنا يوما أو بعض يوم فاسأل العادين (١١٣) قال إن لبثتم إلا قليلا لو أنكم كنتم تعلمون (١١٤)','893','374','0');
INSERT INTO [t9065] VALUES (383,'وقوله: (إن لبثتم إلا قليلا)','894','374','0');
INSERT INTO [t9065] VALUES (384,'سورة النور','899','0','0');
INSERT INTO [t9065] VALUES (385,'وقوله: (وليشهد عذابهما طائفة من المؤمنين (٢)','902','384','0');
INSERT INTO [t9065] VALUES (386,'وقوله: (ولا تقبلوا لهم شهادة أبدا وأولئك هم الفاسقون (٤) إلا الذين تابوا من بعد ذلك وأصلحوا فإن الله غفور رحيم (٥)','934','384','0');
INSERT INTO [t9065] VALUES (387,'وقوله: (والذين يرمون أزواجهم ولم يكن لهم شهداء إلا أنفسهم) إلى قوله: (والخامسة أن غضب الله عليها إن كان من الصادقين (٩)','940','384','0');
INSERT INTO [t9065] VALUES (388,'وقوله: (لولا إذ سمعتموه ظن المؤمنون والمؤمنات بأنفسهم خيرا وقالوا هذا إفك مبين (١٢)','952','384','0');
INSERT INTO [t9065] VALUES (389,'وقوله: (إذ تلقونه بألسنتكم وتقولون بأفواهكم ما ليس لكم به علم وتحسبونه هينا وهو عند الله عظيم (١٥)','954','384','0');
INSERT INTO [t9065] VALUES (390,'وقوله: (وأنكحوا الأيامى منكم)','961','384','0');
INSERT INTO [t9065] VALUES (391,'وقوله: (إن يكونوا فقراء يغنهم الله من فضله)','967','384','0');
INSERT INTO [t9065] VALUES (392,'وقوله: (والذين يبتغون الكتاب مما ملكت أيمانكم فكاتبوهم إن علمتم فيهم خيرا وآتوهم من مال الله الذي آتاكم)','980','384','0');
INSERT INTO [t9065] VALUES (393,'قوله: (ولا تكرهوا فتياتكم على البغاء إن أردن تحصنا)','986','384','0');
INSERT INTO [t9065] VALUES (394,'وقوله: (لتبتغوا عرض الحياة الدنيا) .','987','384','0');
INSERT INTO [t9065] VALUES (395,'وقوله: (ومن يكرههن فإن الله من بعد إكراههن غفور رحيم (٣٣) ،','988','384','0');
INSERT INTO [t9065] VALUES (396,'قوله: (وليستعفف الذين لا يجدون نكاحا حتى يغنيهم الله من فضله)','990','384','0');
INSERT INTO [t9065] VALUES (397,'قوله: (يوقد من شجرة مباركة)','993','384','0');
INSERT INTO [t9065] VALUES (398,'وقوله: (في بيوت أذن الله أن ترفع ويذكر فيها اسمه يسبح له فيها بالغدو والآصال (٣٦)','994','384','0');
INSERT INTO [t9065] VALUES (399,'وقوله: (والله خلق كل دابة من ماء)','996','384','0');
INSERT INTO [t9065] VALUES (400,'وقوله: (وإذا دعوا إلى الله ورسوله ليحكم بينهم إذا فريق منهم معرضون (٤٨)','996','384','0');
INSERT INTO [t9065] VALUES (401,'وقوله: (وعد الله الذين آمنوا منكم وعملوا الصالحات ليستخلفنهم في الأرض كما استخلف الذين من قبلهم)','997','384','0');
INSERT INTO [t9065] VALUES (402,'وقوله: (وإذا بلغ الأطفال منكم الحلم فليستأذنوا كما استأذن الذين من قبلهم)','1009','384','0');
INSERT INTO [t9065] VALUES (403,'وقوله: (كما استأذن الذين من قبلهم)','1010','384','0');
INSERT INTO [t9065] VALUES (404,'وقوله: (والقواعد من النساء اللاتي لا يرجون نكاحا فليس عليهن جناح أن يضعن ثيابهن غير متبرجات بزينة وأن يستعففن خير لهن)','1010','384','0');
INSERT INTO [t9065] VALUES (405,'وقوله: (لا يحب الله الجهر بالسوء من القول إلا من ظلم وكان الله سميعا عليما (١٤٨) إن تبدوا خيرا أو تخفوه أو تعفوا عن سوء فإن الله كان عفوا قديرا (١٤٩) .','1012','384','0');
INSERT INTO [t9065] VALUES (406,'وقوله: (إنما المؤمنون الذين آمنوا بالله ورسوله وإذا كانوا معه على أمر جامع لم يذهبوا حتى يستأذنوه إن الذين يستأذنونك أولئك الذين يؤمنون بالله ورسوله)','1018','384','0');
INSERT INTO [t9065] VALUES (407,'سورة الفرقان','1019','0','0');
INSERT INTO [t9065] VALUES (408,'وقوله: (ولا يملكون لأنفسهم ضرا ولا نفعا)','1019','407','0');
INSERT INTO [t9065] VALUES (409,'وقوله: (وقال الذين لا يرجون لقاءنا لولا أنزل علينا الملائكة أو نرى ربنا لقد استكبروا في أنفسهم وعتوا عتوا كبيرا (٢١)','1020','407','0');
INSERT INTO [t9065] VALUES (410,'وقوله: (ويوم تشقق السماء بالغمام ونزل الملائكة تنزيلا (٢٥) الملك يومئذ الحق للرحمن وكان يوما على الكافرين عسيرا (٢٦)','1022','407','0');
INSERT INTO [t9065] VALUES (411,'وقوله: (وكان يوما على الكافرين عسيرا (٢٦)','1023','407','0');
INSERT INTO [t9065] VALUES (412,'وقوله: (وقال الرسول يا رب إن قومي اتخذوا هذا القرآن مهجورا (٣٠)','1024','407','0');
INSERT INTO [t9065] VALUES (413,'قوله: (وكذلك جعلنا لكل نبي عدوا من المجرمين وكفى بربك هاديا ونصيرا (٣١)','1024','407','0');
INSERT INTO [t9065] VALUES (414,'وقوله: (أم تحسب أن أكثرهم يسمعون أو يعقلون إن هم إلا كالأنعام بل هم أضل سبيلا (٤٤)','1026','407','0');
INSERT INTO [t9065] VALUES (415,'وقوله: (إن هم إلا كالأنعام بل هم أضل سبيلا (٤٤)','1027','407','0');
INSERT INTO [t9065] VALUES (416,'قوله: (وهو الذي جعل الليل والنهار خلفة لمن أراد أن يذكر أو أراد شكورا (٦٢)','1028','407','0');
INSERT INTO [t9065] VALUES (417,'قوله: (والذين يبيتون لربهم سجدا وقياما (٦٤)','1028','407','0');
INSERT INTO [t9065] VALUES (418,'وقوله: (والذين يقولون ربنا اصرف عنا عذاب جهنم إن عذابها كان غراما (٦٥)','1030','407','0');
INSERT INTO [t9065] VALUES (419,'وقوله: (والذين إذا أنفقوا لم يسرفوا ولم يقتروا)','1032','407','0');
INSERT INTO [t9065] VALUES (420,'وقوله: (وكان بين ذلك قواما (٦٧)','1033','407','0');
INSERT INTO [t9065] VALUES (421,'وقوله: (والذين يقولون ربنا هب لنا من أزواجنا وذرياتنا قرة أعين)','1033','407','0');
INSERT INTO [t9065] VALUES (422,'سورة الشعراء','1035','0','0');
INSERT INTO [t9065] VALUES (423,'قوله: (وإذ نادى ربك موسى أن ائت القوم الظالمين (١٠)','1035','422','0');
INSERT INTO [t9065] VALUES (424,'وقوله: (قال كلا فاذهبا بآياتنا إنا معكم مستمعون (١٥)','1037','422','0');
INSERT INTO [t9065] VALUES (425,'وقوله: (فقولا إنا رسول رب العالمين (١٦)','1038','422','0');
INSERT INTO [t9065] VALUES (426,'وقوله: (فأخرجناهم من جنات وعيون (٥٧) وكنوز ومقام كريم (٥٨) كذلك وأورثناها بني إسرائيل (٥٩)','1039','422','0');
INSERT INTO [t9065] VALUES (427,'وقوله: (فإنهم عدو لي إلا رب العالمين (٧٧)','1042','422','0');
INSERT INTO [t9065] VALUES (428,'قوله: (والذي أطمع أن يغفر لي خطيئتي يوم الدين (٨٢)','1044','422','0');
INSERT INTO [t9065] VALUES (429,'وقوله: (واجعل لي لسان صدق في الآخرين (٨٤)','1045','422','0');
INSERT INTO [t9065] VALUES (430,'وقوله: (إن هذا إلا خلق الأولين (١٣٧) وما نحن بمعذبين (١٣٨)','1047','422','0');
INSERT INTO [t9065] VALUES (431,'قوله: (كذبت قوم نوح المرسلين (١٠٥)','1047','422','0');
INSERT INTO [t9065] VALUES (432,'وقوله: (هل أنبئكم على من تنزل الشياطين (٢٢١) تنزل على كل أفاك أثيم (٢٢٢) يلقون السمع وأكثرهم كاذبون (٢٢٣)','1050','422','0');
INSERT INTO [t9065] VALUES (433,'سورة النمل','1054','0','0');
INSERT INTO [t9065] VALUES (434,'وقوله: (إن الذين لا يؤمنون بالآخرة زينا لهم أعمالهم فهم يعمهون (٤)','1054','433','0');
INSERT INTO [t9065] VALUES (435,'وقوله: (إذ قال موسى لأهله إني آنست نارا سآتيكم منها بخبر أو آتيكم بشهاب قبس لعلكم تصطلون (٧)','1055','433','0');
INSERT INTO [t9065] VALUES (436,'وقوله: (يا موسى إنه أنا الله العزيز الحكيم (٩)','1055','433','0');
INSERT INTO [t9065] VALUES (437,'وقوله: (فلما جاءتهم آياتنا مبصرة قالوا هذا سحر مبين (١٣)','1055','433','0');
INSERT INTO [t9065] VALUES (438,'وقوله: (وورث سليمان داوود)','1056','433','0');
INSERT INTO [t9065] VALUES (439,'وقوله: (أيكم يأتيني بعرشها قبل أن يأتوني مسلمين (٣٨)','1062','433','0');
INSERT INTO [t9065] VALUES (440,'وقوله: (فتلك بيوتهم خاوية بما ظلموا)','1064','433','0');
INSERT INTO [t9065] VALUES (441,'قوله: (من جاء بالحسنة فله خير منها)','1065','433','0');
INSERT INTO [t9065] VALUES (442,'سورة القصص','1066','0','0');
INSERT INTO [t9065] VALUES (443,'وقوله تعالى: (واضمم إليك جناحك من الرهب)','1076','442','0');
INSERT INTO [t9065] VALUES (444,'قوله: (فلما جاءهم موسى بآياتنا بينات قالوا ما هذا إلا سحر مفترى وما سمعنا بهذا في آبائنا الأولين (٣٦)','1077','442','0');
INSERT INTO [t9065] VALUES (445,'وقوله: (وجعلناهم أئمة يدعون إلى النار)','1080','442','0');
INSERT INTO [t9065] VALUES (446,'وقوله: (ومن أضل ممن اتبع هواه بغير هدى من الله إن الله لا يهدي القوم الظالمين (٥٠) .','1081','442','0');
INSERT INTO [t9065] VALUES (447,'وقوله: (إنك لا تهدي من أحببت ولكن الله يهدي من يشاء)','1082','442','0');
INSERT INTO [t9065] VALUES (448,'قوله: (وآتيناه من الكنوز ما إن مفاتحه)','1085','442','0');
INSERT INTO [t9065] VALUES (449,'سورة العنكبوت','1088','0','0');
INSERT INTO [t9065] VALUES (450,'قوله: (وقال الذين كفروا للذين آمنوا اتبعوا سبيلنا ولنحمل خطاياكم وما هم بحاملين من خطاياهم من شيء إنهم لكاذبون (١٢)','1088','449','0');
INSERT INTO [t9065] VALUES (451,'قوله: (وليحملن أثقالهم وأثقالا مع أثقالهم)','1088','449','0');
INSERT INTO [t9065] VALUES (452,'وقوله: (فما كان جواب قومه إلا أن قالوا اقتلوه أو حرقوه)','1090','449','0');
INSERT INTO [t9065] VALUES (453,'قوله: (وتلك الأمثال نضربها للناس وما يعقلها إلا العالمون (٤٣)','1095','449','0');
INSERT INTO [t9065] VALUES (454,'قوله: (وما يجحد بآياتنا إلا الكافرون (٤٧)','1101','449','0');
INSERT INTO [t9065] VALUES (455,'قوله: (وما كنت تتلو من قبله من كتاب ولا تخطه بيمينك إذا لارتاب المبطلون (٤٨)','1101','449','0');
INSERT INTO [t9065] VALUES (456,'وقوله: (أولم يكفهم أنا أنزلنا عليك الكتاب يتلى عليهم) .','1103','449','0');
INSERT INTO [t9065] VALUES (457,'وقوله: (وقالوا لولا أنزل عليه آيات من ربه)','1104','449','0');
INSERT INTO [t9065] VALUES (458,'وقوله: (ويستعجلونك بالعذاب ولولا أجل مسمى لجاءهم العذاب وليأتينهم بغتة وهم لا يشعرون (٥٣)','1105','449','0');
INSERT INTO [t9065] VALUES (459,'وقوله: (ويقول ذوقوا ما كنتم تعملون (٥٥)','1106','449','0');
INSERT INTO [t9065] VALUES (460,'وقوله: (يا عبادي الذين آمنوا إن أرضي واسعة فإياي فاعبدون (٥٦)','1106','449','0');
INSERT INTO [t9065] VALUES (461,'سورة الروم','1107','0','0');
INSERT INTO [t9065] VALUES (462,'وقوله: (الم (١) غلبت الروم (٢) في أدنى الأرض وهم من بعد غلبهم سيغلبون (٣) في بضع سنين لله الأمر من قبل ومن بعد ويومئذ يفرح المؤمنون (٤) بنصر الله ينصر من يشاء','1107','461','0');
INSERT INTO [t9065] VALUES (463,'قوله: (فسبحان الله حين تمسون وحين تصبحون (١٧) وله الحمد في السماوات والأرض وعشيا وحين تظهرون (١٨) .','1109','461','0');
INSERT INTO [t9065] VALUES (464,'وقوله: (يخرج الحي من الميت ويخرج الميت من الحي ويحيي الأرض بعد موتها وكذلك تخرجون (١٩)','1112','461','0');
INSERT INTO [t9065] VALUES (465,'قوله: (ومن آياته أن خلقكم من تراب)','1117','461','0');
INSERT INTO [t9065] VALUES (466,'وقوله: (وهو الذي يبدأ الخلق ثم يعيده وهو أهون عليه) .','1121','461','0');
INSERT INTO [t9065] VALUES (467,'وقوله: (بل اتبع الذين ظلموا أهواءهم بغير علم فمن يهدي من أضل الله)','1122','461','0');
INSERT INTO [t9065] VALUES (468,'وقوله: (فطرت الله التي فطر الناس عليها)','1123','461','0');
INSERT INTO [t9065] VALUES (469,'قوله: - (ليكفروا بما آتيناهم فتمتعوا فسوف تعلمون (٣٤)','1125','461','0');
INSERT INTO [t9065] VALUES (470,'وقوله: (وإن كانوا من قبل أن ينزل عليهم من قبله لمبلسين (٤٩)','1130','461','0');
INSERT INTO [t9065] VALUES (471,'قوله: (ولئن أرسلنا ريحا فرأوه مصفرا)','1131','461','0');
INSERT INTO [t9065] VALUES (472,'قوله: (كذلك يطبع الله على قلوب الذين لا يعلمون (٥٩)','1132','461','0');
INSERT INTO [t9065] VALUES (473,'سورة لقمان','1133','0','0');
INSERT INTO [t9065] VALUES (474,'وقوله: (ومن الناس من يشتري لهو الحديث ليضل عن سبيل الله بغير علم)','1133','473','0');
INSERT INTO [t9065] VALUES (475,'قوله: (ويتخذها هزوا)','1134','473','0');
INSERT INTO [t9065] VALUES (476,'قوله: (وإذا تتلى عليه آياتنا ولى مستكبرا كأن لم يسمعها كأن في أذنيه وقرا)','1134','473','0');
INSERT INTO [t9065] VALUES (477,'قوله: (ووصينا الإنسان بوالديه حملته أمه وهنا)','1135','473','0');
INSERT INTO [t9065] VALUES (478,'قوله: (واغضض من صوتك)','1136','473','0');
INSERT INTO [t9065] VALUES (479,'وقوله: (وما تدري نفس ماذا تكسب غدا)','1139','473','0');
INSERT INTO [t9065] VALUES (480,'سورة السجدة','1142','0','0');
INSERT INTO [t9065] VALUES (481,'قوله: (ولو شئنا لآتينا كل نفس هداها ولكن حق القول مني لأملأن جهنم من الجنة والناس أجمعين (١٣)','1143','480','0');
INSERT INTO [t9065] VALUES (482,'وقوله: (أفمن كان مؤمنا كمن كان فاسقا لا يستوون (١٨)','1147','480','0');
INSERT INTO [t9065] VALUES (483,'قوله: (وجعلنا منهم أئمة يهدون بأمرنا)','1148','480','0');
INSERT INTO [t9065] VALUES (484,'سورة الأحزاب','1150','0','0');
INSERT INTO [t9065] VALUES (485,'وقوله: (وليس عليكم جناح فيما أخطأتم به ولكن ما تعمدت قلوبكم)','1151','484','0');
INSERT INTO [t9065] VALUES (486,'وقوله: (وأولو الأرحام بعضهم أولى ببعض في كتاب الله من المؤمنين والمهاجرين)','1153','484','0');
INSERT INTO [t9065] VALUES (487,'قوله: (إلا أن تفعلوا إلى أوليائكم معروفا)','1154','484','0');
INSERT INTO [t9065] VALUES (488,'وقوله: (يا أيها الذين آمنوا اذكروا نعمة الله عليكم إذ جاءتكم جنود فأرسلنا عليهم ريحا وجنودا لم تروها)','1155','484','0');
INSERT INTO [t9065] VALUES (489,'قوله: (قد يعلم الله المعوقين منكم والقائلين لإخوانهم هلم إلينا)','1156','484','0');
INSERT INTO [t9065] VALUES (490,'قوله: ولما رأى المؤمنون الأحزاب) إلى قوله: (وما زادهم إلا إيمانا وتسليما (٢٢)','1156','484','0');
INSERT INTO [t9065] VALUES (491,'قوله: (ورد الله الذين كفروا بغيظهم لم ينالوا خيرا وكفى الله المؤمنين القتال وكان الله قويا عزيزا (٢٥) وأنزل الذين ظاهروهم من أهل الكتاب من صياصيهم وقذف في ق','1157','484','0');
INSERT INTO [t9065] VALUES (492,'قوله: (يا نساء النبي لستن كأحد من النساء إن اتقيتن)','1161','484','0');
INSERT INTO [t9065] VALUES (493,'وقوله: (فلا تخضعن بالقول فيطمع الذي في قلبه مرض)','1162','484','0');
INSERT INTO [t9065] VALUES (494,'قوله: (إنما يريد الله ليذهب عنكم الرجس أهل البيت)','1164','484','0');
INSERT INTO [t9065] VALUES (495,'وقوله تعالى: (فلما قضى زيد منها وطرا زوجناكها)','1164','484','0');
INSERT INTO [t9065] VALUES (496,'قوله: (لكي لا يكون على المؤمنين حرج في أزواج أدعيائهم إذا قضوا)','1166','484','0');
INSERT INTO [t9065] VALUES (497,'قوله: (ثم طلقتموهن من قبل أن تمسوهن فما لكم عليهن من عدة تعتدونها)','1173','484','0');
INSERT INTO [t9065] VALUES (498,'وقوله: (فمتعوهن)','1174','484','0');
INSERT INTO [t9065] VALUES (499,'وقوله: (وسرحوهن سراحا جميلا (٤٩)','1175','484','0');
INSERT INTO [t9065] VALUES (500,'وقوله: (قد علمنا ما فرضنا عليهم في أزواجهم وما ملكت أيمانهم لكيلا يكون عليك حرج)','1176','484','0');
INSERT INTO [t9065] VALUES (501,'قوله: (ترجي من تشاء منهن وتؤوي إليك من تشاء ومن ابتغيت ممن عزلت فلا جناح عليك)','1178','484','0');
INSERT INTO [t9065] VALUES (502,'وقوله: (لا يحل لك النساء من بعد ولا أن تبدل بهن من أزواج ولو أعجبك حسنهن إلا ما ملكت يمينك)','1185','484','0');
INSERT INTO [t9065] VALUES (503,'سورة سبأ','1194','0','0');
INSERT INTO [t9065] VALUES (504,'قوله: (وقال الذين كفروا هل ندلكم على رجل ينبئكم إذا مزقتم كل ممزق إنكم لفي خلق جديد (٧) أفترى على الله كذبا أم به جنة)','1194','503','0');
INSERT INTO [t9065] VALUES (505,'قوله: (ولقد آتينا داوود منا فضلا يا جبال أوبي معه والطير)','1195','503','0');
INSERT INTO [t9065] VALUES (506,'وقوله: (أن اعمل سابغات وقدر في السرد)','1196','503','0');
INSERT INTO [t9065] VALUES (507,'وقوله: (ولا تنفع الشفاعة عنده إلا لمن أذن له)','1197','503','0');
INSERT INTO [t9065] VALUES (508,'وقوله: (حتى إذا فزع عن قلوبهم قالوا ماذا قال ربكم قالوا الحق)','1198','503','0');
INSERT INTO [t9065] VALUES (509,'وقوله: (ويوم يحشرهم جميعا ثم يقول للملائكة أهؤلاء إياكم كانوا يعبدون (٤٠)','1199','503','0');
INSERT INTO [t9065] VALUES (510,'قوله: (قل جاء الحق وما يبدئ الباطل وما يعيد (٤٩)','1200','503','0');
INSERT INTO [t9065] VALUES (511,'سورة الملائكة','1202','0','0');
INSERT INTO [t9065] VALUES (512,'قوله: (إن الشيطان لكم عدو فاتخذوه عدوا)','1202','511','0');
INSERT INTO [t9065] VALUES (513,'قوله: (أفمن زين له سوء عمله فرآه حسنا فإن الله يضل من يشاء ويهدي من يشاء)','1203','511','0');
INSERT INTO [t9065] VALUES (514,'قوله: (إليه يصعد الكلم الطيب والعمل الصالح يرفعه)','1203','511','0');
INSERT INTO [t9065] VALUES (515,'وقوله: (وما تحمل من أنثى ولا تضع إلا بعلمه وما يعمر من معمر ولا ينقص من عمره إلا في كتاب إن ذلك على الله يسير (١١)','1205','511','0');
INSERT INTO [t9065] VALUES (516,'قوله: (وما يستوي الأعمى والبصير (١٩) ولا الظلمات ولا النور (٢٠) ولا الظل ولا الحرور (٢١) وما يستوي الأحياء ولا الأموات إن الله يسمع من يشاء وما أنت بمسمع من في الق','1208','511','0');
INSERT INTO [t9065] VALUES (517,'وقوله: (كذلك إنما يخشى الله من عباده العلماء)','1209','511','0');
INSERT INTO [t9065] VALUES (518,'وقوله: (فأخرجنا به ثمرات مختلفا ألوانها)','1210','511','0');
INSERT INTO [t9065] VALUES (519,'سورة يس','1226','0','0');
INSERT INTO [t9065] VALUES (520,'وقوله: (وفجرنا فيها من العيون (٣٤) ليأكلوا من ثمره وما عملته أيديهم أفلا يشكرون (٣٥)','1231','519','0');
INSERT INTO [t9065] VALUES (521,'وقوله: (أنعاما فهم لها مالكون (٧١) وذللناها لهم فمنها ركوبهم ومنها يأكلون (٧٢) ولهم فيها منافع ومشارب أفلا يشكرون (٧٣)','1232','519','0');
INSERT INTO [t9065] VALUES (522,'سورة الصافات','1233','0','0');
INSERT INTO [t9065] VALUES (523,'وقوله: (إنهم ألفوا آباءهم ضالين (٦٩) فهم على آثارهم يهرعون (٧٠)','1235','522','0');
INSERT INTO [t9065] VALUES (524,'وقوله: (قال أتعبدون ما تنحتون (٩٥) والله خلقكم وما تعملون (٩٦)','1236','522','0');
INSERT INTO [t9065] VALUES (525,'وقوله: (وقال إني ذاهب إلى ربي سيهدين (٩٩)','1238','522','0');
INSERT INTO [t9065] VALUES (526,'وقوله: (فلما بلغ معه السعي قال يا بني إني أرى في المنام أني أذبحك فانظر ماذا ترى قال يا أبت افعل ما تؤمر ستجدني إن شاء الله من الصابرين (١٠٢)','1238','522','0');
INSERT INTO [t9065] VALUES (527,'قوله: (وبشرناه بإسحاق نبيا من الصالحين (١١٢)','1242','522','0');
INSERT INTO [t9065] VALUES (528,'قوله: (فإنكم وما تعبدون (١٦١) ما أنتم عليه بفاتنين (١٦٢) إلا من هو صال الجحيم (١٦٣)','1246','522','0');
INSERT INTO [t9065] VALUES (529,'وقوله: (وإنا لنحن الصافون (١٦٥) وإنا لنحن المسبحون (١٦٦)','1247','522','0');
INSERT INTO [t9065] VALUES (530,'قوله: (وإن كانوا ليقولون (١٦٧) لو أن عندنا ذكرا من الأولين (١٦٨) لكنا عباد الله المخلصين (١٦٩)','1249','522','0');
INSERT INTO [t9065] VALUES (531,'وقوله: (ولقد سبقت كلمتنا لعبادنا المرسلين (١٧١) إنهم لهم المنصورون (١٧٢) وإن جندنا لهم الغالبون (١٧٣)','1250','522','0');
INSERT INTO [t9065] VALUES (532,'سورة ص','1251','0','0');
INSERT INTO [t9065] VALUES (533,'وقوله: (وهل أتاك نبأ الخصم إذ تسوروا المحراب (٢١) إذ دخلوا على داوود ففزع منهم)','1254','532','0');
INSERT INTO [t9065] VALUES (534,'وقوله: (وظن داوود أنما فتناه)','1260','532','0');
INSERT INTO [t9065] VALUES (535,'قوله: (يا داوود إنا جعلناك خليفة في الأرض فاحكم بين الناس بالحق ولا تتبع الهوى فيضلك عن سبيل الله)','1260','532','0');
INSERT INTO [t9065] VALUES (536,'قوله: (كتاب أنزلناه إليك مبارك ليدبروا آياته)','1261','532','0');
INSERT INTO [t9065] VALUES (537,'قوله: (وخذ بيدك ضغثا فاضرب به ولا تحنث)','1270','532','0');
INSERT INTO [t9065] VALUES (538,'وقوله: (إنا أخلصناهم بخالصة ذكرى الدار (٤٦)','1274','532','0');
INSERT INTO [t9065] VALUES (539,'سورة الزمر','1275','0','0');
INSERT INTO [t9065] VALUES (540,'قوله تعالى: (والذين اتخذوا من دونه أولياء ما نعبدهم إلا ليقربونا إلى الله زلفى)','1275','539','0');
INSERT INTO [t9065] VALUES (541,'قوله: (إن الله لا يهدي من هو كاذب كفار (٣)','1275','539','0');
INSERT INTO [t9065] VALUES (542,'قوله: (لو أراد الله أن يتخذ ولدا لاصطفى مما يخلق ما يشاء) .','1278','539','0');
INSERT INTO [t9065] VALUES (543,'قوله: (خلقكم من نفس واحدة ثم جعل منها زوجها)','1278','539','0');
INSERT INTO [t9065] VALUES (544,'قوله: (وأنزل لكم من الأنعام ثمانية أزواج)','1279','539','0');
INSERT INTO [t9065] VALUES (545,'قوله: (ولا يرضى لعباده الكفر)','1281','539','0');
INSERT INTO [t9065] VALUES (546,'قوله: (وإذا مس الإنسان ضر دعا ربه منيبا إليه ثم إذا خوله نعمة منه نسي ما كان يدعو إليه من قبل وجعل لله أندادا ليضل عن سبيله) .','1283','539','0');
INSERT INTO [t9065] VALUES (547,'وقوله: (أمن هو قانت آناء الليل ساجدا وقائما يحذر الآخرة ويرجو رحمة ربه)','1284','539','0');
INSERT INTO [t9065] VALUES (548,'وقوله: (قل إني أمرت أن أعبد الله مخلصا له الدين (١١)','1288','539','0');
INSERT INTO [t9065] VALUES (549,'وقوله: (وأمرت لأن أكون أول المسلمين (١٢)','1288','539','0');
INSERT INTO [t9065] VALUES (550,'وقوله: (فاعبدوا ما شئتم من دونه) .','1289','539','0');
INSERT INTO [t9065] VALUES (551,'وقوله: (قل إن الخاسرين الذين خسروا أنفسهم وأهليهم)','1290','539','0');
INSERT INTO [t9065] VALUES (552,'وقوله: (فبشر عباد (١٧) الذين يستمعون القول فيتبعون أحسنه)','1292','539','0');
INSERT INTO [t9065] VALUES (553,'وقوله: (أفمن حق عليه كلمة العذاب)','1294','539','0');
INSERT INTO [t9065] VALUES (554,'وقوله: (الله نزل أحسن الحديث كتابا متشابها مثاني)','1295','539','0');
INSERT INTO [t9065] VALUES (555,'وقوله: (والذي جاء بالصدق وصدق به)','1295','539','0');
INSERT INTO [t9065] VALUES (556,'وقوله: (أليس الله بكاف عبده)','1295','539','0');
INSERT INTO [t9065] VALUES (557,'وقوله: (ومن يضلل الله فما له من هاد (٣٦) ومن يهد الله فما له من مضل)','1296','539','0');
INSERT INTO [t9065] VALUES (558,'قوله: (الله يتوفى الأنفس حين موتها والتي لم تمت في منامها فيمسك التي قضى عليها الموت ويرسل الأخرى إلى أجل مسمى)','1296','539','0');
INSERT INTO [t9065] VALUES (559,'قوله: (وبدا لهم من الله ما لم يكونوا يحتسبون (٤٧)','1296','539','0');
INSERT INTO [t9065] VALUES (560,'وقوله: (قل يا عبادي الذين أسرفوا على أنفسهم لا تقنطوا من رحمة الله)','1297','539','0');
INSERT INTO [t9065] VALUES (561,'وقوله: (وأنيبوا إلى ربكم وأسلموا له من قبل أن يأتيكم العذاب ثم لا تنصرون (٥٤)','1298','539','0');
INSERT INTO [t9065] VALUES (562,'وقوله: (أو تقول لو أن الله هداني لكنت من المتقين (٥٧)','1298','539','0');
INSERT INTO [t9065] VALUES (563,'وقوله: (الله خالق كل شيء)','1299','539','0');
INSERT INTO [t9065] VALUES (564,'وقوله: (ولقد أوحي إليك وإلى الذين من قبلك لئن أشركت ليحبطن عملك ولتكونن من الخاسرين (٦٥)','1299','539','0');
INSERT INTO [t9065] VALUES (565,'وقوله: (وما قدروا الله حق قدره والأرض جميعا قبضته يوم القيامة والسماوات مطويات بيمينه) .','1307','539','0');
INSERT INTO [t9065] VALUES (566,'سورة المؤمن','1309','0','0');
INSERT INTO [t9065] VALUES (567,'قوله: (لا يخفى على الله منهم شيء)','1310','566','0');
INSERT INTO [t9065] VALUES (568,'قوله: (وأنذرهم يوم الآزفة إذ القلوب لدى الحناجر كاظمين) .','1313','566','0');
INSERT INTO [t9065] VALUES (569,'قوله: (أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين كانوا من قبلهم)','1314','566','0');
INSERT INTO [t9065] VALUES (570,'وقوله: (ومن يضلل الله فما له من هاد (٣٣) ، و (كذلك يضل الله من هو مسرف مرتاب (٣٤) ، و (كذلك يطبع الله على كل قلب متكبر جبار (٣٥)','1317','566','0');
INSERT INTO [t9065] VALUES (571,'وقوله: (وقال فرعون يا هامان ابن لي صرحا لعلي أبلغ الأسباب (٣٦) أسباب السماوات)','1318','566','0');
INSERT INTO [t9065] VALUES (572,'وقوله: (وحاق بآل فرعون سوء العذاب (٤٥) النار يعرضون عليها غدوا وعشيا)','1324','566','0');
INSERT INTO [t9065] VALUES (573,'وقوله: (هو الحي لا إله إلا هو فادعوه مخلصين له الدين) ،','1331','566','0');
INSERT INTO [t9065] VALUES (574,'قوله: (هو الذي خلقكم من تراب ثم من نطفة)','1331','566','0');
INSERT INTO [t9065] VALUES (575,'وقوله: (وما كان لرسول أن يأتي بآية إلا بإذن الله فإذا جاء أمر الله قضي بالحق)','1331','566','0');
INSERT INTO [t9065] VALUES (576,'وقوله: (أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم)','1333','566','0');
INSERT INTO [t9065] VALUES (577,'سورة فصلت','1335','0','0');
INSERT INTO [t9065] VALUES (578,'وقوله: (وويل للمشركين (٦) الذين لا يؤتون الزكاة وهم بالآخرة هم كافرون (٧)','1336','577','0');
INSERT INTO [t9065] VALUES (579,'وقوله: (فأما عاد فاستكبروا في الأرض بغير الحق وقالوا من أشد منا قوة أولم يروا أن الله الذي خلقهم هو أشد منهم قوة)','1338','577','0');
INSERT INTO [t9065] VALUES (580,'قوله: (فأرسلنا عليهم ريحا صرصرا في أيام نحسات)','1344','577','0');
INSERT INTO [t9065] VALUES (581,'قوله: (وأما ثمود فهديناهم)','1345','577','0');
INSERT INTO [t9065] VALUES (582,'قوله: (وقيضنا لهم قرناء فزينوا لهم ما بين أيديهم وما خلفهم وحق عليهم القول)','1345','577','0');
INSERT INTO [t9065] VALUES (583,'وقوله: (فإن استكبروا فالذين عند ربك يسبحون له بالليل والنهار وهم لا يسأمون (٣٨)','1349','577','0');
INSERT INTO [t9065] VALUES (584,'قوله: (أفمن يلقى في النار خير أم من يأتي آمنا يوم القيامة اعملوا ما شئتم)','1351','577','0');
INSERT INTO [t9065] VALUES (585,'قوله: (قل هو للذين آمنوا هدى وشفاء والذين لا يؤمنون في آذانهم وقر وهو عليهم عمى)','1352','577','0');
