INSERT INTO [t9065] VALUES (634,'قوله: (يا أيها الذين آمنوا إن جاءكم فاسق بنبإ فتبينوا أن تصيبوا قوما بجهالة فتصبحوا على ما فعلتم نادمين (٦)','1443','631','0');
