INSERT INTO [t9182] VALUES (1,'سورة الفاتحة','3','0','0');
INSERT INTO [t9182] VALUES (2,'قول: (أعوذ بالله من الشيطان الرجيم)','3','1','0');
INSERT INTO [t9182] VALUES (3,'قول: (بسم الله الرحمن الرحيم)','6','1','0');
INSERT INTO [t9182] VALUES (4,'سورة البقرة','17','0','0');
INSERT INTO [t9182] VALUES (5,'قال الله عز وجل: (يكاد البرق يخطف أبصارهم كلما أضاء لهم مشوا فيه وإذا أظلم عليهم قاموا)','17','4','0');
INSERT INTO [t9182] VALUES (6,'قال الله تعالى (. . . وقودها الناس والحجارة. . .)','18','4','0');
INSERT INTO [t9182] VALUES (7,'قال الله عز وجل: (وأقيموا الصلاة وآتوا الزكاة)','19','4','0');
INSERT INTO [t9182] VALUES (8,'قال الله عز وجل: (إن الله يأمركم أن تذبحوا بقرة) .','26','4','0');
INSERT INTO [t9182] VALUES (9,'قال الله عز وجل: (فويل للذين يكتبون الكتاب بأيديهم ثم يقولون هذا من عند الله ليشتروا به ثمنا قليلا)','28','4','0');
INSERT INTO [t9182] VALUES (10,'قال الله عز وجل: (ما ننسخ من آية أو ننسها نأت بخير منها أو مثلها ألم تعلم أن الله على كل شيء قدير (١٠٦) .','33','4','0');
INSERT INTO [t9182] VALUES (11,'قال الله عز وجل: (ولله المشرق والمغرب فأينما تولوا فثم وجه الله)','38','4','0');
INSERT INTO [t9182] VALUES (12,'قال الله عز وجل: (وإذ جعلنا البيت مثابة للناس وأمنا)','39','4','0');
INSERT INTO [t9182] VALUES (13,'قال الله عز وجل: (ربنا وابعث فيهم رسولا منهم يتلو عليهم آياتك ويعلمهم الكتاب والحكمة ويزكيهم إنك أنت العزيز الحكيم (١٢٩)','40','4','0');
INSERT INTO [t9182] VALUES (14,'قال الله عز وجل: (ووصى بها إبراهيم بنيه ويعقوب يا بني إن الله اصطفى لكم الدين فلا تموتن إلا وأنتم مسلمون (١٣٢)','42','4','0');
INSERT INTO [t9182] VALUES (15,'قال الله عز وجل: (قولوا آمنا بالله وما أنزل إلينا وما أنزل إلى إبراهيم وإسماعيل وإسحاق)','43','4','0');
INSERT INTO [t9182] VALUES (16,'قال الله عز وجل: (سيقول السفهاء من الناس ما ولاهم عن قبلتهم التي كانوا عليها قل لله المشرق والمغرب يهدي من يشاء إلى صراط مستقيم (١٤٢)','44','4','0');
INSERT INTO [t9182] VALUES (17,'قال الله عز وجل: (وما جعلنا القبلة التي كنت عليها إلا لنعلم من يتبع الرسول ممن ينقلب على عقبيه)','47','4','0');
INSERT INTO [t9182] VALUES (18,'قال الله عز وجل: (ومن حيث خرجت فول وجهك شطر المسجد الحرام وإنه للحق من ربك وما الله بغافل عما تعملون (١٤٩)','52','4','0');
INSERT INTO [t9182] VALUES (19,'قال الله عز وجل: (كما أرسلنا فيكم رسولا منكم يتلو عليكم آياتنا ويزكيكم ويعلمكم الكتاب والحكمة ويعلمكم ما لم تكونوا تعلمون (١٥١)','59','4','0');
INSERT INTO [t9182] VALUES (20,'قال الله عز وجل: (ولنبلونكم بشيء من الخوف والجوع ونقص من الأموال والأنفس والثمرات وبشر الصابرين (١٥٥)','60','4','0');
INSERT INTO [t9182] VALUES (21,'قال الله عز وجل: (إن الصفا والمروة من شعائر الله)','60','4','0');
INSERT INTO [t9182] VALUES (22,'قال الله عز وجل: (إن في خلق السماوات والأرض واختلاف الليل والنهار والفلك التي تجري في البحر بما ينفع الناس وما أنزل الله من السماء من ماء فأحيا به الأرض بعد','63','4','0');
INSERT INTO [t9182] VALUES (23,'قال الله - عز وجل -: (إنما حرم عليكم الميتة والدم ولحم الخنزير وما أهل به لغير الله فمن اضطر غير باغ ولا عاد فلا إثم عليه إن الله غفور رحيم (١٧٣)','65','4','0');
INSERT INTO [t9182] VALUES (24,'قال الله - عز وجل -: (ليس البر أن تولوا وجوهكم قبل المشرق والمغرب ولكن البر من آمن بالله واليوم الآخر والملائكة والكتاب والنبيين وآتى المال على حبه','68','4','0');
INSERT INTO [t9182] VALUES (25,'قال الله عز وجل: (كتب عليكم القصاص في القتلى)','70','4','0');
INSERT INTO [t9182] VALUES (26,'قال الله عز وجل: (ولكم في القصاص حياة يا أولي الألباب لعلكم تتقون (١٧٩)','83','4','0');
INSERT INTO [t9182] VALUES (27,'قال الله عز وجل: (كتب عليكم إذا حضر أحدكم الموت إن ترك خيرا الوصية للوالدين والأقربين بالمعروف حقا على المتقين (١٨٠)','85','4','0');
INSERT INTO [t9182] VALUES (28,'قال الله عز وجل: (يا أيها الذين آمنوا كتب عليكم الصيام كما كتب على الذين من قبلكم لعلكم تتقون (١٨٣)','96','4','0');
INSERT INTO [t9182] VALUES (29,'قال الله عز وجل: (شهر رمضان الذي أنزل فيه القرآن هدى للناس وبينات من الهدى والفرقان فمن شهد منكم الشهر فليصمه ومن كان مريضا أو على سفر فعدة من أيام أخر','103','4','0');
INSERT INTO [t9182] VALUES (30,'قال الله عز وجل: (أحل لكم ليلة الصيام الرفث إلى نسائكم هن لباس لكم وأنتم لباس لهن علم الله أنكم كنتم تختانون أنفسكم فتاب عليكم وعفا عنكم فالآن باشروهن','112','4','0');
INSERT INTO [t9182] VALUES (31,'قال الله عز وجل: (ولا تأكلوا أموالكم بينكم بالباطل وتدلوا بها إلى الحكام لتأكلوا فريقا من أموال الناس بالإثم وأنتم تعلمون (١٨٨)','114','4','0');
INSERT INTO [t9182] VALUES (32,'قال الله عز وجل: (يسألونك عن الأهلة قل هي مواقيت للناس والحج وليس البر بأن تأتوا البيوت من ظهورها ولكن البر من اتقى وأتوا البيوت من أبوابها واتقوا الله لع','115','4','0');
INSERT INTO [t9182] VALUES (33,'قال الله - عز وجل -: (وقاتلوا في سبيل الله الذين يقاتلونكم ولا تعتدوا إن الله لا يحب المعتدين (١٩٠)','117','4','0');
INSERT INTO [t9182] VALUES (34,'قال الله - عز وجل -: (الشهر الحرام بالشهر الحرام والحرمات قصاص فمن اعتدى عليكم فاعتدوا عليه بمثل ما اعتدى عليكم واتقوا الله واعلموا أن الله مع المتقين (١٩٤)','120','4','0');
INSERT INTO [t9182] VALUES (35,'قال الله عز وجل: (وأتموا الحج والعمرة لله)','121','4','0');
INSERT INTO [t9182] VALUES (36,'قال الله عز وجل: (الحج أشهر معلومات فمن فرض فيهن الحج فلا رفث ولا فسوق ولا جدال في الحج وما تفعلوا من خير يعلمه الله وتزودوا فإن خير الزاد التقوى واتقون ي','134','4','0');
INSERT INTO [t9182] VALUES (37,'قال الله عز وجل: (ليس عليكم جناح أن تبتغوا فضلا من ربكم فإذا أفضتم من عرفات فاذكروا الله عند المشعر الحرام واذكروه كما هداكم وإن كنتم من قبله لمن الضالين','137','4','0');
INSERT INTO [t9182] VALUES (38,'قال الله عز وجل: (ثم أفيضوا من حيث أفاض الناس واستغفروا الله إن الله غفور رحيم (١٩٩)','138','4','0');
INSERT INTO [t9182] VALUES (39,'قال الله عز وجل: (ومنهم من يقول ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة وقنا عذاب النار (٢٠١)','139','4','0');
INSERT INTO [t9182] VALUES (40,'قال الله عز وجل: (أولئك لهم نصيب مما كسبوا والله سريع الحساب (٢٠٢)','140','4','0');
INSERT INTO [t9182] VALUES (41,'قال الله عز وجل: (واذكروا الله في أيام معدودات فمن تعجل في يومين فلا إثم عليه ومن تأخر فلا إثم عليه لمن اتقى واتقوا الله واعلموا أنكم إليه تحشرون (٢٠٣)','140','4','0');
INSERT INTO [t9182] VALUES (42,'قال الله عز وجل: (واذكروا الله في أيام معدودات فمن تعجل في يومين فلا إثم عليه ومن تأخر فلا إثم عليه لمن اتقى واتقوا الله واعلموا أنكم إليه تحشرون (٢٠٣)','140','4','0');
INSERT INTO [t9182] VALUES (43,'قال الله عز وجل: (وإذا تولى سعى في الأرض ليفسد فيها ويهلك الحرث والنسل والله لا يحب الفساد (٢٠٥)','141','4','0');
INSERT INTO [t9182] VALUES (44,'قال الله عز وجل: (كان الناس أمة واحدة فبعث الله النبيين مبشرين ومنذرين)','142','4','0');
INSERT INTO [t9182] VALUES (45,'قال الله عز وجل: (كتب عليكم القتال وهو كره لكم وعسى أن تكرهوا شيئا وهو خير لكم وعسى أن تحبوا شيئا وهو شر لكم والله يعلم وأنتم لا تعلمون (٢١٦)','143','4','0');
INSERT INTO [t9182] VALUES (46,'قال الله عز وجل: (يسألونك عن الشهر الحرام قتال فيه قل قتال فيه كبير)','145','4','0');
INSERT INTO [t9182] VALUES (47,'قال الله عز وجل: (ولا تنكحوا المشركات حتى يؤمن ولأمة مؤمنة خير من مشركة ولو أعجبتكم ولا تنكحوا المشركين حتى يؤمنوا ولعبد مؤمن خير من مشرك ولو أعجبكم)','147','4','0');
INSERT INTO [t9182] VALUES (48,'قال الله عز وجل: (ويسألونك عن المحيض قل هو أذى فاعتزلوا النساء في المحيض ولا تقربوهن حتى يطهرن فإذا تطهرن فأتوهن من حيث أمركم الله إن الله يحب التوابين','150','4','0');
INSERT INTO [t9182] VALUES (49,'قال الله - عز وجل -: (نساؤكم حرث لكم فأتوا حرثكم أنى شئتم وقدموا لأنفسكم واتقوا الله واعلموا أنكم ملاقوه وبشر المؤمنين (٢٢٣)','158','4','0');
INSERT INTO [t9182] VALUES (50,'قال الله عز وجل: (لا يؤاخذكم الله باللغو في أيمانكم ولكن يؤاخذكم بما كسبت قلوبكم والله غفور حليم (٢٢٥)','159','4','0');
INSERT INTO [t9182] VALUES (51,'قال الله عز وجل: (للذين يؤلون من نسائهم تربص أربعة أشهر فإن فاءوا فإن الله غفور رحيم (٢٢٦) وإن عزموا الطلاق فإن الله سميع عليم (٢٢٧)','160','4','0');
INSERT INTO [t9182] VALUES (52,'قال الله عز وجل: (والمطلقات يتربصن بأنفسهن ثلاثة قروء ولا يحل لهن أن يكتمن ما خلق الله في أرحامهن إن كن يؤمن بالله واليوم الآخر وبعولتهن أحق برده','166','4','0');
INSERT INTO [t9182] VALUES (53,'قال الله عز وجل: (الطلاق مرتان فإمساك بمعروف أو تسريح بإحسان ولا يحل لكم أن تأخذوا مما آتيتموهن شيئا إلا أن يخافا ألا يقيما حدود الله فإن خفتم ألا يقيما ح','177','4','0');
INSERT INTO [t9182] VALUES (54,'قال الله عز وجل: (فإن طلقها فلا تحل له من بعد حتى تنكح زوجا غيره فإن طلقها فلا جناح عليهما أن يتراجعا إن ظنا أن يقيما حدود الله وتلك حدود الله يبينها لق','186','4','0');
INSERT INTO [t9182] VALUES (55,'قال الله عز وجل: (وإذا طلقتم النساء فبلغن أجلهن فأمسكوهن بمعروف أو سرحوهن بمعروف ولا تمسكوهن ضرارا لتعتدوا ومن يفعل ذلك فقد ظلم نفسه','193','4','0');
INSERT INTO [t9182] VALUES (56,'قال الله عز وجل: (والوالدات يرضعن أولادهن حولين كاملين لمن أراد أن يتم الرضاعة)','198','4','0');
INSERT INTO [t9182] VALUES (57,'قال الله عز وجل: (والذين يتوفون منكم ويذرون أزواجا يتربصن بأنفسهن أربعة أشهر وعشرا فإذا بلغن أجلهن فلا جناح عليكم فيما فعلن في أنفسهن بالمعروف والل','204','4','0');
INSERT INTO [t9182] VALUES (58,'قال الله عز وجل: (ولا جناح عليكم فيما عرضتم به من خطبة النساء أو أكننتم في أنفسكم علم الله أنكم ستذكرونهن ولكن لا تواعدوهن سرا إلا أن تقولوا قولا معروف','208','4','0');
INSERT INTO [t9182] VALUES (59,'قال الله عز وجل: (لا جناح عليكم إن طلقتم النساء ما لم تمسوهن أو تفرضوا لهن فريضة ومتعوهن على الموسع قدره وعلى المقتر قدره متاعا بالمعروف حقا على المحسن','213','4','0');
INSERT INTO [t9182] VALUES (60,'قال الله عز وجل: (وإن طلقتموهن من قبل أن تمسوهن وقد فرضتم لهن فريضة فنصف ما فرضتم إلا أن يعفون أو يعفو الذي بيده عقدة النكاح','217','4','0');
INSERT INTO [t9182] VALUES (61,'قال الله عز وجل: (حافظوا على الصلوات والصلاة الوسطى وقوموا لله قانتين (٢٣٨)','225','4','0');
INSERT INTO [t9182] VALUES (62,'قال الله عز وجل: (فإن خفتم فرجالا أو ركبانا فإذا أمنتم فاذكروا الله كما علمكم ما لم تكونوا تعلمون (٢٣٩)','233','4','0');
INSERT INTO [t9182] VALUES (63,'قال الله عز وجل: (والذين يتوفون منكم ويذرون أزواجا وصية لأزواجهم متاعا إلى الحول غير إخراج','237','4','0');
INSERT INTO [t9182] VALUES (64,'قال الله عز وجل: (وللمطلقات متاع بالمعروف حقا على المتقين (٢٤١)','240','4','0');
INSERT INTO [t9182] VALUES (65,'قال الله عز وجل: (وقاتلوا في سبيل الله واعلموا أن الله سميع عليم (٢٤٤)','241','4','0');
INSERT INTO [t9182] VALUES (66,'قال الله عز وجل: (ولا يحيطون بشيء من علمه إلا بما شاء وسع كرسيه السماوات والأرض ولا يئوده حفظهما وهو العلي العظيم (٢٥٥)','241','4','0');
INSERT INTO [t9182] VALUES (67,'قال الله عز وجل: (فبهت الذي كفر)','242','4','0');
INSERT INTO [t9182] VALUES (68,'قال الله عز وجل: (ولا تيمموا الخبيث منه تنفقون ولستم بآخذيه إلا أن تغمضوا فيه واعلموا أن الله غني حميد (٢٦٧)','244','4','0');
INSERT INTO [t9182] VALUES (69,'قال الله عز وجل: (ليس عليك هداهم ولكن الله يهدي من يشاء)','246','4','0');
INSERT INTO [t9182] VALUES (70,'قال الله عز وجل: (ذلك بأنهم قالوا إنما البيع مثل الربا وأحل الله البيع وحرم الربا)','246','4','0');
INSERT INTO [t9182] VALUES (71,'قال الله - عز وجل -: (يا أيها الذين آمنوا اتقوا الله وذروا ما بقي من الربا إن كنتم مؤمنين (٢٧٨)','253','4','0');
INSERT INTO [t9182] VALUES (72,'قال الله عز وجل: (وإن كان ذو عسرة فنظرة إلى ميسرة وأن تصدقوا خير لكم إن كنتم تعلمون (٢٨٠)','256','4','0');
INSERT INTO [t9182] VALUES (73,'قال الله - عز وجل -: (يا أيها الذين آمنوا إذا تداينتم بدين إلى أجل مسمى فاكتبوه)','257','4','0');
INSERT INTO [t9182] VALUES (74,'قال الله عز وجل: (وإن كنتم على سفر ولم تجدوا كاتبا فرهان مقبوضة فإن أمن بعضكم بعضا فليؤد الذي اؤتمن أمانته وليتق الله ربه ولا تكتموا الشهادة','271','4','0');
INSERT INTO [t9182] VALUES (75,'قال الله عز وجل: (لله ما في السماوات وما في الأرض وإن تبدوا ما في أنفسكم أو تخفوه يحاسبكم به الله فيغفر لمن يشاء ويعذب من يشاء','276','4','0');
INSERT INTO [t9182] VALUES (76,'سورة آل عمران','279','0','0');
INSERT INTO [t9182] VALUES (77,'قال الله - عز وجل -: (ربنا لا تزغ قلوبنا بعد إذ هديتنا وهب لنا من لدنك رحمة إنك أنت الوهاب (٨)','279','76','0');
INSERT INTO [t9182] VALUES (78,'قال الله عز وجل: (كدأب آل فرعون والذين من قبلهم كذبوا بآياتنا فأخذهم الله بذنوبهم والله شديد العقاب (١١)','280','76','0');
INSERT INTO [t9182] VALUES (79,'قال الله عز وجل: (زين للناس حب الشهوات من النساء والبنين والقناطير المقنطرة من الذهب والفضة والخيل المسومة والأنعام والحرث ذلك متاع الحياة الدنيا','281','76','0');
INSERT INTO [t9182] VALUES (80,'قال الله - عز وجل - (شهد الله أنه لا إله إلا هو والملائكة وأولو العلم قائما بالقسط لا إله إلا هو العزيز الحكيم (١٨)','282','76','0');
INSERT INTO [t9182] VALUES (81,'قال الله عز وجل: (إن الدين عند الله الإسلام)','283','76','0');
INSERT INTO [t9182] VALUES (82,'قال الله - عز وجل -: (يوم تجد كل نفس ما عملت من خير محضرا وما عملت من سوء تود لو أن بينها وبينه أمدا بعيدا ويحذركم الله نفسه والله رءوف بالعباد (٣٠)','284','76','0');
INSERT INTO [t9182] VALUES (83,'قال الله عز وجل: (إن الله اصطفى آدم ونوحا وآل إبراهيم وآل عمران على العالمين (٣٣)','286','76','0');
INSERT INTO [t9182] VALUES (84,'قال الله عز وجل: (وسيدا وحصورا ونبيا من الصالحين (٣٩)','287','76','0');
INSERT INTO [t9182] VALUES (85,'قال الله عز وجل: (وما كنت لديهم إذ يلقون أقلامهم أيهم يكفل مريم وما كنت لديهم إذ يختصمون (٤٤)','288','76','0');
INSERT INTO [t9182] VALUES (86,'قال الله عز وجل: (قل يا أهل الكتاب تعالوا إلى كلمة سواء بيننا وبينكم ألا نعبد إلا الله ولا نشرك به شيئا ولا يتخذ بعضنا بعضا أربابا من دون الله','292','76','0');
INSERT INTO [t9182] VALUES (87,'قال الله عز وجل: (إن الذين يشترون بعهد الله وأيمانهم ثمنا قليلا أولئك لا خلاق لهم في الآخرة ولا يكلمهم الله ولا ينظر إليهم يوم القيامة ولا يزكيهم ولهم عذ','293','76','0');
INSERT INTO [t9182] VALUES (88,'قال الله عز وجل: (وإن منهم لفريقا يلوون ألسنتهم بالكتاب لتحسبوه من الكتاب وما هو من الكتاب ويقولون هو من عند الله وما هو من عند الله','295','76','0');
INSERT INTO [t9182] VALUES (89,'قال الله عز وجل: (ومن يبتغ غير الإسلام دينا فلن يقبل منه وهو في الآخرة من الخاسرين (٨٥)','296','76','0');
INSERT INTO [t9182] VALUES (90,'قال الله عز وجل: (لن تنالوا البر حتى تنفقوا مما تحبون وما تنفقوا من شيء فإن الله به عليم (٩٢)','297','76','0');
INSERT INTO [t9182] VALUES (91,'قال الله عز وجل: (كل الطعام كان حلا لبني إسرائيل إلا ما حرم إسرائيل على نفسه من قبل أن تنزل التوراة قل فأتوا بالتوراة فاتلوها إن كنتم صادقين (٩٣)','297','76','0');
INSERT INTO [t9182] VALUES (92,'قال الله عز وجل: (ولله على الناس حج البيت من استطاع إليه سبيلا ومن كفر فإن الله غني عن العالمين (٩٧)','298','76','0');
INSERT INTO [t9182] VALUES (93,'قال الله عز وجل: (واذكروا نعمت الله عليكم إذ كنتم أعداء فألف بين قلوبكم فأصبحتم بنعمته إخوانا وكنتم على شفا حفرة من النار فأنقذكم منها','305','76','0');
INSERT INTO [t9182] VALUES (94,'قال الله عز وجل: (ولا تكونوا كالذين تفرقوا واختلفوا من بعد ما جاءهم البينات وأولئك لهم عذاب عظيم (١٠٥)','306','76','0');
INSERT INTO [t9182] VALUES (95,'قال الله - عز وجل -: (كنتم خير أمة أخرجت للناس)','309','76','0');
INSERT INTO [t9182] VALUES (96,'قال الله عز وجل: (وما محمد إلا رسول قد خلت من قبله الرسل أفإن مات أو قتل انقلبتم على أعقابكم ومن ينقلب على عقبيه فلن يضر الله شيئا','309','76','0');
INSERT INTO [t9182] VALUES (97,'قال الله عز وجل: (وليبتلي الله ما في صدوركم وليمحص ما في قلوبكم والله عليم بذات الصدور (١٥٤)','310','76','0');
INSERT INTO [t9182] VALUES (98,'قال الله عز وجل: (فاعف عنهم واستغفر لهم وشاورهم في الأمر فإذا عزمت فتوكل على الله إن الله يحب المتوكلين (١٥٩)','311','76','0');
INSERT INTO [t9182] VALUES (99,'قال الله عز وجل: (لقد من الله على المؤمنين إذ بعث فيهم رسولا من أنفسهم يتلو عليهم آياته ويزكيهم ويعلمهم الكتاب والحكمة','314','76','0');
INSERT INTO [t9182] VALUES (100,'قال الله عز وجل: (الذين قال لهم الناس إن الناس قد جمعوا لكم فاخشوهم فزادهم إيمانا وقالوا حسبنا الله ونعم الوكيل (١٧٣)','315','76','0');
INSERT INTO [t9182] VALUES (101,'قال الله عز وجل: (ولا يحسبن الذين يبخلون بما آتاهم الله من فضله هو خيرا لهم بل هو شر لهم سيطوقون ما بخلوا به يوم القيامة ولله ميراث السماوات والأرض','317','76','0');
INSERT INTO [t9182] VALUES (102,'سورة النساء','321','0','0');
INSERT INTO [t9182] VALUES (103,'قال الله: (وخلق منها زوجها)','321','102','0');
INSERT INTO [t9182] VALUES (104,'قال الله عز وجل: (وآتوا اليتامى أموالهم ولا تتبدلوا الخبيث بالطيب ولا تأكلوا أموالهم إلى أموالكم إنه كان حوبا كبيرا (٢)','321','102','0');
INSERT INTO [t9182] VALUES (105,'قال الله عز وجل: (وإن خفتم ألا تقسطوا في اليتامى فانكحوا ما طاب لكم من النساء مثنى وثلاث ورباع فإن خفتم ألا تعدلوا فواحدة أو ما ملكت أيمانكم ذلك أدنى ألا تع','322','102','0');
INSERT INTO [t9182] VALUES (106,'قال الله عز وجل - (وآتوا النساء صدقاتهن نحلة فإن طبن لكم عن شيء منه نفسا فكلوه هنيئا مريئا (٤)','326','102','0');
INSERT INTO [t9182] VALUES (107,'قال الله عز وجل: (ولا تؤتوا السفهاء أموالكم التي جعل الله لكم قياما وارزقوهم فيها واكسوهم وقولوا لهم قولا معروفا (٥)','331','102','0');
INSERT INTO [t9182] VALUES (108,'قال الله عز وجل: (وابتلوا اليتامى حتى إذا بلغوا النكاح فإن آنستم منهم رشدا فادفعوا إليهم أموالهم)','331','102','0');
INSERT INTO [t9182] VALUES (109,'قال الله عز وجل: (للرجال نصيب مما ترك الوالدان والأقربون وللنساء نصيب مما ترك الوالدان والأقربون مما قل منه أو كثر نصيبا مفروضا (٧)','339','102','0');
INSERT INTO [t9182] VALUES (110,'قال الله عز وجل: (وإذا حضر القسمة أولو القربى واليتامى والمساكين فارزقوهم منه وقولوا لهم قولا معروفا (٨)','340','102','0');
INSERT INTO [t9182] VALUES (111,'قال الله عز وجل: (إن الذين يأكلون أموال اليتامى ظلما إنما يأكلون في بطونهم نارا وسيصلون سعيرا (١٠)','341','102','0');
INSERT INTO [t9182] VALUES (112,'قال الله عز وجل: (يوصيكم الله في أولادكم للذكر مثل حظ الأنثيين)','342','102','0');
INSERT INTO [t9182] VALUES (113,'قال الله عز وجل: (ولكم نصف ما ترك أزواجكم إن لم يكن لهن ولد فإن كان لهن ولد فلكم الربع مما تركن من بعد وصية يوصين بها أو دين','347','102','0');
INSERT INTO [t9182] VALUES (114,'قال الله عز وجل: (واللاتي يأتين الفاحشة من نسائكم فاستشهدوا عليهن أربعة منكم فإن شهدوا فأمسكوهن في البيوت حتى يتوفاهن الموت أو يجعل الله لهن سبيلا (١٥)','354','102','0');
INSERT INTO [t9182] VALUES (115,'قال الله عز وجل: (واللذان يأتيانها منكم فآذوهما فإن تابا وأصلحا فأعرضوا عنهما إن الله كان توابا رحيما (١٦)','364','102','0');
INSERT INTO [t9182] VALUES (116,'قال الله عز وجل: (إنما التوبة على الله للذين يعملون السوء بجهالة ثم يتوبون من قريب فأولئك يتوب الله عليهم وكان الله عليما حكيما (١٧)','364','102','0');
INSERT INTO [t9182] VALUES (117,'قال الله عز وجل: (يا أيها الذين آمنوا لا يحل لكم أن ترثوا النساء كرها ولا تعضلوهن لتذهبوا ببعض ما آتيتموهن إلا أن يأتين بفاحشة مبينة وعاشروهن بالمعروف','365','102','0');
INSERT INTO [t9182] VALUES (118,'قال الله عز وجل: (وإن أردتم استبدال زوج مكان زوج وآتيتم إحداهن قنطارا فلا تأخذوا منه شيئا أتأخذونه بهتانا وإثما مبينا (٢٠)','369','102','0');
INSERT INTO [t9182] VALUES (119,'قال الله عز وجل: (وكيف تأخذونه وقد أفضى بعضكم إلى بعض وأخذن منكم ميثاقا غليظا (٢١)','372','102','0');
INSERT INTO [t9182] VALUES (120,'قال الله تعالى: (ولا تنكحوا ما نكح آباؤكم من النساء إلا ما قد سلف إنه كان فاحشة ومقتا وساء سبيلا (٢٢)','373','102','0');
INSERT INTO [t9182] VALUES (121,'قال الله عز وجل: (حرمت عليكم أمهاتكم وبناتكم وأخواتكم وعماتكم وخالاتكم وبنات الأخ وبنات الأخت وأمهاتكم اللاتي أرضعنكم وأخواتكم من الرضاعة','375','102','0');
INSERT INTO [t9182] VALUES (122,'قال الله عز وجل: (والمحصنات من النساء إلا ما ملكت أيمانكم كتاب الله عليكم وأحل لكم ما وراء ذلكم أن تبتغوا بأموالكم محصنين غير مسافحين','382','102','0');
INSERT INTO [t9182] VALUES (123,'قال الله - عز وجل -: (ومن لم يستطع منكم طولا أن ينكح المحصنات المؤمنات فمن ما ملكت أيمانكم من فتياتكم المؤمنات والله أعلم بإيمانكم بعضكم من بعض','390','102','0');
INSERT INTO [t9182] VALUES (124,'قال الله عز وجل: (يا أيها الذين آمنوا لا تأكلوا أموالكم بينكم بالباطل إلا أن تكون تجارة عن تراض منكم ولا تقتلوا أنفسكم إن الله كان بكم رحيما (٢٩)','397','102','0');
INSERT INTO [t9182] VALUES (125,'قال الله عز وجل: (الرجال قوامون على النساء بما فضل الله بعضهم على بعض وبما أنفقوا من أموالهم فالصالحات قانتات حافظات للغيب بما حفظ الله','404','102','0');
INSERT INTO [t9182] VALUES (126,'قال الله عز وجل: (وإن خفتم شقاق بينهما فابعثوا حكما من أهله وحكما من أهلها إن يريدا إصلاحا يوفق الله بينهما إن الله كان عليما خبيرا (٣٥)','411','102','0');
INSERT INTO [t9182] VALUES (127,'قال الله عز وجل: (يا أيها الذين آمنوا لا تقربوا الصلاة وأنتم سكارى حتى تعلموا ما تقولون ولا جنبا إلا عابري سبيل حتى تغتسلوا)','414','102','0');
INSERT INTO [t9182] VALUES (128,'قال الله عز وجل: (ألم تر إلى الذين أوتوا نصيبا من الكتاب يؤمنون بالجبت والطاغوت ويقولون للذين كفروا هؤلاء أهدى من الذين آمنوا سبيلا (٥١)','423','102','0');
INSERT INTO [t9182] VALUES (129,'قال الله عز وجل: (إن الله يأمركم أن تؤدوا الأمانات إلى أهلها وإذا حكمتم بين الناس أن تحكموا بالعدل إن الله نعما يعظكم به إن الله كان سميعا بصيرا (٥٨)','424','102','0');
INSERT INTO [t9182] VALUES (130,'قال الله عز وجل: (يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول وأولي الأمر منكم فإن تنازعتم في شيء فردوه إلى الله والرسول إن كنتم تؤمنون بالله واليوم الآخر','425','102','0');
INSERT INTO [t9182] VALUES (131,'قال الله عز وجل: (فلا وربك لا يؤمنون حتى يحكموك فيما شجر بينهم ثم لا يجدوا في أنفسهم حرجا مما قضيت ويسلموا تسليما (٦٥)','427','102','0');
INSERT INTO [t9182] VALUES (132,'قال الله عز وجل: (ولو أنا كتبنا عليهم أن اقتلوا أنفسكم أو اخرجوا من دياركم ما فعلوه إلا قليل منهم ولو أنهم فعلوا ما يوعظون به لكان خيرا لهم وأشد تثبيتا (٦٦)','431','102','0');
INSERT INTO [t9182] VALUES (133,'قال الله عز وجل: (ومن يطع الله والرسول فأولئك مع الذين أنعم الله عليهم من النبيين والصديقين والشهداء والصالحين وحسن أولئك رفيقا (٦٩)','432','102','0');
INSERT INTO [t9182] VALUES (134,'قال الله عز وجل: (وما لكم لا تقاتلون في سبيل الله والمستضعفين من الرجال والنساء والولدان الذين يقولون ربنا أخرجنا من هذه القرية الظالم أهلها','433','102','0');
INSERT INTO [t9182] VALUES (135,'قال الله عز وجل: (من يطع الرسول فقد أطاع الله ومن تولى فما أرسلناك عليهم حفيظا (٨٠)','434','102','0');
INSERT INTO [t9182] VALUES (136,'قال الله عز وجل: (أفلا يتدبرون القرآن ولو كان من عند غير الله لوجدوا فيه اختلافا كثيرا (٨٢)','438','102','0');
INSERT INTO [t9182] VALUES (137,'قال الله عز وجل: (وإذا حييتم بتحية فحيوا بأحسن منها أو ردوها إن الله كان على كل شيء حسيبا (٨٦)','439','102','0');
INSERT INTO [t9182] VALUES (138,'قال الله عز وجل: (الله لا إله إلا هو ليجمعنكم إلى يوم القيامة لا ريب فيه ومن أصدق من الله حديثا (٨٧)','440','102','0');
INSERT INTO [t9182] VALUES (139,'قال الله عز وجل: (وما كان لمؤمن أن يقتل مؤمنا إلا خطأ ومن قتل مؤمنا خطأ فتحرير رقبة مؤمنة ودية مسلمة إلى أهله إلا أن يصدقوا','441','102','0');
INSERT INTO [t9182] VALUES (140,'قال الله عز وجل: (ومن يقتل مؤمنا متعمدا فجزاؤه جهنم خالدا فيها وغضب الله عليه ولعنه وأعد له عذابا عظيما (٩٣)','452','102','0');
INSERT INTO [t9182] VALUES (141,'قال الله عز وجل: (إذا ضربتم في سبيل الله فتبينوا)','453','102','0');
INSERT INTO [t9182] VALUES (142,'قال الله عز وجل: (لا يستوي القاعدون من المؤمنين غير أولي الضرر والمجاهدون في سبيل الله بأموالهم وأنفسهم فضل الله المجاهدين بأموالهم وأنفسهم على القاعدين در','453','102','0');
INSERT INTO [t9182] VALUES (143,'قال الله - عز وجل -: (إن الذين توفاهم الملائكة ظالمي أنفسهم قالوا فيم كنتم قالوا كنا مستضعفين في الأرض قالوا ألم تكن أرض الله واسعة فتهاجروا فيها','455','102','0');
INSERT INTO [t9182] VALUES (144,'قال الله عز وجل: (ومن يهاجر في سبيل الله يجد في الأرض مراغما كثيرا وسعة ومن يخرج من بيته مهاجرا إلى الله ورسوله ثم يدركه الموت فقد وقع أجره على الله','456','102','0');
INSERT INTO [t9182] VALUES (145,'قال الله عز وجل: (وإذا ضربتم في الأرض فليس عليكم جناح أن تقصروا من الصلاة إن خفتم أن يفتنكم الذين كفروا إن الكافرين كانوا لكم عدوا مبينا (١٠١)','457','102','0');
INSERT INTO [t9182] VALUES (146,'قال الله عز وجل: (وإذا كنت فيهم فأقمت لهم الصلاة فلتقم طائفة منهم معك وليأخذوا أسلحتهم فإذا سجدوا فليكونوا من ورائكم ولتأت طائفة أخرى لم يصلوا فليصلوا م','461','102','0');
INSERT INTO [t9182] VALUES (147,'قال الله عز وجل: (إن الصلاة كانت على المؤمنين كتابا موقوتا (١٠٣)','471','102','0');
INSERT INTO [t9182] VALUES (148,'قال الله عز وجل: (يستخفون من الناس ولا يستخفون من الله وهو معهم إذ يبيتون ما لا يرضى من القول وكان الله بما يعملون محيطا (١٠٨)','475','102','0');
INSERT INTO [t9182] VALUES (149,'قال الله عز وجل: (وأنزل الله عليك الكتاب والحكمة وعلمك ما لم تكن تعلم وكان فضل الله عليك عظيما (١١٣)','476','102','0');
INSERT INTO [t9182] VALUES (150,'قال الله عز وجل: (ومن يشاقق الرسول من بعد ما تبين له الهدى ويتبع غير سبيل المؤمنين نوله ما تولى ونصله جهنم وساءت مصيرا (١١٥)','477','102','0');
INSERT INTO [t9182] VALUES (151,'قال الله عز وجل: (ومن أحسن دينا ممن أسلم وجهه لله وهو محسن واتبع ملة إبراهيم حنيفا واتخذ الله إبراهيم خليلا (١٢٥)','478','102','0');
INSERT INTO [t9182] VALUES (152,'قال الله عز وجل: (وإن امرأة خافت من بعلها نشوزا أو إعراضا فلا جناح عليهما أن يصلحا بينهما صلحا والصلح خير وأحضرت الأنفس الشح','479','102','0');
INSERT INTO [t9182] VALUES (153,'قال الله عز وجل: (ولن تستطيعوا أن تعدلوا بين النساء ولو حرصتم فلا تميلوا كل الميل فتذروها كالمعلقة وإن تصلحوا وتتقوا فإن الله كان غفورا رحيما (١٢٩)','481','102','0');
INSERT INTO [t9182] VALUES (154,'قال الله عز وجل: (يا أيها الذين آمنوا كونوا قوامين بالقسط شهداء لله ولو على أنفسكم أو الوالدين والأقربين إن يكن غنيا أو فقيرا فالله أولى بهما فلا تتبعوا اله','483','102','0');
INSERT INTO [t9182] VALUES (155,'قال الله عز وجل: (يا أيها الذين آمنوا آمنوا بالله ورسوله والكتاب الذي نزل على رسوله والكتاب الذي أنزل من قبل','484','102','0');
INSERT INTO [t9182] VALUES (156,'قال الله عز وجل: (وقد نزل عليكم في الكتاب أن إذا سمعتم آيات الله يكفر بها ويستهزأ بها فلا تقعدوا معهم حتى يخوضوا في حديث غيره إنكم إذا مثلهم','485','102','0');
INSERT INTO [t9182] VALUES (157,'قال الله عز وجل: (إن المنافقين في الدرك الأسفل من النار ولن تجد لهم نصيرا (١٤٥)','486','102','0');
INSERT INTO [t9182] VALUES (158,'قال الله عز وجل: (فبظلم من الذين هادوا حرمنا عليهم طيبات أحلت لهم وبصدهم عن سبيل الله كثيرا (١٦٠)','490','102','0');
INSERT INTO [t9182] VALUES (159,'قال الله عز وجل: (والمقيمين الصلاة والمؤتون الزكاة)','490','102','0');
INSERT INTO [t9182] VALUES (160,'قال الله عز وجل: (إنا أوحينا إليك كما أوحينا إلى نوح والنبيين من بعده وأوحينا إلى إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط وعيسى وأيوب ويونس وهارون وسليما','491','102','0');
INSERT INTO [t9182] VALUES (161,'قال الله عز وجل: (وكلم الله موسى تكليما (١٦٤)','491','102','0');
INSERT INTO [t9182] VALUES (162,'قال الله عز وجل: (فآمنوا بالله ورسله ولا تقولوا ثلاثة انتهوا خيرا لكم إنما الله إله واحد سبحانه أن يكون له ولد له)','492','102','0');
INSERT INTO [t9182] VALUES (163,'قال الله عز وجل: (يستفتونك قل الله يفتيكم في الكلالة إن امرؤ هلك ليس له ولد وله أخت فلها نصف ما ترك وهو يرثها إن لم يكن لها ولد','495','102','0');
INSERT INTO [t9182] VALUES (164,'سورة المائدة','500','0','0');
INSERT INTO [t9182] VALUES (165,'قال الله عز وجل: (يا أيها الذين آمنوا أوفوا بالعقود أحلت لكم بهيمة الأنعام إلا ما يتلى عليكم غير محلي الصيد وأنتم حرم إن الله يحكم ما يريد (١)','500','164','0');
INSERT INTO [t9182] VALUES (166,'قال الله عز وجل: (يا أيها الذين آمنوا لا تحلوا شعائر الله ولا الشهر الحرام ولا الهدي ولا القلائد ولا آمين البيت الحرام يبتغون فضلا من ربهم ورضوانا وإذا حللتم','502','164','0');
INSERT INTO [t9182] VALUES (167,'قال الله عز وجل: (حرمت عليكم الميتة والدم ولحم الخنزير وما أهل لغير الله به والمنخنقة والموقوذة والمتردية والنطيحة وما أكل السبع إلا ما ذكيتم','504','164','0');
INSERT INTO [t9182] VALUES (168,'قال الله عز وجل: (يسألونك ماذا أحل لهم قل أحل لكم الطيبات وما علمتم من الجوارح مكلبين تعلمونهن مما علمكم الله','507','164','0');
INSERT INTO [t9182] VALUES (169,'قال الله عز وجل: (اليوم أحل لكم الطيبات وطعام الذين أوتوا الكتاب حل لكم وطعامكم حل لهم والمحصنات من المؤمنات والمحصنات من الذين أوتوا الكتاب من قبلكم','510','164','0');
INSERT INTO [t9182] VALUES (170,'قال الله عز وجل: (يا أيها الذين آمنوا إذا قمتم إلى الصلاة فاغسلوا وجوهكم وأيديكم إلى المرافق وامسحوا برءوسكم وأرجلكم إلى الكعبين وإن كنتم جنبا فاطهروا','513','164','0');
INSERT INTO [t9182] VALUES (171,'قال الله عز وجل: (يا أيها الذين آمنوا كونوا قوامين لله شهداء بالقسط ولا يجرمنكم شنئان قوم على ألا تعدلوا اعدلوا هو أقرب للتقوى واتقوا الله إن الله خبير بما','537','164','0');
INSERT INTO [t9182] VALUES (172,'قال الله عز وجل: (وعزرتموهم)','538','164','0');
INSERT INTO [t9182] VALUES (173,'قال الله عز وجل: (يا أهل الكتاب قد جاءكم رسولنا يبين لكم على فترة من الرسل أن تقولوا ما جاءنا من بشير ولا نذير فقد جاءكم بشير ونذير والله على كل شيء قدير (١٩)','538','164','0');
INSERT INTO [t9182] VALUES (174,'قال الله عز وجل: (واتل عليهم نبأ ابني آدم بالحق إذ قربا قربانا فتقبل من أحدهما ولم يتقبل من الآخر قال لأقتلنك قال إنما يتقبل الله من المتقين (٢٧)','539','164','0');
INSERT INTO [t9182] VALUES (175,'قال الله عز وجل: (إنما جزاء الذين يحاربون الله ورسوله ويسعون في الأرض فسادا أن يقتلوا أو يصلبوا أو تقطع أيديهم وأرجلهم من خلاف أو ينفوا من الأرض','540','164','0');
INSERT INTO [t9182] VALUES (176,'قال الله عز وجل: (والسارق والسارقة فاقطعوا أيديهما جزاء بما كسبا نكالا من الله والله عزيز حكيم (٣٨)','544','164','0');
INSERT INTO [t9182] VALUES (177,'قال الله عز وجل: (يا أيها الرسول لا يحزنك الذين يسارعون في الكفر من الذين قالوا آمنا بأفواههم ولم تؤمن قلوبهم)','548','164','0');
INSERT INTO [t9182] VALUES (178,'قال الله عز وجل: (فإن جاءوك فاحكم بينهم أو أعرض عنهم)','549','164','0');
INSERT INTO [t9182] VALUES (179,'قال الله عز وجل: (وكيف يحكمونك وعندهم التوراة فيها حكم الله ثم يتولون من بعد ذلك وما أولئك بالمؤمنين (٤٣)','554','164','0');
INSERT INTO [t9182] VALUES (180,'قال الله عز وجل: (وكتبنا عليهم فيها أن النفس بالنفس)','556','164','0');
INSERT INTO [t9182] VALUES (181,'قال الله عز وجل: (وأنزلنا إليك الكتاب بالحق مصدقا لما بين يديه من الكتاب ومهيمنا عليه فاحكم بينهم بما أنزل الله ولا تتبع أهواءهم عما جاءك من الحق)','564','164','0');
INSERT INTO [t9182] VALUES (182,'قال الله عز وجل: (لكل جعلنا منكم شرعة ومنهاجا','565','164','0');
INSERT INTO [t9182] VALUES (183,'قال الله عز وجل: (وأن احكم بينهم بما أنزل الله ولا تتبع أهواءهم واحذرهم أن يفتنوك)','565','164','0');
INSERT INTO [t9182] VALUES (184,'قال الله عز وجل: (أفحكم الجاهلية يبغون ومن أحسن من الله حكما لقوم يوقنون (٥٠)','569','164','0');
INSERT INTO [t9182] VALUES (185,'قال الله عز وجل: (يا أيها الذين آمنوا لا تتخذوا اليهود والنصارى أولياء بعضهم أولياء بعض ومن يتولهم منكم فإنه منهم)','570','164','0');
INSERT INTO [t9182] VALUES (186,'قال الله عز وجل: (وإذا ناديتم إلى الصلاة اتخذوها هزوا ولعبا ذلك بأنهم قوم لا يعقلون (٥٨)','573','164','0');
INSERT INTO [t9182] VALUES (187,'قال الله عز وجل: (يا أيها الرسول بلغ ما أنزل إليك من ربك وإن لم تفعل فما بلغت رسالته والله يعصمك من الناس)','574','164','0');
INSERT INTO [t9182] VALUES (188,'قال الله عز وجل: (لا يؤاخذكم الله باللغو في أيمانكم ولكن يؤاخذكم بما عقدتم الأيمان فكفارته إطعام عشرة مساكين)','575','164','0');
INSERT INTO [t9182] VALUES (189,'قال الله عز وجل: (ليس على الذين آمنوا وعملوا الصالحات جناح فيما طعموا إذا ما اتقوا وآمنوا وعملوا الصالحات ثم اتقوا وآمنوا ثم اتقوا وأحسنوا','580','164','0');
INSERT INTO [t9182] VALUES (190,'قال الله عز وجل: (يا أيها الذين آمنوا ليبلونكم الله بشيء من الصيد تناله أيديكم ورماحكم ليعلم الله من يخافه بالغيب فمن اعتدى بعد ذلك فله عذاب أليم (٩٤)','581','164','0');
INSERT INTO [t9182] VALUES (191,'قال الله عز وجل: (يا أيها الذين آمنوا لا تقتلوا الصيد وأنتم حرم ومن قتله منكم متعمدا فجزاء مثل ما قتل من النعم يحكم به ذوا عدل منكم هديا بالغ الكعبة','582','164','0');
INSERT INTO [t9182] VALUES (192,'قال الله عز وجل: (أحل لكم صيد البحر وطعامه متاعا لكم وللسيارة وحرم عليكم صيد البر ما دمتم حرما واتقوا الله الذي إليه تحشرون (٩٦)','597','164','0');
INSERT INTO [t9182] VALUES (193,'قال الله عز وجل: (ما على الرسول إلا البلاغ والله يعلم ما تبدون وما تكتمون (٩٩)','601','164','0');
INSERT INTO [t9182] VALUES (194,'قال الله عز وجل: (يا أيها الذين آمنوا لا تسألوا عن أشياء إن تبد لكم تسؤكم وإن تسألوا عنها حين ينزل القرآن تبد لكم عفا الله عنها والله غفور حليم (١٠١)','602','164','0');
INSERT INTO [t9182] VALUES (195,'قال الله عز وجل: (ما جعل الله من بحيرة ولا سائبة ولا وصيلة ولا حام ولكن الذين كفروا يفترون على الله الكذب وأكثرهم لا يعقلون (١٠٣)','603','164','0');
INSERT INTO [t9182] VALUES (196,'قال الله عز وجل: (يا أيها الذين آمنوا عليكم أنفسكم)','610','164','0');
INSERT INTO [t9182] VALUES (197,'قال الله عز وجل: (يا أيها الذين آمنوا شهادة بينكم إذا حضر أحدكم الموت)','610','164','0');
INSERT INTO [t9182] VALUES (198,'سووة الأنعام','620','0','0');
INSERT INTO [t9182] VALUES (199,'قال الله عز وجل: (الحمد لله الذي خلق السماوات والأرض وجعل الظلمات والنور ثم الذين كفروا بربهم يعدلون (١)','620','198','0');
INSERT INTO [t9182] VALUES (200,'قال الله عز وجل: (ما عليك من حسابهم من شيء)','621','198','0');
INSERT INTO [t9182] VALUES (201,'قال الله عز وجل: (وإذا رأيت الذين يخوضون في آياتنا فأعرض عنهم حتى يخوضوا في حديث غيره وإما ينسينك الشيطان فلا تقعد بعد الذكرى مع القوم الظالمين (٦٨)','622','198','0');
INSERT INTO [t9182] VALUES (202,'قال الله عز وجل: (وإذ قال إبراهيم لأبيه آزر أتتخذ أصناما آلهة إني أراك وقومك في ضلال مبين (٧٤)','623','198','0');
INSERT INTO [t9182] VALUES (203,'قال الله عز وجل: (لقد تقطع بينكم وضل عنكم ما كنتم تزعمون (٩٤)','623','198','0');
INSERT INTO [t9182] VALUES (204,'قال الله عز وجل: (وهو الذي جعل لكم النجوم لتهتدوا بها في ظلمات البر والبحر قد فصلنا الآيات لقوم يعلمون (٩٧)','624','198','0');
INSERT INTO [t9182] VALUES (205,'قال الله عز وجل: (ذلكم الله ربكم لا إله إلا هو خالق كل شيء فاعبدوه وهو على كل شيء وكيل (١٠٢)','626','198','0');
INSERT INTO [t9182] VALUES (206,'قال الله عز وجل: (اتبع ما أوحي إليك من ربك لا إله إلا هو وأعرض عن المشركين (١٠٦)','627','198','0');
INSERT INTO [t9182] VALUES (207,'قال الله عز وجل: (ولا تسبوا الذين يدعون من دون الله فيسبوا الله عدوا بغير علم كذلك زينا لكل أمة عملهم ثم إلى ربهم مرجعهم فينبئهم بما كانوا يعملون (١٠٨)','628','198','0');
INSERT INTO [t9182] VALUES (208,'قال الله عز وجل: (فكلوا مما ذكر اسم الله عليه إن كنتم بآياته مؤمنين (١١٨)','628','198','0');
INSERT INTO [t9182] VALUES (209,'قال الله عز وجل: (وما لكم ألا تأكلوا مما ذكر اسم الله عليه وقد فصل لكم ما حرم عليكم إلا ما اضطررتم إليه وإن كثيرا ليضلون بأهوائهم بغير علم','629','198','0');
INSERT INTO [t9182] VALUES (210,'قال الله عز وجل: (وجعلوا لله مما ذرأ من الحرث والأنعام نصيبا فقالوا هذا لله بزعمهم وهذا لشركائنا فما كان لشركائهم فلا يصل إلى الله','631','198','0');
INSERT INTO [t9182] VALUES (211,'قال الله عز وجل: (وكذلك زين لكثير من المشركين قتل أولادهم شركاؤهم ليردوهم وليلبسوا عليهم دينهم ولو شاء الله ما فعلوه فذرهم وما يفترون (١٣٧)','632','198','0');
INSERT INTO [t9182] VALUES (212,'قال الله عز وجل: (وقالوا هذه أنعام وحرث حجر لا يطعمها إلا من نشاء بزعمهم وأنعام حرمت ظهورها وأنعام لا يذكرون اسم الله عليها افتراء عليه','633','198','0');
INSERT INTO [t9182] VALUES (213,'قال الله عز وجل: (قد خسر الذين قتلوا أولادهم سفها بغير علم وحرموا ما رزقهم الله افتراء على الله قد ضلوا وما كانوا مهتدين (١٤٠)','634','198','0');
INSERT INTO [t9182] VALUES (214,'قال الله عز وجل: (وآتوا حقه يوم حصاده)','635','198','0');
INSERT INTO [t9182] VALUES (215,'قال الله عز وجل: (ثمانية أزواج من الضأن اثنين ومن المعز اثنين)','638','198','0');
INSERT INTO [t9182] VALUES (216,'قال الله عز وجل: (قل لا أجد في ما أوحي إلي محرما على طاعم يطعمه إلا أن يكون ميتة أو دما مسفوحا أو لحم خنزير فإنه رجس أو فسقا أهل لغير الله به','639','198','0');
INSERT INTO [t9182] VALUES (217,'قال الله عز وجل: (وعلى الذين هادوا حرمنا كل ذي ظفر ومن البقر والغنم حرمنا عليهم شحومهما إلا ما حملت ظهورهما أو الحوايا أو ما اختلط بعظم','647','198','0');
INSERT INTO [t9182] VALUES (218,'قال الله عز وجل: (قل هلم شهداءكم الذين يشهدون أن الله حرم هذا فإن شهدوا فلا تشهد معهم ولا تتبع أهواء الذين كذبوا بآياتنا','648','198','0');
INSERT INTO [t9182] VALUES (219,'قال الله عز وجل: (قل تعالوا أتل ما حرم ربكم عليكم ألا تشركوا به شيئا وبالوالدين إحسانا ولا تقتلوا أولادكم من إملاق نحن نرزقكم وإياهم ولا تقربوا الفواحش ما ظ','648','198','0');
INSERT INTO [t9182] VALUES (220,'قال الله عز وجل: (وإذا قلتم فاعدلوا ولو كان ذا قربى)','650','198','0');
INSERT INTO [t9182] VALUES (221,'قال الله عز وجل: (من جاء بالحسنة فله عشر أمثالها ومن جاء بالسيئة فلا يجزى إلا مثلها وهم لا يظلمون (١٦٠)','650','198','0');
INSERT INTO [t9182] VALUES (222,'قال الله عز وجل: (ولا تزر وازرة وزر أخرى)','651','198','0');
INSERT INTO [t9182] VALUES (223,'سورة الأعراف','654','0','0');
INSERT INTO [t9182] VALUES (224,'قال الله عز وجل: (يا بني آدم قد أنزلنا عليكم لباسا)','654','223','0');
INSERT INTO [t9182] VALUES (225,'قال الله عز وجل: (يا بني آدم لا يفتننكم الشيطان)','655','223','0');
INSERT INTO [t9182] VALUES (226,'قال الله عز وجل: (يا بني آدم خذوا زينتكم عند كل مسجد وكلوا واشربوا ولا تسرفوا إنه لا يحب المسرفين (٣١)','656','223','0');
INSERT INTO [t9182] VALUES (227,'قال الله عز وجل: (حتى إذا اداركوا فيها جميعا)','658','223','0');
INSERT INTO [t9182] VALUES (228,'قال الله عز وجل: (وإلى عاد أخاهم هودا)','658','223','0');
INSERT INTO [t9182] VALUES (229,'قال الله عز وجل: (عسى ربكم أن يهلك عدوكم ويستخلفكم في الأرض فينظر كيف تعملون (١٢٩)','660','223','0');
INSERT INTO [t9182] VALUES (230,'قال الله عز وجل: (وواعدنا موسى ثلاثين ليلة وأتممناها بعشر فتم ميقات ربه أربعين ليلة)','660','223','0');
INSERT INTO [t9182] VALUES (231,'قال الله عز وجل: (الذين يتبعون الرسول النبي الأمي الذي يجدونه مكتوبا عندهم في التوراة والإنجيل يأمرهم بالمعروف وينهاهم عن المنكر','661','223','0');
INSERT INTO [t9182] VALUES (232,'قال الله عز وجل: (فآمنوا بالله ورسوله النبي الأمي الذي يؤمن بالله وكلماته واتبعوه لعلكم تهتدون (١٥٨)','663','223','0');
INSERT INTO [t9182] VALUES (233,'قال الله عز وجل: (واسألهم عن القرية التي كانت حاضرة البحر إذ يعدون في السبت إذ تأتيهم حيتانهم يوم سبتهم شرعا ويوم لا يسبتون لا تأتيهم','664','223','0');
INSERT INTO [t9182] VALUES (234,'قال الله عز وجل: (وإذ قالت أمة منهم لم تعظون قوما الله مهلكهم أو معذبهم عذابا شديدا قالوا معذرة إلى ربكم ولعلهم يتقون (١٦٤)','665','223','0');
INSERT INTO [t9182] VALUES (235,'قال الله عز وجل: (وأشهدهم على أنفسهم ألست بربكم قالوا بلى)','667','223','0');
INSERT INTO [t9182] VALUES (236,'قال الله عز وجل: (والذين كذبوا بآياتنا سنستدرجهم من حيث لا يعلمون (١٨٢)','668','223','0');
INSERT INTO [t9182] VALUES (237,'قال الله عز وجل: (وجعل منها زوجها ليسكن إليها)','670','223','0');
INSERT INTO [t9182] VALUES (238,'قال الله عز وجل: (وإذا قرئ القرآن فاستمعوا له وأنصتوا لعلكم ترحمون (٢٠٤)','672','223','0');
INSERT INTO [t9182] VALUES (239,'سورة الأنفال','674','0','0');
INSERT INTO [t9182] VALUES (240,'قال الله عز وجل: (يسألونك عن الأنفال قل الأنفال لله والرسول فاتقوا الله وأصلحوا ذات بينكم وأطيعوا الله ورسوله إن كنتم مؤمنين (١)','674','239','0');
INSERT INTO [t9182] VALUES (241,'قال الله عز وجل: (يا أيها الذين آمنوا إذا لقيتم الذين كفروا زحفا فلا تولوهم الأدبار (١٥)','676','239','0');
INSERT INTO [t9182] VALUES (242,'قال الله عز وجل: (يا أيها الذين آمنوا أطيعوا الله ورسوله ولا تولوا عنه وأنتم تسمعون (٢٠)','678','239','0');
INSERT INTO [t9182] VALUES (243,'قال الله عز وجل: (قل للذين كفروا إن ينتهوا يغفر لهم ما قد سلف وإن يعودوا فقد مضت سنت الأولين (٣٨)','679','239','0');
INSERT INTO [t9182] VALUES (244,'قال الله عز وجل: (وقاتلوهم حتى لا تكون فتنة ويكون الدين كله لله فإن انتهوا فإن الله بما يعملون بصير (٣٩)','680','239','0');
INSERT INTO [t9182] VALUES (245,'قال الله عز وجل: (واعلموا أنما غنمتم من شيء فأن لله خمسه وللرسول ولذي القربى واليتامى والمساكين وابن السبيل إن كنتم آمنتم بالله وما أنزلنا على عبدنا','684','239','0');
INSERT INTO [t9182] VALUES (246,'قال الله عز وجل: (وإما تخافن من قوم خيانة فانبذ إليهم على سواء إن الله لا يحب الخائنين (٥٨)','693','239','0');
INSERT INTO [t9182] VALUES (247,'قال الله عز وجل: (وأعدوا لهم ما استطعتم من قوة ومن رباط الخيل ترهبون به عدو الله وعدوكم وآخرين من دونهم لا تعلمونهم الله يعلمهم','695','239','0');
INSERT INTO [t9182] VALUES (248,'قال الله عز وجل: (يا أيها النبي حرض المؤمنين على القتال إن يكن منكم عشرون صابرون يغلبوا مائتين)','698','239','0');
INSERT INTO [t9182] VALUES (249,'قال الله عز وجل: (وأولو الأرحام بعضهم أولى ببعض في كتاب الله)','700','239','0');
INSERT INTO [t9182] VALUES (250,'سورة التوبة','703','0','0');
INSERT INTO [t9182] VALUES (251,'قال الله عز وجل: (براءة من الله ورسوله إلى الذين عاهدتم من المشركين (١)','703','250','0');
INSERT INTO [t9182] VALUES (252,'قال الله عز وجل: (وأذان من الله ورسوله إلى الناس يوم الحج الأكبر أن الله بريء من المشركين ورسوله)','705','250','0');
INSERT INTO [t9182] VALUES (253,'قال الله عز وجل: (فإذا انسلخ الأشهر الحرم فاقتلوا المشركين حيث وجدتموهم وخذوهم واحصروهم واقعدوا لهم كل مرصد','707','250','0');
INSERT INTO [t9182] VALUES (254,'قال الله عز وجل: (وإن أحد من المشركين استجارك فأجره حتى يسمع كلام الله ثم أبلغه مأمنه ذلك بأنهم قوم لا يعلمون (٦)','711','250','0');
INSERT INTO [t9182] VALUES (255,'قال الله عز وجل: (كيف يكون للمشركين عهد عند الله وعند رسوله إلا الذين عاهدتم عند المسجد الحرام فما استقاموا لكم فاستقيموا لهم إن الله يحب المتقين (٧)','715','250','0');
INSERT INTO [t9182] VALUES (256,'قال الله عز وجل: (يا أيها الذين آمنوا إنما المشركون نجس فلا يقربوا المسجد الحرام بعد عامهم هذا وإن خفتم عيلة فسوف يغنيكم الله من فضله إن شاء','716','250','0');
INSERT INTO [t9182] VALUES (257,'قال الله عز وجل: (قاتلوا الذين لا يؤمنون بالله ولا باليوم الآخر ولا يحرمون ما حرم الله ورسوله ولا يدينون دين الحق من الذين أوتوا الكتاب حتى يعطوا الجزية','718','250','0');
INSERT INTO [t9182] VALUES (258,'قال الله عز وجل: (وقالت اليهود عزير ابن الله وقالت النصارى المسيح ابن الله ذلك قولهم بأفواههم يضاهئون قول الذين كفروا من قبل قاتلهم الله أنى يؤفكون (٣٠)','729','250','0');
INSERT INTO [t9182] VALUES (259,'قال الله عز وجل: (هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون (٣٣)','730','250','0');
INSERT INTO [t9182] VALUES (260,'قال الله عز وجل: (والذين يكنزون الذهب والفضة ولا ينفقونها في سبيل الله فبشرهم بعذاب أليم (٣٤)','731','250','0');
INSERT INTO [t9182] VALUES (261,'قال الله عز وجل: (إن عدة الشهور عند الله اثنا عشر شهرا في كتاب الله يوم خلق السماوات والأرض منها أربعة حرم ذلك الدين القيم فلا تظلموا فيهن أنفسكم','734','250','0');
INSERT INTO [t9182] VALUES (262,'قال الله عز وجل: (إنما النسيء زيادة في الكفر يضل به الذين كفروا يحلونه عاما ويحرمونه عاما ليواطئوا عدة ما حرم الله فيحلوا ما حرم الله زين لهم سوء أعمالهم','736','250','0');
INSERT INTO [t9182] VALUES (263,'قال الله عز وجل: (يا أيها الذين آمنوا ما لكم إذا قيل لكم انفروا في سبيل الله اثاقلتم إلى الأرض أرضيتم بالحياة الدنيا من الآخرة فما متاع الحياة الدنيا في الآخرة إل','737','250','0');
INSERT INTO [t9182] VALUES (264,'قال الله عز وجل: (انفروا خفافا وثقالا وجاهدوا بأموالكم وأنفسكم في سبيل الله ذلكم خير لكم إن كنتم تعلمون (٤١)','740','250','0');
INSERT INTO [t9182] VALUES (265,'قال الله عز وجل: (ولو أرادوا الخروج لأعدوا له عدة ولكن كره الله انبعاثهم فثبطهم وقيل اقعدوا مع القاعدين (٤٦)','742','250','0');
INSERT INTO [t9182] VALUES (266,'قال الله عز وجل: (إنما الصدقات للفقراء والمساكين والعاملين عليها والمؤلفة قلوبهم وفي الرقاب والغارمين وفي سبيل الله وابن السبيل فريضة من الله والله عليم حكي','743','250','0');
INSERT INTO [t9182] VALUES (267,'قال الله عز وجل: (يحلفون بالله ما قالوا ولقد قالوا كلمة الكفر وكفروا بعد إسلامهم وهموا بما لم ينالوا وما نقموا إلا أن أغناهم الله ورسوله من فضله','745','250','0');
INSERT INTO [t9182] VALUES (268,'قال الله عز وجل: (استغفر لهم أو لا تستغفر لهم إن تستغفر لهم سبعين مرة فلن يغفر الله لهم ذلك بأنهم كفروا بالله ورسوله والله لا يهدي القوم الفاسقين (٨٠)','747','250','0');
INSERT INTO [t9182] VALUES (269,'قال الله عز وجل: (فرح المخلفون بمقعدهم خلاف رسول الله وكرهوا أن يجاهدوا بأموالهم وأنفسهم في سبيل الله)','748','250','0');
INSERT INTO [t9182] VALUES (270,'قال الله عز وجل: (ولا تصل على أحد منهم مات أبدا ولا تقم على قبره إنهم كفروا بالله ورسوله وماتوا وهم فاسقون (٨٤)','749','250','0');
INSERT INTO [t9182] VALUES (271,'قال الله عز وجل: (ليس على الضعفاء ولا على المرضى ولا على الذين لا يجدون ما ينفقون حرج إذا نصحوا لله ورسوله ما على المحسنين من سبيل والله غفور رحيم (٩١)','751','250','0');
INSERT INTO [t9182] VALUES (272,'قال الله عز وجل: (قل لا تعتذروا لن نؤمن لكم قد نبأنا الله من أخباركم)','754','250','0');
INSERT INTO [t9182] VALUES (273,'قال الله عز وجل: (سيحلفون بالله لكم إذا انقلبتم إليهم لتعرضوا عنهم فأعرضوا عنهم)','755','250','0');
INSERT INTO [t9182] VALUES (274,'قال الله عز وجل: (والسابقون الأولون من المهاجرين والأنصار والذين اتبعوهم بإحسان رضي الله عنهم ورضوا عنه)','755','250','0');
INSERT INTO [t9182] VALUES (275,'قال الله عز وجل: (خذ من أموالهم صدقة تطهرهم وتزكيهم بها)','756','250','0');
INSERT INTO [t9182] VALUES (276,'قال الله عز وجل: (ألم يعلموا أن الله هو يقبل التوبة عن عباده ويأخذ الصدقات وأن الله هو التواب الرحيم (١٠٤)','765','250','0');
INSERT INTO [t9182] VALUES (277,'قال الله عز وجل: (فيه رجال يحبون أن يتطهروا والله يحب المطهرين (١٠٨)','766','250','0');
INSERT INTO [t9182] VALUES (278,'قال الله عز وجل: (إن الله اشترى من المؤمنين أنفسهم وأموالهم بأن لهم الجنة)','766','250','0');
INSERT INTO [t9182] VALUES (279,'قال الله عز وجل: (ذلك بأنهم لا يصيبهم ظمأ ولا نصب ولا مخمصة في سبيل الله ولا يطئون موطئا يغيظ الكفار ولا ينالون من عدو نيلا إلا كتب لهم به عمل صالح','767','250','0');
INSERT INTO [t9182] VALUES (280,'قال الله عز وجل: (وما كان المؤمنون لينفروا كافة فلولا نفر من كل فرقة منهم طائفة ليتفقهوا في الدين ولينذروا قومهم إذا رجعوا إليهم لعلهم يحذرون (١٢٢)','769','250','0');
INSERT INTO [t9182] VALUES (281,'قال الله عز وجل: (يا أيها الذين آمنوا قاتلوا الذين يلونكم من الكفار وليجدوا فيكم غلظة واعلموا أن الله مع المتقين (١٢٣)','770','250','0');
INSERT INTO [t9182] VALUES (282,'قال الله عز وجل: (وإذا ما أنزلت سورة فمنهم من يقول أيكم زادته هذه إيمانا فأما الذين آمنوا فزادتهم إيمانا وهم يستبشرون (١٢٤)','772','250','0');
INSERT INTO [t9182] VALUES (283,'قال الله عز وجل: (لقد جاءكم رسول من أنفسكم عزيز عليه ما عنتم حريص عليكم بالمؤمنين رءوف رحيم (١٢٨)','773','250','0');
INSERT INTO [t9182] VALUES (284,'سورة يونس','774','0','0');
INSERT INTO [t9182] VALUES (285,'قال الله عز وجل: (يدبر الأمر ما من شفيع إلا من بعد إذنه)','774','284','0');
INSERT INTO [t9182] VALUES (286,'قال الله عز وجل: (وإذا تتلى عليهم آياتنا بينات قال الذين لا يرجون لقاءنا ائت بقرآن غير هذا أو بدله قل ما يكون لي أن أبدله من تلقاء نفسي إن أتبع إلا ما يوحى','774','284','0');
INSERT INTO [t9182] VALUES (287,'قال الله عز وجل: (والله يدعو إلى دار السلام ويهدي من يشاء إلى صراط مستقيم (٢٥)','776','284','0');
INSERT INTO [t9182] VALUES (288,'قال الله عز وجل: (هو الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا)','777','284','0');
INSERT INTO [t9182] VALUES (289,'سورة هود','778','0','0');
INSERT INTO [t9182] VALUES (290,'قال الله عز وجل: (وأن استغفروا ربكم ثم توبوا إليه يمتعكم متاعا حسنا إلى أجل مسمى ويؤت كل ذي فضل فضله وإن تولوا فإني أخاف عليكم عذاب يوم كبير (٣)','778','289','0');
INSERT INTO [t9182] VALUES (291,'قال الله عز وجل: (وما من دابة في الأرض إلا على الله رزقها)','779','289','0');
INSERT INTO [t9182] VALUES (292,'قال الله عز وجل: (ولقد أرسلنا نوحا إلى قومه إني لكم نذير مبين (٢٥)','779','289','0');
INSERT INTO [t9182] VALUES (293,'قال الله عز وجل: (احمل فيها من كل زوجين اثنين وأهلك)','780','289','0');
INSERT INTO [t9182] VALUES (294,'قال الله عز وجل: (ونادى نوح ابنه وكان في معزل)','780','289','0');
INSERT INTO [t9182] VALUES (295,'قال الله عز وجل: (إن ابني من أهلي وإن وعدك الحق وأنت أحكم الحاكمين (٤٥) قال يا نوح إنه ليس من أهلك إنه عمل غير صالح)','781','289','0');
INSERT INTO [t9182] VALUES (296,'قال الله عز وجل: (وإلى عاد أخاهم هودا)','783','289','0');
INSERT INTO [t9182] VALUES (297,'قال الله عز وجل: (تمتعوا في داركم ثلاثة أيام)','784','289','0');
INSERT INTO [t9182] VALUES (298,'قال الله عز وجل: (فبشرناها بإسحاق ومن وراء إسحاق يعقوب (٧١)','784','289','0');
INSERT INTO [t9182] VALUES (299,'قال الله عز وجل: (وإلى مدين أخاهم شعيبا)','784','289','0');
INSERT INTO [t9182] VALUES (300,'قال الله عز وجل: (أولو بقية ينهون عن الفساد في الأرض)','785','289','0');
INSERT INTO [t9182] VALUES (301,'سورة يوسف','786','0','0');
INSERT INTO [t9182] VALUES (302,'قال الله عز وجل: (وكذلك مكنا ليوسف في الأرض)','786','301','0');
INSERT INTO [t9182] VALUES (303,'قال الله عز وجل: (وقال نسوة في المدينة)','787','301','0');
INSERT INTO [t9182] VALUES (304,'قال الله عز وجل: (وادكر بعد أمة)','788','301','0');
INSERT INTO [t9182] VALUES (305,'قال الله - عز وجل -: (وما شهدنا إلا بما علمنا)','788','301','0');
INSERT INTO [t9182] VALUES (306,'قال الله عز وجل: (واسأل القرية التي كنا فيها والعير التي أقبلنا فيها وإنا لصادقون (٨٢)','790','301','0');
INSERT INTO [t9182] VALUES (307,'قال الله عز وجل: (إن الله يجزي المتصدقين (٨٨)','791','301','0');
INSERT INTO [t9182] VALUES (308,'سورة الرعد','792','0','0');
INSERT INTO [t9182] VALUES (309,'قال الله عز وجل: (ويرسل الصواعق فيصيب بها من يشاء)','792','308','0');
INSERT INTO [t9182] VALUES (310,'قال الله عز وجل: (الله خالق كل شيء)','792','308','0');
INSERT INTO [t9182] VALUES (311,'قال الله عز وجل: (إنما يتذكر أولو الألباب (١٩)','793','308','0');
INSERT INTO [t9182] VALUES (312,'قال الله عز وجل: (يوفون بعهد الله ولا ينقضون الميثاق (٢٠)','793','308','0');
INSERT INTO [t9182] VALUES (313,'قال الله عز وجل: (أولئك لهم اللعنة)','794','308','0');
INSERT INTO [t9182] VALUES (314,'قال الله عز وجل: (ألا بذكر الله تطمئن القلوب (٢٨)','794','308','0');
INSERT INTO [t9182] VALUES (315,'قال الله عز وجل: (ألا بذكر الله تطمئن القلوب (٢٨)','794','308','0');
INSERT INTO [t9182] VALUES (316,'قال الله عز وجل: (وكذلك أنزلناه حكما عربيا)','795','308','0');
INSERT INTO [t9182] VALUES (317,'قال الله عز وجل: (يمحو الله ما يشاء ويثبت وعنده أم الكتاب (٣٩)','797','308','0');
INSERT INTO [t9182] VALUES (318,'قال الله عز وجل: (لا معقب لحكمه)','798','308','0');
INSERT INTO [t9182] VALUES (319,'سورة إبراهيم','799','0','0');
INSERT INTO [t9182] VALUES (320,'قال الله عز وجل: (كتاب أنزلناه إليك لتخرج الناس من الظلمات إلى النور بإذن ربهم إلى صراط العزيز الحميد (١)','799','319','0');
INSERT INTO [t9182] VALUES (321,'قال الله عز وجل: (وما أرسلنا من رسول إلا بلسان قومه ليبين لهم)','800','319','0');
INSERT INTO [t9182] VALUES (322,'قال الله عز وجل: (خلق السماوات والأرض)','801','319','0');
INSERT INTO [t9182] VALUES (323,'قال الله عز وجل: (فاجعل أفئدة من الناس تهوي إليهم)','801','319','0');
INSERT INTO [t9182] VALUES (324,'سورة الحجر','803','0','0');
INSERT INTO [t9182] VALUES (325,'قال الله عز وجل: (وأرسلنا الرياح لواقح)','803','324','0');
INSERT INTO [t9182] VALUES (326,'قال الله عز وجل: (ولقد آتيناك سبعا من المثاني والقرآن العظيم (٨٧)','804','324','0');
INSERT INTO [t9182] VALUES (327,'قال الله عز وجل: (فاصدع بما تؤمر وأعرض عن المشركين (٩٤) إنا كفيناك المستهزئين (٩٥)','805','324','0');
INSERT INTO [t9182] VALUES (328,'قال الله عز وجل: (ولقد نعلم أنك يضيق صدرك بما يقولون (٩٧) فسبح بحمد ربك)','806','324','0');
INSERT INTO [t9182] VALUES (329,'سورة النحل','807','0','0');
INSERT INTO [t9182] VALUES (330,'قال الله عز وجل: (خلق السماوات والأرض بالحق تعالى عما يشركون (٣)','807','329','0');
INSERT INTO [t9182] VALUES (331,'قال الله عز وجل: (خلق الإنسان من نطفة)','807','329','0');
INSERT INTO [t9182] VALUES (332,'قال الله عز وجل: (فيه تسيمون (١٠)','808','329','0');
INSERT INTO [t9182] VALUES (333,'قال الله عز وجل: (وعلامات وبالنجم هم يهتدون (١٦)','809','329','0');
INSERT INTO [t9182] VALUES (334,'قال الله عز وجل: (وأنزلنا إليك الذكر لتبين للناس ما نزل إليهم ولعلهم يتفكرون (٤٤)','810','329','0');
INSERT INTO [t9182] VALUES (335,'قال الله عز وجل: (وإن لكم في الأنعام لعبرة نسقيكم مما في بطونه من بين فرث ودم لبنا خالصا سائغا للشاربين (٦٦)','811','329','0');
INSERT INTO [t9182] VALUES (336,'قال الله عز وجل: (ضرب الله مثلا عبدا مملوكا لا يقدر على شيء ومن رزقناه منا رزقا حسنا فهو ينفق منه سرا وجهرا)','814','329','0');
INSERT INTO [t9182] VALUES (337,'قال الله عز وجل: (والله أخرجكم من بطون أمهاتكم لا تعلمون شيئا وجعل لكم السمع والأبصار والأفئدة لعلكم تشكرون (٧٨)','815','329','0');
INSERT INTO [t9182] VALUES (338,'قال الله عز وجل: (ونزلنا عليك الكتاب تبيانا لكل شيء وهدى ورحمة)','816','329','0');
INSERT INTO [t9182] VALUES (339,'قال الله عز وجل: (فإذا قرأت القرآن فاستعذ بالله من الشيطان الرجيم (٩٨)','820','329','0');
INSERT INTO [t9182] VALUES (340,'قال الله عز وجل: (وإذا بدلنا آية مكان آية والله أعلم بما ينزل قالوا إنما أنت مفتر بل أكثرهم لا يعلمون (١٠١)','822','329','0');
INSERT INTO [t9182] VALUES (341,'قال الله عز وجل: (ولقد نعلم أنهم يقولون إنما يعلمه بشر لسان الذي يلحدون إليه أعجمي وهذا لسان عربي مبين (١٠٣)','823','329','0');
INSERT INTO [t9182] VALUES (342,'قال الله عز وجل: (من كفر بالله من بعد إيمانه إلا من أكره وقلبه مطمئن بالإيمان ولكن من شرح بالكفر صدرا فعليهم غضب من الله ولهم عذاب عظيم (١٠٦)','826','329','0');
INSERT INTO [t9182] VALUES (343,'قال الله عز وجل: (فكلوا مما رزقكم الله حلالا طيبا)','830','329','0');
INSERT INTO [t9182] VALUES (344,'قال الله عز وجل: (إن إبراهيم كان أمة قانتا لله حنيفا ولم يك من المشركين (١٢٠)','831','329','0');
INSERT INTO [t9182] VALUES (345,'سورة الإسراء','833','0','0');
INSERT INTO [t9182] VALUES (346,'قال الله عز وجل: (ومن أراد الآخرة وسعى لها سعيها وهو مؤمن فأولئك كان سعيهم مشكورا (١٩)','833','345','0');
INSERT INTO [t9182] VALUES (347,'قال الله عز وجل (وآت ذا القربى حقه والمسكين وابن السبيل ولا تبذر تبذيرا (٢٦)','834','345','0');
INSERT INTO [t9182] VALUES (348,'قال الله عز وجل: (ولا تقربوا الزنا إنه كان فاحشة وساء سبيلا (٣٢)','834','345','0');
INSERT INTO [t9182] VALUES (349,'قال الله عز وجل: (ومن قتل مظلوما فقد جعلنا لوليه سلطانا فلا يسرف في القتل إنه كان منصورا (٣٣)','836','345','0');
INSERT INTO [t9182] VALUES (350,'قال الله عز وجل: (ولا تقف ما ليس لك به علم إن السمع والبصر والفؤاد كل أولئك كان عنه مسئولا (٣٦)','845','345','0');
INSERT INTO [t9182] VALUES (351,'قال الله عز وجل: (ولا تمش في الأرض مرحا إنك لن تخرق الأرض ولن تبلغ الجبال طولا (٣٧)','847','345','0');
INSERT INTO [t9182] VALUES (352,'قال الله عز وجل: (وإذا قرأت القرآن جعلنا بينك وبين الذين لا يؤمنون بالآخرة حجابا مستورا (٤٥)','847','345','0');
INSERT INTO [t9182] VALUES (353,'قال الله عز وجل: (ولقد فضلنا بعض النبيين على بعض وآتينا داوود زبورا (٥٥)','848','345','0');
INSERT INTO [t9182] VALUES (354,'قال الله عز وجل: (وما جعلنا الرؤيا التي أريناك إلا فتنة للناس والشجرة الملعونة في القرآن)','848','345','0');
INSERT INTO [t9182] VALUES (355,'قال الله عز وجل: (ولقد كرمنا بني آدم)','850','345','0');
INSERT INTO [t9182] VALUES (356,'قال الله عز وجل: (أقم الصلاة لدلوك الشمس إلى غسق الليل وقرآن الفجر إن قرآن الفجر كان مشهودا (٧٨)','851','345','0');
INSERT INTO [t9182] VALUES (357,'قال الله عز وجل: (وقالوا لن نؤمن لك حتى تفجر لنا من الأرض ينبوعا (٩٠) أو تكون لك جنة من نخيل وعنب فتفجر الأنهار خلالها تفجيرا (٩١)','854','345','0');
INSERT INTO [t9182] VALUES (358,'قال الله عز وجل: (يخرون للأذقان سجدا)','855','345','0');
INSERT INTO [t9182] VALUES (359,'قال الله عز وجل: (ولا تجهر بصلاتك ولا تخافت بها)','856','345','0');
INSERT INTO [t9182] VALUES (360,'سورة الكهف','858','0','0');
INSERT INTO [t9182] VALUES (361,'قال الله عز وجل: (إنهم فتية آمنوا بربهم وزدناهم هدى (١٣)','858','360','0');
INSERT INTO [t9182] VALUES (362,'قال الله عز وجل: (ولا تقولن لشيء إني فاعل ذلك غدا (٢٣) إلا أن يشاء الله)','859','360','0');
INSERT INTO [t9182] VALUES (363,'قال الله عز وجل: (حتى إذا أتيا أهل قرية استطعما أهلها فأبوا أن يضيفوهما)','860','360','0');
INSERT INTO [t9182] VALUES (364,'سورة مريم','861','0','0');
INSERT INTO [t9182] VALUES (365,'قال الله عز وجل: (يا زكريا إنا نبشرك بغلام اسمه يحيى لم نجعل له من قبل سميا (٧)','861','364','0');
INSERT INTO [t9182] VALUES (366,'قال الله عز وجل: (آيتك ألا تكلم الناس ثلاث ليال سويا (١٠) فخرج على قومه من المحراب فأوحى إليهم أن سبحوا بكرة وعشيا (١١)','861','364','0');
INSERT INTO [t9182] VALUES (367,'قال الله عز وجل: (واذكر في الكتاب إبراهيم إنه كان صديقا نبيا (٤١) إذ قال لأبيه يا أبت لم تعبد ما لا يسمع ولا يبصر ولا يغني عنك شيئا (٤٢)','862','364','0');
INSERT INTO [t9182] VALUES (368,'قال الله عز وجل: (واذكر في الكتاب إسماعيل إنه كان صادق الوعد وكان رسولا نبيا (٥٤)','863','364','0');
INSERT INTO [t9182] VALUES (369,'سورة طه','864','0','0');
INSERT INTO [t9182] VALUES (370,'قال الله عز وجل: (الرحمن على العرش استوى (٥)','864','369','0');
INSERT INTO [t9182] VALUES (371,'قال الله عز وجل: (يعلم السر وأخفى (٧)','864','369','0');
INSERT INTO [t9182] VALUES (372,'قال الله عز وجل: (وأقم الصلاة لذكري (١٤)','865','369','0');
INSERT INTO [t9182] VALUES (373,'قال الله عز وجل: (لتجزى كل نفس بما تسعى (١٥)','869','369','0');
INSERT INTO [t9182] VALUES (374,'قال الله عز وجل: (واحلل عقدة من لساني (٢٧) يفقهوا قولي (٢٨)','870','369','0');
INSERT INTO [t9182] VALUES (375,'سورة الأنبياء','872','0','0');
INSERT INTO [t9182] VALUES (376,'قال الله عز وجل: (وكم قصمنا من قرية كانت ظالمة وأنشأنا بعدها قوما آخرين (١١) فلما أحسوا بأسنا إذا هم منها يركضون (١٢)','872','375','0');
INSERT INTO [t9182] VALUES (377,'قال الله عز وجل: (لا يسأل عما يفعل وهم يسألون (٢٣)','872','375','0');
INSERT INTO [t9182] VALUES (378,'قال الله عز وجل: (وإن كان مثقال حبة من خردل أتينا بها وكفى بنا حاسبين (٤٧)','873','375','0');
INSERT INTO [t9182] VALUES (379,'قال الله عز وجل: (ما هذه التماثيل التي أنتم لها عاكفون (٥٢)','874','375','0');
INSERT INTO [t9182] VALUES (380,'قال الله عز وجل: (وتالله لأكيدن أصنامكم بعد أن تولوا مدبرين (٥٧)','874','375','0');
INSERT INTO [t9182] VALUES (381,'قال الله عز وجل: (وداوود وسليمان إذ يحكمان في الحرث إذ نفشت فيه غنم القوم وكنا لحكمهم شاهدين (٧٨) ففهمناها سليمان وكلا آتينا حكما وعلما)','875','375','0');
INSERT INTO [t9182] VALUES (382,'قال الله عز وجل: (وعلمناه صنعة لبوس لكم لتحصنكم من بأسكم فهل أنتم شاكرون (٨٠)','876','375','0');
INSERT INTO [t9182] VALUES (383,'قال الله عز وجل: (وآتيناه أهله ومثلهم معهم)','876','375','0');
INSERT INTO [t9182] VALUES (384,'قال الله عز وجل: (إن الذين سبقت لهم منا الحسنى أولئك عنها مبعدون (١٠١)','877','375','0');
INSERT INTO [t9182] VALUES (385,'قال الله عز وجل: (ولقد كتبنا في الزبور من بعد الذكر أن الأرض يرثها عبادي الصالحون (١٠٥)','878','375','0');
INSERT INTO [t9182] VALUES (386,'سورة الحج','879','0','0');
INSERT INTO [t9182] VALUES (387,'قال الله عز وجل: (والمسجد الحرام الذي جعلناه للناس سواء العاكف فيه والباد)','879','386','0');
INSERT INTO [t9182] VALUES (388,'قال الله عز وجل: (وأذن في الناس بالحج يأتوك رجالا وعلى كل ضامر يأتين من كل فج عميق (٢٧)','881','386','0');
INSERT INTO [t9182] VALUES (389,'قال الله عز وجل: (ويذكروا اسم الله في أيام معلومات على ما رزقهم من بهيمة الأنعام فكلوا منها وأطعموا البائس الفقير (٢٨)','882','386','0');
INSERT INTO [t9182] VALUES (390,'قال الله عز وجل: (ثم ليقضوا تفثهم وليوفوا نذورهم وليطوفوا بالبيت العتيق (٢٩)','886','386','0');
INSERT INTO [t9182] VALUES (391,'قال الله عز وجل: (ذلك ومن يعظم شعائر الله فإنها من تقوى القلوب (٣٢)','888','386','0');
INSERT INTO [t9182] VALUES (392,'قال الله عز وجل: (ثم محلها إلى البيت العتيق (٣٣)','888','386','0');
INSERT INTO [t9182] VALUES (393,'قال الله عز وجل: (والبدن جعلناها لكم من شعائر الله لكم فيها خير)','890','386','0');
INSERT INTO [t9182] VALUES (394,'قال الله - عز وجل -: (أذن للذين يقاتلون بأنهم ظلموا وإن الله على نصرهم لقدير (٣٩) الذين أخرجوا من ديارهم بغير حق)','893','386','0');
INSERT INTO [t9182] VALUES (395,'قال الله عز وجل: (يا أيها الناس ضرب مثل فاستمعوا له إن الذين تدعون من دون الله لن يخلقوا ذبابا ولو اجتمعوا له)','893','386','0');
INSERT INTO [t9182] VALUES (396,'قال الله عز وجل: (يا أيها الذين آمنوا اركعوا واسجدوا واعبدوا ربكم وافعلوا الخير لعلكم تفلحون (٧٧)','894','386','0');
INSERT INTO [t9182] VALUES (397,'قال الله عز وجل: (وجاهدوا في الله حق جهاده)','895','386','0');
INSERT INTO [t9182] VALUES (398,'سورة المؤمنون','897','0','0');
INSERT INTO [t9182] VALUES (399,'قال الله عز وجل: (قد أفلح المؤمنون (١) الذين هم في صلاتهم خاشعون (٢) والذين هم عن اللغو معرضون (٣) والذين هم للزكاة فاعلون (٤)','897','398','0');
INSERT INTO [t9182] VALUES (400,'قال الله عز وجل: (والذين هم لفروجهم حافظون (٥) إلا على أزواجهم أو ما ملكت أيمانهم فإنهم غير ملومين (٦)','898','398','0');
INSERT INTO [t9182] VALUES (401,'قال الله عز وجل: (ولقد أرسلنا نوحا إلى قومه)','902','398','0');
INSERT INTO [t9182] VALUES (402,'سورة النور','903','0','0');
INSERT INTO [t9182] VALUES (403,'قال الله عز وجل: (الزانية والزاني فاجلدوا كل واحد منهما مائة جلدة ولا تأخذكم بهما رأفة في دين الله إن كنتم تؤمنون بالله واليوم الآخر وليشهد عذابهما طائفة م','903','402','0');
INSERT INTO [t9182] VALUES (404,'قال الله عز وجل: (الزاني لا ينكح إلا زانية أو مشركة والزانية لا ينكحها إلا زان أو مشرك وحرم ذلك على المؤمنين (٣)','913','402','0');
INSERT INTO [t9182] VALUES (405,'قال الله عز وجل: (والذين يرمون المحصنات ثم لم يأتوا بأربعة شهداء فاجلدوهم ثمانين جلدة)','917','402','0');
INSERT INTO [t9182] VALUES (406,'قال الله عز وجل: (والذين يرمون أزواجهم ولم يكن لهم شهداء إلا أنفسهم فشهادة أحدهم أربع شهادات بالله إنه لمن الصادقين (٦)','923','402','0');
INSERT INTO [t9182] VALUES (407,'قال الله عز وجل: (لولا جاءوا عليه بأربعة شهداء فإذ لم يأتوا بالشهداء فأولئك عند الله هم الكاذبون (١٣)','936','402','0');
INSERT INTO [t9182] VALUES (408,'قال الله عز وجل: (قل للمؤمنين يغضوا من أبصارهم ويحفظوا فروجهم)','937','402','0');
INSERT INTO [t9182] VALUES (409,'قال الله عز وجل: (وأنكحوا الأيامى منكم والصالحين من عبادكم وإمائكم إن يكونوا فقراء يغنهم الله من فضله والله واسع عليم (٣٢)','938','402','0');
INSERT INTO [t9182] VALUES (410,'قال الله عز وجل: (والذين يبتغون الكتاب مما ملكت أيمانكم فكاتبوهم إن علمتم فيهم خيرا وآتوهم من مال الله الذي آتاكم)','940','402','0');
INSERT INTO [t9182] VALUES (411,'قال الله عز وجل: (ومن لم يجعل الله له نورا فما له من نور (٤٠)','944','402','0');
INSERT INTO [t9182] VALUES (412,'قال الله عز وجل: (وإذا دعوا إلى الله ورسوله ليحكم بينهم إذا فريق منهم معرضون (٤٨) وإن يكن لهم الحق يأتوا إليه مذعنين (٤٩)','945','402','0');
INSERT INTO [t9182] VALUES (413,'قال الله عز وجل: (فإن تولوا فإنما عليه ما حمل وعليكم ما حملتم وإن تطيعوه تهتدوا)','946','402','0');
INSERT INTO [t9182] VALUES (414,'قال الله عز وجل: (وأقيموا الصلاة وآتوا الزكاة وأطيعوا الرسول لعلكم ترحمون (٥٦)','946','402','0');
INSERT INTO [t9182] VALUES (415,'قال الله عز وجل: (وإذا بلغ الأطفال منكم الحلم فليستأذنوا كما استأذن الذين من قبلهم كذلك يبين الله لكم آياته والله عليم حكيم (٥٩)','947','402','0');
INSERT INTO [t9182] VALUES (416,'قال الله عز وجل (والقواعد من النساء اللاتي لا يرجون نكاحا فليس عليهن جناح أن يضعن ثيابهن غير متبرجات بزينة وأن يستعففن خير لهن والله سميع عليم (٦٠)','949','402','0');
INSERT INTO [t9182] VALUES (417,'قال الله عز وجل: (ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج ولا على أنفسكم أن تأكلوا من بيوتكم)','950','402','0');
INSERT INTO [t9182] VALUES (418,'قال الله عز وجل: (إنما المؤمنون الذين آمنوا بالله ورسوله وإذا كانوا معه على أمر جامع لم يذهبوا حتى يستأذنوه)','951','402','0');
INSERT INTO [t9182] VALUES (419,'قال الله عز وجل: (لا تجعلوا دعاء الرسول بينكم كدعاء بعضكم بعضا)','952','402','0');
INSERT INTO [t9182] VALUES (420,'سورة الفرقان','956','0','0');
INSERT INTO [t9182] VALUES (421,'قال الله عز وجل: (تبارك الذي نزل الفرقان على عبده ليكون للعالمين نذيرا (١)','956','420','0');
INSERT INTO [t9182] VALUES (422,'قال الله عز وجل: (وأنزلنا من السماء ماء طهورا (٤٨)','957','420','0');
INSERT INTO [t9182] VALUES (423,'قال الله عز وجل: (فجعله نسبا وصهرا)','959','420','0');
INSERT INTO [t9182] VALUES (424,'قال الله عز وجل: (وتوكل على الحي الذي لا يموت وسبح بحمده وكفى به بذنوب عباده خبيرا (٥٨)','960','420','0');
INSERT INTO [t9182] VALUES (425,'قال الله عز وجل: (والذين لا يدعون مع الله إلها آخر ولا يقتلون النفس التي حرم الله إلا بالحق ولا يزنون ومن يفعل ذلك يلق أثاما (٦٨) يضاعف له العذاب يوم القيامة','960','420','0');
INSERT INTO [t9182] VALUES (426,'قال الله عز وجل: (والذين لا يشهدون الزور وإذا مروا باللغو مروا كراما (٧٢)','962','420','0');
INSERT INTO [t9182] VALUES (427,'سورة الشعراء','963','0','0');
INSERT INTO [t9182] VALUES (428,'قال الله عز وجل: (واتل عليهم نبأ إبراهيم (٦٩) إذ قال لأبيه وقومه ما تعبدون (٧٠) قالوا نعبد أصناما فنظل لها عاكفين (٧١) قال هل يسمعونكم إذ تدعون (٧٢)','963','427','0');
INSERT INTO [t9182] VALUES (429,'قال الله عز وجل: (كذبت قوم لوط المرسلين (١٦٠) إذ قال لهم أخوهم لوط ألا تتقون (١٦١) إني لكم رسول أمين (١٦٢) فاتقوا الله وأطيعون (١٦٣)','963','427','0');
INSERT INTO [t9182] VALUES (430,'قال الله عز وجل: (وإنه لتنزيل رب العالمين (١٩٢) نزل به الروح الأمين (١٩٣) على قلبك لتكون من المنذرين (١٩٤) بلسان عربي مبين (١٩٥)','964','427','0');
INSERT INTO [t9182] VALUES (431,'قال الله عز وجل: (وإنه لفي زبر الأولين (١٩٦)','964','427','0');
INSERT INTO [t9182] VALUES (432,'قال الله عز وجل: (وأنذر عشيرتك الأقربين (٢١٤)','965','427','0');
INSERT INTO [t9182] VALUES (433,'سورة النمل','966','0','0');
INSERT INTO [t9182] VALUES (434,'قال الله عز وجل: (قل لا يعلم من في السماوات والأرض الغيب إلا الله)','966','433','0');
INSERT INTO [t9182] VALUES (435,'سورة القصص','968','0','0');
INSERT INTO [t9182] VALUES (436,'قال الله عز وجل: (قالت إحداهما يا أبت استأجره إن خير من استأجرت القوي الأمين (٢٦)','968','435','0');
INSERT INTO [t9182] VALUES (437,'قال الله عز وجل: (وإذا سمعوا اللغو أعرضوا عنه)','970','435','0');
INSERT INTO [t9182] VALUES (438,'سورة العنكبوت','971','0','0');
INSERT INTO [t9182] VALUES (439,'قال الله عز وجل: (ووصينا الإنسان بوالديه حسنا)','971','438','0');
INSERT INTO [t9182] VALUES (440,'قال الله عز وجل: (ولقد أرسلنا نوحا إلى قومه)','971','438','0');
INSERT INTO [t9182] VALUES (441,'قال الله عز وجل: (وإلى مدين أخاهم شعيبا)','972','438','0');
INSERT INTO [t9182] VALUES (442,'قال الله عز وجل: (أولم يروا أنا جعلنا حرما آمنا ويتخطف الناس من حولهم)','972','438','0');
INSERT INTO [t9182] VALUES (443,'سورة الروم','973','0','0');
INSERT INTO [t9182] VALUES (444,'قال الله عز وجل: (فسبحان الله حين تمسون وحين تصبحون (١٧) وله الحمد في السماوات والأرض وعشيا وحين تظهرون (١٨)','973','443','0');
INSERT INTO [t9182] VALUES (445,'قال الله عز وجل: (ومن آياته أن خلق لكم من أنفسكم أزواجا لتسكنوا إليها)','974','443','0');
INSERT INTO [t9182] VALUES (446,'قال الله عز وجل: (وهو الذي يبدأ الخلق ثم يعيده وهو أهون عليه)','974','443','0');
INSERT INTO [t9182] VALUES (447,'قال الله - عز وجل -: (ومن آياته أن يرسل الرياح مبشرات)','975','443','0');
INSERT INTO [t9182] VALUES (448,'قال الله عز وجل: (فاصبر إن وعد الله حق ولا يستخفنك الذين لا يوقنون (٦٠)','976','443','0');
INSERT INTO [t9182] VALUES (449,'سورة لقمان','977','0','0');
INSERT INTO [t9182] VALUES (450,'قال الله عز وجل: (أن اشكر لي ولوالديك إلي المصير)','977','449','0');
INSERT INTO [t9182] VALUES (451,'قال الله عز وجل: (إن الله عنده علم الساعة وينزل الغيث ويعلم ما في الأرحام)','977','449','0');
INSERT INTO [t9182] VALUES (452,'سورة السجدة','979','0','0');
INSERT INTO [t9182] VALUES (453,'قال الله عز وجل: (الم (١) تنزيل الكتاب لا ريب فيه من رب العالمين (٢)','979','452','0');
INSERT INTO [t9182] VALUES (454,'سورة الأحزاب','981','0','0');
INSERT INTO [t9182] VALUES (455,'قال الله عز وجل: (يا أيها النبي اتق الله ولا تطع الكافرين والمنافقين إن الله كان عليما حكيما (١) واتبع ما يوحى إليك من ربك إن الله كان بما تعملون خبيرا (٢)','981','454','0');
INSERT INTO [t9182] VALUES (456,'قال الله عز وجل: (وما جعل أدعياءكم أبناءكم ذلكم قولكم بأفواهكم والله يقول الحق وهو يهدي السبيل (٤) ادعوهم لآبائهم هو أقسط عند الله','982','454','0');
INSERT INTO [t9182] VALUES (457,'قال الله عز وجل: (النبي أولى بالمؤمنين من أنفسهم وأزواجه أمهاتهم)','986','454','0');
INSERT INTO [t9182] VALUES (458,'قال الله عز وجل: (وإذ يقول المنافقون والذين في قلوبهم مرض ما وعدنا الله ورسوله إلا غرورا (١٢)','988','454','0');
INSERT INTO [t9182] VALUES (459,'قال الله عز وجل (لقد كان لكم في رسول الله أسوة حسنة)','989','454','0');
INSERT INTO [t9182] VALUES (460,'قال الله عز وجل: (وكفى الله المؤمنين القتال وكان الله قويا عزيزا (٢٥)','990','454','0');
INSERT INTO [t9182] VALUES (461,'قال الله عز وجل: (يا أيها النبي قل لأزواجك إن كنتن تردن الحياة الدنيا وزينتها فتعالين أمتعكن وأسرحكن سراحا جميلا (٢٨)','991','454','0');
INSERT INTO [t9182] VALUES (462,'قال الله عز وجل: (يا نساء النبي لستن كأحد من النساء إن اتقيتن)','994','454','0');
