INSERT INTO [t9182] VALUES (465,'قال الله عز وجل: (وما كان لمؤمن ولا مؤمنة إذا قضى الله ورسوله أمرا أن يكون لهم الخيرة)','996','454','0');
INSERT INTO [t9182] VALUES (466,'قال الله عز وجل: (وإذ تقول للذي أنعم الله عليه وأنعمت عليه)','999','454','0');
