INSERT INTO [t9182] VALUES (468,'قال الله عز وجل: (ما كان محمد أبا أحد من رجالكم ولكن رسول الله وخاتم النبيين)','1002','454','0');
INSERT INTO [t9182] VALUES (469,'قال الله عز وجل: (إذا نكحتم المؤمنات ثم طلقتموهن)','1002','454','0');
INSERT INTO [t9182] VALUES (470,'قال الله عز وجل: (يا أيها النبي إنا أحللنا لك أزواجك اللاتي آتيت أجورهن وما ملكت يمينك مما أفاء الله عليك وبنات عمك وبنات عماتك','1008','454','0');
INSERT INTO [t9182] VALUES (471,'قال الله عز وجل: (ترجي من تشاء منهن وتؤوي إليك من تشاء ومن ابتغيت ممن عزلت فلا جناح عليك)','1014','454','0');
INSERT INTO [t9182] VALUES (472,'قال الله عز وجل: (لا يحل لك النساء من بعد ولا أن تبدل بهن من أزواج ولو أعجبك حسنهن إلا ما ملكت يمينك)','1014','454','0');
INSERT INTO [t9182] VALUES (473,'قال الله عز وجل: (وما كان لكم أن تؤذوا رسول الله ولا أن تنكحوا أزواجه من بعده أبدا)','1015','454','0');
INSERT INTO [t9182] VALUES (474,'قال الله عز وجل: (إن الله وملائكته يصلون على النبي يا أيها الذين آمنوا صلوا عليه وسلموا تسليما (٥٦)','1016','454','0');
