INSERT INTO [t9182] VALUES (508,'قال الله عز وجل: (وكذلك أوحينا إليك روحا من أمرنا ما كنت تدري ما الكتاب ولا الإيمان ولكن جعلناه نورا نهدي به من نشاء من عبادنا)','1043','503','0');
