INSERT INTO [t9182] VALUES (523,'قال الله عز وجل: (فإذا لقيتم الذين كفروا فضرب الرقاب حتى إذا أثخنتموهم فشدوا الوثاق)','1056','522','0');
INSERT INTO [t9182] VALUES (524,'قال الله عز وجل: (ذلك بأن الله مولى الذين آمنوا وأن الكافرين لا مولى لهم (١١)','1058','522','0');
