INSERT INTO [t9182] VALUES (538,'قال الله عز وجل: (وإن طائفتان من المؤمنين اقتتلوا فأصلحوا بينهما فإن بغت إحداهما على الأخرى فقاتلوا التي تبغي حتى تفيء إلى أمر الله','1072','534','0');
