INSERT INTO [t9182] VALUES (570,'قال الله عز وجل: (يا أيها الذين آمنوا إذا قيل لكم تفسحوا في المجالس فافسحوا يفسح الله لكم وإذا قيل انشزوا فانشزوا يرفع الله الذين آمنوا منكم والذين أوتوا العلم د','1116','566','0');
