INSERT INTO [t9182] VALUES (572,'قال الله عز وجل: (لا تجد قوما يؤمنون بالله واليوم الآخر يوادون من حاد الله ورسوله ولو كانوا آباءهم أو أبناءهم أو إخوانهم أو عشيرتهم)','1117','566','0');
