INSERT INTO [t9196] VALUES (57,'ومن ذكر أبي سلمى راعي رسول الله صلى الله عليه وسلم قال أبو بكر بن أبي عاصم: يقولون: إن اسم أبي سلمى رضي الله عنه حريث','494','12','0');
