INSERT INTO [t9200] VALUES (92,'ومن أهل الشام والثغور والعواصم أرطاة بن المنذر , وعبد الرحمن بن عمرو الأوزاعي , وسلمة بن عمرو العقيلي , وأبو إسحاق إبراهيم بن محمد الفزاري , ومخلد بن الحسين المصيصي , وعلي بن بكار , ومحمد بن سلمة الحراني , وبقية بن الوليد , والوليد بن مسلم , وضمرة بن','531','38','0');
INSERT INTO [t9200] VALUES (93,'ومن أهل مصر ومن يعد فيهم أبو الحارث الليث بن سعد الفهمي , وعبد الله بن لهيعة , وعمار بن سعد التجيبي , وسعيد بن الحكم بن أبي مريم , وعمرو بن الربيع بن طارق , وأبو الأسود النضر بن عبد الجبار , وأصبغ بن الفرج , وأحمد بن مسلم , وأبو يعقوب يوسف بن يحيى','536','38','0');
INSERT INTO [t9200] VALUES (94,'ومن أهل الري ومن عد فيهم جرير بن عبد الحميد , وأبو جعفر عيسى بن ماهان الرازي , وعمرو بن أبي قيس , وعثمان بن زائدة , ويحيى بن الضريس , وسلمة بن الفضل الأنصاري , وعنبسة بن سعيد قاضي الري , وعبد الله بن أبي جعفر الرازي , وعبد العزيز بن أبي عثمان ختن عثمان','537','38','0');
INSERT INTO [t9200] VALUES (95,'ومن كور الجبال أهل أصبهان عصام بن يزيد خادم الثوري يعرف بجبر , وصالح بن مهران صاحب النعمان بن عبد السلام , وأبو مسعود أحمد بن الفرات الرازي , وعبد الرزاق بن بكر الأصبهاني , وأسيد بن عاصم , وإبراهيم بن بويه , وأحمد بن مهدي , وأحمد بن عصام بن عبد الكبير بن','538','38','0');
INSERT INTO [t9200] VALUES (96,'ذكر أهل خراسان ومن عد فيهم إبراهيم بن طهمان الهروي , وعبد الله بن المبارك المروزي , والفضل بن موسى السيناني , والنضر بن شميل المروزي , والنضر بن محمد المروزي , وأبو ثميلة يحيى بن واضح الأنصاري , وعباد بن راشد المروزي , وخارجة بن مصعب السرخسي , وسهل بن','539','38','0');
INSERT INTO [t9200] VALUES (97,'جماعة من البلخيين عمر بن هارون البلخي , والحسين بن سليمان , وأبو مطيع , ومقاتل بن الفضل , ومسافر بن ماهان , وابن الرماح قاضي بلخ , والليث بن مساور , وإبراهيم بن يوسف البلخي , وابنه عبد الرحمن , وسعد بن معاذ المروزي , وحفص بن عبد الرحمن , وشداد بن حكمي ,','540','38','0');
INSERT INTO [t9200] VALUES (98,'أهل نيسابور وبخارى وسمرقند وغيرهم يحيى بن يحيى النيسابوري , وأحمد بن النضر النيسابوري , ومحمد بن يحيى الذهلي , ومحمد بن رافع النيسابوري , وأحمد بن سعيد الرازي , ومحمد بن عقيل النيسابوري , وأحمد بن سعيد الدارمي , ويحيى بن محمد بن يحيى الذهلي , ومحمد بن','541','38','0');
