INSERT INTO [t9200] VALUES (101,'ومن الخلفاء أبو جعفر المنصور , ومعتمر بن سليمان التيمي , ويحيى بن سعيد القطان , وعبد الرحمن بن مهدي , ومعاذ بن معاذ , ووكيع بن الجراح , ووالده , وعبد الله بن داود الخريبي , وعلي بن عاصم , وشبابة بن سوار , وأبو النضر هاشم بن القاسم , وحماد بن مسعدة ,','546','38','0');
