INSERT INTO [t9200] VALUES (107,'أبو عبيد القاسم بن سلام:','562','38','0');
INSERT INTO [t9200] VALUES (108,'من قال إنه لا يرث ولا يورث: يحيى بن سعيد القطان , وعبد الرحمن بن مهدي , ومحمد بن مقاتل العباداني , ومحمد بن أبي صفوان , ومحمد بن جرير الطبري.','566','38','0');
INSERT INTO [t9200] VALUES (109,'ومن قال امرأته طالق:','569','38','0');
INSERT INTO [t9200] VALUES (110,'من قال: لا ينكحون , ولا يصلى خلفهم , ولا تعاد مرضاهم , ولا تشهد جنائزهم , وإن موالاة الإسلام انقطعت بينهم وبين المسلمين وروي عن سلام بن أبي مطيع , وحماد بن زيد , وسفيان بن عيينة , وسفيان الثوري , وأبي ضمرة أنس بن عياض , وأبي معاوية الضرير , ويزيد بن','572','38','0');
INSERT INTO [t9200] VALUES (111,'سياق ما روي في تكفير من وقف في القرآن شاكا فيه أنه غير مخلوق','577','38','0');
INSERT INTO [t9200] VALUES (112,'فروى عن أهل المدينة هارون بن أبي علقمة الفروي قال: سمعت عبد الملك بن عبد العزيز الماجشون وغيره من علمائنا يقولون: من وقف في القرآن بالشك فهو كافر. قال: وسمعت عبد الملك خاصة يقول: من وقف في القرآن بالشك فهو مثل من قال مخلوق. وأبي مصعب أحمد بن أبي','578','38','0');
INSERT INTO [t9200] VALUES (113,'ومن أهل الكوفة وكيع بن الجراح فيما روى عنه يحيى بن يحيى النيسابوري: من شك أن القرآن كلام الله يعني غير مخلوق فهو كافر. وعن أبي بكر بن أبي شيبة وأخيه عثمان , والحسين بن علي بن الأسود , وأبي هشام الرفاعي , وأبي سعيد الأشج , وإسحاق بن موسى الخطمي , ومحمد','579','38','0');
INSERT INTO [t9200] VALUES (114,'ومن أهل بغداد ومن عد فيهم عبيد الله بن عمر القواريري , ويحيى بن أيوب , وداود بن رشيد , وسويد بن سعيد الأنباري , وأحمد بن حنبل , ويحيى بن معين , وأبو خيثمة زهير بن حرب , وأبو معمر إسماعيل بن إبراهيم , وأبو ثور إبراهيم بن خالد الكلبي , ويعقوب بن إبراهيم','580','38','0');
INSERT INTO [t9200] VALUES (115,'ومن أهل مصر ومن عد فيهم نعيم بن حماد المروزي , وأحمد بن صالح المصري , ومؤمل بن إهاب الربعي المكي نزيل مصر , وأبو عبيد الله أحمد بن عبد الرحمن ابن أخي عبد الله بن وهب , والربيع بن سليمان المرادي المصري','581','38','0');
INSERT INTO [t9200] VALUES (116,'ومن أهل الشام هشام بن عمار , والمسيب بن واضح , ومحمد بن خلف العسقلاني , والقاسم بن عثمان الجوعي , وإبراهيم بن يعقوب الجوزجاني نزيل دمشق.','582','38','0');
INSERT INTO [t9200] VALUES (117,'ومن أهل الجزيرة والثغور حامد بن يحيى البلخي , وأبو بكر محمد بن يزيد الأسلمي الطرسوسي , وإبراهيم بن سعيد الجوهري البغدادي نزيل الري , وسعيد بن رحمة المصيصي , وأحمد بن حرب الموصلي , ومحمد بن أيوب الأصبهاني , ومحمد بن جبلة الرافقي , وزرقان بن محمد البغدادي','583','38','0');
INSERT INTO [t9200] VALUES (118,'ومن أهل خراسان: إسحاق بن إبراهيم بن مخلد المعروف بابن راهويه أنه سئل عن الرجل يقول: القرآن كلام الله ويقف , قال: هو عندي شر من الذي يقول مخلوق؛ لأنه يقتدي به غيره. فيما روى عنه حرب بن إسماعيل الكرماني , وفيما روى عنه أحمد بن سلمة: ومن وقف فهو كذا.','584','38','0');
INSERT INTO [t9200] VALUES (119,'سياق ما دل من الآيات من كتاب الله تعالى وما روي عن رسول الله صلى الله عليه وسلم والصحابة والتابعين على أن القرآن تكلم الله به على الحقيقة , وأنه أنزله على محمد صلى الله عليه وسلم , وأمره أن يتحدى به , وأن يدعو الناس إليه , وأنه القرآن على الحقيقة. متلو','588','38','0');
INSERT INTO [t9200] VALUES (120,'سياق ما روي في تكفير من قال: لفظي بالقرآن مخلوق روي ذلك عن الأئمة. عن محمد بن إدريس الشافعي , وأبي مصعب أحمد بن أبي بكر الزهري , وأحمد , وإسحاق , وأبي عبيد , وأبي ثور , وسويد بن سعيد , وأبي همام الوليد بن شجاع , ومحمد بن يحيى بن أبي عمر العدني , وهارون','620','38','0');
INSERT INTO [t9200] VALUES (121,'ومن أهل البصرة محمد بن بشار , وعمرو بن علي , ومحمد بن المثنى , ومحمد بن يحيى بن أبي حزم القطعي , والعباس بن عبد العظيم العنبري , وأحمد بن سنان الواسطي , ومحمد بن عبادة بن البختري','622','38','0');
INSERT INTO [t9200] VALUES (122,'ومن أهل الكوفة أبو سعيد الأشج , وهارون بن إسحاق الهمداني','623','38','0');
INSERT INTO [t9200] VALUES (123,'ومن أهل مصر والعواصم والثغور أحمد بن صالح المصري , والمؤمل بن إهاب الربعي المكي نزيل مصر , ومحمد بن سليمان بن حبيب الأسدي المعروف بلوين , وإبراهيم بن سعيد الجوهري نزيل ثغر , وميمون بن الأصبغ النصيبي , وسعيد بن رحمة بن نعيم المصيصي , وأحمد بن حرب الموصلي','624','38','0');
INSERT INTO [t9200] VALUES (124,'ومن أهل خراسان عن محمد بن أسلم الطوسي: إن من قال: إن القرآن يكون مخلوقا بالألفاظ , فقد زعم أن القرآن مخلوق. وعن محمد بن يحيى الذهلي مثله , وقال: هو مبتدع. وأمر بمباينته ومجانبته. وعن علي بن خشرم المروزي: من قال: القرآن بلفظي أو لفظي بالقرآن أو','625','38','0');
