INSERT INTO [t9200] VALUES (128,'قول إسحاق بن راهويه','633','38','0');
INSERT INTO [t9200] VALUES (129,'قول أبي عبيد القاسم بن سلام ذكره عبد الرحمن بن أبي حاتم قال: ثنا عبد الله بن محمد بن إبرهيم السلمي بالكوفة قال: قال أبو عبيد القاسم بن سلام: لو أن رجلا حلف فقال: والله لا تكلمت اليوم بشيء , فقرأ القرآن في غير صلاة أو في صلاة لم يحنث؛ لأن أيمان الناس','635','38','0');
INSERT INTO [t9200] VALUES (130,'قول أبي مصعب أحمد بن أبي بكر الزهري ذكره عبد الرحمن بن أبي حاتم قال: ثنا أبو بكر عبد الله بن محمد بن الفضل الأسدي الصيداوي قال: أتى قوم أبا مصعب الزهري المديني فقالوا: إن قبلنا ببغداد رجلا يقول: لفظه بالقرآن مخلوق. فقال: يا أهل العراق , ما يأتينا','637','38','0');
INSERT INTO [t9200] VALUES (131,'قول محمد بن إسماعيل البخاري','638','38','0');
INSERT INTO [t9200] VALUES (132,'سياق ما روي عن النبي صلى الله عليه وسلم في أن من رآه في النوم فقد رأى الحق وأن الشيطان لا يتمثل به. وفي من رآه وسأله عن القرآن فأجاب بأنه غير مخلوق من العلماء والصالحين','644','38','0');
INSERT INTO [t9200] VALUES (133,'سياق ما رئي من الرؤيا السوء لمن قال بخلق القرآن في الدنيا , وما أعد الله له في الآخر أكثر','652','38','0');
INSERT INTO [t9200] VALUES (134,'متى حدث القول بخلق القرآن في الإسلام، ومن أول من قاله:','657','38','0');
INSERT INTO [t9200] VALUES (135,'أخبار الجعد بن درهم لعنه الله','669','38','0');
INSERT INTO [t9200] VALUES (136,'سياق ما روي في قوله تعالى الرحمن على العرش استوى وأن الله على عرشه في السماء وقال: عز وجل إليه يصعد الكلم الطيب والعمل الصالح يرفعه وقال: أأمنتم من في السماء أن يخسف بكم الأرض وقال: وهو القاهر فوق عباده ويرسل عليكم حفظة فدلت هذه الآيات أنه تعالى في','676','38','0');
