INSERT INTO [t9200] VALUES (138,'قول عمر','686','38','0');
INSERT INTO [t9200] VALUES (139,'سياق ما دل من كتاب الله وما روي عن النبي صلى الله عليه وسلم , في أن الله عالم بعلم وأن علمه غير مخلوق قال الله عز وجل فلنقصن عليهم بعلم وما كنا غائبين وقال ويعلم ما تفعلون وقال ولا يحيطون بشيء من علمه وقال بما أنزل إليك أنزله بعلمه وقال: فاعلموا أنما','706','38','0');
INSERT INTO [t9200] VALUES (140,'سياق ما دل من كتاب الله تعالى وسنة رسول الله صلى الله عليه وسلم , بأن الله سميع بسمع , بصير ببصر , قادر بقدره قال الله , عز وجل والله سميع عليم وقال تبارك وتعالى لم تعبد ما لا يسمع ولا يبصر ولا يغني عنك شيئا وقال في قصة موسى إنني معكما أسمع وأرى وقال عز','713','38','0');
INSERT INTO [t9200] VALUES (141,'سياق ما دل من كتاب الله عز وجل وسنة رسوله صلى الله عليه وسلم على أن من صفات الله عز وجل: الوجه والعينين واليدين قال الله عز وجل ويبقى وجه ربك ذو الجلال والإكرام وقال كل شيء هالك إلا وجهه له الحكم وإليه ترجعون وقال خلقت بيدي وقال بل يداه مبسوطتان ينفق','721','38','0');
INSERT INTO [t9200] VALUES (142,'سياق ما روي عن النبي صلى الله عليه وسلم في نزول الرب تبارك وتعالى رواه عن النبي صلى الله عليه وسلم عشرون نفسا، وروى ذلك من الصحابة: عن ابن مسعود، وابن عباس، وأم سلمة، ومن التابعين: عطاء، وعمر بن عبد العزيز، ومكحول، وكعب الأحبار','764','38','0');
