INSERT INTO [t9200] VALUES (246,'وفي قوله تبارك وتعالى: سيقول الذين أشركوا لو شاء الله ما أشركنا ولا آباؤنا وقوله: لو شاء الله ما أشركنا ولا آباؤنا، وقوله ولو شاء الله لجمعهم على الهدى','1067','38','0');
INSERT INTO [t9200] VALUES (247,'وقوله: ما نعبدهم إلا ليقربونا إلى الله زلفى وقوله: ولو شاء الله ما أشركوا. يقول جل ثناؤه: ولو شاء الله لجمعهم على الهدى','1069','38','0');
INSERT INTO [t9200] VALUES (248,'قوله: فمن شاء فليؤمن ومن شاء فليكفر','1071','38','0');
