INSERT INTO [t9200] VALUES (336,'سياق ما روي في نبوة النبي صلى الله عليه وسلم متى كانت، وبما عرفت من العلامات','1534','335','0');
INSERT INTO [t9200] VALUES (337,'سياق ما روى النبي صلى الله عليه وسلم في ابتداء الوحي، وصفته، وأنه بعث وأنزل إليه وله أربعون سنة','1538','335','0');
INSERT INTO [t9200] VALUES (338,'سياق ما روي من فضائل النبي صلى الله عليه وسلم التي خصه الله بها من بين سائر الأنبياء فمنها أوتي جوامع الكلم، وهي القرآن وبعث إلى الناس عامة، وكان النبي يبعث إلى قومه، ونصر بأن يرعب عدوه منه على مسيرة شهر، وختم به النبيون فلا نبي بعده، وأعطي الشفاعة','1561','335','0');
INSERT INTO [t9200] VALUES (339,'سياق ما روي في معجزات النبي صلى الله عليه وسلم مما يدل على صدقه، وخرق الله العادة الجارية؛ لوضوح دلالته وإثبات نبوته، ونفي الشك والارتياب في أمره','1573','335','0');
