INSERT INTO [t9200] VALUES (478,'سياق ما روي عن النبي صلى الله عليه وسلم في تقديم التوبة عن المعاصي، واستحلال بعضهم بعضا قبل نزول الموت من مال، أو عرض، أو دم','2107','352','0');
