INSERT INTO [t9200] VALUES (480,'سياق ما روي في أن القاتل عمدا له توبة وتفسير قوله تعالى: ومن يقتل مؤمنا متعمدا فجزاؤه جهنم خالدا فيها، وأنها منسوخة بقوله إن الله لا يغفر أن يشرك به وروي ذلك عن عمر وابن عمر، وعبد الله بن عمرو بن العاص، وإحدى الروايتين عن ابن عباس، ومن التابعين','2129','352','0');
INSERT INTO [t9200] VALUES (481,'عمر','2132','352','0');
INSERT INTO [t9200] VALUES (482,'ابن عباس','2134','352','0');
INSERT INTO [t9200] VALUES (483,'سياق ما روي عن النبي صلى الله عليه وسلم في أن المسلمين لا تضرهم الذنوب التي هي الكبائر إذا ماتوا عن توبة من غير إصرار، ولا يوجب التكفير وإن ماتوا عن غير توبة، فأمرهم إلى الله عز وجل إن شاء عذبهم، وإن شاء غفر لهم وعن أبي سفيان، قلت لجابر: " كنتم','2143','352','0');
INSERT INTO [t9200] VALUES (484,'سياق ما روي عن النبي صلى الله عليه وسلم في جواز الكذب للإصلاح بين الزوجين والناس، وفي الحرب، وأنه ليس بقبيح لنفسه، وإنما هو من جهة السمع قبيح','2200','352','0');
INSERT INTO [t9200] VALUES (485,'باب الشفاعة لأهل الكبائر','2206','0','0');
INSERT INTO [t9200] VALUES (486,'سياق ما روي عن النبي صلى الله عليه وسلم في الشفاعة لأمته، وأن أهل الكبائر إذا ماتوا عن غير توبة يدخلهم الله إن شاء النار، ثم يخرجهم منها بفضل رحمته، ويدخلهم الجنة وقد مضى في حديث جابر وغيره في فضائل النبي صلى الله عليه وسلم " أعطيت خمسا لم يعطهن نبي','2207','485','0');
