INSERT INTO [t9200] VALUES (509,'سياق ما روي عن النبي صلى الله عليه وسلم في أن المسلمين إذا دلوا في حفرتهم يسألهم منكر، ونكير، وأن عذاب القبر حق، والإيمان به واجب','2289','485','0');
INSERT INTO [t9200] VALUES (510,'سياق ما روي بما أرى الله أو أسمع من عذاب القبر في الصحابة والتابعين، ومن بعدهم؛ ليزدادوا إيمانا وعلى ربهم يتوكلون','2311','485','0');
INSERT INTO [t9200] VALUES (511,'سياق ما روي عن النبي صلى الله عليه وسلم في أن أرواح المؤمنين في حواصل طير خضر تعلق في شجر الجنة حتى يردها الله إلى أجسادهم','2324','485','0');
INSERT INTO [t9200] VALUES (512,'سياق ما روي عن النبي صلى الله عليه وسلم في استحباب الصدقة، وقراءة القرآن، والاستغفار، والترحم، والدعاء للميت، وأنه ينفعه ذلك ويخفف عنه','2331','485','0');
INSERT INTO [t9200] VALUES (513,'سياق ما روي عن النبي صلى الله عليه وسلم في أن الموتى في قبورهم لا يعلمون مما عليه الأحياء إلا إذا رد الله عليهم الأرواح، قال الله تبارك وتعالى: وما أنت بمسمع من في القبور','2343','485','0');
INSERT INTO [t9200] VALUES (514,'باب جماع وجوب الإيمان بالجنة والنار، والبعث بعد الموت، والميزان، والحساب والصراط يوم القيامة','2346','0','0');
INSERT INTO [t9200] VALUES (515,'سياق ما روي عن النبي صلى الله عليه وسلم في الصور، والحشر، والنشر','2348','514','0');
INSERT INTO [t9200] VALUES (516,'سياق ما روي عن النبي صلى الله عليه وسلم في العرض والحساب يوم القيامة','2356','514','0');
INSERT INTO [t9200] VALUES (517,'سياق ما روي عن النبي صلى الله عليه وسلم في أن اليهود والنصارى إذا ماتوا على غير ملة الإسلام يدخلون النار قال الله عز وجل: ومن يكفر به من الأحزاب فالنار موعده، فروي عن سعيد بن جبير، وقتادة أن " الهاء " راجع إلى اليهود والنصارى. وعن السدي: ومن يكفر به','2367','514','0');
INSERT INTO [t9200] VALUES (518,'سياق ما روي في أن الإيمان بأن الحسنات والسيئات توزن بالميزان واجب قال الله عز وجل: ونضع الموازين القسط ليوم القيامة فلا تظلم نفس شيئا وإن كان مثقال حبة من خردل أتينا بها وكفى بنا حاسبين، وقال تعالى: فمن ثقلت موازينه فأولئك هم المفلحون، ومن خفت','2369','514','0');
INSERT INTO [t9200] VALUES (519,'سياق ما روي عن النبي صلى الله عليه وسلم مما يدل على أن الكفار لا يحاسبون روي ذلك من الصحابة، عن عائشة','2378','514','0');
