INSERT INTO [t9200] VALUES (525,'سياق ما روي في أن السحر له حقيقة قال الله عز وجل: ولكن الشياطين كفروا يعلمون الناس السحر، وقال: فلما جاء السحرة، وقال: وجاءوا بسحر عظيم، وعن عمر، وعثمان، وجندب، وعائشة، وحفصة، أنهم أمروا بقتل الساحر','2427','514','0');
INSERT INTO [t9200] VALUES (526,'سياق ما روي في كيف السحر','2435','514','0');
INSERT INTO [t9200] VALUES (527,'سياق ما روي عن النبي صلى الله عليه وسلم في أن إبليس خلق من خلق الله يرون من يريهم الله لا كما زعمت المبتدعة أن الجن لا حقيقة لهم، وأن إبليس كل رجل سوء','2437','514','0');
INSERT INTO [t9200] VALUES (528,'سياق ما روي عن النبي صلى الله عليه وسلم في خروج الدجال والإيمان به خلاف ما قالت المبتدعة: إن الدجال كل رجل خبيث','2442','514','0');
