INSERT INTO [t9200] VALUES (530,'سياق ما روي عن النبي صلى الله عليه وسلم في الخوارج','2465','514','0');
INSERT INTO [t9200] VALUES (531,'سياق ما دل من كتاب الله وسنة نبيه صلى الله عليه وسلم في أن بني آدم خير من الملائكة قال الله عز وجل: وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس أبى واستكبر وكان من الكافرين، وقال تعالى: الذين يحملون العرش ومن حوله يسبحون بحمد ربهم ويؤمنون به','2475','514','0');
