INSERT INTO [t9292] VALUES (1,'باب قوله عز وجل " ولا يشفعون إلا لمن ارتضى وهم من خشيته مشفقون مع سائر ما يحتج به من أنكر الشفاعة "','1','0','0');
INSERT INTO [t9292] VALUES (2,'باب قول الله عز وجل " إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء هذا البيان أن المراد والله أعلم أنه يغفر لمن يشاء ذنبه - الذي هو دون الشرك - فلا يعاقبه عليه، ولا يغفره لمن يشاء ويعاقبه عليه، ثم يكون عاقبته الجنة ولا يخلد في النار من وافى','20','0','0');
INSERT INTO [t9292] VALUES (3,'باب قول الله عز وجل " ثم أورثنا الكتاب الذين اصطفينا من عبادنا فمنهم ظالم لنفسه ومنهم مقتصد ومنهم سابق بالخيرات بإذن الله "','57','0','0');
INSERT INTO [t9292] VALUES (4,'باب قول الله عز وجل ربما يود الذين كفروا لو كانوا مسلمين وقوله: يومئذ يود الذين كفروا وعصوا الرسول لو تسوى بهم الأرض ولا يكتمون الله حديثا "','73','0','0');
INSERT INTO [t9292] VALUES (5,'باب ما جاء في المؤمن يفدى بالكافر، فيقال هذا فداؤك من النار، والكافر لا يؤخذ منه فدية ولا تنفعه شفاعة','84','0','0');
INSERT INTO [t9292] VALUES (6,'باب ما جاء في آخر من يخرج من النار، ويدخل الجنة','94','0','0');
INSERT INTO [t9292] VALUES (7,'باب ما جاء في أصحاب الأعراف قال الله عز وجل وبينهما حجاب، وعلى الأعراف رجال يعني على السور رجال يعرفون كلا بسيماهم الآية وبينهما حجاب يقول: " بين الجنة، والنار سور "','99','0','0');
INSERT INTO [t9292] VALUES (8,'باب ما جاء في حوض النبي صلى الله عليه وسلم قال الله عز وجل: " إنا أعطيناك الكوثر','112','0','0');
INSERT INTO [t9292] VALUES (9,'جماع أبواب الإيمان بالجنة والنار , وإنهما مخلوقتان معدتان لأهلهما وما جاء فيهما وفي صفتهما','159','0','0');
INSERT INTO [t9292] VALUES (10,'باب الإيمان بالجنة والنار','160','0','0');
INSERT INTO [t9292] VALUES (11,'باب ما يستدل به على أن الجنة , والنار قد خلقتا , وأعدتا لأهلهما فنسأل الله الجنة , ونعوذ به من النار , قال الله عز وجل: وسارعوا إلى مغفرة من ربكم وجنة عرضها الآية , فوصف عرضها , والعرض لا يكون إلا لمخلوق فأما المعدوم , فلا عرض له , وأخبر بأنها أعدت','162','0','0');
INSERT INTO [t9292] VALUES (12,'باب قول الله عز وجل وقلنا يا آدم اسكن أنت وزوجك الجنة، وكلا منها رغدا حيث شئتما، ولا تقربا هذه الشجرة فتكونا من الظالمين " إلى قوله إلى حين " وقوله في سورة الأعراف ويا آدم اسكن أنت وزوجك الجنة " إلى قوله كما أخرج أبويكم من الجنة، وقال في سورة طه فقلنا','176','0','0');
INSERT INTO [t9292] VALUES (13,'باب ما يستدل به على أن النبي صلى الله عليه وسلم رأى الجنة، والنار ورأى في كل واحدة منهما بعض أهلها، وما أعد لبعض أهلها، والمعدوم لا يرى وأخبر عن مصير أرواح أهلها إليها قبل القيامة، وغير ذلك مما يستدل به على خلقهما. قال الله عز وجل: ولقد رآه نزلة','183','0','0');
INSERT INTO [t9292] VALUES (14,'باب ما ورد في عدد الجنان قال الله عز وجل ولمن خاف مقام ربه جنتان فبأي آلاء ربكما تكذبان ذواتا أفنان فبأي آلاء ربكما تكذبان "، وكتب إلى آخر السورة، فذكر الله سبحانه في هذه الآيات أربع جنات، وأشار إلى الفردوس الأولتين اللتين لمن خاف مقام ربه، واللتين','218','0','0');
INSERT INTO [t9292] VALUES (15,'باب ما ورد في أبواب الجنة، وما يقال لأهلها عند دخولهم، وما يقولون قال الله عز وجل: وسيق الذين اتقوا ربهم إلى الجنة زمرا " قرأها إلى قوله: فنعم أجر العاملين "، وقال: ونزعنا ما في صدورهم من غل تجري من تحتهم الأنهار، وقالوا الحمد لله الذي هدانا لهذا','231','0','0');
INSERT INTO [t9292] VALUES (16,'باب ما جاء في غرف الجنة قال الله تعالى: وهم في الغرفات آمنون "، وقال: يجزون الغرفة بما صبروا "، وقال: لكن الذين اتقوا ربهم لهم غرف من فوقها غرف مبنية تجري من تحتها الأنهار وعد الله لا يخلف الله الميعاد "','253','0','0');
INSERT INTO [t9292] VALUES (17,'باب ما جاء في حائط الجنة، وترابها وحصائها','262','0','0');
INSERT INTO [t9292] VALUES (18,'باب ما جاء في أشجار الجنة، وأنهارها، وثمارها، وظلالها أخبرنا الشيخ الفقيه أبو عبد الله محمد بن الفضل بن أحمد الفراوي قراءة عليه قال: أنبأ أبو بكر أحمد بن الحسين بن علي البيهقي قال: قال الله عز وجل: إن المتقين في جنات ونهر في مقعد صدق عند مليك مقتدر','269','0','0');
INSERT INTO [t9292] VALUES (19,'باب ما جاء في لباس أهل الجنة، وفرشهم، وسررهم، وأرائكهم، وخيامهم، وأكوابهم، وغير ذلك قال الله تعالى: جنات عدن يدخلونها يحلون فيها من أساور من ذهب ولؤلؤا ولباسهم فيها حرير "، وقال: يحلون فيها من أساور من ذهب ويلبسون ثيابا خضرا من سندس وإستبرق','299','0','0');
INSERT INTO [t9292] VALUES (20,'باب ما جاء في طعام أهل الجنة، وشرابهم، وفاكهتهم، وما ترجع إليه أطعمتهم قال الله عز وجل: وبشر الذين آمنوا وعملوا الصالحات أن لهم جنات تجري من تحتها الأنهار كلما رزقوا منها من ثمرة رزقا، قالوا: هذا الذي رزقنا من قبل، وأتوا به متشابها، وقال: أولئك','323','0','0');
INSERT INTO [t9292] VALUES (21,'باب ما جاء في صفة حور العين، والولدان، والغلمان قال الله عز وجل فيهن قاصرات الطرف لم يطمثهن إنس قبلهم، ولا جان إلى قوله: كأنهن الياقوت والمرجان، وقال: حور مقصورات في الخيام، وقال: وعندهم قاصرات الطرف أتراب، وقال: وزوجناهم بحور عين، وقال: وحور','344','0','0');
INSERT INTO [t9292] VALUES (22,'باب ما جاء في سوق أهل الجنة','387','0','0');
INSERT INTO [t9292] VALUES (23,'باب السماع في الجنة، والتغني بذكر الله عز وجل قال الله عز وجل: فأما الذين آمنوا وعملوا الصالحات فهم في روضة يحبرون','391','0','0');
INSERT INTO [t9292] VALUES (24,'باب قول الله عز وجل: ادخلوا الجنة أنتم وأزواجكم تحبرون، إلى قوله: فاكهة كثيرة منها تأكلون، وقوله: فلا تعلم نفس ما أخفي لهم من قرة أعين جزاء بما كانوا يعملون، الآية، وقوله: وإذا رأيت ثم رأيت نعيما وملكا كبيرا','402','0','0');
INSERT INTO [t9292] VALUES (25,'باب أول من يدخل، وما جاء في صفة أهل الجنة','418','0','0');
INSERT INTO [t9292] VALUES (26,'باب آخر من يدخل الجنة، ومن يكون أدنى من أهل الجنة منزلة، ومن يكون منهم أرفع منزلة','439','0','0');
INSERT INTO [t9292] VALUES (27,'باب قول الله عز وجل: لا يذوقون فيها الموت إلا الموتة الأولى','449','0','0');
INSERT INTO [t9292] VALUES (28,'باب قول الله عز وجل: للذين اتقوا عند ربهم جنات تجري من تحتها الأنهار خالدين فيها وأزواج مطهرة ورضوان من الله والله بصير بالعباد','457','0','0');
INSERT INTO [t9292] VALUES (29,'باب قول الله عز وجل: للذين أحسنوا الحسنى وزيادة','459','0','0');
INSERT INTO [t9292] VALUES (30,'باب ما جاء في موضع الجنة وموضع النار','463','0','0');
INSERT INTO [t9292] VALUES (31,'باب ما جاء في عدد أبواب جهنم قال الله تعالى: إن عبادي ليس لك عليهم سلطان إلا من اتبعك من الغاوين وإن جهنم لموعدهم أجمعين لها سبعة أبواب لكل باب منهم جزء مقسوم','473','0','0');
INSERT INTO [t9292] VALUES (32,'باب ما جاء في خزنة جهنم','478','0','0');
INSERT INTO [t9292] VALUES (33,'باب ما جاء في أودية جهنم','481','0','0');
INSERT INTO [t9292] VALUES (34,'باب ما جاء في قعر جهنم ودركاتها، وتفاوت أهلها في عذابها، وما ورد في أهونهم عذابا قال الله عز وجل: إن المنافقين في الدرك الأسفل من النار، وقال: سأرهقه صعودا','499','0','0');
INSERT INTO [t9292] VALUES (35,'باب ما جاء في شدة حر جهنم، وما جاء في وقود نارها وشدة برد زمهريرها قال الله عز وجل: قل نار جهنم أشد حرا لو كانوا يفقهون، وقوله: كلما خبت زدناهم سعيرا، وقال: فاتقوا النار التي وقودها الناس والحجارة أعدت للكافرين','515','0','0');
INSERT INTO [t9292] VALUES (36,'باب ما جاء في ثياب أهل النار وسلاسلهم وأغلالهم، وما يصب عليهم من الحميم، ويقمعون بمقامع من حديد قال الله عز وجل: فالذين كفروا قطعت لهم ثياب من نار يصب من فوق رءوسهم الحميم يصهر به ما في بطونهم والجلود ولهم مقامع من حديد كلما أرادوا أن يخرجوا منها من','547','0','0');
INSERT INTO [t9292] VALUES (37,'باب ما جاء في طعام أهل النار وشرابهم قال الله عز وجل: إن شجرة الزقوم، طعام الأثيم , كالمهل يغلي في البطون، كغلي الحميم، وقال: ثم إنكم أيها الضالون المكذبون، لآكلون من شجر من زقوم، فمالئون منها البطون، فشاربون عليه من الحميم، فشاربون شرب الهيم،','566','0','0');
INSERT INTO [t9292] VALUES (38,'باب ما جاء في حيات جهنم وعقاربها قال الله عز وجل: سيطوقون ما بخلوا به يوم القيامة، وقال: زدناهم عذابا فوق العذاب','582','0','0');
INSERT INTO [t9292] VALUES (39,'باب قول الله عز وجل: إن الذين كفروا بآياتنا سوف نصليهم نارا كلما نضجت جلودهم بدلناهم جلودا غيرها ليذوقوا العذاب، وما ورد في غلظ جلد الكافر وعظم نفسه في النار وقوله عز وجل: كلما أرادوا أن يخرجوا منها أعيدوا فيها الآية: وقوله: كلما أرادوا أن يخرجوا','589','0','0');
INSERT INTO [t9292] VALUES (40,'باب قول الله عز وجل في المجرمين: ونادوا يا مالك ليقض علينا ربك قال إنكم ماكثون وقوله: ويأتيه الموت من كل مكان وما هو بميت ومن ورائه عذاب غليظ، والبيان أن أهل النار من الكفار لا يموتون، وهم فيها خالدون، والبيان أن أهل الجنة من المسلمين لا يموتون فيها','611','0','0');
INSERT INTO [t9292] VALUES (41,'باب دعاء أهل النار بالويل والثبور والزفير والشهيق ونكالهم قال الله عز وجل: وأعتدنا لمن كذب بالساعة سعيرا إذا رأتهم من مكان بعيد سمعوا لها تغيظا وزفيرا وإذا ألقوا منها مكانا ضيقا مقرنين دعوا هنالك ثبورا لا تدعوا اليوم ثبورا واحدا وادعوا ثبورا كثيرا،','617','0','0');
INSERT INTO [t9292] VALUES (42,'باب قول الله عز وجل: يوم يأت لا تكلم نفس إلا بإذنه فمنهم شقي وسعيد فأما الذين شقوا ففي النار لهم فيها زفير وشهيق، خالدين فيها ما دامت السموات والأرض إلا ما شاء ربك إن ربك فعال لما يريد، وأما الذين سعدوا ففي الجنة خالدين فيها ما دامت السموات والأرض إلا','632','0','0');
INSERT INTO [t9292] VALUES (43,'حديث الصور','637','0','0');
