INSERT INTO [t9307] VALUES (1,'انتظار الفرج من الله عز وجل عبادة، ومن رضي بالقليل من الرزق رضي الله عز وجل منه بالقليل','1','0','0');
INSERT INTO [t9307] VALUES (2,'سلوا الله عز وجل من فضله، فإن الله عز وجل يحب أن يسأل من فضله، وأفضل العبادة انتظار','2','0','0');
INSERT INTO [t9307] VALUES (3,'لم تعطوا عطاء خيرا ولا أوسع من الصبر','3','0','0');
INSERT INTO [t9307] VALUES (4,'المخرج من كل ما ضاق على الناس','4','0','0');
INSERT INTO [t9307] VALUES (5,'من شأنه أن يغفر ذنبا، ويكشف كربا، ويرفع قوما، ويضع آخرين','5','0','0');
INSERT INTO [t9307] VALUES (6,'احفظ الله يحفظك، احفظه تجده تجاهك، إذا سألت فسل الله، وإذا استعنت فاستعن بالله، جفت','6','0','0');
INSERT INTO [t9307] VALUES (7,'احفظ الله يحفظك، احفظ الله تجده أمامك، تعرف إلى الله في الرخاء يعرفك في الشدة، إذا سألت','7','0','0');
INSERT INTO [t9307] VALUES (8,'من أكثر من الاستغفار جعل الله له من كل هم فرجا، ومن كل ضيق مخرجا، ورزقه من حيث لا','8','0','0');
INSERT INTO [t9307] VALUES (9,'لو أن الناس كلهم أخذوا بها لكفتهم','9','0','0');
INSERT INTO [t9307] VALUES (10,'بني فلان أغاروا علي، فذهبوا بإبلي وابني، فقال رسول الله صلى الله عليه وسلم إن آل محمد كذا','10','0','0');
INSERT INTO [t9307] VALUES (11,'لا حول ولا قوة إلا بالله دواء من تسعة وتسعين داء، أيسرها الهم','11','0','0');
INSERT INTO [t9307] VALUES (12,'ساعات الأذى يذهبن ساعات الخطايا','12','0','0');
INSERT INTO [t9307] VALUES (13,'ما أبالي على أي حال أصبحت، على ما أحب أو على ما أكره، وذلك لأني لا أدري الخير فيما أحب أو','13','0','0');
INSERT INTO [t9307] VALUES (14,'إن لم يكن لنا خير فيما نكره، لم يكن لنا خير فيما نحب','14','0','0');
INSERT INTO [t9307] VALUES (15,'ما أصاب من مصيبة في الأرض ولا في أنفسكم إلا في كتاب من قبل أن نبرأها} [الحديد: ٢٢] ،','15','0','0');
INSERT INTO [t9307] VALUES (16,'أدخل نفسك في هموم الدنيا، واخرج منها بالصبر، وليردك عن الناس ما تعلم من','16','0','0');
INSERT INTO [t9307] VALUES (17,'فلما أعيته الأمور نادى مرارا: لا حول ولا قوة إلا بالله، فكف الله عز وجل الفيلة بذلك، وسلط','17','0','0');
INSERT INTO [t9307] VALUES (18,'يستحب إذا لقي عدوا، أو ناهض حصنا، قول: لا حول ولا قوة إلا بالله، وأنه ناهض يوما حصنا','18','0','0');
INSERT INTO [t9307] VALUES (19,'لقد بورك لعبد في حاجة أكثر فيها دعاء ربه كائنة ما كانت','19','0','0');
INSERT INTO [t9307] VALUES (20,'لا تكثر همك، ما يقدر يكن، وما ترزق يأتك','20','0','0');
INSERT INTO [t9307] VALUES (21,'ما يكره العبد خير له مما يحب؛ لأن ما يكرهه يهيجه على الدعاء، وما يحب يلهيه','21','0','0');
INSERT INTO [t9307] VALUES (22,'سبحان مستخرج الدعاء بالبلاء، سبحان مستخرج الشكر بالرخاء','22','0','0');
INSERT INTO [t9307] VALUES (23,'يبتلي العبد وهو يحبه؛ ليسمع تضرعه','23','0','0');
INSERT INTO [t9307] VALUES (24,'ألق نفسك مع القدر حيث ألقاك، فهو أحرى أن يفرغ قلبك، وأن يقل همك، وإياك أن يسخطك ذلك فيحل','24','0','0');
INSERT INTO [t9307] VALUES (25,'من نزلت به حاجة فأنزلها بالناس لم تسد فاقته، فإن أنزلها بالله أوشك الله له بأجل حاضر أو','25','0','0');
INSERT INTO [t9307] VALUES (26,'من انقطع إلى الله عز وجل كفاه الله كل مؤونة، ورزقه من حيث لا يحتسب، ومن انقطع إلى الدنيا','26','0','0');
INSERT INTO [t9307] VALUES (27,'اطلبوا الخير دهركم كله، وتعرضوا لنفحات رحمة الله، فإن لله عز وجل نفحات من رحمته يصيب بها','27','0','0');
INSERT INTO [t9307] VALUES (28,'لا تكثر همك، فإنه ما يقدر يكن، وما ترزق يأتك','28','0','0');
INSERT INTO [t9307] VALUES (29,'ما أقرب النعيم من البؤس يعقبان ويوشكان زوالا','29','0','0');
INSERT INTO [t9307] VALUES (30,'لو أن العسر دخل في جحر لجاء اليسر حتى يدخل معه، ثم قال: قال الله، عز وجل: {فإن مع العسر','30','0','0');
INSERT INTO [t9307] VALUES (31,'مهما ينزل بأمرك شدة يجعل الله له بعدها فرجا، وإنه لن يغلب عسر يسرين، وإنه يقول: {اصبروا','31','0','0');
INSERT INTO [t9307] VALUES (32,'ناداه وهو في بطن الحوت، فقال: اللهم {لا إله إلا أنت سبحانك إني كنت من الظالمين}','32','0','0');
INSERT INTO [t9307] VALUES (33,'طرح بالعراء فأنبت الله عليه اليقطينة، فقلنا: يا أبا هريرة وما اليقطينة؟ قال: شجرة الدباء','33','0','0');
INSERT INTO [t9307] VALUES (34,'إذا نزل برجل منكم كرب أو بلاء من أمر الدنيا دعا ربه ففرج عنه؟ قال: فقالوا: بلى، قال: دعاء','34','0','0');
INSERT INTO [t9307] VALUES (35,'تب إلى الله، فإنك تجده عند أول قدم ترجع بها إليه، فقال يونس: ما منعك من التوبة؟ قال: إن','35','0','0');
INSERT INTO [t9307] VALUES (36,'واتخذت لك مسجدا حيث لم يتخذه أحد','36','0','0');
INSERT INTO [t9307] VALUES (37,'فلولا أنه كان من المسبحين} [الصافات: ١٤٣] ، قال: من المصلين','37','0','0');
INSERT INTO [t9307] VALUES (38,'لما ابتلع الحوت يونس عليه السلام، أهوى به إلى قرار الأرض، فسمع يونس عليه السلام تسبيح','38','0','0');
INSERT INTO [t9307] VALUES (39,'يا ذا المعروف الذي لا ينقطع أبدا ولا يحصيه غيره قال: فما طلع الفجر حتى أتي بقميص يوسف','39','0','0');
INSERT INTO [t9307] VALUES (40,'اللهم يا شاهدا غير غائب، ويا قريبا غير بعيد، ويا غالبا غير مغلوب، اجعل لي من أمري فرجا','40','0','0');
INSERT INTO [t9307] VALUES (41,'يا من لا يعلم كيف هو إلا هو، ويا من لا يبلغ كنه قدرته غيره، فرج عني، فأتاه','41','0','0');
INSERT INTO [t9307] VALUES (42,'اللهم اجعل لي من كل ما أهمني وكربني من أمر دنياي وآخرتي فرجا ومخرجا، واغفر لي ذنوبي، وثبت','42','0','0');
INSERT INTO [t9307] VALUES (43,'لو عزي من البلاء أحد لعزي منه آل يعقوب، جاسهم البلاء ثمانين سنة','43','0','0');
INSERT INTO [t9307] VALUES (44,'اللهم أشكو إليك ما لقيت من ودي وعدوي، أما ودي فباعوني وأخذوا ثمني، وأما عدوي فسجنني،','44','0','0');
INSERT INTO [t9307] VALUES (45,'اللهم اجعل لي من كل ما أهمني وكربني من أمر دنياي وآخرتي فرجا ومخرجا، وارزقني من حيث لا','45','0','0');
INSERT INTO [t9307] VALUES (46,'تملق ربك، قال: يا جبريل، كيف أقول؟ قال: قل: يا كثير الخير، يا دائم المعروف، قال: فأوحى','46','0','0');
INSERT INTO [t9307] VALUES (47,'كان ليعقوب أخ مؤاخ، فقال له: يا يعقوب ما الذي أذهب بصرك وقوس ظهرك؟ قال: أما الذي أذهب','47','0','0');
INSERT INTO [t9307] VALUES (48,'كلمات الفرج: لا إله إلا الله الحليم الكريم، لا إله إلا الله العلي العظيم، لا إله إلا الله','48','0','0');
INSERT INTO [t9307] VALUES (49,'دعوات المكروب: اللهم لا إله إلا أنت، رحمتك أرجو فلا تكلني إلى نفسي طرفة عين، وأصلح لي','49','0','0');
INSERT INTO [t9307] VALUES (50,'لا إله إلا الله الحليم الكريم، سبحان الله، وتبارك الله رب العرش العظيم، والحمد لله رب','50','0','0');
INSERT INTO [t9307] VALUES (51,'يا حي، يا قيوم، برحمتك أستغيث','51','0','0');
INSERT INTO [t9307] VALUES (52,'من أصابه غم، أو هم، أو سقم، أو شدة، أو ذل، أو لأواء، فقال: الله ربي لا شريك له، كشف ذلك','52','0','0');
INSERT INTO [t9307] VALUES (53,'ما أصاب مسلما قط هم ولا حزن فقال: اللهم إني عبدك، ابن عبدك، ابن أمتك، ناصيتي في يدك، ماض','53','0','0');
INSERT INTO [t9307] VALUES (54,'حسبي الرب من العباد، حسبي الخالق من المخلوقين، حسبي الرزاق من المرزوقين، حسبي الذي هو','54','0','0');
INSERT INTO [t9307] VALUES (55,'يا من لا يعلم كيف هو إلا هو، ويا من لا يعرف قدرته إلا هو، فرج عني ما أنا فيه، فلا والله،','55','0','0');
INSERT INTO [t9307] VALUES (56,'إن الله عز وجل قد رآكم أهلا ليبتليكم، فأروه أهلا للصبر، فقالوا: من أنت رحمك الله؟ قال:','56','0','0');
INSERT INTO [t9307] VALUES (57,'أوصيكم أن تذكروني عند الرب الذي فوق الرب الذي سأل يوسف أن يذكره عند','57','0','0');
INSERT INTO [t9307] VALUES (58,'دخل علينا إبراهيم التيمي سجن الحجاج، فتكلم، فقال أهل السجن: ما نحب أنا','58','0','0');
INSERT INTO [t9307] VALUES (59,'أطيعوا الله لا يعصكم','59','0','0');
INSERT INTO [t9307] VALUES (60,'اللهم، إنك تعلم على إساءتي وظلمي وإسرافي أني لم أجعل لك ولدا، ولا ندا، ولا صاحبة، ولا','60','0','0');
INSERT INTO [t9307] VALUES (61,'إني لأستحيي أن أ‍دعو الله أن يفرج عني مما لي فيه أجر','61','0','0');
INSERT INTO [t9307] VALUES (62,'سبحان الملك القدوس، سبحان الله وبحمده، فأخرج من غير أن يكون أخرجه','62','0','0');
INSERT INTO [t9307] VALUES (63,'يا من يكتفي من خلقه جميعا ولا يكتفي منه أحد من خلقه، يا أحد من لا أحد له، انقطع الرجاء','63','0','0');
INSERT INTO [t9307] VALUES (64,'اصرف عني شر كل جبار عنيد','64','0','0');
INSERT INTO [t9307] VALUES (65,'أشهد أن كل معبود ما دون عرشك إلى قرار الأرضين باطل غير وجهك الكريم، قد تري ما أنا فيه،','65','0','0');
INSERT INTO [t9307] VALUES (66,'ما كربني أمر إلا تمثل لي جبريل فقال: يا محمد، قل: توكلت على الحي الذي لا يموت، و {الحمد','66','0','0');
INSERT INTO [t9307] VALUES (67,'يا كائنا قبل كل شيء، ويا مكون كل شيء، ويا كائنا بعد كل شيء، افعل بي كذا','67','0','0');
INSERT INTO [t9307] VALUES (68,'اغفر لي الذنوب التي تحل النقم، واغفر لي الذنوب التي تغير النعم، واغفر لي الذنوب التي تورث','68','0','0');
INSERT INTO [t9307] VALUES (69,'تكلم بكلمات الفرج يفرج الله عنك: لا إله إلا الله الحليم الكريم، سبحان الله رب السماوات','69','0','0');
INSERT INTO [t9307] VALUES (70,'عبيدك بفنائك، مسكينك بفنائك، فقيرك بفنائك، سائلك بفنائك قال طاووس: فحفظتهن فما دعوت بهن','70','0','0');
INSERT INTO [t9307] VALUES (71,'كنت وتكون وأنت حي لا تموت، تنام العيون، وتنكدر النجوم، وأنت حي قيوم، ولا تأخذك سنة ولا','71','0','0');
INSERT INTO [t9307] VALUES (72,'اللهم، إن ذنوبي لم تبق لي إلا رجاء عفوك، وقد قدمت آلة الحرمان بين يدي، فأنا أسألك بما لا','72','0','0');
INSERT INTO [t9307] VALUES (73,'اللهم رب إبراهيم وإسماعيل وإسحاق ويعقوب، ورب جبريل وميكائيل وإسرافيل، ومنزل التوراة','73','0','0');
INSERT INTO [t9307] VALUES (74,'يا وليي في نعمتي، ويا صاحبي في وحدتي، وعدتي في كربتي، فلم يزل يدعو بها حتى خلي سبيله، فمر','74','0','0');
INSERT INTO [t9307] VALUES (75,'إن سليمان أعطي فشكر، وإن أيوب ابتلي فصبر، وإن يوسف ظلم فغفر، وأنت أسمح من ذلك. فنكس طويلا','75','0','0');
INSERT INTO [t9307] VALUES (76,'لو سابقني ملك الموت إلى قبض روحك لسبقته، علي بالسيف والنطع، قال: فجيء بالنطع، فأقعدت فيه،','76','0','0');
INSERT INTO [t9307] VALUES (77,'واكفني شر ابن أبي مسلم، وسلط عليه من لا يرحمه، واجعل ذلك من قبل أن يرتد إلي طرفي، وجعلت','77','0','0');
INSERT INTO [t9307] VALUES (78,'اذكر الله في السراء يذكرك في الضراء، وإذا ذكرت الموتى فاجعل نفسك كأحدهم، وإذا أشرفت نفسك','78','0','0');
INSERT INTO [t9307] VALUES (79,'إلهي، وأنت الذي تعرض إساءتي بإحسانك، وفضائحي بسترك، فلم أقو على معصيتك إلا بنعمتك، ولم','79','0','0');
INSERT INTO [t9307] VALUES (80,'عسى مشرب يصفو فيروي ظمية ... أطال صداها المنهل المتكدر عسى بالجنوب الغاديات سيكتفي ...','80','0','0');
INSERT INTO [t9307] VALUES (81,'دخلت على رجل من الملوك السجن وهو يتمثل بهذه الأبيات وقد طال حبسه، فلم يلبث أن','81','0','0');
INSERT INTO [t9307] VALUES (82,'عسى فرج يأتي به الله إنه ... له كل يوم في خليقته أمر','82','0','0');
INSERT INTO [t9307] VALUES (83,'ربما تكره النفوس من الأمـ ... ـر له فرجة كحل العقال','83','0','0');
INSERT INTO [t9307] VALUES (84,'الحمد لله الذي لا ينسى من ذكره، والحمد لله الذي لا يخيب من دعاه، والحمد لله الذي من وثق','84','0','0');
INSERT INTO [t9307] VALUES (85,'أكثر من ذكر الموت، فإنك لا تكون في واسع من الأمر إلا ضيقه عليك، ولا تكون في ضيق من الأمر','85','0','0');
INSERT INTO [t9307] VALUES (86,'إذا كنت من الدنيا فيما يسوؤك فاذكر الموت، فإنه يسهل عليك','86','0','0');
INSERT INTO [t9307] VALUES (87,'إن أقل الناس هما في الآخرة أقلهم في الدنيا','87','0','0');
INSERT INTO [t9307] VALUES (88,'أصبحت بعدك في مسير إلى الآخرة، منتقلا عن الدنيا بشدتها ورخائها، قال أبو الحسن، وكان به','88','0','0');
INSERT INTO [t9307] VALUES (89,'هون عليك، فما يسرني رجوعهما بفلسين','89','0','0');
INSERT INTO [t9307] VALUES (90,'إذا شاب الغراب أتيت أهلي ... وصار القار كاللبن الحليب فقال أمير المؤمنين هارون الرشيد:','90','0','0');
INSERT INTO [t9307] VALUES (91,'عسى فرج يكون عسى ... نعلل أنفسا بعسى','91','0','0');
INSERT INTO [t9307] VALUES (92,'إذا تضايق أمر فانتظر فرجا ... فأصعب الأمر أدناه من الفرج','92','0','0');
INSERT INTO [t9307] VALUES (93,'أحسن الظن برب عودك ... حسنا أمس وسوى أودك إن ربا كان يكفيك الذي ... كان بالأمس سيكفيك','93','0','0');
INSERT INTO [t9307] VALUES (94,'يا صاحب الهم إن الهم منقطع ... لا تيأسن كأن قد فرج الله','94','0','0');
INSERT INTO [t9307] VALUES (95,'كن للمكاره بالعزاء مقطعا ... فلعل يوما لا ترى ما تكره ولربما ابتسم الوقور من الأذى ...','95','0','0');
INSERT INTO [t9307] VALUES (96,'ويوم الوشاح من تعاجيب ربنا ... إلا أنه من بلدة الكفر أنجاني','96','0','0');
INSERT INTO [t9307] VALUES (97,'مفتاح باب الفرج الصبر ... وكل عسر بعده يسر والدهر لا يبقى على حالة ... والأمر يأتي بعده','97','0','0');
INSERT INTO [t9307] VALUES (98,'إذا اشتملت على اليأس القلوب ... وضاق لما بها الصدر الرحيب وأوطنت المكاره واطمأنت ...','98','0','0');
INSERT INTO [t9307] VALUES (99,'ألم تر أن ربك ليس تحصى ... أياديه الحديثة والقديمه تسل عن الهموم فليس شيء ... يقيم وما','99','0','0');
INSERT INTO [t9307] VALUES (100,'يمثل ذو العقل في نفسه ... مصيبته قبل أن تنزلا فإن نزلت بغتة لم ترعه ... لما كان في نفسه','100','0','0');
INSERT INTO [t9307] VALUES (101,'حنا على يوسف رب فأخرجه ... من قعر جب وبيت حوله عمم قال: فحمدت الله وقلت: أتى الفرج، فمكثت','101','0','0');
INSERT INTO [t9307] VALUES (102,'هي الأيام والغير ... وأمر الله ينتظر','102','0','0');
INSERT INTO [t9307] VALUES (103,'هل الدهر إلا ساعة ثم تنقضي ... بما كان فيها من عناء ومن خفض','103','0','0');
INSERT INTO [t9307] VALUES (104,'لعمر بنيي اللذين أراهما ... جزوعين إن الشيخ غير جزوع','104','0','0');
INSERT INTO [t9307] VALUES (105,'بئس ما سننت على أهل العراق، أما تخاف أن يؤذن فيك بمثل هذا؟','105','0','0');
INSERT INTO [t9307] VALUES (106,'لما صنع خالد به ما صنع ذهب يتقلب وهو في الحديد، فتكشف، فكأنما ثم صوفه، فقال: {لا إله إلا','106','0','0');
INSERT INTO [t9307] VALUES (107,'ولما رأيت الأرض قد سد ظهرها ... ولم يك إلا بطنها لك مخرجا دعوت الذي ناداه يونس بعدما ...','107','0','0');
INSERT INTO [t9307] VALUES (108,'قد وهبته لك','108','0','0');
INSERT INTO [t9307] VALUES (109,'وإنك لتعين على نفسك، قال: فلم ألتفت إليه، وشخصت حتى قدمت بغداد، وقد بنى أبو جعفر مدينته،','109','0','0');
INSERT INTO [t9307] VALUES (110,'أسأل الله العفو والعافية والمعافاة الدائمة في الدنيا والآخرة، فقلتها ثلاثا، واستيقظت،','110','0','0');
INSERT INTO [t9307] VALUES (111,'ربما تكره النفوس من الأمـ ... ـر له فرجة كحل العقال','111','0','0');
INSERT INTO [t9307] VALUES (112,'اصبر لدهر نال منـ ... ـك فهكذا مضت الدهور فرح وحزن مرة ... لا الحزن دام ولا السرور. أبو','112','0','0');
INSERT INTO [t9307] VALUES (113,'فلا تجزع وإن أعسرت يوما ... فقد أيسرت في الزمن الطويل ولا تيأس فإن اليأس كفر ... لعل الله','113','0','0');
INSERT INTO [t9307] VALUES (114,'عسى ما ترى أن لا يدوم وأن ترى ... له فرجا مما ألح به الدهر عسى فرج يأتي به الله إنه ...','114','0','0');
INSERT INTO [t9307] VALUES (115,'إذا لم تسامح في الأمور تعسرت ... عليك فسامح وامزج العسر باليسر','115','0','0');
