INSERT INTO [t9311] VALUES (1,'عليك بالصوم فإنه لا عدل له ثم أتيته ثانية فقال: عليك بالصوم فإنه لا عدل','1','0','0');
INSERT INTO [t9311] VALUES (2,'يطلع الله عز وجل إلى خلقه ليلة النصف من شعبان، فيغفر لعباده إلا اثنين مشاحنا وقاتل','2','0','0');
INSERT INTO [t9311] VALUES (3,'يهبط الله عز وجل إلى سماء الدنيا إلى عباده في ليلة النصف من شعبان، فيطلع إليهم، فيغفر لكل','3','0','0');
INSERT INTO [t9311] VALUES (4,'إذا كان ليلة النصف من شعبان نادى مناد: هل من مستغفر فأغفر له، هل من سائل فأعطيه، فلا يسأل','4','0','0');
INSERT INTO [t9311] VALUES (5,'تقطع الآجال من شعبان إلى شعبان، حتى إن الرجل لينكح، ويولد له، ولقد خرج اسمه في','5','0','0');
INSERT INTO [t9311] VALUES (6,'إن الله عز وجل ليشفع يوم القيامة ثلاثة: الأنبياء، ثم العلماء، ثم','6','0','0');
INSERT INTO [t9311] VALUES (7,'أحبوا الأيامى، وتوددوا إليهم، واعلموا أنه لا يتم شرف إلا بولاية علي بن أبي طالب عليه','7','0','0');
INSERT INTO [t9311] VALUES (8,'لحد للنبي صلى الله عليه وسلم ولأبي بكر، وعمر رضي الله عنهما','8','0','0');
INSERT INTO [t9311] VALUES (9,'أعمال الأبرار من الرجال من أمتي: الخياطة، وأعمال الأبرار من النساء من أمتي','9','0','0');
INSERT INTO [t9311] VALUES (10,'لكل شيء آفة وآفة هذا الدين: ولاة السوء','10','0','0');
INSERT INTO [t9311] VALUES (11,'أتدري من جالست؟ إنك مع قوم يدفنون ما جاء به رسول الله صلى الله عليه','11','0','0');
INSERT INTO [t9311] VALUES (12,'كنا سبيا من سبي الجنة فسبانا إبليس أخزاه الله بالخطيئة، فليس ينبغي لنا إلا البكاء والحزن،','12','0','0');
INSERT INTO [t9311] VALUES (13,'إنما الأعمال بالنية، وإنما لكل امرئ ما نوى، فمن كانت هجرته إلى الله ورسوله؛ فهجرته إلى','13','0','0');
INSERT INTO [t9311] VALUES (14,'فاتحة الكتاب، وأية الكرسي، والآيتين من آل عمران {شهد الله أنه لا إله إلا هو والملائكة','14','0','0');
INSERT INTO [t9311] VALUES (15,'لئن صدق ليدخلن الجنة','15','0','0');
INSERT INTO [t9311] VALUES (16,'رأيت رسول الله صلى الله عليه وسلم قائما يصلي، ويده اليمنى على اليسرى قابضا','16','0','0');
INSERT INTO [t9311] VALUES (17,'المدينة قبة الإسلام، ودار الإيمان، وأرض الهجرة، ومبين الحلال والحرام','17','0','0');
INSERT INTO [t9311] VALUES (18,'إذا أشار العبد بأصبعه في الصلاة، يقول الله عز وجل: أخلص لي عبدي، وإذا رفع يديه في الصلاة','18','0','0');
INSERT INTO [t9311] VALUES (19,'ما خطب رسول الله صلى الله عليه وسلم، إلا أمر بالصدقة، ونهى عن المثلة','19','0','0');
INSERT INTO [t9311] VALUES (20,'من فطر صائما كان له مثل أجر الصائم، من غير أن ينقص من أجر الصائم شيء','20','0','0');
INSERT INTO [t9311] VALUES (21,'هذا شهر رمضان يستقبلكم وتستقبلونه، ألا وإن الله عز وجل ليس بتارك صبيحة الصوم أحدا من أهل','21','0','0');
INSERT INTO [t9311] VALUES (22,'أفطر عندكم الصائمون، وأكل طعامكم الأبرار، وتنزلت عليكم الملائكة','22','0','0');
INSERT INTO [t9311] VALUES (23,'إذا لقم أحدكم أول لقمة - يعني عند إفطاره - فليقل: يا واسع المغفرة، اغفر','23','0','0');
INSERT INTO [t9311] VALUES (24,'الله عز وجل فرض عليكم صيام رمضان، وسننت لكم قيامه فمن صامه وقامه إيمانا واحتسابا، أخرج من','24','0','0');
INSERT INTO [t9311] VALUES (25,'رب قائم حظه من قيامه السهر، ورب صائم حظه من صيامه الجوع والعطش','25','0','0');
INSERT INTO [t9311] VALUES (26,'من قرأ في أول ليلة من شهر رمضان إنا فتحنا لك فتحا مبينا في التطوع حفظ في ذلك','26','0','0');
INSERT INTO [t9311] VALUES (27,'يوحي الله عز وجل إلى الملكين: لا تكتبا على عبدي الصائم بعد العصر سيئة','27','0','0');
INSERT INTO [t9311] VALUES (28,'رأيت رسول الله صلى الله عليه وسلم في المنام فقال لي: أنت الهيثم القارئ الذي تزين القرآن','28','0','0');
INSERT INTO [t9311] VALUES (29,'من عمل بغير مشورة فذاك باطل يتعنى، ومن لم ينصر من ظالمه بيد ولا بلسان ولا حقد فذاك','29','0','0');
INSERT INTO [t9311] VALUES (30,'أفش السلام، وأطب الكلام، وأطعم الطعام، وأدم الصيام، وصل بالليل والناس نيام تدخل الجنة','30','0','0');
INSERT INTO [t9311] VALUES (31,'أنزلت الصحف الأولى أول يوم من رمضان، وأنزلت التوراة في ستة من رمضان، وأنزل الإنجيل في','31','0','0');
INSERT INTO [t9311] VALUES (32,'فصلا ما بين صيامنا، وصيام أهل الكتاب: أكلة السحر','32','0','0');
INSERT INTO [t9311] VALUES (33,'أمرنا معاشر الأنبياء، أن نؤخر سحورنا، ونمسك بأيماننا على شمائلنا في','33','0','0');
INSERT INTO [t9311] VALUES (34,'كان المسلمون إذا أفطروا لم يزيدوا على العشاء، ثم لا يأكلون بعد ذلك، ولا يقربون النساء حتى','34','0','0');
INSERT INTO [t9311] VALUES (35,'خرج عليهم فصلى بهم ليلة، ثم خرج الثانية، ثم خرج الثالثة، فكثر الناس، فلم يخرج الرابعة،','35','0','0');
INSERT INTO [t9311] VALUES (36,'نزل رسول الله صلى الله عليه وسلم ذات ليلة في رمضان فصلى خلفه الناس، فلما أصبحوا ذكروا ذلك','36','0','0');
INSERT INTO [t9311] VALUES (37,'الجماعة بركة، والثريد بركة، والسحور بركة، تسحروا فإنه يزيد في القوة، وهو من السنة تسحروا','37','0','0');
INSERT INTO [t9311] VALUES (38,'أقم الصلاة للصيام من هذه الساعة إلى الليل، من أخلاق الأنبياء عليهم السلام: تعجيل الإفطار،','38','0','0');
INSERT INTO [t9311] VALUES (39,'كان رسول الله صلى الله عليه وسلم يقبل وهو صائم هذا حديث صحيح أخرجه مسلم بن الحجاج، عن أبي','39','0','0');
INSERT INTO [t9311] VALUES (40,'نوم الصائم عبادة، وسكوته تسبيح، ودعاؤه مستجاب، وعمله متقبل قال الشيخ: هذا غريب من حديث','40','0','0');
INSERT INTO [t9311] VALUES (41,'إن الصيام ليس من الأكل والشرب فقط، إنما الصيام من اللغو والرفث، فإن سابك أحد، أو جهل عليك','41','0','0');
INSERT INTO [t9311] VALUES (42,'من لم يدع الخنا، والكذب، فلا حاجة لله أن يدع طعامه وشرابه','42','0','0');
INSERT INTO [t9311] VALUES (43,'قد دنا مني حقوق من بين أظهركم، فمن كنت جلدت له ظهرا، فهذا ظهري فليستقد منه، ومن كنت أخذت','43','0','0');
INSERT INTO [t9311] VALUES (44,'رأيت رب العزة عز وجل في النوم فقلت: يا رب ما أفضل ما تقرب المتقربون به إليك؟ فقال: كلامي','44','0','0');
INSERT INTO [t9311] VALUES (45,'أن النبي صلى الله عليه وسلم كان يقرأ في الصبح، ب ق، والقرآن المجيد، وكانت صلاته بعد ذلك','45','0','0');
INSERT INTO [t9311] VALUES (46,'تعجيل الإفطار، وتأخير السحور من شعائر الإسلام','46','0','0');
INSERT INTO [t9311] VALUES (47,'لا يزال الدين ظاهرا ما عجلوا الناس الإفطار، فإن اليهود والنصارى يؤخرون','47','0','0');
INSERT INTO [t9311] VALUES (48,'إذا كان يوم الفطر قامت الملائكة على أفواه الطرق، فنادوا: يا معشر المسلمين اغدوا إلى ربكم،','48','0','0');
INSERT INTO [t9311] VALUES (49,'من أفطر يوما من شهر رمضان من غير عذر، ولا رخصة فلا يقضيه صيام الدهر كله وإن','49','0','0');
INSERT INTO [t9311] VALUES (50,'إن قدرت على يوم من رمضان فارغا فصمه مكانه قال: وهل أجد يوما من رمضان فارغا؟ فقال ابن عباس','50','0','0');
INSERT INTO [t9311] VALUES (51,'وعزة ربي والذي بعثني بالحق، لو عاش مائة سنة فصامها ما أدرك فضل ذلك','51','0','0');
INSERT INTO [t9311] VALUES (52,'من كان عليه صيام من رمضان فليسرده، ولا يقطعه','52','0','0');
INSERT INTO [t9311] VALUES (53,'ليست بمنسوخة هو الشيخ الكبير، والمرأة الكبيرة لا يستطيعان أن يصوما فيطعمان مكان كل يوم','53','0','0');
INSERT INTO [t9311] VALUES (54,'فليطعم عنه لكل يوم نصف صاع من بر','54','0','0');
INSERT INTO [t9311] VALUES (55,'ذلك إليك، أرأيت لو كان على أحدكم دين، وقضى الدرهم والدرهمين، ألم يكن قضاؤها لله عز وجل','55','0','0');
INSERT INTO [t9311] VALUES (56,'ثم أورثنا الكتاب الذين اصطفينا من عبادنا فمنهم ظالم لنفسه} [فاطر: ٣٢] قالت: مثلي ومثلك','56','0','0');
INSERT INTO [t9311] VALUES (57,'إذا رأيتم الذين يسبون أصحابي فالعنوهم','57','0','0');
INSERT INTO [t9311] VALUES (58,'لأعطين الراية غدا لرجل يحبه الله ورسوله، وهو من أهل الجنة وكان علي عليه السلام أرمد فدعاه','58','0','0');
INSERT INTO [t9311] VALUES (59,'رأيت ليلة أسري بي مكتوبا حول العرش في فرندة خضراء، مكتوبا فيها بقلم من نور أبيض: لا إله','59','0','0');
INSERT INTO [t9311] VALUES (60,'شهر بركة وخير يغشاكم الله تعالى فيه بتنزيل الرحمة، ويحط الخطايا، ويستجيب فيه الدعاء، ينظر','60','0','0');
INSERT INTO [t9311] VALUES (61,'من صام ستة أيام بعد الفطر فكأنما صام الدهر كله','61','0','0');
INSERT INTO [t9311] VALUES (62,'هذا الشهر كتب الله صيامه، ولم يكتب عليكم قيامه، فمن قامه فإنها من نوافل الخير، ومن لا','62','0','0');
INSERT INTO [t9311] VALUES (63,'ألا أنبئكم بأفضل الملائكة: جبريل عليه السلام، وأفضل النبيين: آدم عليه السلام، وأفضل','63','0','0');
INSERT INTO [t9311] VALUES (64,'تنكح المرأة لأربع: لجمالها، ولحسبها، ولمالها، ولدينها، فاظفر بذات الدين تربت','64','0','0');
INSERT INTO [t9311] VALUES (65,'هي إحدى عشرة كبيرة: منها أربع في الرأس، وهي: الشرك بالله عز وجل، وقذف المحصنة، واليمين','65','0','0');
INSERT INTO [t9311] VALUES (66,'لكل داء دواء فإذا أصيب دواء الداء برأ بإذن الله عز وجل قال الشيخ: هذا حديث صحيح، أخرجه','66','0','0');
INSERT INTO [t9311] VALUES (67,'الناس يكثرون، وأصحابي يقلون، فلا تسبوهم، ومن سبهم فعليه لعنة الله','67','0','0');
INSERT INTO [t9311] VALUES (68,'ليلة أسري بي قلت لجبريل عليه السلام: إن قومي لا يصدقوني، قال لي جبريل عليه السلام: يصدقك','68','0','0');
INSERT INTO [t9311] VALUES (69,'من أشقى الأولين؟ فقال علي عليه السلام: عاقر الناقة قال: فمن أشقى الآخرين؟ قال: لا أدري','69','0','0');
INSERT INTO [t9311] VALUES (70,'ألا أعلمك كلمات تقولها، لو كانت عليك من الذنوب كذر النمل لغفرها الله لك، مع أنه مغفور لك','70','0','0');
INSERT INTO [t9311] VALUES (71,'من تهاون بصلاته، فإن الله عز وجل يعاقبه بخمس عشرة خصلة ست منها في الدنيا، وثلاث عند','71','0','0');
INSERT INTO [t9311] VALUES (72,'لا أعلم شيئا يراد الله به أفضل من طلب العلم','72','0','0');
INSERT INTO [t9311] VALUES (73,'أكلت مع رسول الله صلى الله عليه وسلم، وأبي بكر وعمر وعثمان خبزا ولحما وصلوا ولم','73','0','0');
INSERT INTO [t9311] VALUES (74,'حرم أبو بكر الخمر على نفسه في الجاهلية لأنه مر بسكران يتناول العذرة بيده، فإذا أدناها من','74','0','0');
INSERT INTO [t9311] VALUES (75,'الشهادة من ورائك، فكيف صبرك إذا خضبت هذه من هذا بدم وأهوى بيده إلى لحيته ورأسه، فقال علي','75','0','0');
INSERT INTO [t9311] VALUES (76,'مثل الصلوات الخمس، كمثل رجل، على بابه نهر غمر يغتسل منه كل يوم خمس مرات فما عسى أن يبقى','76','0','0');
INSERT INTO [t9311] VALUES (77,'نعم البيت الحمام، يدخله الرجل المسلم فيتعوذ فيه بالله، ومن النار، ويسأل الله عز وجل فيه','77','0','0');
INSERT INTO [t9311] VALUES (78,'إن أول ما يكفأ من الدين كما يكفأ من الإناء هي: الخمر، يشربونها ويدعونها بغير','78','0','0');
INSERT INTO [t9311] VALUES (79,'كلوها، ولا بأس بها','79','0','0');
INSERT INTO [t9311] VALUES (80,'الله عز وجل أفرح بتوبة عبده المؤمن من الضال الواجد، ومن الضامن الوارد، ومن العقيم الوالد،','80','0','0');
INSERT INTO [t9311] VALUES (81,'فأمره ألا يستقبل الريح، وأن ينتكب القبلة ولا يستقبلها، ولا يستدبرها، وأن يستنجي بثلاثة','81','0','0');
INSERT INTO [t9311] VALUES (82,'من ألقى جلباب الحياء فلا غيبة له','82','0','0');
INSERT INTO [t9311] VALUES (83,'يأتي زمان على الناس من لم يكن معه أصفر، أو أبيض، لم يتهنأ على العيش','83','0','0');
INSERT INTO [t9311] VALUES (84,'أصبحت تحدد الدنيا بطول الأمل، وتمنى على الله الأماني بشر العمل، هيهات تضرب في حديد','84','0','0');
INSERT INTO [t9311] VALUES (85,'أيما صبي حج، ثم أدرك فعليه أن يحج حجة أخرى، وأيما عبد حج، ثم أعتق، فعليه أن يحج حجة','85','0','0');
INSERT INTO [t9311] VALUES (86,'اثبت فإنما عليك نبي وصديق وشهيدان','86','0','0');
INSERT INTO [t9311] VALUES (87,'لقد فقدتم رجلا لم يسبقه الأولون، ولم يدركه الآخرون، إن كان رسول الله صلى الله عليه وسلم','87','0','0');
INSERT INTO [t9311] VALUES (88,'كل شراب أسكر فهو حرام','88','0','0');
INSERT INTO [t9311] VALUES (89,'دعا بماء فتوضأ مرة فقال هذا الذي لا يقبل الله الصلاة إلا به وتوضأ مرتين مرتين وقال: هذا','89','0','0');
INSERT INTO [t9311] VALUES (90,'أكثروا من ذكر هادم اللذات','90','0','0');
INSERT INTO [t9311] VALUES (91,'أنا النبي لا كذب، أنا ابن عبد المطلب','91','0','0');
INSERT INTO [t9311] VALUES (92,'اللهم منزل الكتاب، سريع الحساب، هازم الأحزاب، اهزمهم وزلزلهم','92','0','0');
INSERT INTO [t9311] VALUES (93,'تعوذ يا أبا ذر من شياطين الإنس، والجن. فقال: يا نبي الله، أو إن من الإنس شياطين؟ قال','93','0','0');
INSERT INTO [t9311] VALUES (94,'لا تمازح الصبيان، فتهون عليهم','94','0','0');
