INSERT INTO [t9313] VALUES (1,'آتي يوم القيامة باب الجنة فأستفتح، فيقول الخازن: من أنت؟ فأقول: محمد، فيقول: بك أمرت أن','1','0','0');
INSERT INTO [t9313] VALUES (2,'دعا له أن يزيده الله فهما وعلما، قال: ثم رأيت رسول الله صلى الله عليه وسلم نام، حتى سمعته','2','0','0');
INSERT INTO [t9313] VALUES (3,'ائتني بكتف؛ حتى أكتب لأبي بكر كتابا، لا يختلف عليه من بعدي، قال: فلما قام عبد الرحمن قال','3','0','0');
INSERT INTO [t9313] VALUES (4,'أتيت في المنام بعس مملوء لبنا، فشربت منه حتى امتلأت، فرأيته يجري في عروقي، ففضلت فضلة،','4','0','0');
INSERT INTO [t9313] VALUES (5,'عمر سراج أهل الجنة','5','0','0');
INSERT INTO [t9313] VALUES (6,'ليلة عرج بي إلى السماء، فما مررت بسماء إلا وجدت فيها اسمي مكتوبا: محمد رسول الله، وأبو','6','0','0');
INSERT INTO [t9313] VALUES (7,'لو كان قتل عثمان هدى، لاحتلبت به الأمة لبنا، ولكنه كان ضلالا، فاحتلبت به الأمة','7','0','0');
INSERT INTO [t9313] VALUES (8,'طوبى لمن أحبك، وصدق فيك، وويل لمن أبغضك، وكذب فيك','8','0','0');
INSERT INTO [t9313] VALUES (9,'أنا الله، أنا الرحمن، أنا الملك، أنا القدوس، أنا السلام، أنا المؤمن، أنا المهيمن، أنا','9','0','0');
INSERT INTO [t9313] VALUES (10,'ذوقوا مس سقر، إنا كل شيء خلقناه بقدر} [القمر: ٤٩] ، أولئك شرار هذه الأمة، لا تعودوا','10','0','0');
INSERT INTO [t9313] VALUES (11,'يستتابون، فإن تابوا، وإلا نفوا من ديار المسلمين','11','0','0');
INSERT INTO [t9313] VALUES (12,'بكروا بالصلاة في يوم الغيم، فإنه من ترك صلاة العصر، حبط عمله','12','0','0');
INSERT INTO [t9313] VALUES (13,'الطلح المنضود، قال: الموز','13','0','0');
INSERT INTO [t9313] VALUES (14,'كانت الفتيا في الماء من الماء رخصة في أول الإسلام، ثم نهي عنها','14','0','0');
INSERT INTO [t9313] VALUES (15,'ليس بحكيم من لم يعاشر بالمعروف، من لم يجد من معاشرته بدا، حتى يجعل الله له فرجا، أو قال:','15','0','0');
INSERT INTO [t9313] VALUES (16,'كفنوه بثوبيه، وغسلوه بماء وسدر، ولا تقربوه طيبا، ولا تغطوا رأسه، فإنه يبعث يوم القيامة','16','0','0');
INSERT INTO [t9313] VALUES (17,'همدان هامة اليمن، وكندة في اليمن كالشاسبرنب في الريحان','17','0','0');
INSERT INTO [t9313] VALUES (18,'من قال: لا إله إلا الله، وحده لا شريك له، له الملك، وله الحمد، وهو على كل شيء قدير، بعدما','18','0','0');
INSERT INTO [t9313] VALUES (19,'أعجب الخلق إلي إيمانا لقوم يكونون من بعدكم، يجدون صحفا فيها كتب يؤمنون بما','19','0','0');
INSERT INTO [t9313] VALUES (20,'رديه يا عائشة فوالله لو شئت لأجرى الله عز وجل معي جبال الذهب والفضة','20','0','0');
INSERT INTO [t9313] VALUES (21,'إذا وقع الذباب في إناء أحدكم، فإن في أحد جناحيه داء، وفي الآخر شفاء، وإنه يتقي بالجناح','21','0','0');
INSERT INTO [t9313] VALUES (22,'إنك لتنظر إلى الطير في الجنة، فتشتهيه، فيخر بين يديك مشويا','22','0','0');
INSERT INTO [t9313] VALUES (23,'للذين أحسنوا العمل في الدنيا الحسنى: وهي الجنة، قال: والزيادة: النظر إلى وجه الله','23','0','0');
INSERT INTO [t9313] VALUES (24,'إذا دخل أهل الجنة الجنة، نودوا: أن يا أهل الجنة إن لكم عند الله موعدا لم تروه، فيقولون:','24','0','0');
INSERT INTO [t9313] VALUES (25,'رأيت رسول الله صلى الله عليه وسلم يرفع يديه في صلواته إذا ركع، وإذا رفع رأسه من الركوع،','25','0','0');
INSERT INTO [t9313] VALUES (26,'الذهب بالذهب، وزنا بوزن، مثلا بمثل، من زاد أو ازداد فقد أربى قال شرحبيل: إن لم أكن سمعته','26','0','0');
INSERT INTO [t9313] VALUES (27,'أهل في دبر الصلاة','27','0','0');
INSERT INTO [t9313] VALUES (28,'لو رأيتم ما رأيت، لضحكتم قليلا، ولبكيتم كثيرا، قالوا: يا رسول الله وما الذي رأيت؟، قال:','28','0','0');
INSERT INTO [t9313] VALUES (29,'لتنبأن: أن تصدق وأنت صحيح شحيح، تأمل البقاء، وتخاف الفقر، ولا تمهل، حتى إذا بلغت الحلقوم،','29','0','0');
INSERT INTO [t9313] VALUES (30,'ذنبان يعجلان، ولا يغفران: البغي، وقطيعة الرحم','30','0','0');
INSERT INTO [t9313] VALUES (31,'اجعلوا حجكم عمرة، قال: فقال الناس: يا رسول الله قد أحرمنا بالحج، فكيف نجعلها عمرة؟ قال:','31','0','0');
INSERT INTO [t9313] VALUES (32,'أول من أسلم أبو بكر رضي الله عنه','32','0','0');
INSERT INTO [t9313] VALUES (33,'أعطيت فواتح الكلم، وخواتمه، وجوامعه، فقلنا: يا رسول الله علمنا مما علمك الله عز وجل،','33','0','0');
INSERT INTO [t9313] VALUES (34,'أنا أول شفيع يوم القيامة، وأنا أكثر الأنبياء تبعا يوم القيامة، إن من الأنبياء لمن يأتي','34','0','0');
INSERT INTO [t9313] VALUES (35,'أتاني جبريل عليه السلام آنفا، فقلت: يا جبريل حدثني بفضائل عمر بن الخطاب رضي الله عنه في','35','0','0');
INSERT INTO [t9313] VALUES (36,'علمه الكتاب والحساب، وقه العذاب','36','0','0');
INSERT INTO [t9313] VALUES (37,'لو كتبت نفسك لكنت لذلك أهلا','37','0','0');
INSERT INTO [t9313] VALUES (38,'نادى ملك من السماء يوم بدر، يقال له رضوان: لا سيف إلا ذو الفقار، ولا فتى إلا علي عليه','38','0','0');
INSERT INTO [t9313] VALUES (39,'يوم كلم الله تعالى موسى عليه السلام، كانت عليه جبة صوف، وسراويل صوف، وكساء صوف، وكمة صوف،','39','0','0');
INSERT INTO [t9313] VALUES (40,'ليس على المختلس، ولا على المنتهب، ولا على الخائن قطع','40','0','0');
INSERT INTO [t9313] VALUES (41,'سبعة لا ينظر الله عز وجل إليهم يوم القيامة، ولا يزكيهم، ولا يجمعهم مع العالمين، يدخلون','41','0','0');
INSERT INTO [t9313] VALUES (42,'هو الندم على الذنب حين يفرط منك، فتستغفر الله عز وجل بندامتك عند الحافر، ثم لا تعود إليه','42','0','0');
INSERT INTO [t9313] VALUES (43,'هو نور الإسلام','43','0','0');
INSERT INTO [t9313] VALUES (44,'ما بقي لأمتي من الدنيا إلا كمقدار الشمس إذا صليت العصر، إن حوضي ما بين أيلة إلى المدينة،','44','0','0');
INSERT INTO [t9313] VALUES (45,'ذاكر الله في الغافلين مثل الذي يقاتل عن الفارين، وذاكر الله في الغافلين مثل الشجرة','45','0','0');
INSERT INTO [t9313] VALUES (46,'هل من لبن؟ قال: قلت: نعم، ولكني مؤتمن، قال: فهل من شاة لم ينز عليها الفحل؟ ، فأتيته بشاة،','46','0','0');
INSERT INTO [t9313] VALUES (47,'أبو بكر، قال: قلت: ثم من؟ قال: يا بني أوما تعلم؟، قال: قلت: لا، قال: ثم عمر، قال: ثم','47','0','0');
INSERT INTO [t9313] VALUES (48,'غفر الله لك يا عثمان ما قدمت، وما أخرت، وما أسررت، وما أعلنت، وما أخفيت، وما أبديت، وما','48','0','0');
INSERT INTO [t9313] VALUES (49,'ثلاث لأن يكون لي واحدة منهن، أحب إلي من حمر النعم نزل على رسول الله صلى الله عليه وسلم','49','0','0');
INSERT INTO [t9313] VALUES (50,'مات ابن عباس بالطائف، فجاء طائر لم ير على خلقته، فدخل نعشه، ثم لم ير خارجا منه، قال: فلما','50','0','0');
INSERT INTO [t9313] VALUES (51,'من أحب جميع أصحابي وتولاهم، واستغفر لهم، جعله الله عز وجل يوم القيامة معهم في','51','0','0');
INSERT INTO [t9313] VALUES (52,'اعملوا، فكل ميسر أو كما قال','52','0','0');
INSERT INTO [t9313] VALUES (53,'يلعن القدرية','53','0','0');
INSERT INTO [t9313] VALUES (54,'يبتدئون فيكونون مرجئة، ثم يكونون قدرية، ثم يصيرون مجوسا','54','0','0');
INSERT INTO [t9313] VALUES (55,'ولا يدافعن أحد منكم في بطنه غائطا ولا بولا، وإن حك أحدكم فرجه فمرشة، أو مرشتين، وليكن ذلك','55','0','0');
INSERT INTO [t9313] VALUES (56,'اغفر للأنصار، ولأبناء الأنصار، ولأبناء أبناء الأنصار، ولنساء أبناء الأنصار، ولنساء أبناء','56','0','0');
INSERT INTO [t9313] VALUES (57,'إذا مر بشجرة بين مكة والمدينة، أناخ عندها، ثم صب في أصلها إداوة من ماء، وإن لم يكن معه','57','0','0');
INSERT INTO [t9313] VALUES (58,'من اتخذ كلبا إلا كلب ماشية أو كلبا ضاريا، نقص من عمله كل يوم قيراط','58','0','0');
INSERT INTO [t9313] VALUES (59,'ارم فداك أبي وأمي','59','0','0');
INSERT INTO [t9313] VALUES (60,'لا يقرأ الجنب، ولا الحائض شيئا من القرآن','60','0','0');
INSERT INTO [t9313] VALUES (61,'إذا لم يصل في الجماعة، لم يكبر أيام التشريق','61','0','0');
INSERT INTO [t9313] VALUES (62,'جئت من الدثينة مجاهدا في سبيلك، وابتغاء مرضاتك، وأنا أشهد أنك تحيي الموتى، وتبعث من في','62','0','0');
INSERT INTO [t9313] VALUES (63,'من بلغه عن الله عز وجل شيء فيه فضل فأخذه إيمانا به، ورجاء ثوابه، أعطاه الله عز وجل ذلك،','63','0','0');
INSERT INTO [t9313] VALUES (64,'إياكم وكثرة الحلف عند البيع؛ فإنه ينفق، ثم يمحق','64','0','0');
INSERT INTO [t9313] VALUES (65,'من سره أن ينظر إلى وصية محمد رسول الله صلى الله عليه وسلم التي عليها خاتمه، فليقرأ: {قل','65','0','0');
INSERT INTO [t9313] VALUES (66,'علمه الكتاب والحساب، وقه العذاب','66','0','0');
INSERT INTO [t9313] VALUES (67,'صليت مع رسول الله صلى الله عليه وسلم','67','0','0');
INSERT INTO [t9313] VALUES (68,'أما إنكم ترون ربكم عز وجل كما ترون هذا، لا تضامون في رؤيته، فإن قدرتم أن لا تغلبوا على','68','0','0');
INSERT INTO [t9313] VALUES (69,'أتاني جبريل بدابة فوق الحمار ودون البغل، فحملني عليه، ثم انطلق يهوي بنا، كلما صعد عقبة،','69','0','0');
INSERT INTO [t9313] VALUES (70,'ما أسكر كثيره فقليله حرام','70','0','0');
INSERT INTO [t9313] VALUES (71,'كل مسكر حرام، وما أسكر منه الفرق، فالحسوة منه حرام','71','0','0');
INSERT INTO [t9313] VALUES (72,'قولوا: اللهم صل على محمد، وعلى آل محمد، كما صليت على إبراهيم، وآل إبراهيم، إنك حميد مجيد،','72','0','0');
INSERT INTO [t9313] VALUES (73,'فإذا خرج رسول الله صلى الله عليه وسلم لعب، وذهب، وجاء، فإذا جاء النبي صلى الله عليه وسلم','73','0','0');
INSERT INTO [t9313] VALUES (74,'أهل الدرجات العلى ليراهم من تحتهم، كما يرى النجم الطالع في أفق من أفاق السماء، وإن أبا','74','0','0');
INSERT INTO [t9313] VALUES (75,'ألا أستحي ممن تستحي منه الملائكة','75','0','0');
INSERT INTO [t9313] VALUES (76,'انطلق، فإن الله سيهدي قلبك، ويثبت لسانك، قال: فما شككت في قضاء بين','76','0','0');
INSERT INTO [t9313] VALUES (77,'كائنة، ولم يأت تأويلها بعد','77','0','0');
INSERT INTO [t9313] VALUES (78,'إن كنت لأجده في ثوب رسول الله صلى الله عليه وسلم، فأحته عنه يعني المني','78','0','0');
INSERT INTO [t9313] VALUES (79,'أيمنع أحدكم أن يكبر في دبر كل صلاة عشرا، ويسبح عشرا، ويحمد عشرا، فذلك في خمس صلوات خمسون','79','0','0');
INSERT INTO [t9313] VALUES (80,'فأمرها رسول الله صلى الله عليه وسلم أن تتزوج','80','0','0');
INSERT INTO [t9313] VALUES (81,'فأخذ بذواب كان لي أو برأسي، فأقامني عن يمينه','81','0','0');
INSERT INTO [t9313] VALUES (82,'لا، حتى يميز بينه وبينه، قال: إنما أردت الحجارة، قال: لا، حتى يميز بينهما، قال: فرد، حتى','82','0','0');
INSERT INTO [t9313] VALUES (83,'أدبوا الخيل، ولا يرفعن بين ظهرانيكم الصليب، ولا تجاورنكم الخنازير','83','0','0');
INSERT INTO [t9313] VALUES (84,'الجاهر بالقرآن كالجاهر بالصدقة، والمسر بالقرآن كالمسر بالصدقة','84','0','0');
INSERT INTO [t9313] VALUES (85,'اللهم فاطر السماوات والأرض، عالم الغيب والشهادة، لا إله إلا أنت، رب كل شيء ومليكه، أعوذ','85','0','0');
INSERT INTO [t9313] VALUES (86,'لا تقوم الساعة حتى لا تنطح ذات قرن جماء','86','0','0');
INSERT INTO [t9313] VALUES (87,'من ذا الذي يقرض الله قرضا حسنا فيضاعفه له} [البقرة: ٢٤٥] قال أبو الدحداح الأنصاري: يا','87','0','0');
INSERT INTO [t9313] VALUES (88,'العبد ليعمل الزمن الطويل من عمره، أو أكثره، أو كله بعمل أهل الجنة، وإنه لمكتوب عند الله','88','0','0');
INSERT INTO [t9313] VALUES (89,'ينبغي لأهل القدر أن يتقدم إليهم فيما أحدثوا من القدر، فإن كفوا، وإلا استلت ألسنتهم من','89','0','0');
INSERT INTO [t9313] VALUES (90,'إياكم والظلم، فإن الظلم ظلمات يوم القيامة، وإياكم والفحش، فإن الله عز وجل لا يحب الفحش','90','0','0');
INSERT INTO [t9313] VALUES (91,'وا نبياه، وا خليلاه، وا صفياه','91','0','0');
INSERT INTO [t9313] VALUES (92,'أحبوا قريشا؛ فإنه من أحبهم أحبه الله تعالى','92','0','0');
INSERT INTO [t9313] VALUES (93,'خيرت بين الشفاعة وبين أن يدخل شطر أمتي الجنة، فاخترت الشفاعة؛ لأنها أعم وأكفى، أترونها','93','0','0');
INSERT INTO [t9313] VALUES (94,'أهل حين استوت به راحلته قائمة','94','0','0');
