INSERT INTO [t9323] VALUES (247,'الرحم شجنة آخذة بحجزة الرحمن تبارك وتعالى، يصل من وصلها ويقطع من قطعها','247','0','0');
INSERT INTO [t9323] VALUES (248,'من أطاعني فقد أطاع الله، ومن عصاني فقد عصى الله، ومن أطاع أميري فقد أطاعني، ومن عصى أميري','248','0','0');
INSERT INTO [t9323] VALUES (249,'من الشعر حكمة','249','0','0');
INSERT INTO [t9323] VALUES (250,'أجب عن رسول الله صلى الله عليه وسلم اللهم أيده بروح القدس قال: اللهم','250','0','0');
INSERT INTO [t9323] VALUES (251,'ما من غازية تغزو في سبيل الله فيصيبون غنيمة إلا تعجلوا ثلثي أجرهم من الأجر ويبقي لهم','251','0','0');
INSERT INTO [t9323] VALUES (252,'لا ترغبوا عن آبائكم، فمن رغب عن أبيه فإنه كفر','252','0','0');
INSERT INTO [t9323] VALUES (253,'من سمع رجلا ينشد ضالة في المسجد فليقل له: لا أداها الله إليك، فإن المساجد لم تبن','253','0','0');
INSERT INTO [t9323] VALUES (254,'خير الأصحاب عند الله عز وجل خيرهم لصاحبه، وخير الجيران عند الله عز وجل خيرهم','254','0','0');
INSERT INTO [t9323] VALUES (255,'الدنيا كلها متاع، وخير متاع الدنيا المرأة الصالحة','255','0','0');
INSERT INTO [t9323] VALUES (256,'لو أنكم لا تذنبون لجاء الله عز وجل بقوم يذنبون فيغفر لهم','256','0','0');
INSERT INTO [t9323] VALUES (257,'لو أنكم تخطئون حتى تبلغ خطيئة أحدكم السماء، ثم تتوبون لتاب الله عز وجل','257','0','0');
INSERT INTO [t9323] VALUES (258,'سيأتي على المسلمين زمان يبعث منهم البعث فيقولون: انظروا هل فيكم من أصحاب رسول الله صلى','258','0','0');
INSERT INTO [t9323] VALUES (259,'سيأتي على الناس يوم ولو سمعوا بالرجل من أصحاب رسول الله صلى الله عليه وسلم من وراء البحور','259','0','0');
INSERT INTO [t9323] VALUES (260,'أكل ولدك نحلت؟ ، قال: فقلت: لا، فقال النبي صلى الله عليه وسلم: فلا قال لنا أبو يحيى: سمعت','260','0','0');
INSERT INTO [t9323] VALUES (261,'ثلث القرآن أو تعدله','261','0','0');
INSERT INTO [t9323] VALUES (262,'يرخص في الكذب، إلا في ثلاث كان النبي صلى الله عليه وسلم يقول: لا أعدهن كذبا: الرجل يصلح','262','0','0');
INSERT INTO [t9323] VALUES (263,'هممت أن أنهى عن الغيلة، فنظرت في الروم وفارس، فإذا هم يغيلون أولادهم، ولا يضر ذلك أولادهم','263','0','0');
INSERT INTO [t9323] VALUES (264,'أيام أكل وشرب على عهد رسول الله صلى الله عليه وسلم فقلت: من هذا؟ قالوا: علي بن أبي','264','0','0');
INSERT INTO [t9323] VALUES (265,'من استطاع منكم أن يموت بالمدينة فليمت، فإنه لن يموت بها أحد إلا كنت له شفيعا أو شهيدا يوم','265','0','0');
INSERT INTO [t9323] VALUES (266,'لو رأيته لقلت: الشمس طالعة','266','0','0');
INSERT INTO [t9323] VALUES (267,'الدنيا خضرة حلوة، وإن رجالا يتخوضون في مال الله بغير حق لهم النار يوم','267','0','0');
INSERT INTO [t9323] VALUES (268,'ما تركنا عهد نبينا صلى الله عليه وسلم ولا قطعنا أرحامنا حتى قمت أنت وأصحابك بين','268','0','0');
INSERT INTO [t9323] VALUES (269,'ما أذن الله عز وجل لشيء ما أذن لنبي حسن الصوت أن يتغنى بالقرآن','269','0','0');
INSERT INTO [t9323] VALUES (270,'تدعها فلا تحنث، ولا تحنث صاحبك','270','0','0');
INSERT INTO [t9323] VALUES (271,'أما أبو جهم فأخاف عليك من قشقاشته للعصا، وأما معاوية فرجل أخلق من المال، فتزوجت أسامة بن','271','0','0');
INSERT INTO [t9323] VALUES (272,'امكثي في بيتك حتى يبلغ الكتاب أجله، ثم إن عثمان بعثت إليه امرأة تسأله أن تنتقل من بيت','272','0','0');
INSERT INTO [t9323] VALUES (273,'يعتكف في العشر الأواخر من شهر رمضان، ثم لم يزل ذلك حتى توفاه الله عز','273','0','0');
INSERT INTO [t9323] VALUES (274,'التمسوا ليلة القدر في العشر الأواخر من شهر رمضان','274','0','0');
INSERT INTO [t9323] VALUES (275,'إذا ركع ركعتي الفجر اضطجع على شقه الأيمن','275','0','0');
INSERT INTO [t9323] VALUES (276,'أريت ليلة القدر ثم أنسيتها فالتمسوها في العشر الأواخر في تاسعه أو سابعه أو خامسه أو ثالثه','276','0','0');
INSERT INTO [t9323] VALUES (277,'أنا بعثت معك ضاغطا؟ قال: لم أجد شيئا أعتذر به إليها إلا ذلك، فضحك عمر وأعطاه شيئا، فقال:','277','0','0');
INSERT INTO [t9323] VALUES (278,'أخوف ما أتخوف عليكم ما يخرج لكم من بركات الأرض فقال رجل - ممن سمع ذلك من النبي صلى الله','278','0','0');
INSERT INTO [t9323] VALUES (279,'نعم المقبرة هذه يعني مقبرة مكة','279','0','0');
INSERT INTO [t9323] VALUES (280,'أردف أختك فأعمرها من التنعيم، فإذا هبطت بها من الأكمة فمرها فلتحرم فإنها عمرة','280','0','0');
INSERT INTO [t9323] VALUES (281,'إذا وقع الذباب في الإناء فاغمسوه كله فإن في أحد جناحيه شفاء، وفي الآخر داء، إنه يتقي','281','0','0');
INSERT INTO [t9323] VALUES (282,'عمرتان في كل شهر حسن','282','0','0');
INSERT INTO [t9323] VALUES (283,'من يتق الله يدخل الجنة ينعم، ولا يبؤس، ويخلد لا يموت، لا تبلى ثيابه ولا يفنى','283','0','0');
INSERT INTO [t9323] VALUES (284,'بعثت لأتمم صالح الأخلاق','284','0','0');
INSERT INTO [t9323] VALUES (285,'أوقفوني على الموضع الذي اعتمرت منه عائشة، فأوقفوني على موضع مسجدي فبنيته قال أبو يحيى:','285','0','0');
INSERT INTO [t9323] VALUES (286,'صلاة في المسجد الحرام مائة ألف صلاة وصلاة في مسجدي ألف صلاة وصلاة في بيت المقدس خمسمائة','286','0','0');
