INSERT INTO [t9348] VALUES (1,'مقدمة المؤلف','1','0','0');
INSERT INTO [t9348] VALUES (2,'باب الاختلاف من جهة المباح','16','0','0');
INSERT INTO [t9348] VALUES (3,'باب القراءة في الصلاة','21','0','0');
INSERT INTO [t9348] VALUES (4,'باب في التشهد','25','0','0');
INSERT INTO [t9348] VALUES (5,'باب في الوتر حدثنا الربيع قال: قال الشافعي: وقد سمعت أن النبي صلى الله عليه وسلم أوتر أول الليل وآخره في حديث يثبت مثله، وحديث دونه، وذلك مما وصفت من المباح له أن يوتر في الليل كله، ونحن نبيح في المكتوبة أن يصلي في أول الوقت وآخره، وهذا في الوتر','27','0','0');
INSERT INTO [t9348] VALUES (6,'باب سجود القرآن','29','0','0');
INSERT INTO [t9348] VALUES (7,'باب القصر والإتمام في السفر في الخوف وغير الخوف حدثنا الربيع قال: قال الشافعي: قال الله جل ثناؤه: وإذا ضربتم في الأرض فليس عليكم جناح أن تقصروا من الصلاة. قال الشافعي: وكان بينا في كتاب الله أن القصر في السفر في الخوف وغير الخوف معا رخصة من الله؛','33','0','0');
INSERT INTO [t9348] VALUES (8,'باب الخلاف في ذلك أخبرنا الربيع قال: قال الشافعي رضي الله عنه: قال لي بعض الناس: من أتم في السفر فسدت صلاته؛ لأن أصل فرض الصلاة في السفر ركعتان، إلا أن يجلس قدر التشهد في مثنى، فيكون ذلك كالقطع للصلاة، أو يدرك مقيما يأتم به في صلاته قبل أن يسلم','37','0','0');
INSERT INTO [t9348] VALUES (9,'باب الفطر والصوم في السفر حدثنا الربيع قال: قال الشافعي: قال الله جل جلاله في فرض الصوم: شهر رمضان الذي أنزل فيه القرآن هدى للناس وبينات من الهدى والفرقان فمن شهد منكم الشهر فليصمه ومن كان مريضا أو على سفر فعدة من أيام أخر، فكان بينا في الآية أنه فرض','40','0','0');
INSERT INTO [t9348] VALUES (10,'باب قتل الأسارى، والمفاداة بهم، والمن عليهم','49','0','0');
INSERT INTO [t9348] VALUES (11,'باب الماء من الماء','52','0','0');
INSERT INTO [t9348] VALUES (12,'باب الخلاف في أن الغسل لا يجب إلا بخروج الماء حدثنا الربيع قال: قال الشافعي: فخالفنا بعض أصحاب الحديث من أهل ناحيتنا وغيرهم فقالوا: لا يجب على الرجل إذا بلغ من امرأته ما شاء الغسل حتى يأتي منه الماء الدافق، واحتج فيه بحديث أبي بن كعب وغيره مما يوافقه','60','0','0');
INSERT INTO [t9348] VALUES (13,'باب التيمم حدثنا الربيع قال: قال الشافعي رضي الله عنه: نزلت آية التيمم في غزوة بني المصطلق، انحل عقد لعائشة، فأقام الناس على التماسه مع رسول الله صلى الله عليه وسلم، وليسوا على ماء، وليس معهم ماء، فأنزل الله آية التيمم. أخبرنا بذلك عدد من أهل','62','0','0');
INSERT INTO [t9348] VALUES (14,'باب صلاة الإمام جالسا ومن خلفه قياما حدثنا الربيع قال: قال الشافعي: إذا لم يقدر الإمام على القيام فصلى بالناس جالسا، صلى الناس وراءه إذا قدروا على القيام قياما، كما يصلي هو قائما، ويصلي من خلفه إذا لم يقدروا على القيام جلوسا، فيصلي كل فرضه، وقد','68','0','0');
INSERT INTO [t9348] VALUES (15,'باب صوم يوم عاشوراء','72','0','0');
INSERT INTO [t9348] VALUES (16,'باب الطهارة بالماء حدثنا الربيع قال: قال الشافعي رضي الله عنه: قال الله تعالى: وأنزلنا من السماء ماء طهورا، وقال في الطهارة: فلم تجدوا ماء فتيمموا صعيدا طيبا، فدل على أن الطهارة بالماء كله.','81','0','0');
INSERT INTO [t9348] VALUES (17,'باب الساعات التي تكره فيها الصلاة','88','0','0');
INSERT INTO [t9348] VALUES (18,'باب الخلاف في هذا الباب حدثنا الربيع قال: قال الشافعي: فخالفنا بعض أهل ناحيتنا وغيرهم فقال: يصلى على الجنائز بعد العصر وبعد الصبح ما لم تقارب الشمس أن تطلع، وما لم تتغير الشمس، واحتج في ذلك بشيء رواه عن ابن عمر، يشبه بعض ما قال. قال الشافعي: وابن','99','0','0');
INSERT INTO [t9348] VALUES (19,'باب أكل الضب','104','0','0');
INSERT INTO [t9348] VALUES (20,'باب المجمل والمفسر حدثنا الربيع قال: قال الشافعي: قال الله عز وجل فإذا انسلخ الأشهر الحرم فاقتلوا المشركين حيث وجدتموهم، وقال الله جل ثناؤه وقاتلوهم حتى لا تكون فتنة ويكون الدين كله لله.','107','0','0');
INSERT INTO [t9348] VALUES (21,'باب الخلاف فيمن تؤخذ منه الجزية وفيمن دان دين أهل الكتاب قبل نزول القرآن حدثنا الربيع قال: قال الشافعي: فخالفنا بعض الناس فقال: تؤخذ الجزية من أهل الكتاب، وممن دان دين أهل الأوثان ما كان، إلا أنها لا تؤخذ من العرب خاصة إذا دانوا دين أهل الأوثان،','114','0','0');
INSERT INTO [t9348] VALUES (22,'باب في المرور بين يدي المصلي','116','0','0');
INSERT INTO [t9348] VALUES (23,'باب خروج النساء إلى المساجد','118','0','0');
INSERT INTO [t9348] VALUES (24,'باب غسل الجمعة حدثنا الربيع قال: قال الشافعي: قال الله جل ثناؤه إذا قمتم إلى الصلاة فاغسلوا وجوهكم وأيديكم إلى المرافق وامسحوا برءوسكم وأرجلكم، قال: فدلت السنة على أن الوضوء من الحدث، وقال الله جل ثناؤه: لا تقربوا الصلاة وأنتم سكارى حتى تعلموا ما','124','0','0');
INSERT INTO [t9348] VALUES (25,'باب نكاح البكر','129','0','0');
INSERT INTO [t9348] VALUES (26,'باب النجش','133','0','0');
INSERT INTO [t9348] VALUES (27,'باب في بيع الرجل على بيع أخيه','136','0','0');
INSERT INTO [t9348] VALUES (28,'باب بيع الحاضر للبادي','140','0','0');
