INSERT INTO [t9348] VALUES (33,'باب المختلفات التي يوجد على ما يؤخذ منها دليل على غسل القدمين ومسحهما حدثنا الربيع قال: قال الشافعي: نحن نقرأ آية الوضوء فاغسلوا وجوهكم وأيديكم إلى المرافق وامسحوا برءوسكم وأرجلكم إلى الكعبين بنصب (أرجلكم) على معنى: فاغسلوا وجوهكم وأيديكم وأرجلكم','156','0','0');
INSERT INTO [t9348] VALUES (34,'باب الإسفار والتغليس بالفجر','159','0','0');
INSERT INTO [t9348] VALUES (35,'باب رفع الأيدي في الصلاة','162','0','0');
INSERT INTO [t9348] VALUES (36,'باب الخلاف فيه حدثنا الربيع قال: قال الشافعي: فخالفنا بعض الناس في رفع اليدين في الصلاة، فقال: إذا افتتح الصلاة المصلي رفع يديه حتى يحاذي أذنيه، ثم لا يعود يرفعهما في شيء من الصلاة، واحتج بحديث رواه يزيد بن أبي زياد، عن عبد الرحمن بن أبي ليلى، عن','165','0','0');
INSERT INTO [t9348] VALUES (37,'باب صلاة المنفرد','167','0','0');
INSERT INTO [t9348] VALUES (38,'باب المختلفات التي يوجد على ما يؤخذ منها دليل على صلاة الخوف حدثنا الربيع قال: قال الشافعي: قال الله جل ثناؤه في صلاة الخوف وإذا كنت فيهم فأقمت لهم الصلاة','171','0','0');
INSERT INTO [t9348] VALUES (39,'باب صلاة كسوف الشمس والقمر','173','0','0');
INSERT INTO [t9348] VALUES (40,'باب الخلاف في ذلك حدثنا الربيع قال: قال الشافعي: فخالفنا في ذلك بعض الناس في صلاة الكسوف، فقال: يصلي في كسوف الشمس والقمر ركعتين، كما يصلي الناس في كل يوم، وليس في كل ركعة ركوعان. قال الشافعي: فذكرت له بعض حديثنا، فقال: هذا ثابت، وإنما أخذنا','178','0','0');
INSERT INTO [t9348] VALUES (41,'باب من أصبح جنبا في شهر رمضان','182','0','0');
INSERT INTO [t9348] VALUES (42,'باب الحجامة للصائم','186','0','0');
INSERT INTO [t9348] VALUES (43,'باب نكاح المحرم','189','0','0');
INSERT INTO [t9348] VALUES (44,'باب ما يكره في الربا من الزيادة في البيوع','196','0','0');
INSERT INTO [t9348] VALUES (45,'باب من أقيم عليه حد في شيء أربع مرات ثم عاد له حدثنا الربيع، قال: أخبرنا الشافعي، قال: أخبرنا محمد بن إسماعيل، عن ابن أبي ذئب، عن الحارث بن عبد الرحمن، عن أبي سلمة، عن أبي هريرة، أن رسول الله صلى الله عليه وسلم قال: وذكر " فاجلدوه "، وذكر','202','0','0');
INSERT INTO [t9348] VALUES (46,'باب لحوم الضحايا','206','0','0');
INSERT INTO [t9348] VALUES (47,'باب العقوبات في المعاصي قال الشافعي: كانت العقوبات في المعاصي قبل أن ينزل الحد، ثم نزلت الحدود، ونسخت العقوبات فيما فيه الحدود','209','0','0');
INSERT INTO [t9348] VALUES (48,'باب نكاح المتعة','215','0','0');
INSERT INTO [t9348] VALUES (49,'باب الخلاف في نكاح المتعة حدثنا الربيع قال: قال الشافعي: فخالفنا مخالفون في نكاح المتعة، فقال بعضهم: النهي عن نكاح المتعة عام خيبر على أنهم استمتعوا من يهوديات في دار الشرك، فكره ذلك لهم لا على تحريمه؛ لأن الناس استمتعوا عام الفتح في حديث عبد','218','0','0');
INSERT INTO [t9348] VALUES (50,'باب في الجنائز','220','0','0');
INSERT INTO [t9348] VALUES (51,'باب الشفعة','223','0','0');
INSERT INTO [t9348] VALUES (52,'باب في بكاء الحي على الميت','228','0','0');
INSERT INTO [t9348] VALUES (53,'باب استقبال القبلة للغائط والبول','231','0','0');
INSERT INTO [t9348] VALUES (54,'باب الصلاة في الثوب ليس على عاتق المرء منه شيء','234','0','0');
INSERT INTO [t9348] VALUES (55,'باب الكلام في الصلاة','237','0','0');
INSERT INTO [t9348] VALUES (56,'باب الخلاف في الكلام في الصلاة ساهيا حدثنا الربيع قال: قال الشافعي: فخالفنا بعض الناس في الكلام في الصلاة، وجمع علينا فيها حججا ما جمعها علينا في شيء غيره إلا في اليمين مع الشاهد، ومسألتين أخريين. قال الشافعي: فسمعته يقول: حديث ذي اليدين حديث ثابت','242','0','0');
INSERT INTO [t9348] VALUES (57,'باب القنوت في الصلوات كلها','245','0','0');
INSERT INTO [t9348] VALUES (58,'باب الطيب للإحرام','248','0','0');
INSERT INTO [t9348] VALUES (59,'باب الخلاف في تطيب المحرم للإحرام حدثنا الربيع قال: قال الشافعي: فخالفنا بعض أهل ناحيتنا في الطيب قبل الإحرام، وبعد الرمي والحلاق، وقبل طواف الزيارة، فقال: لا يتطيب بما يبقى ريحه عليه، ولا بأس أن يدهن قبل الإحرام بما لا يبقى ريحه عليه، وإن بقي','256','0','0');
INSERT INTO [t9348] VALUES (60,'باب ما يأكل المحرم من الصيد','259','0','0');
INSERT INTO [t9348] VALUES (61,'باب خطبة الرجل على خطبة أخيه','263','0','0');
