INSERT INTO [t9348] VALUES (64,'باب في طلاق الثلاث المجموعة','278','0','0');
INSERT INTO [t9348] VALUES (65,'باب طلاق الحائض','284','0','0');
INSERT INTO [t9348] VALUES (66,'باب بيع الرطب باليابس من الطعام','288','0','0');
INSERT INTO [t9348] VALUES (67,'باب الخلاف في العرايا حدثنا الربيع قال: قال الشافعي: ولم يجد الذين يظهرون القول بالحديث في شيء من الأحاديث من الشبه ما وجدوا في المجمل مع المفسر، وذلك أنهم يلقون بهما قوما من أهل الحديث ليس لهم بصر بمذاهبه، فيشبهون عليهم، وقد ذكرنا بعض ما يدل على ما','295','0','0');
INSERT INTO [t9348] VALUES (68,'باب بيع الطعام','296','0','0');
INSERT INTO [t9348] VALUES (69,'باب المصراة: الخراج بالضمان','302','0','0');
INSERT INTO [t9348] VALUES (70,'باب الخلاف في المصراة حدثنا الربيع قال: قال الشافعي: فخالفنا بعض الناس في المصراة فقال: الحديث فيها ثابت، ولكن الناس كلهم تركوه، فقلت له: أفتحكي لي عن أحد من أصحاب رسول الله أنه تركه؟ قال: لا، قلت: فأنت تحكي عن ابن مسعود أنه قال فيها مثل معنى','306','0','0');
INSERT INTO [t9348] VALUES (71,'باب كسب الحجام','308','0','0');
INSERT INTO [t9348] VALUES (72,'باب الدعوى والبينات','314','0','0');
INSERT INTO [t9348] VALUES (73,'باب الخلاف في هذه الأحاديث حدثنا الربيع قال: قال الشافعي رضي الله عنه: فخالفنا بعض الناس في هذه الأحاديث، فجرد خلاف حديث اليمين مع الشاهد، وخالف بعض معنى البينة على المدعي، واليمين على المدعى عليه، وقد كتبت عليه فيها حججا اختصرت في هذا الكتاب بعضها','319','0','0');
INSERT INTO [t9348] VALUES (74,'باب المختلفات التي لا يثبت بعضها: من مات ولم يحج، أو كان عليه نذر','321','0','0');
INSERT INTO [t9348] VALUES (75,'باب المختلفات التي لا يثبت بعضها: من أعتق شركا له في عبد','323','0','0');
INSERT INTO [t9348] VALUES (76,'باب الخلاف في هذا الباب حدثنا الربيع قال: قال الشافعي: وخالف مذهبنا في هذا بعض الناس، فزعم أن الرجل إذا أعتق شركا له في عبد، فشريكه بالخيار بين أن يعتق، أو يضمنه، أو يستسعى العبد، فخالفه أصحابه وعابوا هذا القول عليه فقالوا: إذا كان المعتق للشقص','328','0','0');
INSERT INTO [t9348] VALUES (77,'باب قتل المؤمن بالكافر','331','0','0');
INSERT INTO [t9348] VALUES (78,'باب الخلاف في قتل المؤمن بالكافر قال الشافعي: وبهذا نأخذ، وهو ثابت عندنا عن رسول الله صلى الله عليه وسلم ببعض ما حكيت: " ولا يقتل حر بعبد، ولا مؤمن بكافر ". حدثنا الربيع قال: قال الشافعي: فخالفنا بعض الناس فقال: إذا قتل المؤمن الكافر الحر، أو','334','0','0');
INSERT INTO [t9348] VALUES (79,'باب جرح العجماء جبار','337','0','0');
INSERT INTO [t9348] VALUES (80,'باب المختلفات التي عليها دلالة','341','0','0');
