INSERT INTO [t934] VALUES (1,'الغلاف','1','0','0');
INSERT INTO [t934] VALUES (2,'مقدمة','2','0','0');
INSERT INTO [t934] VALUES (3,'من قوله (هذا ذكر بيان عقيدة أهل السنة والجماعة) إلى قوله (وما يعتقدون من أصول الدين)','4','0','0');
INSERT INTO [t934] VALUES (4,'[المسألة الأولى: معنى العقيدة]','4','3','0');
INSERT INTO [t934] VALUES (5,'[المسألة الثانية: من هم أهل السنة والجماعة؟]','5','3','0');
INSERT INTO [t934] VALUES (6,'[المسألة الثالثة: أن هذه العقيدة موافقة لعقائد السلف إلا في مسألة الإيمان]','6','3','0');
INSERT INTO [t934] VALUES (7,'[المسألة الرابعة: أن كلمة أصول الدين يعبر بها عن العقيدة]','7','3','0');
INSERT INTO [t934] VALUES (8,'قوله: نقول في توحيد الله -معتقدين بتوفيق الله- إن الله واحد لا شريك له','8','0','0');
INSERT INTO [t934] VALUES (9,'الأسئلة','12','0','0');
INSERT INTO [t934] VALUES (10,'قوله: ولا شيء مثله، ولا شيء يعجزه، ولا إله غيره','13','0','0');
INSERT INTO [t934] VALUES (11,'[المسألة الأولى: أن قوله (ولا شيء مثله) مأخوذ من قول الله {ليس كمثله شيء وهو السميع البصير} ]','14','10','0');
INSERT INTO [t934] VALUES (12,'[المسألة الثانية: أن قوله (لا شيء مثله) راجع لنفي المماثلة وليس المشابهة.]','15','10','0');
INSERT INTO [t934] VALUES (13,'[المسألة الثالثة: ليس كل أنواع مشابهة الله لخلقه منفية إذ أن الاشتراك في أصل معنى الاتصاف غير منفي]','16','10','0');
INSERT INTO [t934] VALUES (14,'[المسألة الرابعة: أن أهل السنة عندهم النفي مجملا والإثبات مفصلا والعكس عند أهل البدع]','17','10','0');
INSERT INTO [t934] VALUES (15,'[المسألة الخامسة: معنى الكاف في قوله (ليس كمثله شيء) ]','18','10','0');
INSERT INTO [t934] VALUES (16,'قوله (ولا شيء يعجزه)','19','0','0');
INSERT INTO [t934] VALUES (17,'[المسألة الأولى: أن نفي العجز عن الله في قوله (ولا شيء يعجزه) مأخوذ من قول الله ?وما كان الله ليعجزه من شيء? وذلك لكمال علمه وقدرته]','20','16','0');
INSERT INTO [t934] VALUES (18,'[المسالة الثانية: أن النفي إذا كان في الكتاب والسنة فإنه لا يراد به حقيقة النفي، وإنما يراد به كمال ضده]','21','16','0');
INSERT INTO [t934] VALUES (19,'[المسألة الثالثة: أن التمثيل عن العام ببعض أفراده في توحيد الربوبية صحيح]','22','16','0');
INSERT INTO [t934] VALUES (20,'[المسالة الرابعة: أن دلالة قول الله (وما كان الله ليعجزه من شيء) أبلغ على النفي من قول المصنف (ولا شيء يعجزه) ]','23','16','0');
INSERT INTO [t934] VALUES (21,'قوله (ولا إله غيره)','24','0','0');
INSERT INTO [t934] VALUES (22,'[المسألة الأولى: أن قوله (ولا إله غيره) مطابق لكلمة التوحيد (لا إله إلا الله) ]','25','21','0');
INSERT INTO [t934] VALUES (23,'[المسألة الثانية: في معنى كلمة الإله]','26','21','0');
INSERT INTO [t934] VALUES (24,'[المسألة الثالثة: في معنى كلمة التوحيد (لا إله إلا الله) ]','27','21','0');
INSERT INTO [t934] VALUES (25,'[المسألة الرابعة: في إعراب كلمة التوحيد (لا إله إلا الله) ]','28','21','0');
INSERT INTO [t934] VALUES (26,'[المسألة الخامسة: أن توحيد العبادة لله عز وجل لا يستقيم إلا بشيئين كما ذكرنا: بنفي وبإثبات]','29','21','0');
INSERT INTO [t934] VALUES (27,'الأسئلة:','30','0','0');
INSERT INTO [t934] VALUES (28,'قوله: قديم بلا ابتداء دائم بلا انتهاء','31','0','0');
INSERT INTO [t934] VALUES (29,'[المسألة الأولى: أن إدراج اسم القديم في أسماء الله هذا غلط، ولا يجوز، وذلك لأمور]','32','28','0');
INSERT INTO [t934] VALUES (30,'[المسألة الثانية: ما ضابط كون الاسم من الأسماء الحسنى؟]','33','28','0');
INSERT INTO [t934] VALUES (31,'[المسألة الثالثة: أن الله أوليته عند أهل السنة في ذاته وفي صفاته، وآخر سبحانه في ذاته وفي صفاته]','35','28','0');
INSERT INTO [t934] VALUES (32,'قوله: لا يفنى ولا يبيد','36','0','0');
INSERT INTO [t934] VALUES (33,'قوله: ولا يكون إلا ما يريد','37','0','0');
INSERT INTO [t934] VALUES (34,'[المسألة الأولى: أن إرادة الله منقسمة إلى: إرداة كونية وإرادة شرعية]','38','33','0');
INSERT INTO [t934] VALUES (35,'[المسألة الثانية: أن إرداة الله موافقة للحكمة وأما إرادة العبد فقد توافق الحكمة وقد لا توافقها]','39','33','0');
INSERT INTO [t934] VALUES (36,'قوله: لا تبلغه الأوهام، ولا تدركه الأفهام','40','0','0');
INSERT INTO [t934] VALUES (37,'[المسألة الأولى: نظرية حصول المعارف]','41','36','0');
INSERT INTO [t934] VALUES (38,'[المسألة الثانية: الرب عز وجل لا يمكن تخيله، ولا يمكن أيضا أن يفكر فيه فيدرك]','42','36','0');
INSERT INTO [t934] VALUES (39,'الأسئلة','43','0','0');
INSERT INTO [t934] VALUES (40,'قوله: لا تبلغه الأوهام، ولا تدركه الأفهام، ولا يشبه الأنام، حي لا يموت، قيوم لا ينام، خالق بلا حاجة، رازق بلا مؤنة','45','0','0');
INSERT INTO [t934] VALUES (41,'قوله: حي لا يموت، قيوم لا ينام','46','0','0');
INSERT INTO [t934] VALUES (42,'[المسألة الأولى: اشتراك المخلوقات مع الرب في اسم الحي وفي صفة الحياة هذا اشتراك في أصل المعنى]','47','41','0');
INSERT INTO [t934] VALUES (43,'[المسألة الثانية: وصف الرب بالنفي ليس مقصودا لذاته وإنما هو لإثبات كمال ضد ما نفى]','48','41','0');
INSERT INTO [t934] VALUES (44,'[المسألة الثالثة: تفسير معنى الحي ومعنى القيوم وذكر آثارهما]','49','41','0');
INSERT INTO [t934] VALUES (45,'[المسألة الرابعة: اسم الحي واسم القيوم بلازمهما تدل على بقية صفات الرب]','50','41','0');
INSERT INTO [t934] VALUES (46,'قوله: خالق بلا حاجة، رازق بلا مؤنة','51','0','0');
INSERT INTO [t934] VALUES (47,'الأسئلة','53','0','0');
INSERT INTO [t934] VALUES (48,'من قوله (خالق بلا حاجة، رازق بلا مؤنة، مميت بلا مخافة) إلى قوله (لا يحتاج إلى شيء)','54','0','0');
INSERT INTO [t934] VALUES (49,'قوله: مميت بلا مخافة، باعث بلا مشقة','55','0','0');
INSERT INTO [t934] VALUES (50,'[المسألة الأولى: المميت اسم كمال مع قرينه المحيي، والمميت صفة كمال مع قرينتها المحيي]','56','49','0');
INSERT INTO [t934] VALUES (51,'[المسألة الثانية: الموت عند جمهور أهل السنة ومن وافقهم من غيرهم مخلوق موجود]','57','49','0');
INSERT INTO [t934] VALUES (52,'[المسألة الثالثة: أن إماتة الرب متعلقة بكل شيء، إلا ما استثنى الله عز وجل]','58','49','0');
INSERT INTO [t934] VALUES (53,'من قوله (لم يزدد بكونهم شيئا) إلى قوله (وكما أنه محيي الموتى بعدما أحيا، استحق هذا الاسم قبل إحيائهم)','59','0','0');
INSERT INTO [t934] VALUES (54,'[المسألة الأولى: أن الرب أول بصفاته، وصفاته قديمة وأن صفات الرب لابد أن تظهر آثارها]','60','53','0');
INSERT INTO [t934] VALUES (55,'[المسألة الثانية: أن الطحاوي كأنه يميل إلى قول الماتريدية في أن الرب كان متصفا بالصفات وله الأسماء، ولكن لم تظهر آثار صفاته ولا آثار أسمائه بل كان زمنا طويلا طويلا معطلا عن الأفعال]','62','53','0');
INSERT INTO [t934] VALUES (56,'[المسألة الثالثة: مسألة التسلسل]','63','53','0');
INSERT INTO [t934] VALUES (57,'[المسألة الرابعة: أن أهل السنة يعبرون بقول (كان الله بصفاته أزليا) فيأتون بالباء المقتضية للمصاحبة وأما المعتزلة وأشباههم فيعبرون بالواو فيقولون (الله وصفته) ]','64','53','0');
INSERT INTO [t934] VALUES (58,'[المسألة الخامسة: أن أهل السنة يجعلون قدرة الرب متعلقة بكل شيء أما أهل البدع فيعلقون القدرة بما يشاؤه الرب]','65','53','0');
INSERT INTO [t934] VALUES (59,'قوله: خلق الخلق بعلمه وقدر لهم أقدارا','66','0','0');
INSERT INTO [t934] VALUES (60,'[المسألة الأولى: تعريف القدر لغة وشرعا]','67','59','0');
INSERT INTO [t934] VALUES (61,'[المسألة الثانية: أسباب الضلال في باب القدر، وأن من صنف في القدر يجب أن تنظر إلى كلامه على أنه قابل للأخذ والرد إذا دخل في أمر عقلي لا دليل عليه]','68','59','0');
INSERT INTO [t934] VALUES (62,'[المسألة الثالثة: الفرق في هذا الباب المنتسبة للأمة ثلاث فرق: القدرية والجبرية وأهل السنة والجماعة]','70','59','0');
INSERT INTO [t934] VALUES (63,'[المسألة الرابعة: أن هناك ألفاظا تستعملها الطوائف جميعا في مبحث القدر، ولكن لكل طائفة قصد ومصطلح في استعمالها مثل لفظ (الكسب) و (نفوذ المشيئة) ]','71','59','0');
INSERT INTO [t934] VALUES (64,'قوله: وضرب لهم آجالا','72','0','0');
INSERT INTO [t934] VALUES (65,'قوله: ولم يخف عليه شيء قبل أن يخلقهم. وعلم ما هم عاملون قبل أن يخلقهم','73','0','0');
INSERT INTO [t934] VALUES (66,'الأسئلة','74','0','0');
INSERT INTO [t934] VALUES (67,'قوله: وإن محمدا عبده المصطفى، ونبيه المجتبى، ورسوله المرتضى','77','0','0');
INSERT INTO [t934] VALUES (68,'[المسألة الأولى: تعريف النبي لغة واصطلاحا والفرق بين النبي والرسول]','78','67','0');
INSERT INTO [t934] VALUES (69,'[المسألة الثانية: ذكر أقوال الطوائف في كون نبوة الأنبياء هل هي واجبة أو ممكنة؟]','80','67','0');
INSERT INTO [t934] VALUES (70,'[المسألة الثالثة: نبوة الأنبياء أو رسالة الرسل بما تحصل؟ وكيف يعرف صدقهم؟ وما الفرق ما بين النبي والرسول وبين عامة الناس أو من يدعي أنه نبي أو رسول]','81','67','0');
INSERT INTO [t934] VALUES (71,'[المسألة الرابعة: الأنبياء يجوز في حقهم الأمراض والعاهات، والتفصيل في مسألة الذنوب بالنسبة للأنبياء]','84','67','0');
INSERT INTO [t934] VALUES (72,'[المسألة الخامسة: الرسول والنبي فيهم شروط أو أوصاف عامة جاءت في القرآن والسنة]','85','67','0');
INSERT INTO [t934] VALUES (73,'الأسئلة','86','0','0');
INSERT INTO [t934] VALUES (74,'الأسئلة ٢','88','0','0');
INSERT INTO [t934] VALUES (75,'قوله: وإنه خاتم الأنبياء','89','0','0');
INSERT INTO [t934] VALUES (76,'[المسألة الأولى: ذكر أوجه استدلال العلماء بآية {ولكن رسول الله وخاتم النبيين} بفتح التاء، على أن النبي محمد هو خاتم الأنبياء والمرسلين]','90','75','0');
INSERT INTO [t934] VALUES (77,'[المسألة الثانية: أن من الأفراد المنتسبين إلى الفلسفة وإلى الصوفية الغالية من قال إن النبوة مكتسبة]','91','75','0');
INSERT INTO [t934] VALUES (78,'[المسألة الثالثة: من ادعى أنه يسمع كلام الله فقد ادعى أنه يوحى إليه وادعى النبوة]','92','75','0');
INSERT INTO [t934] VALUES (79,'[المسألة الرابعة: أن ادعاء الوحي كفر كدعوى النبوة، وهذا باتفاق أهل السنة]','93','75','0');
INSERT INTO [t934] VALUES (80,'[المسألة الخامسة: أن ختم النبوة وكون النبي خاتم الأنبياء لا يعارض نزول عيسى عليه السلام في آخر الزمان]','94','75','0');
INSERT INTO [t934] VALUES (81,'قوله: وإمام الأتقياء','95','0','0');
INSERT INTO [t934] VALUES (82,'[المسألة الأولى: أن التفضيل بين الأنبياء جاء به النص كما قال عز وجل {تلك الرسل فضلنا بعضهم على بعض} ]','96','81','0');
INSERT INTO [t934] VALUES (83,'[المسألة الثانية: أن التفاضل بين الأنبياء إما ان يكون عاما كقول (محمد أفضل المرسلين، سيد المرسلين) أو في مقابلة نبي بذاته كقول (محمد أفضل من موسى) هذا لا يجوز]','97','81','0');
INSERT INTO [t934] VALUES (84,'[المسألة الثالثة: أوجه الجمع بين الأدلة التي تدل على عدم التفضيل بين الأنبياء وبين قوله عز وجل {تلك الرسل فضلنا بعضهم على بعض} ]','98','81','0');
INSERT INTO [t934] VALUES (85,'قوله: وحبيب رب العالمين','99','0','0');
INSERT INTO [t934] VALUES (86,'[المسألة الأولى: أن المحبة بمراتبها التي تضاف إلى رب العالمين إنما هي ما ورد، وهي: الإرداة الخاصة التي هي بمعنى المحبة، ولفظ المحبة، والمودة، والخلة]','100','85','0');
INSERT INTO [t934] VALUES (87,'[المسألة الثانية: لفظ العشق هو من مراتب المحبة؛ ولكنه يمنع في إطلاقه من العبد على ربه ومن الرب للعبد]','101','85','0');
INSERT INTO [t934] VALUES (88,'قوله: وكل دعوى النبوة بعده فغي وهوى، وهو المبعوث إلى عامة الجن وكافة الورى بالحق والهدى، وبالنور والضياء','102','0','0');
INSERT INTO [t934] VALUES (89,'[المسألة الأولى: أن الجن ليس منهم رسل]','103','88','0');
INSERT INTO [t934] VALUES (90,'[المسألة الثانية: أن بعثة النبي لا تشمل الملائكة]','104','88','0');
INSERT INTO [t934] VALUES (91,'الأسئلة','105','0','0');
INSERT INTO [t934] VALUES (92,'الأسئلة ٢','106','0','0');
INSERT INTO [t934] VALUES (93,'من قوله (وإن القرآن كلام الله) إلى قوله (ولا يشبه قول البشر)','108','0','0');
INSERT INTO [t934] VALUES (94,'[المسألة الأولى: أسباب نشأة القول بخلق القرآن ومخالفة المخالفين]','109','93','0');
INSERT INTO [t934] VALUES (95,'[المسألة الثانية: ذكر أقوال الفرق في القرآن، وكلام الله]','110','93','0');
INSERT INTO [t934] VALUES (96,'[المسألة الثالثة: أدلة أهل السنة والجماعة على اعتقادهم في القرآن]','111','93','0');
INSERT INTO [t934] VALUES (97,'[المسألة الرابعة: الرد على استدلال المخالفين في القرآن]','112','93','0');
INSERT INTO [t934] VALUES (98,'الأسئلة','115','0','0');
INSERT INTO [t934] VALUES (99,'الأسئلة٢','117','0','0');
INSERT INTO [t934] VALUES (100,'من قوله (فمن سمعه فزعم أنه كلام البشر) إلى قوله (وعلم أنه بصفاته ليس كالبشر)','119','0','0');
INSERT INTO [t934] VALUES (101,'[المسألة الأولى: لفظ (إعجاز القرآن) سمي بهذا الإسم؛ لأجل التحدي وعجز الكفار أن يأتوا بمثله]','121','100','0');
INSERT INTO [t934] VALUES (102,'[المسألة الثانية: أن كلام الله عز وجل هو المعجز ولا يقال أن الله عز وجل أعجز البشر عن الإتيان بمثل هذا القرآن]','122','100','0');
INSERT INTO [t934] VALUES (103,'[المسألة الثالثة: ذكر أقوال الناس في أوجه إعجاز القرآن، لم كان القرآن معجزا؟]','123','100','0');
INSERT INTO [t934] VALUES (104,'الأسئلة','129','0','0');
INSERT INTO [t934] VALUES (105,'الأسئلة٢','130','0','0');
INSERT INTO [t934] VALUES (106,'من قوله (والرؤية حق لأهل الجنة) إلى قوله (ورد علم ما اشتبه عليه إلى عالمه)','132','0','0');
INSERT INTO [t934] VALUES (107,'[المسألة الأولى: أن المؤمن يرى أن الإنعام عليه بأن يكون من أهل الجنة هذا أعظم الإنعام لأن من دخل الجنة قد رضي الله عنه ومتعه بملاذها وأفاض عليه الزيادة وهي رؤية وجه الله الكريم.]','133','106','0');
INSERT INTO [t934] VALUES (108,'[المسألة الثانية: أن أهل السنة والجماعة جعلوا الرؤية حق، والرؤية بالعينين، مع ذكر الأدلة]','134','106','0');
INSERT INTO [t934] VALUES (109,'[المسألة الثالثة: أهل السنة اختلفوا في رؤية الله عز وجل في الموقف: هل هي للمؤمنين وحدهم أم للمؤمنين والمنافقين أم للناس جميعا؟]','135','106','0');
INSERT INTO [t934] VALUES (110,'[المسألة الرابعة: ذكر مذاهب الفرق المخالفة في رؤية الله عز وجل في الآخرة]','136','106','0');
INSERT INTO [t934] VALUES (111,'[المسألة الخامسة: أن رؤية المؤمنين في الجنة لربهم عز وجل عامة بالإنس والجن، للرجال وللنساء، وللملائكة أيضا]','139','106','0');
INSERT INTO [t934] VALUES (112,'[المسألة السادسة: هل رأى النبي صلى الله عليه وسلم ربه أم لا؟]','140','106','0');
INSERT INTO [t934] VALUES (113,'الأسئلة','141','0','0');
INSERT INTO [t934] VALUES (114,'من قوله (لا ندخل في ذلك متأولين بآرائنا) إلى قوله (إلا على ظهر التسليم والاستسلام)','144','0','0');
INSERT INTO [t934] VALUES (115,'[المسألة الأولى: أن الناس في تلقي الشريعة انقسموا إلى ٣ أقسام: من كان عقليا محضا، ومن جعل الشريعة خالية من البرهان العقلي البتة، ومن توسط بين الفئتين]','145','114','0');
INSERT INTO [t934] VALUES (116,'[المسألة الثانية: أن البرهان الديني الشرعي يقين وأن البرهان العقلي ناقص وأن البرهان العاطفي فطري، مع التفصيل]','146','114','0');
INSERT INTO [t934] VALUES (117,'[المسألة الثالثة: أن القرآن محكم كله، والقرآن متشابه كله والقرآن محكم ومتشابه]','149','114','0');
